<div class="section-{{ section.id }} header-{{ section.id }} {% if show_type == "always" %}fixed{% elsif show_type == "scroll_up" %}scroll active fixed{% else %}top{% endif %}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
  {% if announcement_menu != blank %}  
    <div class="header-announcement-{{ section.id }}">
      <div class="section-{{ section.id }}-settings">
        <ul class="header-announcement-list-{{ section.id }}">
          {% for link in announcement_menu.links %}
            <li class="header-announcement-item-{{ section.id }}">
              <a href="{{ link.url }}" class="header-announcement-link-{{ section.id }}">{{ link.title }}</a>
            </li>
          {% endfor %}
        </ul>
      </div>
    </div>
  {% endif %}
    <div class="section-{{ section.id }}-settings">
      <div class="header-body-{{ section.id }}">
        <div class="header-logo-{{ section.id }}">
          {% if logo != blank %}
            <a href="{{ shop.url }}" class="header-logo-image-{{ section.id }} {% if logo_mobile != blank %}header-logo-desktop-{{ section.id }}{% endif %}">
              <img src="{{ logo | image_url }}" alt="{{ logo.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            </a>
            {% if logo_mobile != blank %}
              <a href="{{ shop.url }}" class="header-logo-image-{{ section.id }} header-logo-mobile-{{ section.id }}">
                <img src="{{ logo_mobile | image_url }}" alt="{{ logo_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              </a>
            {% endif %}
          {% else %}
            <a href="{{ shop.url }}" class="header-logo-text-{{ section.id }}">{{ shop.name }}</a>
          {% endif %}
        </div>

        <div class="header-menu-{{ section.id }}">
          <button class="header-menu-burger-{{ section.id }}">
            <span class="header-menu-burger-icon-{{ section.id }}"></span>
          </button>
          {% if section.blocks.size > 0 %}           
            {% if use_search and search_menu_mobile != true %}
              <a href="{{ routes.search_url }}" class="header-search-{{ section.id }} header-search-mobile-{{ section.id }}">
                <span class="header-search-icon-{{ section.id }}">
