liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign border_bottom = section.settings.border_bottom
  assign margin_top = section.settings.margin_top
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign show_type = section.settings.show_type
  assign sticky_only_header = section.settings.sticky_only_header

  assign transparent_bg = section.settings.transparent_bg

  assign menu = section.settings.menu
  assign mobile_menu_divider_color = section.settings.mobile_menu_divider_color

  assign submenu_link_hover_color = section.settings.submenu_link_hover_color
  assign submenu_link_color = section.settings.submenu_link_color
  assign submenu_heading_color = section.settings.submenu_heading_color
  assign submenu_heading_hover_color = section.settings.submenu_heading_hover_color

  assign list_gap = section.settings.list_gap

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_hero_color = section.settings.link_hero_color
  assign link_hover_hero_color = section.settings.link_hover_hero_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height
  assign link_bg_color = section.settings.link_bg_color
  assign link_radius = section.settings.link_radius

  assign submenu_item_align = section.settings.submenu_item_align

  assign submenu_item_horizontal_align = ""
  if submenu_item_align == "center"
    assign submenu_item_horizontal_align = "center"
  elsif submenu_item_align == "right"
    assign submenu_item_horizontal_align = "end"
  else
    assign submenu_item_horizontal_align = "start"
  endif

  assign title_size = section.settings.title_size
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height
  assign title_mt = section.settings.title_mt
  assign title_align = section.settings.title_align

  assign submenu_image_radius = section.settings.submenu_image_radius
  assign submenu_image_border_thickness = section.settings.submenu_image_border_thickness
  assign submenu_image_border_color = section.settings.submenu_image_border_color
  assign submenu_image_ratio = section.settings.submenu_image_ratio

  assign promotional_image_radius = section.settings.promotional_image_radius
  assign promotional_image_border_thickness = section.settings.promotional_image_border_thickness
  assign promotional_image_border_color = section.settings.promotional_image_border_color
  assign promotional_image_ratio = section.settings.promotional_image_ratio
  assign promotional_image_ratio_mobile = section.settings.promotional_image_ratio_mobile
  assign promotional_image_width_desktop = section.settings.promotional_image_width_desktop
  assign use_promo_image_mobile = section.settings.use_promo_image_mobile
  assign promotional_image_overlay = section.settings.promotional_image_overlay
  assign promotional_image_overlay_color = section.settings.promotional_image_overlay_color

  assign promotional_title_size = section.settings.promotional_title_size
  assign promotional_title_size_mobile = section.settings.promotional_title_size_mobile
  assign promotional_title_color = section.settings.promotional_title_color
  assign promotional_title_custom = section.settings.promotional_title_custom
  assign promotional_title_font = section.settings.promotional_title_font
  assign promotional_title_height = section.settings.promotional_title_height
  assign promotional_title_align = section.settings.promotional_title_align
  assign promotional_title_align_mobile = section.settings.promotional_title_align_mobile
  assign promotional_title_vertical_align = section.settings.promotional_title_vertical_align
  assign promotional_title_vertical_align_mobile = section.settings.promotional_title_vertical_align_mobile

  assign logo = section.settings.logo
  assign second_logo = section.settings.second_logo
  assign logo_mobile = section.settings.logo_mobile
  assign second_logo_mobile = section.settings.second_logo_mobile
  assign logo_width = section.settings.logo_width
  assign logo_width_mobile = section.settings.logo_width_mobile

  assign name_size = section.settings.name_size
  assign name_size_mobile = section.settings.name_size_mobile
  assign name_color = section.settings.name_color
  assign name_hover_color = section.settings.name_hover_color
  assign name_hero_color = section.settings.name_hero_color
  assign name_hover_hero_color = section.settings.name_hover_hero_color
  assign name_custom = section.settings.name_custom
  assign name_font = section.settings.name_font
  assign name_height = section.settings.name_height

  assign right_gap_mobile = section.settings.right_gap_mobile
  assign right_gap = section.settings.right_gap

  assign right_link_size = section.settings.right_link_size
  assign right_link_color = section.settings.right_link_color
  assign right_link_hover_color = section.settings.right_link_hover_color
  assign right_link_hero_color = section.settings.right_link_hero_color
  assign right_link_hover_hero_color = section.settings.right_link_hover_hero_color
  assign right_link_custom = section.settings.right_link_custom
  assign right_link_font = section.settings.right_link_font
  assign right_link_height = section.settings.right_link_height

  assign icon_size_mobile = section.settings.icon_size_mobile
  assign icon_size = section.settings.icon_size  

  assign use_search = section.settings.use_search
  assign search_text  = section.settings.search_text
  assign search_icon_desktop  = section.settings.search_icon_desktop
  assign search_menu_mobile = section.settings.search_menu_mobile

  assign use_account = section.settings.use_account
  assign account_text  = section.settings.account_text
  assign account_icon_desktop  = section.settings.account_icon_desktop
  assign account_menu_mobile = section.settings.account_menu_mobile

  assign cart_text  = section.settings.cart_text
  assign cart_icon_desktop  = section.settings.cart_icon_desktop

  assign mobile_menu_button = section.settings.mobile_menu_button
  assign mobile_menu_button_url = section.settings.mobile_menu_button_url

  assign loading_attribute = 'eager'
  if lazy
    assign loading_attribute = 'lazy'
  endif
-%}

{%- style -%}

  {{ link_font | font_face: font_display: 'swap' }}
  {{ name_font | font_face: font_display: 'swap' }}
  {{ right_link_font | font_face: font_display: 'swap' }}
  {{ promotional_title_font | font_face: font_display: 'swap' }}


  .section-{{ section.id }} {
    border: solid {{ border_color }} {{ border_thickness }}px; 
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;  
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .header-{{ section.id }} {
    transition: all 0.25s ease 0s;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}.transparent {
      background-color: transparent !important;
      background-image: none !important;
      position: absolute;
      width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
      top: 0;
      left: 0;
      z-index: 999;
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}.transparent {
      background-color: transparent !important;
      background-image: none !important;
      position: absolute;
      width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
      top: 0;
      left: 0;
      z-index: 999;
    }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.onscroll {
    {% if background_style == 'solid' %}
      background-color:{{ background_color }} !important;
    {%else %}
      background-image: {{ background_gradient }} !important;
    {% endif %}
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%);
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0) !important;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open {
    background-color:{{ background_color }} !important;
    transition: all 0.25s ease 0s;
  }

  .header-body-{{ section.id }} {
    display: grid;
    align-items: center;
    gap: 12px;
    grid-template-columns: 1fr {{ logo_width_mobile }}px 1fr;
  }

  .header-logo-{{ section.id }} {
    text-align: center;
  }

  .header-submenu-menu-{{ section.id }} {
    padding: 25px 0px;
    position: relative;
    width: 100%;
    display: flex;
    gap: 20px;
    align-items: start;
    overflow-y: scroll;
    scrollbar-width: none;
    justify-content: space-between;
  }

  .header-submenu-menu-{{ section.id }} .header-menu-list-{{ section.id }} {
    flex: 1 1 0;
  }

  .header-menu-list-desktop-{{ section.id }} {
    padding-top: 10px !important;
    justify-content: center;
    text-align: center;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: none;
    align-items: center;
    gap: 0px;
    flex-wrap: wrap;
    list-style: none;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-list-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: start;
  }

  .header-menu-item-{{ section.id }} {
    line-height: 100%;
    box-sizing: border-box;
    padding-bottom: 3px;
  }

  .header-menu-sublinks-{{ section.id }} {
    padding: 0;
    padding-top: 20px;
    display: flex;
    flex-direction: column;
    list-style: none;
  }

  .header-menu-sublinks-{{ section.id }} .header-menu-link-{{ section.id }} {
    margin: 0;
    padding: 0;
    font-size: {{ link_size_mobile | minus: 4 }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    background: transparent !important;
  }

  .header-menu-sublinks-{{ section.id }} .header-menu-sublinks-{{ section.id }} .header-menu-link-{{ section.id }} {
    font-size: {{ link_size_mobile | minus: 4 }}px;
    font-weight: 400;
  }

  .header-menu-sublinks-{{ section.id }} svg {
    width: 16px;
  }

  .header-menu-link-wrapper-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }

  .header-menu-link-wrapper-{{ section.id }} svg {
    width: 16px;
  }

  .header-menu-link-{{ section.id }} {
    margin: 0;
    display: block;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 3;
    border-radius: {{ link_radius }}px;
    text-wrap: nowrap;
  }

  .header-menu-item-{{ section.id }}.link-{{ section.id }} .header-menu-link-{{ section.id }} {
    cursor: pointer;
  }

  .header-menu-item-{{ section.id }}.parent-{{ section.id }} .header-menu-link-{{ section.id }} {
    cursor: default;
  }

  .header-submenu-list-{{ section.id }} {
    display: flex;
    flex-direction: column !important;
    gap: 0px;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
      color: {{ link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
      color: {{ link_hero_color }};
    }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.open.transparent .header-menu-link-{{ section.id }} {
    color: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open.transparent .header-menu-link-{{ section.id }}.heading {
    color: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .header-menu-link-open-{{ section.id }} path {
    stroke: {{ link_color }};
  }

  .header-submenu-list-{{ section.id }} .header-menu-item-{{ section.id }} .header-menu-link-{{ section.id }} {
    color: {{ submenu_link_color }} !important;
    font-weight: 400;
  }

  .header-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    max-width: fit-content;
    flex-basis: 0;
  }

  .header-menu-item-icon-{{ section.id }} {
    display: block;
    flex: 0 0 8px;
    width: 8px;
    transition: all 0.25s ease 0s;
    color: {{ link_color }};
  }

  .header-menu-item-icon-{{ section.id }} svg path {
    stroke: {{ link_color }}
  }

  .header-{{ section.id }}:not(.onscroll).transparent.open .header-menu-item-icon-{{ section.id }} svg path {
    stroke: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .header-menu-item-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-submenu-{{ section.id }} {
    position: absolute;
    z-index: 3;
    top: calc(100% - {{ padding_bottom }}px);
    left: 0;
    width: 100%;
    opacity: 0;
    pointer-events: none;
    transition: all 0.25s ease 0s;
    list-style: none;
    margin: 0px;
  }

  .header-submenu-bg-{{ section.id }} {
    display: block !important;
    height: {{ padding_bottom }}px;
    pointer-events: none;
  }

  .header-submenu-inner-{{ section.id }} {
    {% if background_style == 'solid' %}
      background-color:{{ background_color }};
    {%else %}
      background-image: {{ background_gradient }};
    {% endif %}  
  }

  .header-submenu-{{ section.id }} .section-{{ section.id }}-settings {
    margin-top: calc({{padding_bottom }}px + {{ border_thickness }}px) !important;
    overflow: hidden; 
    display: flex;    
  }

  .header-menu-item-parent-{{ section.id }}.active .header-submenu-{{ section.id }} {
    opacity: 1;
    pointer-events: all;
    transition: all 0.25s ease 0s;
  }

  .header-menu-burger-{{ section.id }} {
    display: block;
    width: 20px;
    height: 16px;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease 0s;
    cursor: pointer;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
  }

  .header-menu-burger-icon-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 0;
    margin-top: -1px;
    width: 100%;
    height: 1px;
    background: {{ link_color }};
    transition: transform .25s ease; 
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
      background: {{ link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
        background: {{ link_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }} {
    transform: rotate(45deg);
    transition-delay: .25s;
    background: {{ link_color }} !important;
  }

  .header-menu-burger-icon-{{ section.id }}::before,
  .header-menu-burger-icon-{{ section.id }}::after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 1px;
    background-color: inherit;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::before {
    top: -7px;
    transition: top .25s ease .27s, opacity .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::before {
    top: 0;
    opacity: 0;
    width: 100%;
    transition: top .2s ease, opacity .2s ease .22s, width .1s ease;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::after {
    bottom: -7px;
    transition: bottom .25s ease .27s, transform .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::after {
    bottom: 0;
    transform: rotate(-90deg);
    width: 100%;
    transition: bottom .2s ease, transform .2s ease .22s, width .1s ease;
    background: {{ link_color }} !important;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after{
      background: {{ link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after{
        background: {{ link_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-logo-{{ section.id }} {
    width: 100%;
    text-align: center;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open .header-logo-{{ section.id }} {
    transition: all 0.25s ease 0s;
  }

  .header-logo-image-{{ section.id }} {
    max-width: {{ logo_width_mobile }}px;
  }

  .header-logo-image-{{ section.id }} img,
  .header-logo-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-text-{{ section.id }} {
    margin: 0;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }
  {% if transparent_bg == "all" %}
  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
    color: {{ name_hero_color }};
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-first-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-second-{{ section.id }} {
    display: block !important;
  }
{% elsif transparent_bg == "home" and template.name == "index" %}
  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
    color: {{ name_hero_color }};
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-first-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-second-{{ section.id }} {
    display: block !important;
  }
{% endif %}

.header-{{ section.id }}.open.transparent .header-logo-text-{{ section.id }} {
  color: {{ name_color }};
  transition: all 0.25s ease 0s;
}

.header-{{ section.id }} .header-logo-first-{{ section.id }} {
  display: block !important;
}

.header-{{ section.id }} .header-logo-second-{{ section.id }} {
  display: none !important;
}

.header-{{ section.id }}.open.transparent .header-logo-first-{{ section.id }} {
  display: block !important;
}

.header-{{ section.id }}.open.transparent .header-logo-second-{{ section.id }} {
  display: none !important;
}

  .header-right-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: end;
    gap: {{ right_gap_mobile }}px;
    flex-wrap: nowrap;
    position: relative;
    z-index: 3;
    order: 3;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open .header-right-{{ section.id }} {
    transition: all 0.25s ease 0s;
  }

  .header-search-{{ section.id }},
  .header-account-{{ section.id }},
  .header-cart-{{ section.id }}{
    position: relative;
  }

  .header-search-text-{{ section.id }},
  .header-account-text-{{ section.id }},
  .header-cart-text-{{ section.id }}{
    display: none;
    margin: 0;
    color: {{ right_link_color }};
    line-height: {{ right_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s cubic-bezier(.76,0,.24,1);
  }

  .cart-count-bubble {
    position: initial !important;
  }

  .header-cart-{{ section.id }} .cart-count-bubble span {
    margin: 0;  
    font-size: {{ right_link_size }}px;
    color: {{ right_link_color }};
    line-height: {{ right_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-search-text-{{ section.id }},
    .header-{{ section.id }}:not(.onscroll).transparent .header-account-text-{{ section.id }},
    .header-{{ section.id }}:not(.onscroll).transparent .header-cart-text-{{ section.id }} {
      color: {{ right_link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-search-text-{{ section.id }},
      .header-{{ section.id }}:not(.onscroll).transparent .header-account-text-{{ section.id }},
      .header-{{ section.id }}:not(.onscroll).transparent .header-cart-text-{{ section.id }} {
        color: {{ right_link_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.open.transparent .header-search-text-{{ section.id }},
  .header-{{ section.id }}.open.transparent .header-account-text-{{ section.id }},
  .header-{{ section.id }}.open.transparent .header-cart-text-{{ section.id }} {
    color: {{ right_link_color }};
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-cart-{{ section.id }} .cart-count-bubble span{
      color: {{ right_link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-cart-{{ section.id }} .cart-count-bubble span{
        color: {{ right_link_hero_color }};
      }
