{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign header_logo_dot_color = section.settings.header_logo_dot_color
  assign background_style = section.settings.background_style

  assign logo = section.settings.logo
  assign logo_mobile = section.settings.logo_mobile
  assign logo_width = section.settings.logo_width
  assign logo_width_mobile = section.settings.logo_width_mobile
  assign use_menu_icon = section.settings.use_menu_icon
  assign menu_icon_color = section.settings.menu_icon_color

  assign name_size = section.settings.name_size
  assign name_size_mobile = section.settings.name_size_mobile
  assign name_color = section.settings.name_color
  assign name_hover_color = section.settings.name_hover_color
  assign name_custom = section.settings.name_custom
  assign name_font = section.settings.name_font
  assign name_height = section.settings.name_height

  assign close_color = section.settings.close_color
  assign close_hover_color = section.settings.close_hover_color

  assign menu = section.settings.menu
  assign menu_divider_thickness = section.settings.menu_divider_thickness
  assign menu_divider_color = section.settings.menu_divider_color
  
  assign list_gap_mobile = section.settings.list_gap_mobile
  assign list_gap = section.settings.list_gap

  assign sub_list_gap_mobile = section.settings.sub_list_gap_mobile
  assign sub_list_gap = section.settings.sub_list_gap

  assign link_size = section.settings.link_size
  assign link_size_mobile = section.settings.link_size_mobile
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height
  assign link_align = section.settings.link_align
  assign link_align_mobile = section.settings.link_align_mobile

  assign link_horizontal_align = ""
  if link_align == "center"
    assign link_horizontal_align = "center"
  elsif link_align == "right"
    assign link_horizontal_align = "end"
  else
    assign link_horizontal_align = "start"
  endif

  assign link_horizontal_align_mobile = ""
  if link_align_mobile == "center"
    assign link_horizontal_align_mobile = "center"
  elsif link_align_mobile == "right"
    assign link_horizontal_align_mobile = "end"
  else
    assign link_horizontal_align_mobile = "start"
  endif

  assign sublink_size = section.settings.sublink_size
  assign sublink_size_mobile = section.settings.sublink_size_mobile
  assign sublink_color = section.settings.sublink_color
  assign sublink_hover_color = section.settings.sublink_hover_color
  assign sublink_custom = section.settings.sublink_custom
  assign sublink_font = section.settings.sublink_font
  assign sublink_height = section.settings.sublink_height  

  assign toggle_icon_color = section.settings.toggle_icon_color
  assign arrow_icon_color = section.settings.arrow_icon_color
-%}

{%- style -%}
  {{ name_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}
  {{ sublink_font | font_face: font_display: 'swap' }}
  
  
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;    
    position: fixed;
    width: 100%;
    top: 0;
    transform: translateY(-100vh);
    transition: all 0.3s ease 0s;
    z-index: 10;
  }

  .section-{{ section.id }}.active {
    transform: translateY(0) !important;
    transition: all 0.3s ease 0s;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;    
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    z-index: 3;
    position: relative;
  }

  .header-body-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: center;
    overflow-y: scroll;
    scrollbar-width: none;
    max-height: 70vh;
  }

  .header-menu-{{ section.id }} {
    display: flex;
    width: 100%;
    gap: {{ list_gap_mobile }}px;
    align-items: center;
    flex-direction: column;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: flex;
    list-style: none;
    align-items: {{  link_horizontal_align_mobile }};
    flex-direction: column;
    width: 100%;
  }

  .header-menu-item-{{ section.id }} {
    line-height: 100%;
    cursor: pointer;
    box-sizing: border-box;
  }

  .header-item-{{ section.id }} {
    padding: {{ list_gap_mobile }}px 0px;
  }

  .header-item-{{ section.id }}:not(.hide):not(:last-child) {
    width: 100%;
    border-bottom: {{ menu_divider_thickness }}px solid {{ menu_divider_color }};
  }

  .header-menu-link-{{ section.id }} {
    margin: 0;
    text-align: {{ link_align_mobile }};
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    text-wrap: nowrap;
    position: relative;
    font-weight: 700;
    display: block;
    width: 100%;
  }

  .header-submenu-list-{{ section.id }} {
    overflow: hidden;   
    padding: 0px;
    margin: 0px;
    display: none;
    list-style: none;
    flex-direction: column;
    align-items: center;
    justify-content: {{  link_horizontal_align_mobile }};
    transition: all 0.25s ease 0s;
    flex-wrap: wrap;
  }

  .header-submenu-list-{{ section.id }}.active {
    display: flex;
    width: 100%;
  }

  .header-item-{{ section.id }} {
    width: 100%;
  }

  .header-submenu-item-{{ section.id }} {
    line-height: 100%;
    cursor: pointer;
    box-sizing: border-box;
    width: 100%;
    padding: {{ sub_list_gap_mobile }}px 0px;
  }

  .header-submenu-item-{{ section.id }}:not(:last-child) {
    border-bottom: {{ menu_divider_thickness }}px solid {{ menu_divider_color }};
  }

  .header-submenu-link-{{ section.id }} {
    margin: 0;
    display: block;
    text-align: {{ link_align_mobile }};
    font-size: {{ sublink_size_mobile }}px;
    color: {{ sublink_color }};
    line-height: {{ sublink_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    text-wrap: nowrap;
    position: relative;
    width: 100%;
    font-weight: 700;
  }

  .header-logo-close-icon-{{ section.id }} {
    margin-top: 10px;
    display: none;
  }

  .header-logo-close-icon-{{ section.id }} svg,
  .header-submenu-close-{{ section.id }} svg {
    width: {{ logo_width_mobile }}px;
    height: {{ logo_width_mobile }}px;
    object-fit: cover;
    display: block;
  }

  .header-submenu-close-{{ section.id }} svg path {
    stroke: {{ arrow_icon_color }};
    transition: all 0.25s ease 0s;
  }
  .header-logo-close-icon-{{ section.id }} svg path {
    stroke: {{ close_color }};
    transition: all 0.25s ease 0s;
  }

  .header-logo-click-{{ section.id }}:hover .header-logo-close-icon-{{ section.id }} svg path {
    stroke: {{ close_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .header-logo-{{ section.id }}.active .header-logo-close-icon-{{ section.id }} {
    display: block;
  }

  .header-logo-{{ section.id }}.active .header-logo-image-{{ section.id }} {
    display: none;
  }

  .header-logo-{{ section.id }}.active .header-logo-text-{{ section.id }} {
    display: none;
  }

  .header-logo-{{ section.id }} {
    position: relative;
    margin: 0 auto;
    max-width: fit-content;
    padding: 20px;
    padding-top: 10px;
    position: absolute;
    left: 50%;
    margin-top: -2px;
    cursor: pointer;
    transform: translateX(-50%);
    text-align: center;
    {% if background_style == 'solid' %}
      background-color: {{ background_color }};
    {% else %}
      background-image: {{ background_gradient }};
    {% endif %}
    border-bottom-left-radius: 50px;
    border-bottom-right-radius: 50px;
    z-index: 1;
    transition: all 0.25s ease 0s;
  }

  .header-logo-{{ section.id }}.active .header-logo-menu-icon-{{ section.id }} {
    display: none;
  }

  .header-logo-menu-icon-{{ section.id }} {
    position: absolute;
    z-index: 2;
    bottom: -27px;
    right: -4px;
  }

  .header-logo-menu-icon-{{ section.id }} svg path {
    fill: {{ menu_icon_color }};
  }

  .header-logo-image-{{ section.id }} {
    margin-top: 10px;
    max-width: {{ logo_width_mobile }}px;
  }

  .header-logo-image-{{ section.id }} img,
  .header-logo-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-text-{{ section.id }} {
    margin: 0;
    margin-top: 10px;
    font-weight: 700;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .header-menu-logo-{{ section.id }} {
    margin: 0 auto;
    max-width: fit-content;
    text-align: center;
    transition: all 0.25s ease 0s;
  }

  .header-menu-logo-image-{{ section.id }} {
    display: block;
    width: 100%;
    max-width: {{ logo_width_mobile }}px;
  }

  .header-menu-logo-image-{{ section.id }} img,
  .header-menu-logo-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-menu-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-menu-logo-text-{{ section.id }} {
    margin: 0;
    font-weight: 700;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .header-menu-list-mobile-{{ section.id }} {
    display: flex;
  }

  .header-menu-list-desktop-{{ section.id }} {
    display: none;
  }

  .header-toggle-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: 5px;
    align-items: {{  link_horizontal_align_mobile }};
  }

  .header-toggle-icon-{{ section.id }} {
    display: flex;
  }

  .header-toggle-icon-{{ section.id }} svg {
    width: 30px;
    height: 30px;
  }

  .header-toggle-icon-{{ section.id }} svg path {
    stroke: {{ toggle_icon_color }};
  }

  .header-toggle-{{ section.id }}.active .header-toggle-icon-{{ section.id }} {
    display: none;
  }

  .header-answer-{{ section.id }} {
    transition: all 0.3s ease 0s;
  }

  .header-height-cal-{{ section.id }} {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
    position: absolute;
    top: 0;
    left: 0;  
    padding-top: 20px;
  }

  .header-logo-dots-{{ section.id }} {
    display: flex;
    margin: 0;
    padding: 0 10px;
    position: absolute;
    top: 8px;
    left: 0;
    width: 100%;
    margin-bottom: 8px;
    align-items: center;
    justify-content: space-between;
  }

  .header-logo-dot-{{ section.id }} {
    display: block !important;
    width: 3px;
    height: 3px;
    background-color: {{ header_logo_dot_color }};
  }
   
  @media(min-width: 1024px) {    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }    

    .section-{{ section.id }}:not(.active):hover {
      top: 20px;
      transition: all 0.3s ease 0s;
    }    

    .header-body-{{ section.id }} {
      max-height: 70vh;
    }

    .header-menu-{{ section.id }} {
      gap: {{ list_gap }}px;
    }

    .header-menu-list-{{ section.id }} {
      gap: {{ list_gap }}px;
      align-items: {{  link_horizontal_align }};
    }

    .header-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      text-align: {{ link_align }};
      transition: all 0.25s ease;
      color: {{ link_hover_color }};
    }

    .header-submenu-item-{{ section.id }} {
      width: fit-content;
      padding: 0px !important;
      border-bottom: 0px !important;
    }

    .header-submenu-link-{{ section.id }}:hover {
      color: {{ sublink_hover_color }};
      transition: all 0.25s ease 0s;
    }  

    .header-menu-list-{{ section.id }}:hover .header-menu-link-{{ section.id }} {
      color: {{ link_color }};
      transition: all 0.25s ease 0s;
    }

    .header-menu-list-{{ section.id }} .header-menu-item-{{ section.id }}:hover .header-menu-link-{{ section.id }} {
      color: {{ link_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .header-submenu-list-{{ section.id }} {    
      max-height: 0; 
      flex-direction: row;
      display: flex;
      justify-content: {{  link_horizontal_align }};
      gap: {{ sub_list_gap }}px;
      flex-wrap: nowrap;
      opacity: 0;      
    }

    .header-menu-item-{{ section.id }}:hover .header-submenu-list-{{ section.id }} {
      max-height: calc({{ link_size }}px + 20px);
      padding-top: 20px;
      opacity: 1;
      transition: all 0.6s ease 0s;
    }

    .header-submenu-link-{{ section.id }} {     
      font-size: {{ sublink_size }}px;     
      text-align: {{ link_align }};
    }

    .header-logo-image-{{ section.id }} {
      max-width: {{ logo_width }}px;
    }

    .header-logo-image-desktop-{{ section.id }} {
      display: block;
    }

    .header-logo-image-mobile-{{ section.id }} {
      display: none;
    }

    .header-menu-list-mobile-{{ section.id }} {
      display: none;
    }

    .header-toggle-{{ section.id }} {
      align-items: {{  link_horizontal_align }};
    }

    .header-menu-list-desktop-{{ section.id }} {
      display: flex;
    }

    .header-submenu-list-{{ section.id }} {
      max-height: 0;
    }

    .header-logo-text-{{ section.id }} {
      font-size: {{ name_size }}px;
      transition: all 0.25s ease;
    }    

    .header-logo-text-{{ section.id }}:hover {
      transition: all 0.25s ease;
      color: {{ name_hover_color }};
    }
    .header-menu-logo-image-{{ section.id }} {
      max-width: {{ logo_width }}px;
    }

    .header-menu-logo-image-desktop-{{ section.id }} {
      display: block;
    }

    .header-menu-logo-image-mobile-{{ section.id }} {
      display: none;
    }

    .header-logo-close-icon-{{ section.id }} svg,
    .header-submenu-close-{{ section.id }} svg {
      width: {{ logo_width }}px;
      height: {{ logo_width }}px;
    }

    .header-menu-logo-text-{{ section.id }} {
      font-size: {{ name_size }}px;
      transition: all 0.25s ease;
    }    

    .header-menu-logo-text-{{ section.id }}:hover {
      transition: all 0.25s ease;
      color: {{ name_hover_color }};
    }
  }
  
{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if name_custom %}
  <style>
    .header-menu-logo-text-{{ section.id }},
    .header-logo-text-{{ section.id }} {
      font-family: {{ name_font.family }}, {{ name_font.fallback_families }};
      font-weight: {{ name_font.weight }};
      font-style: {{ name_font.style }};
    }
  </style>
{% endif %}

{% if link_custom %}
  <style>
    .header-menu-link-{{ section.id }} {
      font-family: {{ link_font.family }}, {{ link_font.fallback_families }};
      font-weight: {{ link_font.weight }};
      font-style: {{ link_font.style }};
    }
  </style>
{% endif %}

{% if sublink_custom %}
  <style>
    .header-submenu-link-{{ section.id }} {
      font-family: {{ sublink_font.family }}, {{ sublink_font.fallback_families }};
      font-weight: {{ sublink_font.weight }};
      font-style: {{ sublink_font.style }};
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% if block.type == 'socials' %}  
     {%- liquid
        assign socials_horizontal_align = ""
      if block.settings.socials_align == "center"
        assign socials_horizontal_align = "center"
      elsif block.settings.socials_align == "right"
        assign socials_horizontal_align = "end"
      else
        assign socials_horizontal_align = "start"
      endif
    
      assign socials_horizontal_align_mobile = ""
      if block.settings.socials_align_mobile == "center"
        assign socials_horizontal_align_mobile = "center"
      elsif block.settings.socials_align_mobile == "right"
        assign socials_horizontal_align_mobile = "end"
      else
        assign socials_horizontal_align_mobile = "start"
      endif -%}
    <style>         
      .header-social-list-{{ section.id }} {
        padding: 0px;
        margin: 0px;
        list-style: none;
        display: flex;
        flex-wrap: wrap;
        align-items: center;
        width: 90%;
        justify-content: {{ socials_horizontal_align_mobile }};
        gap: {{ block.settings.social_list_gap_mobile }}px;
        margin-top: 12px;
      }    
    
      .header-social-link-{{ section.id }} {          
        box-sizing: content-box;
        background-color: {{ block.settings.social_icon_bg_color }};
        transition: all 0.25s ease 0s;
        display: block;       
        width: {{ block.settings.social_icon_size_mobile }}px;
        height: {{ block.settings.social_icon_size_mobile }}px;
      }
    
      .header-social-link-{{ section.id }} svg {
        display: block;
        width: 100%;
        height: 100%;
        object-fit: contain;
      }
    
      .header-social-link-{{ section.id }} svg path {
        fill: {{ block.settings.social_icon_color }};
        transition: all 0.25s ease 0s;
      }
      
      @media(min-width: 1024px) {  
        .header-social-list-{{ section.id }} {
          justify-content: {{ socials_horizontal_align }};
          gap: {{ block.settings.social_list_gap }}px;
        }

        .header-social-list-{{ section.id }}:hover .header-social-link-{{ section.id }} svg path {
          fill: {{ block.settings.social_icon_hover_color }};
          transition: all 0.25s ease 0s;
        }
    
        .header-social-link-{{ section.id }} {
          width: {{ block.settings.social_icon_size }}px;
          height: {{ block.settings.social_icon_size }}px;
        }      
        
        .header-social-link-{{ section.id }}:hover svg path {
          fill: {{ block.settings.social_icon_color }} !important;
          transition: all 0.25s ease 0s;
        }
      }
    </style>
  {% endif %}
{% endfor %}

{% comment %} FOR FREE THEMES{% endcomment %}
{%- if settings.cart_type == "drawer" -%}
  {{ 'component-cart-drawer.css' | asset_url | stylesheet_tag }}
  {{ 'component-cart.css' | asset_url | stylesheet_tag }}
  {{ 'component-totals.css' | asset_url | stylesheet_tag }}
  {{ 'component-price.css' | asset_url | stylesheet_tag }}
  {{ 'component-discounts.css' | asset_url | stylesheet_tag }}
{%- endif -%}
{% comment %} FOR FREE THEMES{% endcomment %}

<div class="section-{{ section.id }} header-{{ section.id }}">
    <div class="section-{{ section.id }}-settings" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% elsif background_style == 'gradient' %} style="background-image: {{ background_gradient }};"{% endif %}>
      <div class="header-body-{{ section.id }}">
        <div class="header-menu-{{ section.id }}">
          <div class="header-menu-logo-{{ section.id }}">
            {% if logo != blank %}
              <a href="{{ shop.url }}" class="header-menu-logo-image-{{ section.id }} {% if logo_mobile != blank %}header-menu-logo-image-desktop-{{ section.id }}{% endif %}">
                <img src="{{ logo | image_url }}" alt="{{ logo.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              </a>
              {% if logo_mobile != blank %}
                <a href="{{ shop.url }}" class="header-menu-logo-image-{{ section.id }} header-menu-logo-image-mobile-{{ section.id }}">
                  <img src="{{ logo_mobile | image_url }}" alt="{{ logo_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                </a>
              {% endif %}
            {% else %}
              <a href="{{ shop.url }}" class="header-menu-logo-text-{{ section.id }}">{{ shop.name }}</a>
            {% endif %}
          </div> 
          {% if menu != blank %}           
            <ul class="header-menu-list-{{ section.id }} header-menu-list-desktop-{{ section.id }}">
              {% for link in menu.links %}              
                <li class="header-menu-item-{{ section.id }} header-menu-item-main-{{ section.id }} {% if link.links.size > 0 %}header-menu-item-parent-{{ section.id }}{% endif %}">
                  <{% if link.links.size > 0 %}p{% else %}a href="{{ link.url }}"{% endif %} class="header-menu-link-{{ section.id }}">
                    {{ link.title }}
                  </{% if link.links.size > 0 %}p{% else %}a{% endif %}>
                  {% if link.links.size > 0 %}
                    <ul class="header-submenu-list-{{ section.id }}">
                      {% for sublink in link.links %}
                        <li class="header-submenu-item-{{ section.id }}">
                          <a href="{{ sublink.url }}" class="header-submenu-link-{{ section.id }}">{{ sublink.title }}</a>
                        </li>
                      {% endfor %}
                    </ul>
                  {% endif %}
                </li>              
              {% endfor %}
            </ul>
          {% endif %}
          <ul class="header-menu-list-{{ section.id }} header-menu-list-mobile-{{ section.id }}">     
            {% if menu != blank %}              
              {% for link in menu.links %}
                {% if link.links.size > 0 %}
                  <li class="header-item-{{ section.id }}">        
                    <div class="header-toggle-{{ section.id }}">
                      <{% if link.links.size > 0 %}p{% else %}a href="{{ link.url }}"{% endif %} class="header-menu-link-{{ section.id }} header-question-{{ section.id }}">
                        {{ link.title }}
                      </{% if link.links.size > 0 %}p{% else %}a{% endif %}>
