<script>
function initHeader16() {
  const toggleButtons = document.querySelectorAll('.header-toggle-{{ section.id }}');
  const closeButtons = document.querySelectorAll('.header-submenu-close-{{ section.id }}');
  const allItems = document.querySelectorAll('.header-item-{{ section.id }}');
  
  toggleButtons.forEach(button => {
    button.addEventListener('click', function() {
      const parentItem = this.closest('li');
      
      if(parentItem) {
        allItems.forEach(item => {
          if (item !== parentItem) {
            item.style.display = 'none';
          }
        });
        
        this.style.display = 'none'; 

        parentItem.classList.add('hide'); 

        const submenuList = parentItem.querySelector('.header-submenu-list-{{ section.id }}');
        
        if (submenuList) {
          submenuList.classList.add('active');
        }
      }    
    });
  });

  closeButtons.forEach(closeButton => {
    closeButton.addEventListener('click', function() {
      const parentItem = this.closest('li');
      
      allItems.forEach(item => {
        item.style.display = '';
      });

      parentItem.classList.remove('hide');
      
      const submenuList = parentItem.querySelector('.header-submenu-list-{{ section.id }}');
      if (submenuList) {
        submenuList.classList.remove('active');
      }
      
      const toggleButton = parentItem.querySelector('.header-toggle-{{ section.id }}');
      if (toggleButton) {
        toggleButton.style.display = ''; 
      }
    });
  });
}

  function setupHeaderTransform() {
    const section = document.querySelector('.section-{{ section.id }}');
    const settings = document.querySelector('.section-{{ section.id }}-settings');
    const logoClick = document.querySelector('.header-logo-click-{{ section.id }}');

    if (section && settings && logoClick) {
      const settingsHeight = settings.offsetHeight;
      section.style.transform = `translateY(-${settingsHeight}px)`;

      logoClick.addEventListener('click', () => {
        section.classList.toggle('active');
        logoClick.classList.toggle('active');
        if (!section.classList.contains('active')) {
          section.style.transform = `translateY(-${settingsHeight}px)`;
        }
      });

      let resizeTimerTransform;
      window.addEventListener('resize', () => {
        clearTimeout(resizeTimerTransform);
        resizeTimerTransform = setTimeout(() => {
          if (!section.classList.contains('active')) {
            const newSettingsHeight = settings.offsetHeight;
            section.style.transform = `translateY(-${newSettingsHeight}px)`;
          }
        }, 250);
      });
    }
  }

  document.addEventListener('DOMContentLoaded', () => {
    initHeader16();
    setupHeaderTransform();
  });
  
  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', () => {
      initHeader16();
      setupHeaderTransform();
    });
    document.addEventListener('shopify:section:load', () => {
      initHeader16();
      setupHeaderTransform();
    });
  }
</script>
