<div class="section-{{ section.id }} header-{{ section.id }} {% if transparent_bg != "none" %}transparent{% endif %} {% if show_type == "always" %}fixed{% elsif show_type == "scroll_up" %}scroll active fixed{% else %}top{% endif %}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% elsif background_style == 'gradient' %} style="background-image: {{ background_gradient }};"{% endif %}>
    <div class="section-{{ section.id }}-settings">
      <div class="header-body-{{ section.id }}">
        <div class="header-menu-{{ section.id }} header-menu-burger-mobile-{{ section.id }}">
          {% if section.blocks.size > 0 %}
            <button class="header-menu-burger-{{ section.id }}">
              <span class="header-menu-burger-icon-{{ section.id }}"></span>
            </button>          
          <div class="header-menu-mobile-{{ section.id }}">
            <div class="header-menu-mobile-inner-{{ section.id }}">
              {% if section.blocks.size != 0 %}
                <ul class="header-menu-list-{{ section.id }}">
                  {% for block in section.blocks %}
                    {% if block.type == 'menu_item' %}
                      {% assign has_matching_submenu_items = false %}
                      {% assign current_menu_item_text = block.settings.text %}

                      {% for submenu_block in section.blocks %}
                        {% if submenu_block.type == 'submenu_item' and submenu_block.settings.submenu_title == current_menu_item_text %}
                          {% assign has_matching_submenu_items = true %}
                          {% break %}
                        {% endif %}
                      {% endfor %}

                      <li class="header-menu-item-{{ section.id }} heading" style="padding: 10px 0;">
                        {% if has_matching_submenu_items %}
                          <div class="header-menu-link-wrapper-{{ section.id }}">
                            <{% if block.settings.link_url != blank %}a href="{{ block.settings.link_url }}"{% else %}div{% endif %} class="header-menu-link-{{ section.id }} heading">
                              {{ block.settings.text }}
                            </{% if block.settings.link_url != blank %}a{% else %}div{% endif %}>
                            <svg class="header-menu-link-open-{{ section.id }}" xmlns="http://www.w3.org/2000/svg" width="22" height="22" viewBox="0 0 22 22" fill="none">
                              <path d="M11 1V21" stroke="currentColor" stroke-linecap="square" stroke-linejoin="round"></path>
                              <path d="M1 11H21" stroke="currentColor" stroke-linecap="square" stroke-linejoin="round"></path>
                            </svg>
                          </div>
                          <div class="header-mobile-submenu-{{ section.id }}">
                            <ul class="header-menu-sublinks-{{ section.id }}">
                      {% if has_matching_submenu_items %}
                        {% assign main_title = block.settings.text %}
                        {% for submenu_block in section.blocks %}
                          {% if submenu_block.type == 'submenu_item' and submenu_block.settings.submenu_title == main_title %}
                            <li class="header-menu-item-{{ section.id }} header-submenu-item-{{ section.id }}">       
                              {% if submenu_block.settings.menu_url != blank %}
                                <a href="{{ submenu_block.settings.menu_url }}" style="text-decoration: none;">
                              {% endif %}                       
                                <div class="header-submenu-item-image-{{ section.id }}">
                                  {% if submenu_block.settings.menu_image != blank %}
                                    {{ submenu_block.settings.menu_image | image_url: width: 1500 | image_tag:
                                      loading: loading_attribute,
                                      sizes: "(min-width: 1024px) 100vw, 100vw",
                                      widths: '375, 550, 750, 1100, 1500, 1780, 2000, 3000',
                                      alt: submenu_block.settings.menu_image.alt | escape
                                    }}
                                  {% else %}
                                    {{ 'image' | placeholder_svg_tag }}
                                  {% endif %}
                                  {% if submenu_block.settings.add_tag %}
