                                    {% endif %}
                                  {% endif %}
                                </div>
                                <div class="header-menu-item-content-{{ section.id }}">
                                  {% if submenu_block.settings.menu_heading %}
                                    <p class="header-menu-link-{{ section.id }} header-menu-link-title-{{ section.id }}">{{ submenu_block.settings.menu_heading }}</p>
                                  {% endif %}
                                  {% if submenu_block.settings.menu_text %}
                                    <div class="header-menu-link-text-{{ section.id }}">{{ submenu_block.settings.menu_text }}</div>
                                  {% endif %}
                                </div>      
                              {% if submenu_block.settings.menu_url != blank %}
                                </a>
                              {% endif %}                             
                            </li>
                          {% endif %}
                        {% endfor %}
                      {% endif %}
                      {% if use_promo_image_mobile %}
                        {% if block.settings.show_promotion %}
                          <{% if block.settings.promo_image_url != blank %}a href="{{ block.settings.promo_image_url }}"{% else %}div{% endif %} class="header-submenu-promotion-image-{{ section.id }}">
