                        {% endif %}
                        {% if block.settings.show_second_promotion %}
                          <{% if block.settings.second_promo_image_url != blank %}a href="{{ block.settings.second_promo_image_url }}"{% else %}div{% endif %} class="header-submenu-promotion-image-{{ section.id }}">
                            {% if block.settings.second_promotional_image != blank %}
                              {{ block.settings.second_promotional_image | image_url: width: 1500 | image_tag:
                                loading: loading_attribute,
                                sizes: "(min-width: 1024px) 100vw, 100vw",
                                widths: '375, 550, 750, 1100, 1500, 1780, 2000, 3000',
                                alt: block.settings.second_promotional_image.alt | escape
                              }}
                            {% else %}
                              {{ 'image' | placeholder_svg_tag }}
                            {% endif %}
                            {% if block.settings.second_promotion_tag_text != blank %}
                              <p class="header-submenu-promotion-tag-{{ section.id }}" >{{ block.settings.second_promotion_tag_text }}</p>
                            {% endif %}
                            <div class="header-submenu-promotion-content-{{ section.id }}">
                              {% if block.settings.second_promotional_caption != blank %}
                                <div class="header-submenu-promotion-caption-{{ section.id }}">{{ block.settings.second_promotional_caption }}</div>
                              {% endif %}
                              {% if block.settings.second_promotional_text != blank %}
                                <div class="header-submenu-promotion-text-{{ section.id }}">{{ block.settings.second_promotional_text }}</div>
                              {% endif %}
                            </div>
                            
                          </{% if block.settings.second_promo_image_url != blank %}a{% else %}div{% endif %}>   
                        {% endif %}
                      {% endif %}
                    </ul>
                  </div>
                        {% else %}
                          <{% if block.settings.link_url != blank %}a href="{{ block.settings.link_url }}"{% else %}p{% endif %} class="header-menu-link-{{ section.id }} heading">
                            {{ block.settings.text }}
                          </{% if block.settings.link_url != blank %}a{% else %}p{% endif %}>
                        {% endif %}
                      </li>
                    {% endif %}
                  {% endfor %}

                  {% if use_search and search_menu_mobile %}
                    <li class="header-menu-item-{{ section.id }}">
                      <a href="{{ routes.search_url }}" style="padding: 10px 0;" class="header-menu-link-{{ section.id }}">{{ search_text }}</a>
                    </li>
                  {% endif %}

                  {% if use_account and account_menu_mobile %}
                    <li class="header-menu-item-{{ section.id }}">
                      <a href="{{ routes.account_url }}" style="padding: 10px 0;" class="header-menu-link-{{ section.id }}">{{ account_text }}</a>
                    </li>
                  {% endif %}
                </ul>        
              {% endif %}           
            </div>
          </div>
          {% endif %}
        </div>  

        {% if section.blocks.size != 0 %}
          <ul class="header-menu-list-{{ section.id }} header-menu-list-desktop-{{ section.id }}">
            {% for block in section.blocks %}
              {% if block.type == 'menu_item' %}
                {% assign has_matching_submenu_items = false %}
                {% assign current_menu_item_text = block.settings.text %}

                {% for submenu_block in section.blocks %}
                  {% if submenu_block.type == 'submenu_item' and submenu_block.settings.submenu_title == current_menu_item_text %}
                    {% assign has_matching_submenu_items = true %}
                    {% break %}
                  {% endif %}
                {% endfor %}

                <li class="header-menu-item-{{ section.id }} header-menu-item-main-{{ section.id }} {% if has_matching_submenu_items %}header-menu-item-parent-{{ section.id }}{% endif %} {% if block.settings.link_url != blank %}link-{{ section.id }}{% else %}parent-{{ section.id }}{% endif %}">
                  <{% if block.settings.link_url != blank %}a href="{{ block.settings.link_url }}"{% else %}p{% endif %} class="header-menu-link-{{ section.id }}" {% if block.settings.use_accent_color %} style="color: {{ block.settings.link_accent_color }}" {% endif %}>
                    {{ block.settings.text }}

                  </{% if block.settings.link_url != blank %}a{% else %}p{% endif %}>

                  {% if has_matching_submenu_items %}
                    <div class="header-submenu-{{ section.id }}">
                      <div class="header-submenu-inner-{{ section.id }}">
                        <div class="section-{{ section.id }}-settings">
                          <div class="header-submenu-menu-{{ section.id }}">
                            <ul class="header-menu-list-{{ section.id }}">
                              {% assign main_title = block.settings.text %}
                              {% for submenu_block in section.blocks %}
                                {% if submenu_block.type == 'submenu_item' %}
                                  {% if submenu_block.settings.submenu_title == main_title %}
                                    <li class="header-menu-item-{{ section.id }} header-submenu-item-{{ section.id }}">
                                      {% if submenu_block.settings.menu_url != blank %}
                                        <a href="{{ submenu_block.settings.menu_url }}" style="text-decoration: none;">
                                      {% endif %}

                                      <div class="header-submenu-item-image-{{ section.id }}">
                                        {% if submenu_block.settings.menu_image != blank %}
                                          {{ submenu_block.settings.menu_image | image_url: width: 1500 | image_tag:
                                            loading: loading_attribute,
                                            sizes: "(min-width: 1024px) 100vw, 100vw",
                                            widths: '375, 550, 750, 1100, 1500, 1780, 2000, 3000',
                                            alt: submenu_block.settings.menu_image.alt | escape
                                          }}
                                        {% else %}
                                          {{ 'image' | placeholder_svg_tag }}
                                        {% endif %}
                                        {% if submenu_block.settings.add_tag %}
                                          {% if submenu_block.settings.tag_text != blank %}
                                            <p class="header-submenu-image-tag-{{ section.id }}" style="color: {{ submenu_block.settings.tag_color }}; background-color: {{ submenu_block.settings.tag_bg_color }};" >{{ submenu_block.settings.tag_text }}</p>
                                          {% endif %}
                                        {% endif %}
                                      </div>
                                      <div class="header-menu-item-content-{{ section.id }}">
                                        {% if submenu_block.settings.menu_heading %}
                                          <p class="header-menu-link-{{ section.id }} header-menu-link-title-{{ section.id }}">{{ submenu_block.settings.menu_heading }}</p>
                                        {% endif %}
                                        {% if submenu_block.settings.menu_text %}
                                          <div class="header-menu-link-text-{{ section.id }}">{{ submenu_block.settings.menu_text }}</div>
                                        {% endif %}
                                      </div>   

                                      {% if submenu_block.settings.menu_url != blank %}
                                        </a>
                                      {% endif %}                                
                                    </li>
                                  {% endif %}
                                {% endif %}
                              {% endfor %}
                            </ul>
                            <div class="header-submenu-promotion-wrapper-{{ section.id }}">
                              {% if block.settings.show_promotion %}
                                <{% if block.settings.promo_image_url != blank %}a href="{{ block.settings.promo_image_url }}"{% else %}div{% endif %} class="header-submenu-promotion-image-{{ section.id }}">
                                  {% if block.settings.promotional_image != blank %}
                                    {{ block.settings.promotional_image | image_url: width: 1500 | image_tag:
                                      loading: loading_attribute,
                                      sizes: "(min-width: 1024px) 100vw, 100vw",
                                      widths: '375, 550, 750, 1100, 1500, 1780, 2000, 3000',
                                      alt: block.settings.promotional_image.alt | escape
                                    }}
                                  {% else %}
                                    {{ 'image' | placeholder_svg_tag }}
                                  {% endif %}
                                  {% if block.settings.promotion_tag_text != blank %}
                                    <p class="header-submenu-promotion-tag-{{ section.id }}" >{{ block.settings.promotion_tag_text }}</p>
                                  {% endif %}
                                  <div class="header-submenu-promotion-content-{{ section.id }}">
                                    {% if block.settings.promotional_caption != blank %}
                                      <div class="header-submenu-promotion-caption-{{ section.id }}">{{ block.settings.promotional_caption }}</div>
                                    {% endif %}
                                    {% if block.settings.promotional_text != blank %}
                                      <div class="header-submenu-promotion-text-{{ section.id }}">{{ block.settings.promotional_text }}</div>
                                    {% endif %}
                                  </div>
                                </{% if block.settings.promo_image_url != blank %}a{% else %}div{% endif %}>        
                              {% endif %}
                              {% if block.settings.show_second_promotion %}
                                <{% if block.settings.second_promo_image_url != blank %}a href="{{ block.settings.second_promo_image_url }}"{% else %}div{% endif %} class="header-submenu-promotion-image-{{ section.id }}">
                                  {% if block.settings.second_promotional_image != blank %}
                                    {{ block.settings.second_promotional_image | image_url: width: 1500 | image_tag:
                                      loading: loading_attribute,
                                      sizes: "(min-width: 1024px) 100vw, 100vw",
                                      widths: '375, 550, 750, 1100, 1500, 1780, 2000, 3000',
                                      alt: block.settings.second_promotional_image.alt | escape
                                    }}
                                  {% else %}
                                    {{ 'image' | placeholder_svg_tag }}
                                  {% endif %}
                                  {% if block.settings.second_promotion_tag_text != blank %}
                                    <p class="header-submenu-promotion-tag-{{ section.id }}" >{{ block.settings.second_promotion_tag_text }}</p>
                                  {% endif %}
                                  <div class="header-submenu-promotion-content-{{ section.id }}">
                                    {% if block.settings.second_promotional_caption != blank %}
                                      <div class="header-submenu-promotion-caption-{{ section.id }}">{{ block.settings.second_promotional_caption }}</div>
                                    {% endif %}
                                    {% if block.settings.second_promotional_text != blank %}
                                      <div class="header-submenu-promotion-text-{{ section.id }}">{{ block.settings.second_promotional_text }}</div>
                                    {% endif %}
                                  </div>
                                
                                </{% if block.settings.second_promo_image_url != blank %}a{% else %}div{% endif %}>         
                              {% endif %}
                            </div>                                    
                          </div>
                        </div>
                      </div>
                    </div>
                  {% endif %}
                </li>
              {% endif %}
            {% endfor %}
          </ul>
        {% endif %} 

        <div class="header-logo-{{ section.id }}">
          {% if logo != blank %}
            <a href="{{ shop.url }}" class="header-logo-image-{{ section.id }} {% if logo_mobile != blank %}header-logo-desktop-{{ section.id }}{% endif %}">
              <img src="{{ logo | image_url }}" alt="{{ logo.alt }}" {% if lazy %}loading="lazy"{% endif %} {% if second_logo != blank %}class="header-logo-first-{{ section.id }}"{% endif %}>
              {% if second_logo != blank %}
                <img src="{{ second_logo | image_url }}" alt="{{ second_logo.alt }}" {% if lazy %}loading="lazy"{% endif %} class="header-logo-second-{{ section.id }}">
              {% endif %}
            </a>
            {% if logo_mobile != blank %}
              <a href="{{ shop.url }}" class="header-logo-image-{{ section.id }} header-logo-mobile-{{ section.id }}">
                <img src="{{ logo_mobile | image_url }}" alt="{{ logo_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} {% if second_logo_mobile != blank %}class="header-logo-first-{{ section.id }}"{% endif %}>
                {% if second_logo_mobile != blank %}
                  <img src="{{ second_logo_mobile | image_url }}" alt="{{ second_logo_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} class="header-logo-second-{{ section.id }}">
                {% endif %}
              </a>
            {% endif %}
          {% else %}
            <a href="{{ shop.url }}" class="header-logo-text-{{ section.id }}">{{ shop.name }}</a>
          {% endif %}
        </div>

        <div class="header-right-{{ section.id }}">
          {% if use_search %}
            <a href="{{ routes.search_url }}" class="header-search-{{ section.id }}">
              <span class="header-search-icon-{{ section.id }}">
                <svg viewBox="-0.5 -0.5 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" height="16" width="16">
                  <path d="m11.26925 11.26925 3.015375 3.015375" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="1"></path>
                  <path d="M0.7153750000000001 6.746124999999999c0 3.33075 2.7000625 6.0308125 6.03075 6.0308125 1.6683124999999999 0 3.1783125 -0.6773750000000001 4.270125 -1.7720625 1.0879999999999999 -1.0909375000000001 1.7606875 -2.59625 1.7606875 -4.25875 0 -3.3306875000000002 -2.7000625 -6.03075 -6.0308125 -6.03075 -3.3306875000000002 0 -6.03075 2.7000625 -6.03075 6.03075Z" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="1"></path>
                </svg>
              </span>
              <span class="header-search-text-{{ section.id }}">{{ search_text }}</span>
            </a>
          {% endif %}

          {%- if shop.customer_accounts_enabled and use_account -%}
            <a href="{%- if customer -%}{{ routes.account_url }}{%- else -%}{{ routes.account_login_url }}{%- endif -%}" class="header-account-{{ section.id }}">
              <span class="header-account-icon-{{ section.id }}">
                <svg viewBox="-0.5 -0.5 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" height="16" width="16">
                  <path d="M1.62 14.219999999999999v-0.8400000000000001C1.62 10.132562499999999 4.2525625 7.5 7.5 7.5h0c3.2474375 0 5.88 2.6325625 5.88 5.88v0.8400000000000001" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="1"></path>
                  <path d="M7.5 7.5c1.8556249999999999 0 3.3600000000000003 -1.504375 3.3600000000000003 -3.3600000000000003 0 -1.8556875000000002 -1.504375 -3.3600000000000003 -3.3600000000000003 -3.3600000000000003 -1.8556875000000002 0 -3.3600000000000003 1.5043125 -3.3600000000000003 3.3600000000000003C4.14 5.995625 5.644312500000001 7.5 7.5 7.5Z" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="1"></path>
                </svg>
             </span>
              <span class="header-account-text-{{ section.id }}">{{ account_text }}</span>
            </a>
          {%- endif -%}

          <a href="{{ routes.cart_url }}" class="header-cart-{{ section.id }}">
            <span class="header-cart-icon-{{ section.id }}">
              <svg viewBox="-0.5 -0.5 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" height="16" width="16">
                <path d="m12.973625 5.7630625 1.04375 6.784625c0.1405 0.91325 -0.566125 1.7369375 -1.490125 1.7369375H2.47275c-0.9239999999999999 0 -1.630625 -0.8236875 -1.490125 -1.7369375l1.04375 -6.784625c0.11318750000000001 -0.7355 0.7460625 -1.2784375000000001 1.4901875 -1.2784375000000001h7.9669375c0.744125 0 1.3769375 0.5429375000000001 1.490125 1.2784375000000001Z" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="1"></path>
                <path d="M9.0076875 2.2230625c0 -0.8326875 -0.675 -1.5076875 -1.5076875 -1.5076875s-1.5076875 0.675 -1.5076875 1.5076875" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="1"></path>
              </svg>
            </span>
            <span class="header-cart-text-{{ section.id }}">{{ cart_text }}</span>       
            <div class="cart-count-bubble cart-count-bubble-{{ section.id }}">
              {%- if cart.item_count < 100 -%}
                <span aria-hidden="true">({{ cart.item_count }})</span>
              {%- endif -%}
            </div>      
          </a>
        </div>
      </div>      
    </div>
</div>

<script>
  function initHeader17() {
    let header = document.querySelector('.header-{{ section.id }}');
    let menuMobile = document.querySelector('.header-menu-mobile-{{ section.id }}');
    let cartBubble = document.querySelector('.cart-count-bubble-{{ section.id }}');

    let parentItems = document.querySelectorAll('.header-menu-item-parent-{{ section.id }}');
    if (header) {
      {% if show_type != 'top' %}
      if (window.scrollY > 0) {
        header.classList.add('onscroll');
      } else {
        header.classList.add('top');
      }

      let lastScrollTop = window.scrollY;

      document.addEventListener('scroll', () => {
        const currentScrollTop = window.scrollY;
        const maxScroll = document.documentElement.scrollHeight - window.innerHeight;

        if (currentScrollTop === 0) {
          header.classList.add('top');
        }

        if (currentScrollTop <= 0) {
          header.classList.add('active');
          header.classList.remove('onscroll');
          return;
        }

        if (currentScrollTop >= maxScroll) {
          header.classList.remove('active');
          return;
        }

        if (currentScrollTop !== lastScrollTop) {
          if (header.classList.contains('scroll')) {
            if (currentScrollTop > lastScrollTop) {
              header.classList.remove('active');
            } else {
              header.classList.add('active');
            }
          }

          if (header.classList.contains('transparent')) {
            if (currentScrollTop === 0 && lastScrollTop > currentScrollTop) {
              header.classList.remove('onscroll');
              header.classList.add('top');
            } else if (currentScrollTop > 0 && !header.classList.contains('onscroll')) {
              setTimeout(() => {
                header.classList.add('onscroll');
                header.classList.remove('top');
              }, 50);
            }
          }


          lastScrollTop = currentScrollTop;
        }
      });
      {% endif %}

      header.addEventListener('mouseenter', () => {
        header.classList.add('onscroll');
      });

      header.addEventListener('mouseleave', () => {
        if (window.scrollY === 0) {
          header.classList.remove('onscroll');
        }
      });

      let burger_icon = header.querySelector('.header-menu-burger-{{ section.id }}');
      if(burger_icon) {
        burger_icon.addEventListener('click', () => {
          header.classList.toggle('open');
          header.classList.remove('onscroll');
          header.classList.toggle('menu-open');
          if(header.classList.contains('open')) {
            document.body.style.overflow = 'hidden';
          } else {
            document.body.style.overflow = '';
          }
        })
      }

      document.addEventListener('click', (e) => {
        if(e.target == menuMobile) {
          document.body.style.overflow = '';
          header.classList.remove('open');
        }
      });

      if (parentItems) {
        parentItems.forEach(el => {
          el.addEventListener('mouseenter', () => {
            header.classList.add('onscroll');
            header.classList.add('menu-open');
            el.classList.add('active');
          });

          el.addEventListener('mouseleave', () => {
            header.classList.remove('menu-open');
            el.classList.remove('active');
          });

          document.addEventListener('click', (event) => {
            if (!el.contains(event.target) && !el.classList.contains('active')) {
              return;
            }

            if (!el.contains(event.target) && el.classList.contains('active')) {
              el.classList.remove('active');
            }
          });
        });
      }
    }

    const openButtons = document.querySelectorAll('.header-menu-link-open-{{ section.id }}');
    const submenus = document.querySelectorAll('.header-mobile-submenu-{{ section.id }}');

    const submenuHierarchy = {};

    submenus.forEach(submenu => {
      submenu.style.display = 'none';
      submenu.style.opacity = '0';
      submenu.style.height = '0';
      submenu.style.overflow = 'hidden';

      submenu.isOpen = false;

      const parentSubmenu = submenu.parentElement.closest('.header-mobile-submenu-{{ section.id }}');
      if (parentSubmenu) {
        submenu.setAttribute('data-level', 'nested');

        const parentIndex = Array.from(submenus).indexOf(parentSubmenu);
        const childIndex = Array.from(submenus).indexOf(submenu);

        if (!submenuHierarchy[parentIndex]) {
          submenuHierarchy[parentIndex] = [];
        }
        submenuHierarchy[parentIndex].push(childIndex);
      } else {
        submenu.setAttribute('data-level', 'root');
      }
    });

    openButtons.forEach((button, index) => {
      const listItem = button.closest('li');
      if (!listItem) return;

      const targetSubmenu = listItem.querySelector('.header-mobile-submenu-{{ section.id }}');
      if (!targetSubmenu) return;

      button.submenuIndex = Array.from(submenus).indexOf(targetSubmenu);

      button.addEventListener('click', (e) => {
        e.stopPropagation();
        toggleSubmenu(button.submenuIndex);
      });
    });

    function openSubmenu(index) {
      const submenu = submenus[index];
      if (!submenu) return;

      if (submenu.isOpen) return;

      const isNested = submenu.getAttribute('data-level') === 'nested';
      const parentSubmenu = isNested ? submenu.parentElement.closest('.header-mobile-submenu-{{ section.id }}') : null;


      submenu.style.display = 'block';
      submenu.isOpen = true;

      submenu.style.height = 'auto';
      const submenuHeight = submenu.offsetHeight + 10;
      submenu.style.height = '0';

      setTimeout(() => {
        submenu.style.transition = 'all 0.25s ease 0s';
        submenu.style.height = `${submenuHeight}px`;

        if (isNested && parentSubmenu && parentSubmenu.isOpen) {
          const parentCurrentHeight = parseInt(parentSubmenu.style.height);
          const newParentHeight = parentCurrentHeight + submenuHeight;
          parentSubmenu.style.height = `${newParentHeight}px`;
        }

        setTimeout(() => {
          submenu.style.opacity = '1';
        }, 10);
      }, 10);

      const button = findButtonForSubmenu(index);
      if (button) {
        button.classList.add('active');
        const paths = button.querySelectorAll('path');
        if (paths.length > 0) {
          paths[0].style.opacity = '0';
        }
      }
    }

    function closeSubmenu(index) {
      const submenu = submenus[index];
      if (!submenu) return;


      if (!submenu.isOpen) return;

      const isNested = submenu.getAttribute('data-level') === 'nested';
      const parentSubmenu = isNested ? submenu.parentElement.closest('.header-mobile-submenu-{{ section.id }}') : null;

      const submenuHeight = parseInt(submenu.style.height);

      if (submenuHierarchy[index]) {
        submenuHierarchy[index].forEach(nestedIndex => {
          closeSubmenu(nestedIndex);
        });
      }

      const button = findButtonForSubmenu(index);
      if (button) {
        button.classList.remove('active');
        const paths = button.querySelectorAll('path');
        if (paths.length > 0) {
          paths[0].style.opacity = '1';
        }
      }

      submenu.style.transition = 'all 0.25s ease 0s';
      submenu.style.height = '0';

      if (isNested && parentSubmenu && parentSubmenu.isOpen) {
        const parentCurrentHeight = parseInt(parentSubmenu.style.height);
        const newParentHeight = parentCurrentHeight - submenuHeight;
        parentSubmenu.style.height = `${newParentHeight}px`;
      }

      setTimeout(() => {
        submenu.style.opacity = '0';
      }, 250);

      setTimeout(() => {
        submenu.style.display = 'none';
        submenu.isOpen = false;
      }, 300);
    }

    function toggleSubmenu(index) {
      const submenu = submenus[index];
      if (!submenu) return;

      if (submenu.isOpen) {
        closeSubmenu(index);
      } else {
        openSubmenu(index);
      }
    }

    function findButtonForSubmenu(submenuIndex) {
      for (let i = 0; i < openButtons.length; i++) {
        if (openButtons[i].submenuIndex === submenuIndex) {
          return openButtons[i];
        }
      }
      return null;
    }

    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {

        submenus.forEach((submenu, index) => {
          if (submenu.isOpen) {

            const currentHeight = submenu.style.height;
            submenu.style.height = 'auto';
            let calculatedHeight = submenu.offsetHeight;

            submenu.style.height = currentHeight;
            setTimeout(() => {
              submenu.style.transition = 'all 0.25s ease 0s';
              submenu.style.height = `${calculatedHeight}px`;

              if (submenu.getAttribute('data-level') === 'root' && submenuHierarchy[index]) {
                let additionalHeight = 0;
                submenuHierarchy[index].forEach(nestedIndex => {
                  const nestedSubmenu = submenus[nestedIndex];
                  if (nestedSubmenu.isOpen) {
                    additionalHeight += parseInt(nestedSubmenu.style.height);
                  }
                });

                submenu.style.height = `${calculatedHeight + additionalHeight}px`;
              }
            }, 10);
          }
        });
      }, 250);
    });

    const buyButton = document.querySelector('form[action="/cart/add"] button[type="submit"]');

    if(buyButton) {
      console.log(buyButton);
      buyButton.addEventListener('click', (event) => {
        updateCartCount();
      });
    }   

    function updateCartCount() {
      setTimeout(() => {
        fetch('/cart.js')
            .then(res => res.json())
            .then(cart => {
                let count = cart.item_count;
                console.log(count)
                if (cartBubble) {
                    cartBubble.innerHTML = `<span aria-hidden="true">(${count})</span>`;
                }
            });
      }, 1000);      
    }
  }

  document.addEventListener('DOMContentLoaded', initHeader17);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initHeader17);
    document.addEventListener('shopify:section:load', initHeader17);
  }
</script>
