  assign logo_mobile = section.settings.logo_mobile
  assign second_logo_mobile = section.settings.second_logo_mobile
  assign logo_position = section.settings.logo_position
  assign logo_width = section.settings.logo_width
  assign logo_width_mobile = section.settings.logo_width_mobile

  assign name_size = section.settings.name_size
  assign name_size_mobile = section.settings.name_size_mobile
  assign name_color = section.settings.name_color
  assign name_hover_color = section.settings.name_hover_color
  assign name_hero_color = section.settings.name_hero_color
  assign name_hover_hero_color = section.settings.name_hover_hero_color
  assign name_custom = section.settings.name_custom
  assign name_font = section.settings.name_font
  assign name_height = section.settings.name_height

  assign use_search = section.settings.use_search
  assign search_text = section.settings.search_text
  assign search_menu_mobile = section.settings.search_menu_mobile

  assign use_cart = section.settings.use_cart
  assign cart_text = section.settings.cart_text

  assign use_account = section.settings.use_account
  assign account_text = section.settings.account_text
  assign account_menu_mobile = section.settings.account_menu_mobile

  assign right_size = section.settings.right_size
  assign right_size_mobile = section.settings.right_size_mobile
  assign right_color = section.settings.right_color
  assign right_color_mobile = section.settings.right_color_mobile
  assign right_hero_color_mobile = section.settings.right_hero_color_mobile
  assign right_custom = section.settings.right_custom
  assign right_font = section.settings.right_font
  assign right_height = section.settings.right_height
  assign right_bg_color = section.settings.right_bg_color

  assign right_icon_size_mobile = section.settings.right_icon_size_mobile
  assign right_icon_size = section.settings.right_icon_size
  assign right_icon_color = section.settings.right_icon_color
  assign right_icon_hover_color = section.settings.right_icon_hover_color
  assign right_icon_hero_color = section.settings.right_icon_hero_color
  assign right_icon_hero_hover_color = section.settings.right_icon_hero_hover_color

  assign use_payments = section.settings.use_payments

  assign count_bg_color = section.settings.count_bg_color
  assign count_color = section.settings.count_color

-%}

{%- style -%}

  {{ link_font | font_face: font_display: 'swap' }}
  {{ name_font | font_face: font_display: 'swap' }}
  {{ right_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
  }

  .header-{{ section.id }}.transparent {
    background-color: transparent !important;
    background-image: none !important;
    position: absolute;
    width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
    top: 0;
    left: 0;
    z-index: 999;
  }

  .header-{{ section.id }}.fixed {
    position: fixed;
    top: 0;
    left: 0;
    width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
    z-index: 999;
  }

  .header-{{ section.id }}.onscroll {
    background-color:{{ background_color }} !important;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%) ;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0) ;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.open {
    background-color:{{ background_color }} !important;
    transition: all 0.25s ease;
  }

  .header-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr {{ logo_width_mobile }}px 1fr;
    align-items: center;
    gap: {{ body_gap_mobile }}px;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: none;
    flex-wrap: wrap;
    list-style: none;
  }


  .header-menu-mobile-{{ section.id }} .header-menu-list-{{ section.id }} {
    display: flex;
    flex-direction: column;
      }


  .header-menu-item-{{ section.id }} {
    line-height: 100%;
    cursor: pointer;
    position: relative;
    box-sizing: border-box;
    padding-bottom: 3px;
   justify-content: center;
  }

  .header-submenu-{{ section.id }} .header-menu-item-{{ section.id }} {
    text-align: left; 
  }

  .header-menu-link-{{ section.id }} {
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
    transition: all 0.25s ease;
    position: relative;
    z-index: 3;
    justify-content: center;
    text-align: center; 
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
    color: {{ link_hero_color }};
  }

  .header-{{ section.id }}.open.transparent .header-menu-link-{{ section.id }} {
    color: {{ link_color }};
    transition: all 0.25s ease;
  }

  .header-submenu-{{ section.id }} .header-menu-link-{{ section.id }} {
    color: {{ submenu_link_color }} !important;
    font-weight: 400;
    /* margin: auto; */
  }

  .header-submenu-{{ section.id }} .header-menu-item-icon-{{ section.id }} svg path {
    fill: {{ submenu_link_color }} !important;
  }

  .header-submenu-{{ section.id }} .header-menu-item-{{ section.id }}:hover > .header-menu-link-{{ section.id }} {
    color: {{ submenu_link_hover_color }} !important;
  }

  .header-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    max-width: fit-content;
    flex-basis: 0;
    width: max-content;
  }

  .header-menu-item-icon-{{ section.id }} {
    display: block;
    flex: 0 0 8px;
    width: 8px;
    transition: all 0.25s ease 0s;
    color: {{ link_color }};
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-menu-list-{{ section.id }} > .header-menu-item-{{ section.id }} > .header-menu-item-icon-{{ section.id }} {
    color: {{ link_hero_color }};
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}:not(.onscroll).transparent.open .header-menu-item-icon-{{ section.id }} {
    color: {{ link_color }} !important;
    transition: all 0.25s ease 0s;
  }

  .header-menu-list-{{ section.id }} > .header-menu-item-{{ section.id }}:hover > .header-menu-item-icon-{{ section.id }} svg path {
    fill: {{ link_hover_color }} !important;
    transition: all 0.25s ease 0s;
  }

  .header-menu-item-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-submenu-{{ section.id }} {
    position: absolute;
    z-index: 3;
    top: 100%;
    left: 0;
    padding: calc({{ submenu_padding_vertical }}px + {{ padding_bottom }}px) {{ submenu_padding_horizontal }}px {{ submenu_padding_vertical }}px {{ submenu_padding_horizontal }}px;
    border-radius: {{ submenu_radius }}px;
    background-color: {{ background_color }};
    transform: translateY(30px);
    opacity: 0;
    pointer-events: none;
    transition: all 0.25s ease 0s;
    list-style: none;
    margin: 0px;
    display: flex;
    flex-direction: column;
    gap: 8px;

  }

  .header-submenu-{{ section.id }} .header-submenu-{{ section.id }} {
    padding-top: {{ submenu_padding_vertical }}px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, .15);
    top: 0;
    left: calc(100%);
  }

  .header-menu-burder-{{ section.id }} {
    display: block;
    width: {{ menu_icon_size_mobile }}px;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease;
    cursor: pointer;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
    height: 18px;
  }

  .header-menu-burger-icon-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 0;
    margin-top: -1px;
    width: 100%;
    height: 2px;
    border-radius: 1px;
    background: {{ link_color }};
    transition: transform .4s ease; 
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
    background: {{ link_hero_color }};
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }} {
    transform: rotate(45deg);
    transition-delay: .22s;
    background: {{ link_color }} !important;
  }

  .header-menu-burger-icon-{{ section.id }}::before,
  .header-menu-burger-icon-{{ section.id }}::after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 2px;
    border-radius: 1px;
    background-color: inherit;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::before {
    top: -7px;
    transition: top .25s ease .27s, opacity .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::before {
    top: 0;
    opacity: 0;
    width: 100%;
    transition: top .2s ease, opacity .2s ease .22s, width .1s ease;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::after {
    bottom: -7px;
    transition: bottom .25s ease .27s, transform .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::after {
    bottom: 0;
    transform: rotate(-90deg);
    width: 100%;
    transition: bottom .2s ease, transform .2s ease .22s, width .1s ease;
    background: {{ link_color }} !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
  .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after{
    background: {{ link_hero_color }};
  }

  .header-logo-{{ section.id }} {
    width: 100%;
    max-width: {{ logo_width_mobile }}px;
    text-align: center;
    position: relative;
    z-index: 3;
  }

  .header-logo-image-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-text-{{ section.id }} {
    margin: 0;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
    color: {{ name_hero_color }};
  }

  .header-{{ section.id }}.open.transparent .header-logo-text-{{ section.id }} {
    color: {{ name_color }};
    transition: all 0.25s ease;
  }

  .header-{{ section.id }} .header-logo-first-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }} .header-logo-second-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-first-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}.open.transparent .header-logo-first-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-logo-second-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }}.open.transparent .header-logo-second-{{ section.id }} {
    display: none !important;
  }

  .header-right-{{ section.id }} {
    order: 3;
    display: flex;
    justify-content: end;
    gap: 16px;
  }

  .header-right-icon-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-direction: column;
    gap: 3px;
    text-decoration: none;
    color: {{ right_icon_color }};
    transition: all 0.25s ease 0s;
    position: relative;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-right-icon-{{ section.id }} {
    color: {{ right_icon_hero_color }};
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-right-icon-{{ section.id }}:hover {
     color: {{ right_icon_hero_hover_color }};
     transition: all 0.25s ease 0s;
  }

   .header-{{ section.id }}:not(.onscroll).transparent .header-right-icon-{{ section.id }}:hover svg path {
     fill: {{ right_icon_hero_hover_color }};
     transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open.transparent .header-right-icon-{{ section.id }} {
    color: {{ right_icon_color }};
    transition: all 0.25s ease 0s;
  }

  .header-right-icon-{{ section.id }}:hover {
    color: {{ right_icon_hero_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .header-right-icon-{{ section.id }}:hover svg path {
    stroke: {{ right_icon_hero_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .header-right-icon-{{ section.id }}:hover {
    color: {{ right_icon_hover_color }} !important;
    transition: all 0.25s ease 0s;
  }

  .header-right-icon-{{ section.id }} svg {
    display: block;
    width: {{ right_icon_size_mobile }}px;
    height: 100%;
    object-fit: cover;
  }

  .header-right-text-{{ section.id }} {
    min-width: max-content;
    margin: 0;
    font-size: {{ right_size_mobile }}px;
    color: {{ right_color_mobile }};
    line-height: {{ right_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}:not(.onscroll).transparent .header-right-text-{{ section.id }} {
    color: {{ right_hero_color_mobile }};
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.open.transparent .header-right-text-{{ section.id }} {
    color: {{ right_color_mobile }};
    transition: all 0.25s ease 0s;
  }

  .header-menu-mobile-{{ section.id }} {
    position: absolute;
    top: 100%;
    left: 0;
    width: 100%;
    height: 100dvh;
    overflow-y: auto;
    background-color: rgba(0,0,0,0.5);
    opacity: 0;
    pointer-events: none;
    transition: all 0.25s ease 0s;
    z-index: 0; 
  }

  .header-{{ section.id }}.open .header-menu-mobile-{{ section.id }} {
    opacity: 1;
    pointer-events: all;
    z-index: 99; 
  }

  .header-menu-mobile-inner-{{ section.id }} {
    background-color: {{ background_color }};
    max-width: 80%;
    height: 100%;
    padding: 30px 14px;
    position: relative;
    overflow-x: hidden;
    overflow-y: auto;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }

  .header-mobile-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-{{ section.id }} {
    max-width: 100%;
    width: 100%;
    justify-content: space-between;
    position: static;
    padding: {{ list_gap_mobile }}px 0px;
    border-bottom: 1px solid {{ mobile_menu_divider_color }};
    align-items: center; 
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-{{ section.id }}:first-child {
    border-top: 1px solid {{ mobile_menu_divider_color }};
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
    flex: 0 0 10px;
    width: 10px;
    transform: rotate(-90deg);
  }

  .header-menu-mobile-submenu-{{ section.id }} {
    position: absolute;
    background-color: {{ background_color }};
    z-index: 4;
    padding: 20px;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    overflow-x: hidden;
    overflow-y: auto;   
    transition: all 0.25s ease 0s;
    transform
   }

  .header-menu-mobile-submenu-child-{{ section.id }} {
    transform: translateX(-110%);
    transition: all 0.25s ease 0s;
  }

  .header-mobile-menu-item-parent-{{ section.id }}.active > .header-menu-mobile-submenu-child-{{ section.id }} {
    transform: translateX(0%);
    transition: all 0.25s ease 0s;
  }

  .header-menu-mobile-inner-{{ section.id }} {
     transform: translateX(-110%);
      transition: all 0.25s ease 0s;
  }

   .header-{{ section.id }}.open .header-menu-mobile-inner-{{ section.id }} {
      transform: translateX(0%);
      transition: all 0.25s ease 0.25s;
   }

  .header-menu-mobile-submenu-heading-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 20px;
    width: 100%;
    background-color: transparent;
    border: 0px;
    border-top: 1px solid {{ mobile_menu_divider_color }};
    padding: 0px;
    padding: {{ list_gap_mobile }}px 0px;
    box-shadow: none;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-family: "Montserrat";
    font-weight: 700;
}


  .header-menu-mobile-submenu-heading-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
    transform: rotate(90deg);
  }

  .header-mobile-submenu-list-{{ section.id }} {
    margin: 0px;
    display: flex;
    flex-direction: column;
    padding: 0px;
    list-style: none;
  }

  .header-payments-{{ section.id }} {
    margin: 0px;
    margin-top: 16px;
    display: flex;
    align-items: center;
    justify-content: start;
    flex-wrap: wrap;
    gap: 8px;
    padding: 0px;
    padding-top: 10px;
    border-top: 1px solid {{ mobile_menu_divider_color }};
  }

  .header-payment-{{ section.id }} {
    flex: 0 1 38px;
    list-style: none;
  }

  .header-payment-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-mobile-menu-bottom-{{ section.id }} {
    padding-top: 12px;
    border-top: 1px solid {{ mobile_menu_divider_color }};
  }

  .header-mobile-menu-bottom-{{ section.id }} .header-menu-item-{{ section.id }} {
    border: 0px;
    margin: 0px;
    padding: 8px 0px;
  }

  .header-mobile-menu-bottom-{{ section.id }} .header-menu-item-{{ section.id }}:first-child {
    border: 0px;
  }

  .header-logo-desktop-{{ section.id }} {
    display: none;
  }

  .header-{{ section.id }} .cart-count-bubble {
    display: flex;
    justify-content: center;
    align-items: center;
    background-color: {{ count_bg_color }};
    color: {{ count_color }};
    width: 16px;
    height: 16px;
    border-radius: 16px;
    top: -6px;
    right: -6px;
    left: unset;
    bottom: unset;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .header-{{ section.id }}.transparent {
      width: calc(100% - {{ margin_horizontal | times: 2 }}rem);
    }

    .header-{{ section.id }}.fixed {
      width: calc(100% - {{ margin_horizontal | times: 2 }}rem);
    }

    .header-body-{{ section.id }} {
      gap: {{ body_gap }}px;
    }

    .header-menu-burder-{{ section.id }} {
      display: none;
    }

    .header-menu-list-{{ section.id }} {
      display: flex;
      gap: {{ list_gap }}px;
    }

    .header-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      transition: all 0.25s ease;
    }

    .header-menu-item-{{ section.id }}:hover > .header-menu-link-{{ section.id }} {
      transition: all 0.25s ease;
      color: {{ link_hover_color }};
    }

    .header-menu-item-{{ section.id }}::after {
      content: '';
      position: absolute;
