{%- style -%}

  {{ link_font | font_face: font_display: 'swap' }}
  {{ name_font | font_face: font_display: 'swap' }}
  {{ right_link_font | font_face: font_display: 'swap' }}
  {{ cart_font | font_face: font_display: 'swap' }}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ btn_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    position: relative;
    background-color: {{ background_color }};
    transition: all 0.6s ease 0s;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .header-{{ section.id }}.fixed {
    position: fixed;
    top: 0;
    left: 0;
    width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
    z-index: 999;
  }

  .header-{{ section.id }}.onscroll {
    background-color: {{ background_color }};
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%) ;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0) ;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.open {
    background-color: {{ background_open_color }} !important;
    transition: all 0.6s ease 0s;
  }

  .header-body-{{ section.id }} {
    display: grid;
    align-items: center;
    margin-top: {{ layout_mt_mobile }}px;
    padding: {{ layout_padding_vertical_mobile }}px {{ layout_padding_horizontal_mobile }}px;
    border: {{ layout_border_thickness }}px solid {{ layout_border_color }};
    border-radius: {{ layout_radius }}px;
    gap: {{ layout_gap_mobile }}px;
    background-color: {{ layout_bg_color }};
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.open .header-body-{{ section.id }} {
    background-color: {{ layout_bg_hover_color }};
    transition: all 0.25s ease;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: none;
    align-items: center;
    gap: {{ list_gap_mobile }}px;
    flex-wrap: wrap;
    list-style: none;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-list-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: start;
  }

  .header-menu-item-{{ section.id }} {
    margin: 0;
    /* width: 25%; */
    line-height: 100%;
    cursor: pointer;
    box-sizing: border-box;
    padding-bottom: 0;
  }

  .header-menu-link-{{ section.id }} {
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 400;
    transition: all 0.25s ease;
    position: relative;
    z-index: 3;
  }

  .header-menu-item-main-{{ section.id }} > .header-menu-link-{{ section.id }} {
    display: block;
    padding: {{ link_padding_vertical }}px {{ link_padding_horizontal }}px;
    background-color: {{ link_bg_color }};
    border-radius: {{ link_radius }}px;
  }

  .header-menu-item-main-{{ section.id }}.active > .header-menu-link-{{ section.id }} {
    background-color: {{ link_bg_hover_color }};
  }

  .header-{{ section.id }}.open .header-menu-item-main-{{ section.id }}:not(.active) .header-menu-link-{{ section.id }} {
    background-color: transparent;
    color: {{ link_open_color }};
  }

  .header-{{ section.id }}.open .header-menu-mobile-inner-{{ section.id }} .header-menu-link-{{ section.id }} {
    color: {{ submenu_link_color }};
  }

  .header-submenu-{{ section.id }} .header-menu-link-{{ section.id }} {
    color: {{ submenu_link_color }} !important;
    font-weight: 400;
  }

  .header-submenu-{{ section.id }} .header-menu-link-{{ section.id }}:hover {
    color: {{ submenu_link_hover_color }} !important;
  }

  .header-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    max-width: fit-content;
    flex-basis: 0;
  }

  .header-menu-item-icon-{{ section.id }} {
    display: block;
    flex: 0 0 8px;
    width: 8px;
    transition: all 0.25s ease 0s;
    color: {{ submenu_link_color }};
  }

  .header-menu-item-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-submenu-{{ section.id }} {
    position: absolute;
    z-index: 3;
    top: calc(100% - {{ padding_bottom | plus: 10 }}px);
    left: 0;
    width: 100%;
    opacity: 0;
    pointer-events: none;
    transition: all 0.25s ease 0s;
    list-style: none;
    margin: 0px;
  }

  .header-submenu-bg-{{ section.id }} {
    display: block !important;
    height: {{ padding_bottom }}px;
    pointer-events: none;
  }

  .header-submenu-inner-{{ section.id }} {
    background-color: {{ background_open_color }};
    border-bottom: {{ border_thickness }}px solid {{ border_color }};
  }

  .header-submenu-{{ section.id }} .section-{{ section.id }}-settings {
    padding-top: {{ padding_top }}px;
  }

  .header-menu-item-parent-{{ section.id }}.active .header-submenu-{{ section.id }} {
    opacity: 1;
    pointer-events: all;
    transition: all 0.25s ease 0s;
  }

  .header-menu-burder-wrapper-{{ section.id }} {
    position: relative;
    cursor: pointer;
  }

  .header-menu-burder-wrapper-{{ section.id }}:after {
    content: '';
    position: absolute;
    top: -6px;
    left: 10px;
    width: 18px;
    height: 100%;
    z-index: 2;
    min-height: 14px;
  }

  .header-menu-burder-{{ section.id }} {
    position: relative;
    z-index: 1;
    margin-left: 10px;
    display: block;
    width: {{ menu_icon_size_mobile }}px;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease;
    cursor: pointer;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
    transform: translateY(19px);
  }

  .header-menu-burger-icon-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 0;
    margin-top: -1px;
    width: 100%;
    height: 1.5px;
    background: {{ link_color }};
    transition: transform .4s ease; 
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }} {
    transform: rotate(45deg);
    transition-delay: .22s;
    background: {{ link_open_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::before,
  .header-menu-burger-icon-{{ section.id }}::after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 1.5px;
    background-color: inherit;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::before {
    top: -6px;
    transition: top .25s ease .27s, opacity .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::before {
    top: 0;
    opacity: 0;
    width: 100%;
    transition: top .2s ease, opacity .2s ease .22s, width .1s ease;
    background: {{ link_open_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::after {
    bottom: -6px;
    transition: bottom .25s ease .27s, transform .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::after {
    bottom: 0;
    transform: rotate(-90deg);
    width: 100%;
    transition: bottom .2s ease, transform .2s ease .22s, width .1s ease;
    background: {{ link_open_color }} !important;
  }

  .header-logo-{{ section.id }} {
    width: 100%;
    max-width: {{ logo_width_mobile }}px;
    text-align: center;
    position: relative;
    z-index: 3;
  }

  .header-logo-image-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-text-{{ section.id }} {
    margin: 0;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.open .header-logo-text-{{ section.id }}{
    transition: all 0.25s ease;
    color: {{ name_open_color }};
  }

  .header-{{ section.id }} .header-logo-first-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }} .header-logo-second-{{ section.id }} {
    display: none !important;
  }

  .header-{{ section.id }}.open .header-logo-second-{{ section.id }} {
    display: block !important;
  }

  .header-{{ section.id }}.open .header-logo-first-{{ section.id }} {
    display: none !important;
  }

  .header-right-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: end;
    gap: {{ right_gap_mobile }}px;
    flex-wrap: wrap;
    position: relative;
    z-index: 3;
  }

  .header-search-text-{{ section.id }},
  .header-account-text-{{ section.id }} {
    display: none;
    margin: 0;
    color: {{ right_link_color }};
    line-height: {{ right_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s ease 0s;
  }

  .header-{{ section.id }}.open .header-search-text-{{ section.id }},
  .header-{{ section.id }}.open .header-account-text-{{ section.id }}{
    color: {{ right_link_open_color }};
    transition: all .25s ease 0s;
  }

  .header-cart-text-{{ section.id }} {
    display: none;
    margin: 0;
    font-size: {{ cart_size }}px;
    color: {{ cart_color }};
    line-height: {{ cart_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s ease 0s;
  }

  .header-cart-{{ section.id }} .cart-count-bubble span {
    margin: 0;
    font-size: 10px;
    color: {{ cart_color }};
    line-height: 100%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s ease 0s;
  }

  .header-search-icon-{{ section.id }},
  .header-account-icon-{{ section.id }},
  .header-cart-icon-{{ section.id }} {
    display: block;
    width: {{ icon_size_mobile }}px;
  }

  .header-search-icon-{{ section.id }} svg,
  .header-account-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-cart-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-search-icon-{{ section.id }} svg path,
  .header-account-icon-{{ section.id }} svg path {
    stroke: {{ right_link_color }};
    transition: all .25s ease 0s;
  }

  .header-cart-icon-{{ section.id }} svg path {
    stroke: {{ cart_color }};
    transition: all .25s ease 0s;
  }

  .header-{{ section.id }}.open .header-search-icon-{{ section.id }} svg path,
  .header-{{ section.id }}.open .header-account-icon-{{ section.id }} svg path {
    stroke: {{ right_link_open_color }};
    transition: all .25s ease 0s;
  }

  .header-{{ section.id }}.open .header-cart-icon-{{ section.id }} svg path {
    stroke: {{ cart_color }};
    transition: all .25s ease 0s;
  }

  .header-cart-{{ section.id }},
  .header-account-{{ section.id }} {
    text-decoration: none;
  }

  .header-search-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
    cursor: pointer;
    text-decoration: none;
  }

  .header-cart-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 5px;
    position: relative;
    background-color: {{ cart_bg_color }};
    padding: {{ cart_padding_vertical_mobile }}px {{ cart_padding_horizontal_mobile }}px;
    border: {{ cart_border_thickness }}px solid {{ cart_border_color }};
    border-radius: {{ cart_radius }}px;
  }

  .header-cart-{{ section.id }} .cart-count-bubble {
    position: absolute;
    left: 52%;
    top: 45%;
    transform: translateX(-50%);
    padding: 0px;
    background-color: transparent;
    border: 0px;
    margin: 0px;
    width: fit-content;
    height: fit-content;
  }

  .header-menu-mobile-{{ section.id }} {
    position: absolute;
    top: 100%;
    left: 0;
    width: 100%;
    height: 100dvh;
    overflow-y: auto;
    background-color: rgba(0,0,0,0.5);
    transform:translateY(-130%);
    transition: all 0.25s ease 0s;
    z-index: 99;
  }

  .header-{{ section.id }}.open .header-menu-mobile-{{ section.id }} {
    transform:translateY(0%);
  }

  .header-menu-mobile-inner-{{ section.id }} {
    background-color: {{ background_open_color }};
    max-width: 100%;
    height: 100%;
    padding: 20px;
    position: relative;
    overflow-x: hidden;
    overflow-y: auto;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
  }

  .header-mobile-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-{{ section.id }} {
    max-width: 100%;
    width: 100%;
    justify-content: space-between;
    position: static;
    padding: 12px 0px;
    border-bottom: 1px solid {{ submenu_divider_color }};
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-{{ section.id }}:first-child {
    border-top: 1px solid {{ submenu_divider_color }};
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
    flex: 0 0 18px;
    width: 18px;
    transform: rotate(-90deg);
  }

  .header-menu-mobile-submenu-{{ section.id }} {
    position: absolute;
    background-color: {{ background_open_color }};
    z-index: 4;
    padding: 20px;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    overflow-x: hidden;
    overflow-y: auto;
    transform: translateX(110%);
    transition: all 0.25s ease 0s;
  }

  .header-mobile-menu-item-parent-{{ section.id }}.active > .header-menu-mobile-submenu-{{ section.id }} {
    transform: translateX(0%);
    transition: all 0.25s ease 0s;
  }

  .header-menu-mobile-submenu-heading-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 20px;
    width: 100%;
    background-color: transparent;
    border: 0px;
    border-top: 1px solid {{ submenu_divider_color }};
    padding: 0px;
    padding: 10px 0px;
    box-shadow: none;
    font-size: {{ link_size_mobile }}px;
    color: {{ submenu_link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
  }

  .header-menu-mobile-submenu-heading-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
    transform: rotate(90deg);
  }

  .header-mobile-submenu-list-{{ section.id }} {
    margin: 0px;
    margin-top: {{ list_gap_mobile }}px;
    display: flex;
    flex-direction: column;
    gap: {{ list_gap_mobile }}px;
    padding: 0px;
    list-style: none;
  }

  .header-logo-desktop-{{ section.id }} {
    display: none;
  }

  .header-submenu-inner-{{ section.id }} .section-{{ section.id }}-settings {
    display: grid;
    align-items: start;
    gap: {{ submenu_gap }}px;
  }

  .header-submenu-menu-{{ section.id }} {
    display: flex;
    gap: {{ submenu_gap }}px;
    flex-wrap: wrap;
  }

  .header-submenu-menu-{{ section.id }} .header-menu-list-{{ section.id }} {
    display: flex;
    align-items: stretch;
    gap: {{ submenu_gap }}px;
  }

  .header-submenu-menu-{{ section.id }} > .header-menu-list-{{ section.id }} .header-menu-item-with-submenu-{{ section.id }} > .header-menu-link-{{ section.id }}:first-child {
    display: block;
    max-width: fit-content;
    font-size: {{ link_size | plus: 2 }}px;
    font-weight: 700;
    margin-bottom: 12px;
  }

  .header-submenu-menu-{{ section.id }} .header-menu-list-{{ section.id }} .header-menu-list-{{ section.id }} {
    margin-top: {{ submenu_list_gap }}px;
    display: flex;
    flex-direction: column;
    gap: {{ submenu_list_gap }}px;
  }

  .header-submenu-menu-{{ section.id }} >  .header-menu-list-{{ section.id }} > .header-menu-item-{{ section.id }}:not(:last-child) {
    border-right: {{ submenu_divider_thickness }}px solid {{ submenu_divider_color }};
  }

  .header-submenu-collections-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: {{ collection_gap }}px;
  }

  .header-submenu-collection-{{ section.id }} {
    display: block;
    position: relative;
    overflow: hidden;
    border-radius: {{ collection_radius }}px;
  }

  .header-submenu-collection-{{ section.id }}::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    background-color: rgba(0,0,0, 0.2);
    opacity: 0;
    transition: all 0.25s ease 0s;
  }

  .header-submenu-collection-{{ section.id }}:hover::after {
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  .header-submenu-collection-{{ section.id }} img {
    position: relative;
    z-index: 1;
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

