{%- style -%}
  {{ name_font | font_face: font_display: 'swap' }}
  {{ cart_font | font_face: font_display: 'swap' }}
  {{ link_font | font_face: font_display: 'swap' }}
  {{ newsletter_title_font | font_face: font_display: 'swap' }}
  {{ newsletter_text_font | font_face: font_display: 'swap' }}
  {{ input_font | font_face: font_display: 'swap' }}
  {{ submit_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    position: relative;
    transition: all 0.2s ease 0s;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
  }

  .header-{{ section.id }}.fixed {
    position: fixed;
    top: 0;
    left: 0;
    width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
    z-index: 999;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%);
    transition: all 0.25s ease;
  }

  .header-newsletter-wrapper-{{ section.id }}.scroll {
    transform: translateY(-200%);
    z-index: -1;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0);
    transition: all 0.25s ease;
  }

  .header-newsletter-wrapper-{{ section.id }}.scroll.active {
    transform: translateY(0);
    z-index: 0;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }} {
    position: relative;
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}::after {
    content: '';
    position: absolute;
    background-color: {{ background_open_color }};
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    transform: translateX(-130%);
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.open::after {
    transform: translate(0);
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%) ;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0) ;
    transition: all 0.25s ease;
  }

  .header-body-{{ section.id }} {
    display: flex;
    justify-content: space-between;
  }

  .header-logo-{{ section.id }} {    
    padding: {{ logo_padding_vertical_mobile }}px {{ logo_padding_horizontal_mobile }}px;
    background-color: {{ background_color | hex_to_rgba: header_bg_opacity }};
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border-radius: {{ header_radius }}px;
    display: flex;
    align-items: center;
    position: relative;
    z-index: 9999;
  }

  .header-logo-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-image-{{ section.id }} {
    max-width: {{ logo_width_mobile }}px;
  }

  .header-logo-image-{{ section.id }} img,
  .header-logo-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-logo-image-desktop-{{ section.id }} {
    display: none;
  }

  .header-logo-text-{{ section.id }} {
    margin: 0;
    display: block;
    font-size: {{ name_size_mobile }}px;
    color: {{ name_color }};
    line-height: {{ name_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 700;
    transition: all 0.25s ease;
  }

  .header-{{ section.id }}.open .header-logo-text-{{ section.id }}{
    transition: all 0.25s ease;
    color: {{ name_open_color }};
  }

  .header-right-{{ section.id }} {
    border-radius: {{ header_radius }}px;
    overflow: hidden;
  }

  .header-right-body-{{ section.id }} {
    display: flex;
    align-items: start;
    gap: 20px;
  }

  .header-right-top-{{ section.id }} {
    display: flex;
    position: relative;
    z-index: 2;
    align-items: center; 
    background-color: {{ background_color | hex_to_rgba: header_bg_opacity }};
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    padding: {{ right_padding_vertical_mobile }}px {{ right_padding_horizontal_mobile }}px;   
    gap: {{ list_gap_mobile }}px;   
    height: 100%;
    overflow: hidden;
  }

  .header-right-top-{{ section.id }}.radius {
    border-radius: {{ header_radius }}px;
    transition: all 0.25s ease 0s;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0;
    margin: 0;    
    list-style: none;
    display: none;
    flex-direction: column;
    align-items: center;
    gap: {{ list_gap_mobile }}px;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-list-{{ section.id }} {
    display: flex;
    align-items: start;
    margin-top: {{ newsletter_mt_mobile }}px
  }

  .header-burger-menu-top-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: end;
    gap: 10px;
  } 

  .header-menu-link-{{ section.id }} {
    margin: 0;
    display: block;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    font-weight: 400;
    transition: all 0.25s ease;
    position: relative;
    z-index: 3;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-link-{{ section.id }} {
    color: {{ mobile_menu_link_color }};
  }

  .header-search-text-{{ section.id }},
  .header-account-text-{{ section.id }} {
    display: none;
    margin: 0;
    font-size: {{ link_size_mobile }}px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s ease 0s;
  }

  .header-search-icon-{{ section.id }},
  .header-account-icon-{{ section.id }},
  .header-cart-icon-{{ section.id }} {
    display: block;
    width: {{ icon_size_mobile }}px;
  }

  .header-search-icon-{{ section.id }} svg,
  .header-account-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-cart-text-{{ section.id }} {
    display: none;
    margin: 0;
    font-size: {{ cart_size }}px;
    color: {{ cart_color }};
    line-height: {{ cart_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s ease 0s;
  }

  .header-cart-{{ section.id }} .cart-count-bubble span {
    margin: 0;
    font-size: 8px;
    color: {{ cart_color }};
    line-height: 100%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s ease 0s;
  }

  .header-cart-{{ section.id }},
  .header-account-{{ section.id }},
  .header-search-{{ section.id }} {
    text-decoration: none;
  }

  .header-search-icon-{{ section.id }} svg,
  .header-account-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-cart-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-cart-icon-{{ section.id }} svg path {
    stroke: {{ cart_color }};
    transition: all .25s ease 0s;
  }

  .header-{{ section.id }}.open .header-cart-icon-{{ section.id }} svg path {
    stroke: {{ cart_color }};
    transition: all .25s ease 0s;
  }

  .header-cart-{{ section.id }} {
    text-decoration: none;
  }

  .header-cart-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 5px;
    position: relative;
    background-color: {{ cart_bg_color }};
    padding: {{ cart_padding_vertical_mobile }}px {{ cart_padding_horizontal_mobile }}px;
    border: {{ cart_border_thickness }}px solid {{ cart_border_color }};
    border-radius: {{ cart_radius }}px;
  }

  .header-cart-{{ section.id }} .cart-count-bubble {
    position: absolute;
    left: 51%;
    top: 45%;
    transform: translateX(-50%);
    padding: 0px;
    background-color: transparent;
    border: 0px;
    margin: 0px;
    width: fit-content;
    height: fit-content;
  }

  .header-newsletter-wrapper-{{ section.id }} {
    display: none;
    position: relative;
    z-index: 0;
    background-color: {{ background_color | hex_to_rgba: 0.6 }};
    transition: all 0.25s ease;
  }

  .header-newsletter-top-{{ section.id }} {
    display: flex;
    align-items: end;
    justify-content: space-between;
    gap: 10px;
    margin-top: 32px;
  }

  .header-newsletter-title-{{ section.id }} {
    margin: 0;
    display: block;
    font-size: {{ newsletter_title_size_mobile }}px;
    color: {{ newsletter_title_color }};
    line-height: {{ newsletter_title_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .header-newsletter-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ newsletter_text_size_mobile }}px;
    color: {{ newsletter_text_color }};
    line-height: {{ newsletter_text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .header-newsletter-{{ section.id }} {
    border-radius: {{ newsletter_form_radius }}px;
    border: {{ newsletter_form_border_thickness }}px solid {{ newsletter_form_border_color }};
    padding: 2px;
    margin-top: {{ newsletter_form_mt_mobile }}px;
  }

  .header-newsletter-{{ section.id }} form {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }

  .header-newsletter-input-{{ section.id }} {
    margin: 0;
    box-shadow: none;
    border: 0px;
    border-radius: 0px;
    font-size: {{ input_size_mobile }}px;
    color: {{ input_color }};
    line-height: {{ input_height }}%;
    text-transform: unset;
    background-color: transparent;
    padding-left: 8px;
  }

  .header-menu-mobile-{{ section.id }} .header-newsletter-input-{{ section.id }} {
    width: 100%;
  }

  .header-newsletter-input-{{ section.id }}:focus,
  .header-newsletter-input-{{ section.id }}:focus-visible {
    box-shadow: none;
    outline: none;
  }

  .header-newsletter-input-{{ section.id }}::placeholder {
    color: {{ input_placeholder_color }};
  }

  .header-newsletter-input-{{ section.id }} {
    background-color: transparent;
    border: none;
  }

  .header-newsletter-submit-{{ section.id }} {
    margin-top: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: {{ submit_size_mobile }}px;
    color: {{ submit_color }};
    line-height: {{ submit_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ submit_padding_vertical_mobile }}px {{ submit_padding_horizontal_mobile }}px;        
    border: 0px;    
    border-left: 0px;         
    transition: all 0.25s ease 0s;
    box-shadow: none;
    cursor: pointer;
    position: relative;
    border: {{ submit_border_thickness }}px solid {{ submit_border_color }};
    border-radius: {{ submit_radius }}px;
    background-color: {{ submit_bg_color }};
    font-weight: 700;
  }       

  .header-menu-burder-wrapper-{{ section.id }} {
    position: relative;
    cursor: pointer;
    display: flex;
    align-items: center;
    gap: 10px;
    padding: 0;
    background-color: {{ background_color | hex_to_rgba: header_bg_opacity }};
  }

  .header-burger-menu-top-{{ section.id }} .header-menu-burder-wrapper-{{ section.id }} {
    background-color: transparent;
  }

  .header-menu-burder-wrapper-{{ section.id }}:after {
    content: '';
    position: absolute;
    top: -6px;
    left: 10px;
    width: 18px;
    height: 100%;
    z-index: 2;
    min-height: 14px;
  }

  .header-menu-burder-{{ section.id }} {
    position: relative;
    z-index: 1;
    display: block;
    width: {{ mobile_menu_icon_size }}px;
    position: relative;
    z-index: 3;
    transition: all 0.25s ease;
    cursor: pointer;
    background-color: transparent;
    border: 0px;
    box-shadow: none;
  }

  .header-menu-burger-icon-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 0;
    margin-top: -1px;
    width: 100%;
    height: 1.5px;
    background: {{ mobile_menu_icon_color }};
    transition: transform .4s ease; 
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }} {
    transform: rotate(45deg);
    transition-delay: .22s;
    background: {{ mobile_menu_icon_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::before,
  .header-menu-burger-icon-{{ section.id }}::after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 1.5px;
    background-color: inherit;
    background: {{ link_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::before {
    top: -6px;
    background: {{ mobile_menu_icon_color }};
    transition: top .25s ease .27s, opacity .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::before {
    top: 0;
    opacity: 0;
    width: 100%;
    transition: top .2s ease, opacity .2s ease .22s, width .1s ease;
    background: {{ mobile_menu_icon_color }};
  }

  .header-menu-burger-icon-{{ section.id }}::after {
    bottom: -6px;
    background: {{ mobile_menu_icon_color }};
    transition: bottom .25s ease .27s, transform .25s ease, width .25s ease;
  }

  .header-{{ section.id }}.open .header-menu-burger-icon-{{ section.id }}::after {
    bottom: 0;
    transform: rotate(-90deg);
    width: 100%;
    transition: bottom .2s ease, transform .2s ease .22s, width .1s ease;
    background: {{ mobile_menu_icon_color }} !important;
  }

  .header-menu-mobile-{{ section.id }} {
    position: absolute;
    top: 100%;
    left: 0;
    width: 100%;
    height: 100dvh;
    overflow-y: auto;
    background-color: {{ background_open_color}};
    transform: translateX(-130%);
    transition: all 0.25s ease 0s;
    z-index: 99;
  }

  .header-menu-mobile-inner-{{ section.id }} {
    background-color: {{ background_open_color }};
    max-width: 100%;
    height: 100%;
    position: relative;
    overflow-x: hidden;
    overflow-y: auto;
    display: flex;
    padding: {{ mobile_menu_padding_vertical }}px {{ mobile_menu_padding_horizontal }}px;
    flex-direction: column;
  }

  .header-{{ section.id }}.open .header-menu-mobile-{{ section.id }} {
    transform: translateX(0%);
    transition: all 0.25s ease 0s;
  }


  .header-mobile-menu-item-parent-{{ section.id }}.active > .header-menu-mobile-submenu-{{ section.id }} {
    transform: translateX(0%);
    transition: all 0.25s ease 0s;
  }

  .header-mobile-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-{{ section.id }} {
    max-width: 100%;
    width: 100%;
    justify-content: space-between;
    position: static;
    padding: 6px 0px;
  }

  .header-menu-mobile-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
    flex: 0 0 18px;
    width: 18px;
    transform: rotate(-90deg);
  }

  .header-menu-mobile-submenu-{{ section.id }} {
    position: absolute;
    background-color: {{ background_open_color }};
    z-index: 4;
    padding: 20px;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    overflow-x: hidden;
    overflow-y: auto;
    transform: translateX(110%);
    transition: all 0.25s ease 0s;
  }

