                  {% endform %}             
                </div>
              </div>     
            {% endif %}

          </div>
        </div>

      </div>
      <div class="header-menu-mobile-{{ section.id }}">
        <div class="header-menu-mobile-inner-{{ section.id }}">
          <ul class="header-menu-list-{{ section.id }}">
            {% for link in mobile_menu.links %}
              <li class="header-menu-item-{{ section.id }} {% if link.links.size > 0 %}header-mobile-menu-item-parent-{{ section.id }}{% endif %}">
                <a href="{{ link.url }}" class="header-menu-link-{{ section.id }}">{{ link.title }}</a>
                {% if link.links.size > 0 %}
                  <span class="header-menu-item-icon-{{ section.id }}">
                    <svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                      <path fill="currentColor" d="M207.029 381.476L12.686 187.132c-9.373-9.373-9.373-24.569 0-33.941l22.667-22.667c9.357-9.357 24.522-9.375 33.901-.04L224 284.505l154.745-154.021c9.379-9.335 24.544-9.317 33.901.04l22.667 22.667c9.373 9.373 9.373 24.569 0 33.941L240.971 381.476c-9.373 9.372-24.569 9.372-33.942 0z" class=""></path>
                    </svg>
                  </span>
                  <div class="header-menu-mobile-submenu-{{ section.id }}">
                    <button class="header-menu-mobile-submenu-heading-{{ section.id }}">
                      <span class="header-menu-item-icon-{{ section.id }}">
                        <svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                          <path fill="currentColor" d="M207.029 381.476L12.686 187.132c-9.373-9.373-9.373-24.569 0-33.941l22.667-22.667c9.357-9.357 24.522-9.375 33.901-.04L224 284.505l154.745-154.021c9.379-9.335 24.544-9.317 33.901.04l22.667 22.667c9.373 9.373 9.373 24.569 0 33.941L240.971 381.476c-9.373 9.372-24.569 9.372-33.942 0z" class=""></path>
                        </svg>
                      </span>
                      {{ link.title }}
                    </button>
                    <ul class="header-mobile-submenu-list-{{ section.id }}">
                      {% for child_link in link.links %}
                        <li class="header-menu-item-{{ section.id }} {% if child_link.links.size > 0 %}header-mobile-menu-item-parent-{{ section.id }}{% endif %}">
                          <a href="{{ child_link.url }}" class="header-menu-link-{{ section.id }}">{{ child_link.title }}</a>
                          {% if child_link.links.size > 0 %}
                            <span class="header-menu-item-icon-{{ section.id }}">
                              <svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                                <path fill="currentColor" d="M207.029 381.476L12.686 187.132c-9.373-9.373-9.373-24.569 0-33.941l22.667-22.667c9.357-9.357 24.522-9.375 33.901-.04L224 284.505l154.745-154.021c9.379-9.335 24.544-9.317 33.901.04l22.667 22.667c9.373 9.373 9.373 24.569 0 33.941L240.971 381.476c-9.373 9.372-24.569 9.372-33.942 0z" class=""></path>
                              </svg>
                            </span>
                            <div class="header-menu-mobile-submenu-{{ section.id }}">
                              <button class="header-menu-mobile-submenu-heading-{{ section.id }}">
                                <span class="header-menu-item-icon-{{ section.id }}">
                                  <svg fill="currentColor" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
                                    <path fill="currentColor" d="M207.029 381.476L12.686 187.132c-9.373-9.373-9.373-24.569 0-33.941l22.667-22.667c9.357-9.357 24.522-9.375 33.901-.04L224 284.505l154.745-154.021c9.379-9.335 24.544-9.317 33.901.04l22.667 22.667c9.373 9.373 9.373 24.569 0 33.941L240.971 381.476c-9.373 9.372-24.569 9.372-33.942 0z" class=""></path>
                                  </svg>
                                </span>
                                {{ child_link.title }}
                              </button>
                              <ul class="header-mobile-submenu-list-{{ section.id }}">
                                {% for child_child_link in child_link.links %}
                                  <li class="header-menu-item-{{ section.id }}">
                                    <a href="{{ child_child_link.url }}" class="header-menu-link-{{ section.id }}">{{ child_child_link.title }}</a>
                                  </li>
                                {% endfor %}
                              </ul>
                            </div>
                          {% endif %}
                        </li>
                      {% endfor %}
                    </ul>
                  </div>
                {% endif %}
              </li>
            {% endfor %}
            {% if use_search and search_menu_mobile %}
              <li class="header-menu-item-{{ section.id }}">
                <a href="{{ routes.search_url }}" class="header-menu-link-{{ section.id }}">{{ search_text }}</a>
              </li>
            {% endif %}
            {% if use_account and account_menu_mobile %}
              <li class="header-menu-item-{{ section.id }}">
                <a href="{{ routes.account_url }}" class="header-menu-link-{{ section.id }}">{{ account_text }}</a>
              </li>
            {% endif %}
          </ul>
          {% if use_newsletter %}
            {% if newsletter_title != blank or newsletter_text != blank %}
              <div class="header-newsletter-top-{{ section.id }}">
                {% if newsletter_title != blank %}
                  <h3 class="header-newsletter-title-{{ section.id }}">{{ newsletter_title }}</h3>
                {% endif %}
                {% if newsletter_text != blank %}
                  <div class="header-newsletter-text-{{ section.id }}">{{ newsletter_text }}</div>
                {% endif %}
              </div>
            {% endif %}
            <div class="header-newsletter-{{ section.id }}">        
              {% form 'customer', class: 'header-newsletter-form' %}       
                  <input
                    id="NewsletterForm--{{ section.id }}"
                    type="email"
                    name="contact[email]"
                    class="header-newsletter-input-{{ section.id }}"
                    value="{{ form.email }}"
                    aria-required="true"
                    autocorrect="off"
                    autocapitalize="off"
                    autocomplete="email"
                    {% if form.errors contains 'email'%}
                      autofocus
                      aria-invalid="true"
                      aria-describedby="Newsletter-error--{{ section.id }}"
                    {% elsif form.posted_successfully? %}
                      aria-describedby="Newsletter-success--{{ section.id }}"
                    {% endif %}
                    placeholder="{{ input_placeholder }}"
                    required
                  >     
                  {% if form.posted_successfully? %}
                    <p class="header-newsletter-submit-{{ section.id }}">SUCCESS!</p>                              
                  {% else %}
                    <button type="submit" class="header-newsletter-submit-{{ section.id }}" name="commit">{% if submit != blank %}{{ submit }}{% else %}SUBMIT{% endif %}</button>                              
                  {% endif %}      
                <input type="hidden" name="contact[tags]" value="newsletter">
              {% endform %}             
            </div>
          {% endif %}

        </div>
      </div>
    </div>
</div>

<script>
  function initHeader6() {
      let header = document.querySelector('.header-{{ section.id }}');
      let menuMobile = document.querySelector('.header-menu-mobile-{{ section.id }}');
      let mobileSubMenu = document.querySelectorAll('.header-menu-mobile-submenu-{{ section.id }}');
      let newsletter = document.querySelector('.header-newsletter-{{ section.id }}');
      let newsletterWrapper = document.querySelector('.header-newsletter-wrapper-{{ section.id }}');
      let rightMenu = document.querySelector('.header-right-top-{{ section.id }}');
      let emailInput = document.querySelector('.header-newsletter-input-{{ section.id }}');
      let submitButton = document.querySelector('.header-newsletter-submit-{{ section.id }}');
      const submenus = document.querySelectorAll('.header-submenu-list-{{ section.id }}');

      if (header) {
          let burger_icon = header.querySelectorAll('.header-menu-burder-wrapper-{{ section.id }}');

          if (window.scrollY > 0) {
              header.classList.add('onscroll');
          } else {
              header.classList.add('top');
          }

          let lastScrollTop = window.scrollY;

          document.addEventListener('scroll', () => {
              // Prevent closing menu if email input is focused or submit button is clicked
              if (
                  document.activeElement.matches('.header-newsletter-input-{{ section.id }}') ||
                  document.activeElement.matches('.header-newsletter-submit-{{ section.id }}')
              ) {
                  return;
              }

              header.classList.remove('open');

              const currentScrollTop = window.scrollY;
              const maxScroll = document.documentElement.scrollHeight - window.innerHeight;

              if (currentScrollTop === 0) {
                  header.classList.add('top');
              }

              if (currentScrollTop <= 0) {
                  header.classList.add('active');
                  header.classList.remove('onscroll');
                  return;
              }

              if (currentScrollTop >= maxScroll) {
                  header.classList.remove('active');
                  return;
              }

              if (currentScrollTop !== lastScrollTop) {
                  if (header.classList.contains('scroll')) {
                      if (currentScrollTop > lastScrollTop) {
                          header.classList.remove('active');
                      } else {
                          header.classList.add('active');
                      }
                  }
                  lastScrollTop = currentScrollTop;
              }
          });

          if (burger_icon) {
              burger_icon.forEach(button => {
                  button.addEventListener('click', () => {
                      header.classList.toggle('open');
                      if (header.classList.contains('open')) {
                          document.body.style.overflow = 'hidden';
                      } else {
                          document.body.style.overflow = '';
                      }
                  });
              });
          }

          if (mobileSubMenu) {  
          mobileSubMenu.forEach(el => {  
            let parentItem = el.closest('.header-mobile-menu-item-parent-{{ section.id }}');  
            let heading = el.querySelector('.header-menu-mobile-submenu-heading-{{ section.id }}');  

            if (parentItem) {  
              parentItem.addEventListener('click', () => {  
                parentItem.classList.add('active');  
              });  
            }  

            if (heading) {  
              heading.addEventListener('click', (event) => {  
                event.stopPropagation();
                parentItem.classList.remove('active');  
              });  
            }  
          });  
        } 
      }

      // Ensure menu remains open when interacting with the newsletter input or button
      if (emailInput) {
          emailInput.addEventListener('focus', () => {
              header.classList.add('input-focused');
          });

          emailInput.addEventListener('blur', () => {
              header.classList.remove('input-focused');
          });
      }

      if (submitButton) {
          submitButton.addEventListener('click', () => {
              header.classList.add('input-focused');
              setTimeout(() => header.classList.remove('input-focused'), 3000); // Remove focus class after submission
          });
      }

      if(submenus) {
        submenus.forEach(submenu => {
          const linksParent = submenu.querySelectorAll('.header-submmenu-item-parent-{{ section.id }}');
          linksParent.forEach(link => {
            link.addEventListener('mouseenter', () => {
              submenu.classList.add('radius');
              link.classList.add('active');
            })

            link.addEventListener('mouseleave', () => {
              submenu.classList.remove('radius');
              link.classList.remove('active');
            })
          })
        })
      }

      if(newsletterWrapper && header.classList.contains('always')) {
        let lastScrollTop = window.scrollY;

        document.addEventListener('scroll', () => {
            console.log(newsletterWrapper)
            newsletterWrapper.classList.remove('active');

            const currentScrollTop = window.scrollY;
            const maxScroll = document.documentElement.scrollHeight - window.innerHeight;

            if (currentScrollTop === 0) {
              newsletterWrapper.classList.add('top');
            }

            if (currentScrollTop <= 0) {
                newsletterWrapper.classList.add('active');
                newsletterWrapper.classList.remove('onscroll');
                return;
            }

            if (currentScrollTop >= maxScroll) {
                newsletterWrapper.classList.remove('active');
                return;
            }

            if (currentScrollTop !== lastScrollTop) {
                if (newsletterWrapper.classList.contains('scroll')) {
                    if (currentScrollTop > lastScrollTop) {
                      rightMenu.classList.add('radius');    
                      newsletterWrapper.classList.remove('active');  

                    } else {
                      rightMenu.classList.remove('radius');   
                      newsletterWrapper.classList.add('active');                                                           
                    }
                }

                lastScrollTop = currentScrollTop;
            }
        });
      }
  }

  document.addEventListener('DOMContentLoaded', initHeader6);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initHeader6);
     document.addEventListener('shopify:section:load', initHeader6);
  }
</script>
