liquid_code:
{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_color_mobile = section.settings.border_color_mobile
  assign border_thickness = section.settings.border_thickness
  assign border_bottom = section.settings.border_bottom
  assign margin_top = section.settings.margin_top
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign show_type = section.settings.show_type

  assign transparent_bg = section.settings.transparent_bg

  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_padding_vertical = section.settings.layout_padding_vertical
  assign layout_padding_vertical_mobile = section.settings.layout_padding_vertical_mobile
  assign layout_padding_horizontal = section.settings.layout_padding_horizontal
  assign layout_padding_horizontal_mobile = section.settings.layout_padding_horizontal_mobile

  assign list_gap_mobile = section.settings.list_gap_mobile
  assign list_gap = section.settings.list_gap

  assign link_size = section.settings.link_size
  assign link_color = section.settings.link_color
  assign link_hover_color = section.settings.link_hover_color
  assign link_hero_color = section.settings.link_hero_color
  assign link_hover_hero_color = section.settings.link_hover_hero_color
  assign link_custom = section.settings.link_custom
  assign link_font = section.settings.link_font
  assign link_height = section.settings.link_height

  assign submenu_padding_horizontal = section.settings.submenu_padding_horizontal
  assign submenu_padding_vertical = section.settings.submenu_padding_vertical

  assign image_width = section.settings.image_width
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_ratio = section.settings.image_ratio

  assign products_gap = section.settings.products_gap

  assign product_align = section.settings.product_align

  assign vendor_custom = section.settings.vendor_custom
  assign vendor_font = section.settings.vendor_font
  assign vendor_size = section.settings.vendor_size
  assign vendor_height = section.settings.vendor_height
  assign vendor_color = section.settings.vendor_color    
  assign show_vendor = section.settings.show_vendor    

  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_size = section.settings.title_size
  assign title_height = section.settings.title_height
  assign title_color = section.settings.title_color    
  assign title_mt = section.settings.title_mt

  assign right_link_size = section.settings.right_link_size
  assign right_link_color = section.settings.right_link_color
  assign right_link_hover_color = section.settings.right_link_hover_color
  assign right_link_hero_hover_color = section.settings.right_link_hero_hover_color
  assign right_link_hero_color = section.settings.right_link_hero_color
  assign right_link_custom = section.settings.right_link_custom
  assign right_link_font = section.settings.right_link_font
  assign right_link_height = section.settings.right_link_height

  assign text_link_bg_color = section.settings.text_link_bg_color
  assign text_link_bg_hover_color = section.settings.text_link_bg_hover_color
  assign text_link_bg_hero_color = section.settings.text_link_bg_hero_color
  assign text_link_bg_hero_hover_color = section.settings.text_link_bg_hero_hover_color

  assign icon_size_mobile = section.settings.icon_size_mobile
  assign icon_size = section.settings.icon_size  
-%}

{%- style -%}
  {{ link_font | font_face: font_display: 'swap' }}
  {{ right_link_font | font_face: font_display: 'swap' }}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ vendor_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {   
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;  
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;   
    padding-top: {{ padding_top }}px;
    padding-bottom: {{ padding_bottom }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}.transparent {
      background-color: transparent !important;
      background-image: none !important;
      position: absolute;
      width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
      top: 0;
      left: 0;
      z-index: 999;
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}.transparent {
      background-color: transparent !important;
      background-image: none !important;
      position: absolute;
      width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
      top: 0;
      left: 0;
      z-index: 999;
    }
    {% endif %}
  {% endif %}

  .header-{{ section.id }}.onscroll {
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.fixed {
    position: fixed;
    top: 0;
    left: 0;
    width: calc(100% - {{margin_horizontal_mobile | times: 2 }}rem);
    z-index: 999;
  }

  .header-{{ section.id }}.scroll {
    transform: translateY(-150%);
    transition: all 0.25s ease 0s;
  }

  .header-{{ section.id }}.scroll.active {
    transform: translateY(0);
    transition: all 0.25s ease 0s;
  }

  .header-body-{{ section.id }} {  
    border-top: solid {{ border_color_mobile }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color_mobile }} {{ border_thickness }}px;
    display: grid;
    gap: {{ layout_gap_mobile }}px;
    grid-template-columns: 1fr 1fr 1fr;
    padding: {{ layout_padding_vertical_mobile }}px {{ layout_padding_horizontal_mobile }}px;
  }

  .header-{{ section.id }}.onscroll .header-body-{{ section.id }} {
    background-color:{{ background_color }} !important;
    background-image: {{ background_gradient }} !important;  
    border-bottom: solid {{ border_color_mobile }} {{ border_thickness }}px;
  }

  .header-left-{{ section.id }} {
    justify-content: center;
    display: flex;
    align-items: center;
  }

  .header-menu-{{ section.id }} {
    display: none;
    padding: {{ layout_padding_vertical }}px {{ layout_padding_horizontal }}px;
    order: 1;
  }

  .header-menu-list-{{ section.id }} {
    padding: 0px;
    margin: 0px;
    display: none;
    align-items: center;
    flex-wrap: wrap;
    list-style: none;
  }

  .header-menu-item-{{ section.id }} {
    line-height: 100%;
    cursor: pointer;
    box-sizing: border-box;
    padding-bottom: 3px;
  }

  .header-menu-link-{{ section.id }} {
    margin: 0;
    display: flex;
    align-items: center;
    gap: 5px;
    color: {{ link_color }};
    line-height: {{ link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
    position: relative;
    z-index: 3;
    position: relative;
    text-wrap: nowrap;
  }

  .header-menu-link-{{ section.id }}:after {
    content: "";
    position: absolute;
    bottom: -3px;
    left: 0;
    width: 0%;
    height: 1px;
    background-color: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .header-menu-link-{{ section.id }} svg {
    width: 14px;
    height: 14px;
    transform: rotate(0deg) translateY(0px);
    transition: all 0.25s ease 0s;
  }

  .header-menu-link-{{ section.id }} svg path {
    fill: {{ link_color }};
    transition: all 0.25s ease 0s;
  }

  .header-menu-link-title-{{ section.id }} {
    opacity: 0.7;
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
      color: {{ link_hero_color }};
    }

    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }}:after {
      background-color: {{ link_hero_color }};
    }

    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} svg path {
      fill: {{ link_hero_color }};
      transition: all 0.25s ease 0s;
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} {
      color: {{ link_hero_color }};
    }

    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }}:after {
      background-color: {{ link_hero_color }};
    }

    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-link-{{ section.id }} svg path {
      fill: {{ link_hero_color }};
      transition: all 0.25s ease 0s;
    }
    {% endif %}
  {% endif %}

  .header-menu-item-parent-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
    max-width: fit-content;
    flex-basis: 0;
  }

  .header-menu-item-icon-{{ section.id }} {
    display: block;
    flex: 0 0 8px;
    width: 8px;
    transition: all 0.25s ease 0s;
    color: {{ link_color }};
  }

  .header-menu-item-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-submenu-{{ section.id }} {
    position: absolute;
    z-index: 3;
    top: calc(100% - {{ layout_padding_vertical | plus: 5 }}px);
    left: 0;
    width: 100%;
    transform: translateY(30px);
    opacity: 0;
    pointer-events: none;
    transition: all 0.25s ease 0s;
    list-style: none;
    margin: 0px;
  }

  .header-submenu-bg-{{ section.id }} {
    display: block !important;
    height: {{ layout_padding_vertical | plus: 5 }}px;
    pointer-events: none;
  }

  .header-submenu-inner-{{ section.id }} {
    background-color: {{ background_color }};
    background-image: {{ background_gradient }};
    border-bottom: {{ border_thickness }}px solid {{ border_color }};
  }

  .header-submenu-{{ section.id }} .section-{{ section.id }}-settings {
    padding: {{ submenu_padding_vertical }}px {{ submenu_padding_horizontal }}px;
  }

  .header-menu-item-parent-{{ section.id }}.active .header-submenu-{{ section.id }} {
    transform: translateY(0px);
    opacity: 1;
    pointer-events: all;
    transition: all 0.25s ease 0s;
  }

  .header-menu-burger-icon-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 0;
    margin-top: -1px;
    width: 100%;
    height: 1px;
    background: {{ link_color }};
    transition: all 0.25s ease 0s; 
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
      background: {{ link_hero_color }};
      transition: all 0.25s ease 0s; 
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }} {
        background: {{ link_hero_color }};
        transition: all 0.25s ease 0s; 
      }
    {% endif %}
  {% endif %}

  .header-menu-burger-icon-{{ section.id }}::before,
  .header-menu-burger-icon-{{ section.id }}::after{
    content: "";
    display: block;
    position: absolute;
    width: 100%;
    height: 1px;
    background-color: inherit;
    background: {{ link_color }};
    transition: all 0.25s ease 0s; 
  }

  .header-menu-burger-icon-{{ section.id }}::before {
    top: -7px;
    transition: top .25s ease .27s, opacity .25s ease, width .25s ease;
    transition: all 0.25s ease 0s; 
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
    .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after {
      background: {{ link_hero_color }};
      transition: all 0.25s ease 0s; 
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::before,
      .header-{{ section.id }}:not(.onscroll).transparent .header-menu-burger-icon-{{ section.id }}::after {
        background: {{ link_hero_color }};
        transition: all 0.25s ease 0s; 
      }
    {% endif %}
  {% endif %} 

  .header-right-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: end;
    flex-wrap: nowrap;
    position: relative;
    z-index: 3;
    order: 3;
    transition: all 0.25s ease 0s;
    gap: 10px;
  }

  .header-right-text-link-{{ section.id }},
  .header-search-text-{{ section.id }},
  .header-account-text-{{ section.id }},
  .header-cart-text-{{ section.id }}{
    display: none;
    margin: 0;
    color: {{ right_link_color }};
    line-height: {{ right_link_height }}%;
    flex-direction: column;
    justify-content: center;
    height: 100%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s cubic-bezier(.76,0,.24,1);
    cursor: pointer;
  }

  .header-right-text-link-{{ section.id }} {
    background-color: {{ text_link_bg_color }};
  }

  .cart-count-bubble {
    position: initial !important;
  }

  .header-cart-{{ section.id }} .cart-count-bubble span {
    margin: 0;  
    font-size: {{ right_link_size }}px;
    color: {{ right_link_color }};
    line-height: {{ right_link_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  {% if transparent_bg == "all" %}
    .header-{{ section.id }}:not(.onscroll).transparent .header-cart-{{ section.id }} .cart-count-bubble span{
      color: {{ right_link_hero_color }};
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-cart-{{ section.id }} .cart-count-bubble span{
        color: {{ right_link_hero_color }};
      }
    {% endif %}
  {% endif %}

  .header-search-icon-{{ section.id }},
  .header-account-icon-{{ section.id }},
  .header-cart-icon-{{ section.id }} {
    display: block;
    width: {{ icon_size_mobile }}px;
  }

  .header-search-icon-{{ section.id }} svg,
  .header-account-icon-{{ section.id }} svg,
  .header-cart-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-search-icon-{{ section.id }} svg path,
  .header-account-icon-{{ section.id }} svg path {
    stroke: {{ right_link_color }};
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  .header-cart-icon-{{ section.id }} svg path {
    stroke: {{ right_link_color }};
    transition: all .25s cubic-bezier(.76,0,.24,1);
  }

  {% if transparent_bg == "all" %}
    @media(min-width: 1024px) {
        .header-{{ section.id }}:not(.onscroll).transparent .header-search-{{ section.id }} { 
          border-right: {{ border_thickness }}px solid {{ border_color }};
        }
      }

    .header-{{ section.id }}:not(.onscroll).transparent .header-search-icon-{{ section.id }} svg path,
    .header-{{ section.id }}:not(.onscroll).transparent .header-account-icon-{{ section.id }} svg path {
      stroke: {{ right_link_hero_color }};
    }

    .header-{{ section.id }}:not(.onscroll).transparent .header-cart-icon-{{ section.id }} svg path {
      stroke: {{ right_link_hero_color }}
    }
  {% elsif transparent_bg == "home"  %}
    {% if template.name == "index" %}
      @media(min-width: 1024px) {
        .header-{{ section.id }}:not(.onscroll).transparent .header-search-{{ section.id }} { 
          border-right: {{ border_thickness }}px solid {{ border_color }};
        }
      }     

      .header-{{ section.id }}:not(.onscroll).transparent .header-search-icon-{{ section.id }} svg path,
      .header-{{ section.id }}:not(.onscroll).transparent .header-account-icon-{{ section.id }} svg path {
        stroke: {{ right_link_hero_color }};
      }

      .header-{{ section.id }}:not(.onscroll).transparent .header-cart-icon-{{ section.id }} svg path {
        stroke: {{ right_link_hero_color }}
      }
    {% endif %}
  {% endif %}

  .header-right-item-{{ section.id }}:first-child {
    border-left: 0px;
    border-right: 0px;
  }

  .header-right-item-{{ section.id }}:not(:last-child) {
    border-right: 0px;
  }

  .header-cart-{{ section.id }},
  .header-account-{{ section.id }} {   
    height: auto;
    text-decoration: none;
    display: flex;
    align-items: center;
    justify-content: center;
  }  

  .header-search-{{ section.id }} {
    border-right: {{ border_thickness }}px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    margin: 0px;
    background-color: transparent;
    box-shadow: none;
    cursor: pointer;
    text-decoration: none;
  }

  .header-cart-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 5px;
    position: relative;
  }

  .header-cart-{{ section.id }} .cart-count-bubble {
    padding: 0px;
    background-color: transparent;
    border: 0px;
    margin: 0px;
    width: fit-content;
    height: fit-content;
  }

  .header-menu-card-{{ section.id }} {
    display: block;
    text-decoration: none;
  }

  .header-menu-card-image-{{ section.id }} img,
  .header-menu-card-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: all 0.25s ease 0s;
  }

  .header-menu-card-image-{{ section.id }} svg {
    background-color: #AEAEAE;
  }

  .header-submenu-products-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: {{ products_gap }}px;
  }

  .header-submenu-item-{{ section.id }} {
    display: flex;
    gap: 10px;
  }

  .header-submenu-item-image-{{ section.id }} {
    flex-shrink: 0;
    width: {{ image_width }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .header-submenu-item-image-{{ section.id }} img,
  .header-submenu-item-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .header-submenu-item-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .header-submenu-vendor-{{ section.id }} {
    margin: 0;   
    font-size: {{ vendor_size }}px;
    color: {{ vendor_color }};
    line-height: {{ vendor_height }}%;
    text-align: {{ product_align }};
    text-transform: unset;
    word-break: break-word;
  }

  .header-submenu-title-{{ section.id }} {
    margin: 0;   
    margin-top: {{ title_mt }}px;
    font-size: {{ title_size }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-align: {{ product_align }};
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {  
      margin-top: {{ margin_top }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .header-{{ section.id }}.transparent {
      width: calc(100% - {{ margin_horizontal | times: 2 }}rem);
    }

    .header-{{ section.id }}.fixed {
      width: calc(100% - {{ margin_horizontal | times: 2 }}rem);
    }

    .header-{{ section.id }}.onscroll .header-body-{{ section.id }} {
      background-color:{{ background_color }} !important;
      background-image: {{ background_gradient }} !important;  
      border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    }

    .header-body-{{ section.id }} {
      display: flex;
      align-items: stretch;
      justify-content: space-between;
      border-top: solid {{ border_color }} {{ border_thickness }}px;
      border-bottom: solid {{ border_color }} {{ border_thickness }}px;
      padding: 0;
    }

    .header-menu-{{ section.id }} {
      display: flex;
      order: 2;
    }

    .header-left-{{ section.id }} {
      justify-content: start;
      width: auto;
      padding-left: {{ layout_padding_horizontal }}px;
    }

    .header-menu-list-{{ section.id }} {
      display: flex;
      gap: {{ list_gap }}px;
    }

    .header-menu-link-{{ section.id }} {
      font-size: {{ link_size }}px;
      transition: all 0.25s ease;
    }  

    .header-menu-item-{{ section.id }}:hover .header-menu-link-{{ section.id }}:after {
      width: 100%;
      transition: all 0.25s ease 0s;
    }

    .header-menu-item-parent-{{ section.id }}:hover .header-menu-link-{{ section.id }}:after {
      width: calc(100% - 19px);
      transition: all 0.25s ease 0s;
    }

    {% if transparent_bg == "all" %}
      .header-{{ section.id }}:not(.onscroll).transparent .header-right-text-link-{{ section.id }},
        .header-{{ section.id }}:not(.onscroll).transparent .header-search-text-{{ section.id }},
        .header-{{ section.id }}:not(.onscroll).transparent .header-account-text-{{ section.id }},
        .header-{{ section.id }}:not(.onscroll).transparent .header-cart-text-{{ section.id }} {
          color: {{ right_link_hero_color }};      
        }

        .header-{{ section.id }}:not(.onscroll).transparent .header-right-text-link-{{ section.id }},
        .header-{{ section.id }}:not(.onscroll).transparent .header-search-{{ section.id }},
        .header-{{ section.id }}:not(.onscroll).transparent .header-account-{{ section.id }},
        .header-{{ section.id }}:not(.onscroll).transparent .header-cart-{{ section.id }} {
          background-color: {{ text_link_bg_hero_color }};
        }

        .header-{{ section.id }}:not(.onscroll).transparent .header-right-text-link-{{ section.id }}:hover,
        .header-{{ section.id }}:not(.onscroll).transparent .header-search-{{ section.id }}:hover *,
        .header-{{ section.id }}:not(.onscroll).transparent .header-account-{{ section.id }}:hover *,
        .header-{{ section.id }}:not(.onscroll).transparent .header-cart-{{ section.id }}:hover * {
