    .section-{{ section.id }} {
      border-left: solid {{ border_color_mobile }} {{ border_thickness }}px;
      border-right: solid {{ border_color_mobile }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
