        </style>
      {% endif %}
    {% when 'logo' %}
      <style>
        {{ block.settings.name_font | font_face: font_display: 'swap' }}

        .header-logo-desktop-{{ section.id }} {
          display: none !important;
        }

        .header-logo-{{ section.id }} {
          text-align: center;
          position: relative;
          transition: all 0.25s ease 0s;
        }

        .header-logo-image-{{ section.id }} {
          display: block;
          width: 100%;
          max-width: {{ block.settings.logo_width_mobile }}px;
        }

        .header-logo-image-{{ section.id }} img,
        .header-logo-image-{{ section.id }} svg {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .header-logo-image-desktop-{{ section.id }} {
          display: none;
        }

        .header-logo-text-{{ section.id }} {
          margin: 0;
          font-size: {{ block.settings.name_size_mobile }}px;
          color: {{ block.settings.name_color }};
          line-height: {{ block.settings.name_height }}%;
          text-transform: unset;
          text-decoration: none;
          transition: all 0.25s ease 0s;
        }

        .header-{{ section.id }} .header-logo-first-{{ section.id }} {
          display: block !important;
        }

        .header-{{ section.id }} .header-logo-second-{{ section.id }} {
          display: none !important;
        }

        @media(min-width: 1024px) {     
          .header-logo-{{ section.id }} {
            padding: {{ layout_padding_vertical }}px {{ layout_padding_horizontal }}px;
            text-align: center;
          }

          .header-logo-image-desktop-{{ section.id }} {
            display: block;
          }

          .header-logo-image-mobile-{{ section.id }} {
            display: none;
          }

          .header-logo-image-{{ section.id }} {
            max-width: {{ block.settings.logo_width }}px;
          }

          .header-logo-text-{{ section.id }} {
            font-size: {{ block.settings.name_size }}px;
            transition: all 0.25s ease;
          }   

          .header-logo-{{ section.id }} {
            border-right: {{ border_thickness }}px solid {{ border_color }};
          }

          .header-logo-{{ section.id }} {
            text-align: left;
          }

          .header-logo-desktop-{{ section.id }} {
            display: block !important;
          }

          .header-logo-mobile-{{ section.id }} {
            display: none;
          }

          .header-logo-text-{{ section.id }}:hover {
            transition: all 0.25s ease;
            color: {{ block.settings.name_hover_color }};
          }
        }
      </style>

      {% if block.settings.name_custom %}
        <style>
          .header-logo-text-{{ section.id }} {
            font-family: {{ block.settings.name_font.family }}, {{ block.settings.name_font.fallback_families }};
            font-weight: {{ block.settings.name_font.weight }};
            font-style: {{ block.settings.name_font.style }};
          }
        </style>
      {% endif %}

      {% if transparent_bg == "all" %}
        <style>          
          .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
            color: {{ block.settings.name_hero_color }};
          }

          @media(min-width: 1024px) {
            .header-{{ section.id }}:not(.onscroll).transparent .header-logo-{{ section.id }} {
              border-right: {{ border_thickness }}px solid {{ border_color }};
            }
          }       
        </style>
      {% elsif transparent_bg == "home"  %}
        {% if template.name == "index" %}
          <style>          
            .header-{{ section.id }}:not(.onscroll).transparent .header-logo-text-{{ section.id }} {
              color: {{ block.settings.name_hero_color }};
            }

            @media(min-width: 1024px) {
              .header-{{ section.id }}:not(.onscroll).transparent .header-logo-{{ block.id }} {
                border-right: {{ border_thickness }}px solid {{ border_color }};
              }
