    {% when 'sidebar_menu' %}
      <style>        
        {{ block.settings.sidebar_link_font | font_face: font_display: 'swap' }}     
        {{ block.settings.close_font | font_face: font_display: 'swap' }}    
        {{ block.settings.sidebar_bottom_link_font | font_face: font_display: 'swap' }}      

        .header-menu-burger-{{ section.id }} {
          position: relative;
          z-index: 1;
          display: block;
          width: {{ block.settings.menu_icon_size_mobile }}px;
          position: relative;
          z-index: 3;
          transition: all 0.25s ease;
          cursor: pointer;
          background-color: transparent;
          border: 0px;
          box-shadow: none;
          height: 100%;
        }

        .header-menu-burger-mobile-{{ section.id }} {
          display: block;
        }

        .header-menu-burger-desktop-{{ section.id }} {
          display: none;
        }

        .header-menu-burger-icon-{{ section.id }} {
          position: absolute;
          top: 50%;
          left: 0;
          margin-top: -1px;
          width: 100%;
          height: 1.2px;
          background: {{ mobile_menu_icon_color }};
          transition: transform .4s ease; 
        }

        .header-menu-burger-icon-{{ section.id }}::before,
        .header-menu-burger-icon-{{ section.id }}::after{
          content: "";
          display: block;
          position: absolute;
          width: 100%;
          height: 1.2px;
          background-color: inherit;
          background: {{ link_color }};
        }

        .header-menu-burger-icon-{{ section.id }}::after {
          height: 1.2px;
        }

        .header-menu-burger-icon-{{ section.id }}::before {
          top: -6px;
          background: {{ mobile_menu_icon_color }};
          transition: top .25s ease .27s, opacity .25s ease, width .25s ease;
        }

        .header-menu-burger-icon-{{ section.id }}::after {
          bottom: -6px;
          background: {{ mobile_menu_icon_color }};
          transition: bottom .25s ease .27s, transform .25s ease, width .25s ease;
        }


        .header-menu-sidebar-{{ section.id }} {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100dvh;
          overflow-y: auto;
          background-color: #00000030;
          transform: translateX(-130%);
          z-index: 99;
        }

        .header-menu-sidebar-inner-{{ section.id }} {
          background-color: {{ block.settings.background_open_color }};
          max-width: 100%;
          height: 100%;
          position: relative;
          overflow-x: hidden;
          overflow-y: auto;
          display: flex;
          padding: {{ block.settings.sidebar_menu_padding_vertical_mobile }}px {{ block.settings.sidebar_menu_padding_horizontal_mobile }}px;
          flex-direction: column;
          justify-content: space-between;
          transform: translateX(-130%);
          transition: all 0.25s ease 0s;
        }

        .header-{{ section.id }}.open .header-menu-sidebar-{{ section.id }} {
          transform: translateX(0%);
        }

        .header-{{ section.id }}.open .header-menu-sidebar-inner-{{ section.id }} {
          transform: translateX(0%);
          transition: all 0.25s ease 0s;
        }


        .header-mobile-menu-item-parent-{{ section.id }}.active > .header-menu-mobile-submenu-{{ section.id }} {
          transform: translateX(0%);
          transition: all 0.25s ease 0s;
        }

        .header-mobile-menu-item-parent-{{ section.id }} {
          display: flex;
          align-items: center;
          gap: 10px;
        }

        .header-menu-sidebar-{{ section.id }} .header-menu-item-{{ section.id }} {
          max-width: 100%;
          width: 100%;
          justify-content: space-between;
          position: static;
          padding: 6px 0px;
        }

        .header-menu-sidebar-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
          flex: 0 0 18px;
          width: 18px;
          transform: rotate(-90deg);
        }

        .header-menu-sidebar-{{ section.id }} .header-menu-list-{{ section.id }} {
          display: flex !important;
        }

        .header-menu-mobile-submenu-{{ section.id }} {
          position: absolute;
          background-color: {{ block.settings.background_open_color }};
          z-index: 4;
          padding: 20px;
          top: 0;
          left: 0;
          height: 100%;
          width: 100%;
          overflow-x: hidden;
          overflow-y: auto;
          transform: translateX(110%);
          transition: all 0.25s ease 0s;
        }

        .header-mobile-menu-item-parent-{{ section.id }}.active > .header-menu-mobile-submenu-{{ section.id }} {
          transform: translateX(0%);
          transition: all 0.25s ease 0s;
        }

        .header-menu-mobile-submenu-heading-{{ section.id }} {
          display: flex;
          gap: 10px;
          width: 100%;
          background-color: transparent;
          border: 0px;
          padding: 0px;
          box-shadow: none;
          font-size: {{ block.settings.close_size_mobile }}px;
          color: {{ block.settings.close_color }};
          line-height: {{ block.settings.close_height }}%;
          text-transform: unset;
          text-decoration: none;
          cursor: pointer;
        }

        .header-sidebar-close-{{ section.id }} {
          margin-top: 10px;
          display: flex;
          align-items: center;
          gap: 10px;
          cursor: pointer;
        }

        .header-sidebar-close-text-{{ section.id }} {
          margin: 0;         
          font-size: {{ block.settings.close_size_mobile }}px;
          color: {{ block.settings.close_color }};
          line-height: {{ block.settings.close_height }}%;
          text-transform: unset;
          text-decoration: none;
          transition: all 0.25s ease 0s;
          position: relative;          
        }

        .header-sidebar-close-{{ section.id }} svg {
          width: {{ block.settings.close_icon_size_mobile }}px;
          height: {{ block.settings.close_icon_size_mobile }}px;
        }

        .header-sidebar-close-{{ section.id }} svg path {
          stroke: {{ block.settings.close_color }};
        }

        .header-menu-mobile-submenu-heading-{{ section.id }} .header-menu-item-icon-{{ section.id }} {
          transform: rotate(90deg);          
          fill: {{ block.settings.close_color }} !important;
          flex: 0 0 15px;
          width: 15px;
        }

        .header-menu-mobile-submenu-heading-{{ section.id }} .header-menu-item-icon-{{ section.id }} svg path {      
          fill: {{ block.settings.close_color }} !important;
        }

        .header-sidebar-account-{{ section.id }},
        .header-sidebar-search-{{ section.id }} {
          text-decoration: none;
          display: block;
        }

        .header-mobile-submenu-list-{{ section.id }} {
          margin: 0px;
          text-align: left;
          margin-top: {{ block.settings.sidebar_list_gap_mobile }}px;
          display: flex;
          flex-direction: column;
          gap: {{ block.settings.sidebar_list_gap_mobile }}px;
          padding: 0px;
          list-style: none;
        }

        .header-menu-sidebar-{{ section.id }} .header-menu-list-{{ section.id }} {
          padding: {{ block.settings.sidebar_menu_list_padding_vertical_mobile }}px 0px;
          margin-top: {{ block.settings.sidebar_list_gap_mobile | minus: 5 }}px;
          gap: {{ block.settings.sidebar_list_gap_mobile }}px;
        }


        .header-menu-sidebar-{{ section.id }} .header-menu-link-{{ section.id }} {     
          display: block !important;
        }

        .header-menu-sidebar-{{ section.id }} .header-menu-link-{{ section.id }},
        .header-sidebar-account-text-{{ section.id }},
        .header-sidebar-search-text-{{ section.id }}  {
          margin: 0;
          text-wrap: wrap !important;
          font-size: {{ block.settings.sidebar_link_size_mobile }}px;
          color: {{ block.settings.sidebar_menu_link_color }} !important;
          line-height: {{ block.settings.sidebar_link_height }}%;
          text-transform: unset;
          text-decoration: none;
          position: relative;
          z-index: 3;
          transition: all 0.25s ease 0s;
        }

        .header-menu-sidebar-{{ section.id }} .header-menu-item-{{ section.id }}:hover > .header-menu-link-{{ section.id }} {
          transition: all 0.25s ease;
          color: {{ block.settings.sidebar_menu_link_hover_color }} !important;
        }

        .header-menu-sidebar-{{ section.id }} .header-menu-item-{{ section.id }}:hover > .header-menu-item-icon-{{ section.id }} svg path {
          transition: all 0.25s ease;
          fill: {{ block.settings.sidebar_menu_link_hover_color }} !important;
        }

        .header-menu-item-icon-{{ section.id }} svg path {
          fill: {{ block.settings.sidebar_menu_link_color }}
        }

        .header-menu-sidebar-{{ section.id }} .header-menu-link-{{ section.id }}:after {      
          content: "";
          position: absolute;
          top: 55%;
          left: 0;
          width: 1.2rem !important;
          height: 1.2rem !important;
          background: {{ block.settings.circle_color }} !important;
          border-radius: 50%;
          transform: translate(-2rem, -50%) scale(0) translateZ(0);
          transition: all 0.25s ease 0s;
        }

        .header-sidebar-bottom-menu-{{ section.id }} {
          margin: 0;
          list-style: none;
          padding-top: {{ block.settings.sidebar_menu_padding_vertical_mobile }}px;
          border-top: {{ block.settings.sidebar_bottom_border_thickness }}px solid {{ block.settings.sidebar_bottom_border_color }};
          display: flex;
          align-items: center;
          gap: {{ block.settings.sidebar_bottom_gap_mobile }}px;
          flex-wrap: wrap;    
        }

        .header-sidebar-bottom-menu-item-{{ section.id }}{
          width: calc(50% - {{ block.settings.sidebar_bottom_gap_mobile }}px);
        }

        .header-sidebar-bottom-menu-link-{{ section.id }} {
          margin: 0;
          font-size: {{ block.settings.sidebar_bottom_link_size_mobile }}px;
          color: {{ block.settings.sidebar_bottom_link_color }};
          line-height: {{ block.settings.sidebar_bottom_link_height }}%;
          text-transform: unset;
          text-decoration: none;
          position: relative;
          z-index: 3;
          transition: all 0.25s ease 0s;
        }

        @media(min-width: 1024px) {
          .header-menu-burger-{{ section.id }} {
            width: {{ block.settings.menu_icon_size }}px;
          }

          .header-menu-burger-mobile-{{ section.id }} {
            display: none;
          }

          .header-menu-burger-desktop-{{ section.id }} {
            display: block;
          }

          .header-menu-mobile-submenu-heading-{{ section.id }} {
            font-size: {{ block.settings.close_size }}px;
          }

          .header-menu-sidebar-inner-{{ section.id }} {
            padding: {{ block.settings.sidebar_menu_padding_vertical }}px {{ block.settings.sidebar_menu_padding_horizontal }}px;
          }

          .header-mobile-submenu-list-{{ section.id }} {
            margin-top: {{ block.settings.sidebar_list_gap }}px;
            gap: {{ block.settings.sidebar_list_gap }}px;
          }

          .header-menu-sidebar-inner-{{ section.id }} {
            max-width: calc(30vw + 100px);         
          }

          .header-menu-sidebar-{{ section.id }} .header-menu-link-{{ section.id }},
          .header-sidebar-account-text-{{ section.id }},
          .header-sidebar-search-text-{{ section.id }}  {
            font-size: {{ block.settings.sidebar_link_size }}px;
          }

          .header-sidebar-close-text-{{ section.id }} {
            font-size: {{ block.settings.close_size }}px;
          }

          .header-menu-sidebar-{{ section.id }} .header-menu-list-{{ section.id }} {
            padding: {{ block.settings.sidebar_menu_list_padding_vertical }}px 0px;
            margin-top: {{ block.settings.sidebar_list_gap | minus: 5 }}px;
            gap: {{ block.settings.sidebar_list_gap }}px;
          }

          .header-menu-sidebar-{{ section.id }} .header-menu-item-{{ section.id }}:hover > .header-menu-link-{{ section.id }} {
            transform: translateX(1.8rem);
            transition: all 0.25s ease 0s;
          }

          .header-menu-sidebar-{{ section.id }} .header-menu-item-{{ section.id }}:hover > .header-menu-link-{{ section.id }}:after {
            transition: all 0.25s ease 0s;
            transform: translate(-2rem, -50%) scale(1) translateZ(0);
          }

          .header-sidebar-bottom-menu-{{ section.id }} {
            padding-top: {{ block.settings.sidebar_menu_padding_vertical }}px;
            gap: {{ block.settings.sidebar_bottom_gap }}px;
          }

          .header-sidebar-bottom-menu-link-{{ section.id }} {
            font-size: {{ block.settings.sidebar_bottom_link_size }}px;
          }

          .header-sidebar-bottom-menu-item-{{ section.id }} {
            width: calc(50% - {{ block.settings.sidebar_bottom_gap }}px);
          }

          .header-sidebar-account-{{ section.id }},
          .header-sidebar-search-{{ section.id }} {
            text-decoration: none;
            display: none;
          }

          .header-sidebar-bottom-menu-link-{{ section.id }}:hover {
            color: {{ block.settings.sidebar_bottom_link_hover_color }};
            transition: all 0.25s ease 0s;
          }

          .header-sidebar-close-{{ section.id }} svg {
            width: {{ block.settings.close_icon_size }}px;
            height: {{ block.settings.close_icon_size }}px;
          }
        }
      </style>

      {% if block.settings.sidebar_link_custom %}
        <style>
          .header-menu-sidebar-{{ section.id }} .header-menu-link-{{ section.id }},
          .header-sidebar-account-text-{{ section.id }},
          .header-sidebar-search-text-{{ section.id }} {
            font-family: {{ block.settings.sidebar_link_font.family }}, {{ block.settings.sidebar_link_font.fallback_families }} !important;
            font-weight: {{ block.settings.sidebar_link_font.weight }} !important;
            font-style: {{ block.settings.sidebar_link_font.style }} !important;
          }
        </style>
      {% endif %}

      {% if block.settings.close_custom %}
        <style>
          .header-sidebar-close-text-{{ section.id }} {
            font-family: {{ block.settings.close_font.family }}, {{ block.settings.close_font.fallback_families }};
            font-weight: {{ block.settings.close_font.weight }};
            font-style: {{ block.settings.close_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.sidebar_bottom_link_custom %}
        <style>
          .header-sidebar-bottom-menu-link-{{ section.id }} {
            font-family: {{ block.settings.sidebar_bottom_link_font.family }}, {{ block.settings.sidebar_bottom_link_font.fallback_families }};
            font-weight: {{ block.settings.sidebar_bottom_link_font.weight }};
            font-style: {{ block.settings.sidebar_bottom_link_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.close_text_mobile %}
        <style>
          .header-sidebar-close-text-{{ section.id }} {
            display: none;            
          }

          @media(min-width: 1024px) {
            .header-sidebar-close-text-{{ section.id }} {
              display: block;            
            }
          }
        </style>
      {% endif %}   

      {% if block.settings.use_account_link_mobile %}
        <style>
          .header-account-mobile-{{ section.id }} {
            display: none;
          }
        </style>
      {% endif %}

      {% if block.settings.use_search_link_mobile %}
        <style>
          .header-search-mobile-{{ section.id }} {
            display: none;
          }
        </style>
      {% endif %}
  {% endcase %}
{% endfor %}

{% comment %} FOR FREE THEMES{% endcomment %}
{%- if settings.cart_type == "drawer" -%}
  {{ 'component-cart-drawer.css' | asset_url | stylesheet_tag }}
  {{ 'component-cart.css' | asset_url | stylesheet_tag }}
  {{ 'component-totals.css' | asset_url | stylesheet_tag }}
  {{ 'component-price.css' | asset_url | stylesheet_tag }}
  {{ 'component-discounts.css' | asset_url | stylesheet_tag }}
{%- endif -%}
{% comment %} FOR FREE THEMES{% endcomment %}

<div class="section-{{ section.id }} header-{{ section.id }} {% if transparent_bg %}transparent{% endif %} {% if show_type == "always" %}fixed{% elsif show_type == "scroll_up" %}scroll active fixed{% else %}top{% endif %}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="header-body-{{ section.id }}">
        {% for block in section.blocks %}
          {% if block.type == "sidebar_menu" %}
            <button class="header-menu-burger-{{ section.id }} header-menu-burger-mobile-{{ section.id }}">
              <span class="header-menu-burger-icon-{{ section.id }}"></span>
            </button>
          {% endif %}
        {% endfor %}

        <div class="header-left-{{ section.id }}">
          {% for block in section.blocks %}
            {% if block.type == "sidebar_menu" %}
              <button class="header-menu-burger-{{ section.id }} header-menu-burger-desktop-{{ section.id }}">
                <span class="header-menu-burger-icon-{{ section.id }}"></span>
              </button>
            {% endif %}
          {% endfor %}
          {% for block in section.blocks %}
            {% if block.type == "logo" %}
              <div class="header-logo-{{ section.id }}">
                {% if block.settings.logo != blank %}
                  <a href="{{ shop.url }}" class="header-logo-image-{{ section.id }} {% if block.settings.logo_mobile != blank %}header-logo-desktop-{{ section.id }}{% endif %}">
                    <img src="{{ block.settings.logo | image_url }}" alt="{{ block.settings.logo.alt }}" {% if lazy %}loading="lazy"{% endif %} {% if block.settings.second_logo != blank %}class="header-logo-first-{{ section.id }}"{% endif %}>
                    {% if block.settings.second_logo != blank %}
                      <img src="{{ block.settings.second_logo | image_url }}" alt="{{ block.settings.second_logo.alt }}" {% if lazy %}loading="lazy"{% endif %} class="header-logo-second-{{ section.id }}">
                    {% endif %}
                  </a>
                  {% if block.settings.logo_mobile != blank %}
                    <a href="{{ shop.url }}" class="header-logo-image-{{ section.id }} header-logo-mobile-{{ section.id }}">
                      <img src="{{ block.settings.logo_mobile | image_url }}" alt="{{ block.settings.logo_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} {% if block.settings.second_logo_mobile != blank %}class="header-logo-first-{{ section.id }}"{% endif %}>
                      {% if block.settings.second_logo_mobile != blank %}
                        <img src="{{ block.settings.second_logo_mobile | image_url }}" alt="{{ block.settings.second_logo_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} class="header-logo-second-{{ section.id }}">
                      {% endif %}
                    </a>
                  {% endif %}
                {% else %}
                  <a href="{{ shop.url }}" class="header-logo-text-{{ section.id }}">{{ shop.name }}</a>
                {% endif %}
              </div>
            {% endif %}
          {% endfor %}       
        </div>     

        <div class="header-menu-{{ section.id }}">
          {% if section.blocks.size > 0 %}
            <ul class="header-menu-list-{{ section.id }}">
              {% for block in section.blocks %}
                {% if block.type == 'menu_item' %}
                  <li class="header-menu-item-{{ section.id }} header-menu-item-main-{{ section.id }} {% if block.settings.products != blank %}header-menu-item-parent-{{ section.id }}{% endif %}">
                    <{% if block.settings.products != blank %}p{% else %}a href="{{ block.settings.url }}"{% endif %} class="header-menu-link-{{ section.id }}">
                      {{ block.settings.text }}
                      {% if block.settings.products != blank %}
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" role="presentation" class="header_subMenuIcon__j5wuI"><path fill="currentColor" fill-rule="evenodd" d="M2.183 5.183a.625.625 0 0 1 .884 0l4.827 4.828a.208.208 0 0 0 .295 0l4.827-4.828a.625.625 0 0 1 .884.884l-4.827 4.827c-.57.57-1.493.57-2.063 0L2.183 6.067a.625.625 0 0 1 0-.884Z" clip-rule="evenodd"></path></svg>
                      {% endif %}
                    </{% if block.settings.products != blank %}p{% else %}a{% endif %}>
                    {% if block.settings.products != blank %}  
                      <div class="header-submenu-{{ section.id }}">
                        <div class="header-submenu-bg-{{ section.id }}"></div>
                        <div class="header-submenu-inner-{{ section.id }}">
                          <div class="section-{{ section.id }}-settings" style="display: flex; justify-content: center;">
                            <div class="header-submenu-menu-{{ section.id }}">
                              {% if block.settings.products != blank %}
                                <div class="header-submenu-products-{{ section.id }}">
                                  {% for product in block.settings.products %}
                                    <div class="header-submenu-item-{{ section.id }}">
                                      <a href="{{ product.url }}" class="header-submenu-item-image-{{ section.id }}">
                                        {% if product.featured_image != blank %}
                                          <img src="{{ product.featured_image | image_url }}" alt="{{ product.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>            
                                        {% else %}
                                          {{ 'image' | placeholder_svg_tag }}
                                        {% endif %}
                                      </a>
                                      <div class="header-submenu-item-content-{{ section.id }}">
                                        {% if show_vendor %}
                                          <p class="header-submenu-vendor-{{ section.id }}">{{ product.vendor }}</p>
                                        {% endif %}
                                        <a href="{{ product.url }}" class="header-submenu-title-{{ section.id }}">{{ product.title }}</a>
                                      </div>
                                    </div>
                                  {% endfor %}
                                </div>
                              {% endif %}
                            </div>
                          </div>
                        </div>
                      </div>
                    {% endif %}
                  </li>
                {% endif %}             
              {% endfor %}
            </ul>
          {% endif %}
        </div>      

        <div class="header-right-{{ section.id }}">
          {% for block in section.blocks %}
            {% case block.type %}
              {% when 'text_link' %}
                {% if block.settings.text_link != blank %}
                  <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}p{% endif %} class="header-right-text-link-{{ section.id }} header-right-item-{{ section.id }}">{{ block.settings.text_link }}</{% if block.settings.url != blank %}a{% else %}p{% endif %}>
                {% endif %}
              {% when 'search' %}               
                <a href="{{ routes.search_url }}" class="header-search-{{ section.id }} header-search-{{ block.id }} header-right-item-{{ section.id }} header-search-mobile-{{ section.id }}">
                  <span class="header-search-icon-{{ section.id }}">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="-0.5 -0.5 16 16" stroke-linecap="round" stroke-linejoin="round" stroke="#121212" height="16" width="16"><path d="m10.558125 10.575000000000001 1.925 1.925m-0.555625 -5.261875c0 2.6168750000000003 -2.114375 4.738125 -4.721875 4.738125 -2.6081250000000002 0 -4.7225 -2.12125 -4.7225 -4.7375C2.483125 4.6206249999999995 4.5975 2.5 7.205 2.5c2.6081250000000002 0 4.7225 2.12125 4.7225 4.738125" stroke-width="1"></path></svg>
                  </span>
                  <span class="header-search-text-{{ section.id }}">{{ block.settings.search_text }}</span>
                </a>             
              {% when 'cart' %}
                <a href="{{ routes.cart_url }}" class="header-cart-{{ section.id }} header-cart-{{ block.id }} header-right-item-{{ section.id }}">
                  <span class="header-cart-icon-{{ section.id }}">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="-0.5 -0.5 16 16" stroke-linecap="round" stroke-linejoin="round" stroke="#121212" height="16" width="16"><path d="M10.3125 13.125a0.9375 0.9375 0 1 0 0 -1.875 0.9375 0.9375 0 0 0 0 1.875m-5 0a0.9375 0.9375 0 1 0 0 -1.875 0.9375 0.9375 0 0 0 0 1.875M2.31875 3.375h9.508750000000001c0.86125 0 1.4831250000000002 0.79375 1.2468750000000002 1.5925l-1.03375 3.5C11.881250000000001 9.004999999999999 11.3725 9.375 10.79375 9.375H5.07c-0.579375 0 -1.08875 -0.370625 -1.2475 -0.9075zm0 0L1.875 1.875" stroke-width="1"></path></svg>
                  </span>
                  <span class="header-cart-text-{{ section.id }}">{{ block.settings.cart_text }}</span>       
                  <div class="cart-count-bubble">
                    {%- if cart.item_count < 100 -%}
                      <span aria-hidden="true">({{ cart.item_count }})</span>
                    {%- endif -%}
                  </div>      
                </a>
              {% when 'account' %}
                {%- if shop.customer_accounts_enabled -%}
                  <a href="{%- if customer -%}{{ routes.account_url }}{%- else -%}{{ routes.account_login_url }}{%- endif -%}" class="header-account-{{ section.id }} header-account-{{ block.id }} header-right-item-{{ section.id }} header-account-mobile-{{ section.id }}">
                    <span class="header-account-icon-{{ section.id }}">
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="-0.5 -0.5 16 16" stroke-linecap="round" stroke-linejoin="round" stroke="#121212" height="16" width="16"><path d="M5.625 4.6875a1.875 1.875 0 1 0 3.75 0 1.875 1.875 0 1 0 -3.75 0" stroke-width="1"></path><path d="M12.1875 12.8125c-0.296875 -5.833125 -9.078125 -5.833125 -9.375 0" stroke-width="1"></path></svg>
                    </span>
                    <span class="header-account-text-{{ section.id }}">{{ block.settings.account_text }}</span>
                  </a>
                {%- endif -%}
              {% else %}  
            {% endcase %}
          {% endfor %}                
        </div>
      </div>

      {% for block in section.blocks %}
        {% if block.type == 'sidebar_menu' %}
          <div class="header-menu-sidebar-{{ section.id }}">
            <div class="header-menu-sidebar-inner-{{ section.id }}">
              <div class="header-menu-sidebar-top-{{ section.id }}">               
                  <div class="header-sidebar-close-{{ section.id }}">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" role="presentation"><path stroke="currentColor" d="M1 15 15 1M15 15 1 1"></path></svg>
                    {% if block.settings.close_text != blank %}
                      <p class="header-sidebar-close-text-{{ section.id }}">{{ block.settings.close_text }}</p>
                    {% endif %}
                  </div>            
                <ul class="header-menu-list-{{ section.id }}">
                  {% for link in block.settings.sidebar_menu.links %}
                    <li class="header-menu-item-{{ section.id }} {% if link.links.size > 0 %}header-mobile-menu-item-parent-{{ section.id }}{% endif %}">
                      <a href="{{ link.url }}" class="header-menu-link-{{ section.id }}">{{ link.title }}</a>
                      {% if link.links.size > 0 %}
                        <span class="header-menu-item-icon-{{ section.id }}">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" role="presentation"><path fill="currentColor" fill-rule="evenodd" d="M2.183 5.183a.625.625 0 0 1 .884 0l4.827 4.828a.208.208 0 0 0 .295 0l4.827-4.828a.625.625 0 0 1 .884.884l-4.827 4.827c-.57.57-1.493.57-2.063 0L2.183 6.067a.625.625 0 0 1 0-.884Z" clip-rule="evenodd"></path></svg>
                        </span>
                        <div class="header-menu-mobile-submenu-{{ section.id }}">
                          <button class="header-menu-mobile-submenu-heading-{{ section.id }}">
                            <span class="header-menu-item-icon-{{ section.id }}">
                              <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" role="presentation"><path fill="currentColor" fill-rule="evenodd" d="M2.183 5.183a.625.625 0 0 1 .884 0l4.827 4.828a.208.208 0 0 0 .295 0l4.827-4.828a.625.625 0 0 1 .884.884l-4.827 4.827c-.57.57-1.493.57-2.063 0L2.183 6.067a.625.625 0 0 1 0-.884Z" clip-rule="evenodd"></path></svg>
                            </span>
                            {{ link.title }}
                          </button>
                          <ul class="header-mobile-submenu-list-{{ section.id }}">
                            {% for child_link in link.links %}
                              <li class="header-menu-item-{{ section.id }} {% if child_link.links.size > 0 %}header-mobile-menu-item-parent-{{ section.id }}{% endif %}">
                                <a href="{{ child_link.url }}" class="header-menu-link-{{ section.id }}">{{ child_link.title }}</a>
                                {% if child_link.links.size > 0 %}
                                  <span class="header-menu-item-icon-{{ section.id }}">
                                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" role="presentation"><path fill="currentColor" fill-rule="evenodd" d="M2.183 5.183a.625.625 0 0 1 .884 0l4.827 4.828a.208.208 0 0 0 .295 0l4.827-4.828a.625.625 0 0 1 .884.884l-4.827 4.827c-.57.57-1.493.57-2.063 0L2.183 6.067a.625.625 0 0 1 0-.884Z" clip-rule="evenodd"></path></svg>
                                  </span>
                                  <div class="header-menu-mobile-submenu-{{ section.id }}">
                                    <button class="header-menu-mobile-submenu-heading-{{ section.id }}">
                                      <span class="header-menu-item-icon-{{ section.id }}">
                                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" role="presentation"><path fill="currentColor" fill-rule="evenodd" d="M2.183 5.183a.625.625 0 0 1 .884 0l4.827 4.828a.208.208 0 0 0 .295 0l4.827-4.828a.625.625 0 0 1 .884.884l-4.827 4.827c-.57.57-1.493.57-2.063 0L2.183 6.067a.625.625 0 0 1 0-.884Z" clip-rule="evenodd"></path></svg>
                                      </span>
                                      {{ child_link.title }}
                                    </button>
                                    <ul class="header-mobile-submenu-list-{{ section.id }}">
                                      {% for child_child_link in child_link.links %}
                                        <li class="header-menu-item-{{ section.id }}">
                                          <a href="{{ child_child_link.url }}" class="header-menu-link-{{ section.id }}">{{ child_child_link.title }}</a>
                                        </li>
                                      {% endfor %}
                                    </ul>
                                  </div>
                                {% endif %}
                              </li>
                            {% endfor %}
                          </ul>
                        </div>
                      {% endif %}
                    </li>
                  {% endfor %}
                  {% for block in section.blocks %}
                    {%- if shop.customer_accounts_enabled and block.settings.use_account_link_mobile -%}
                      <li class="header-menu-item-{{ section.id }}">
                        <a href="{%- if customer -%}{{ routes.account_url }}{%- else -%}{{ routes.account_login_url }}{%- endif -%}" class="header-sidebar-account-{{ section.id }}">
                          <span class="header-sidebar-account-text-{{ section.id }}">{{ block.settings.account_text }}</span>
                        </a>
                      </li>                        
                    {%- endif -%}
                    {% if block.settings.use_search_link_mobile %}
                      <li class="header-menu-item-{{ section.id }}">                
                        <a href="{{ routes.search_url }}" class="header-sidebar-search-{{ section.id }}">
                          <span class="header-sidebar-search-text-{{ section.id }}">{{ block.settings.search_text }}</span>
                        </a>                    
                      </li>     
                    {% endif %}   
                  {% endfor %}                         
                </ul>    
              </div>             
              <ul class="header-sidebar-bottom-menu-{{ section.id }}">
                {% for link in block.settings.bottom_sidebar_menu.links %}
                  <li class="header-sidebar-bottom-menu-item-{{ section.id }}">
                    <a href="{{ link.url }}" class="header-sidebar-bottom-menu-link-{{ section.id }}">{{ link.title }}</a>
                  </li>
                {% endfor %}
              </ul>     
            </div>
          </div>
        {% endif %}
      {% endfor %}    
    </div>
</div>

<script>
  function initHeader3() {
      let header = document.querySelector('.header-{{ section.id }}');
      let menuMobile = document.querySelector('.header-menu-sidebar-{{ section.id }}');
      let mobileSubMenu = document.querySelectorAll('.header-menu-mobile-submenu-{{ section.id }}');
      let parentItems = document.querySelectorAll('.header-menu-item-parent-{{ section.id }}');

      if (header) {
        {% if show_type != 'top' %}
          if (window.scrollY > 0) {
            header.classList.add('onscroll');
          } else {
            header.classList.add('top');
          }

          let lastScrollTop = window.scrollY;

          document.addEventListener('scroll', () => {

              if(parentItems) {
                parentItems.forEach(el => el.classList.remove('active'));
              }

              const currentScrollTop = window.scrollY;
              const maxScroll = document.documentElement.scrollHeight - window.innerHeight;

              if (currentScrollTop === 0) {
                header.classList.add('top');
              }

              if (currentScrollTop <= 0) {
                  header.classList.add('active');
                  header.classList.remove('onscroll');
                  return;
              }

              if (currentScrollTop >= maxScroll) {
                  header.classList.remove('active');
                  return;
              }

              if (currentScrollTop !== lastScrollTop) {
                  if (header.classList.contains('scroll')) {
                      if (currentScrollTop > lastScrollTop) {
                          header.classList.remove('active');
                      } else {
                          header.classList.add('active');
                      }
                  }

                  if (header.classList.contains('transparent')) {
                    {% if show_type == 'scroll_up' %}
                      if (currentScrollTop < 100) {
                          header.classList.remove('onscroll');
                          header.classList.add('top');
                      } else {
                          header.classList.add('onscroll');
                          header.classList.remove('top');
                      }
                    {% else %}
                    if (currentScrollTop === 0) {
                          header.classList.remove('onscroll');
                          header.classList.add('top');
                      } else {
                          header.classList.add('onscroll');
                          header.classList.remove('top');
                      }
                    {% endif %}
                  }

                  lastScrollTop = currentScrollTop;
              }
          });
        {% endif %}

        let burger_icon = header.querySelectorAll('.header-menu-burger-{{ section.id }}');
        let close = header.querySelector('.header-sidebar-close-{{ section.id }}');
        let sidebar = header.querySelector('.header-menu-sidebar-inner-{{ section.id }}');

        if (burger_icon) {
           burger_icon.forEach(burger => {
            burger.addEventListener('click', () => {
                header.classList.add('open');
                sidebar.style.transform = 'translateX(0)';
                document.body.style.overflow = 'hidden';
            });
           })
        }

        if (close) {
            close.addEventListener('click', () => {
                sidebar.style.transform = 'translateX(-130%)';

                setTimeout(() => {
                    header.classList.remove('open');
                    sidebar.style.transform = '';
                    document.body.style.overflow = '';
                }, 300)
            });
        }


          document.addEventListener('click', (e) => {
              if (e.target === menuMobile) {
                sidebar.style.transform = 'translateX(-130%)';

                setTimeout(() => {
                    header.classList.remove('open');
                    sidebar.style.transform = '';
                    document.body.style.overflow = '';
                }, 300)
              }
          });

          if (parentItems) {
              parentItems.forEach(el => {
                  el.addEventListener('mouseenter', () => {
                      header.classList.add('onscroll'); 
                      el.classList.add('active');
                  });

                  el.addEventListener('mouseleave', () => {
                      el.classList.remove('active');
                      if (window.scrollY === 0) {
                          header.classList.remove('onscroll'); 
                      }
                  });

                  document.addEventListener('click', (event) => {
                      if (!el.contains(event.target) && el.classList.contains('active')) {
                          el.classList.remove('active');
                      }
                  });
              });
          }

          if (mobileSubMenu) {
              mobileSubMenu.forEach(el => {
                  let parentItem = el.closest('.header-mobile-menu-item-parent-{{ section.id }}');
                  let heading = el.querySelector('.header-menu-mobile-submenu-heading-{{ section.id }}');

                  if (parentItem) {
                      parentItem.addEventListener('click', () => {
                          parentItem.classList.add('active');
                      });
                  }

                  if (heading) {
                      heading.addEventListener('click', (event) => {
                          event.stopPropagation();
                          parentItem.classList.remove('active');
                      });
                  }
              });
          }
      }
  }



  document.addEventListener('DOMContentLoaded', initHeader3);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initHeader3);
     document.addEventListener('shopify:section:load', initHeader3);
  }
</script>
