-%}

{%- style -%}

  {{  review_text_font | font_face: font_display: 'swap' }}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
    height: 100%;
  }

  .hero-{{ section.id }} {
    height: auto;
    border-radius: {{ hero_radius }}px;
    overflow: hidden;
  }

  .hero-desktop-image-{{ section.id }} {
    position: absolute;
    display: none;
    width: 100%;
    height: 100%;
    object-fit: cover;
    top: 0;
    left: 0;
    z-index: 1;
  }

  .hero-desktop-image-{{ section.id }} img,
  .hero-desktop-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-desktop-image-{{ section.id }}:after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-color: {{ overlay_color | hex_to_rgba: overlay }};
    z-index: 1;
  }

  .hero-desktop-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .hero-image-{{ section.id }} {
    margin-top: {{ image_mt }}px;
  }

  .hero-image-{{ section.id }} img,
  .hero-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-body-{{ section.id }} {
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: {{ content_horizontal_position }};
    z-index: 2;
    height: 100%;
  }

  .hero-content-{{ section.id }} {
    width: 100%;
    max-width: 100%;
    padding: 0px {{ content_padding_horizontal_mobile }}px {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
  }

  .hero-top-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: {{ stars_align_mobile }};
    gap: 5px;
    background-color: {{ stars_bg_color_mobile }};
    padding: 10px;
    margin: 0px -{{ content_padding_horizontal_mobile }}px;
  }

  .hero-stars-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 1px;
  }

  .hero-stars-desktop-{{ section.id }} {
    display: none !important;
  }

  .hero-stars-{{ section.id }} svg,
  .hero-stars-{{ section.id }} img {
    display: block;
    width: {{ stars_size }}px;
    height: {{ stars_size }}px;
    object-fit: cover;
  }

  .hero-review-text-{{ section.id }} {
    margin: 0;
    font-size: {{ review_text_size_mobile }}px;
    color: {{ review_text_color_mobile }};
    line-height: {{ review_text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .hero-heading-{{ section.id }} {
    margin-top: {{ heading_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ heading_align_mobile }};
  }

  .hero-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color_mobile }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .hero-text-{{ section.id }} {
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ text_align_mobile }};
  }

  .hero-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color_mobile }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .hero-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: {{ button_width_mobile }}px;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
  }

  .hero-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .hero-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .hero-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .hero-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hero-body-{{ section.id }} {
      justify-content: {{ content_vertical_position }};
    }

    .hero-desktop-image-{{ section.id }} {
      display: block;
    }

    .hero-image-{{ section.id }} {
      display: none;
    }

    .hero-content-{{ section.id }} {
      max-width: {{ content_maxwidth }}%;
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .hero-top-{{ section.id }} {
      justify-content: {{ stars_align }};
      background-color: transparent;
      padding: 0px;
      margin: 0px;
    }

    .hero-review-text-{{ section.id }} {
      font-size: {{ review_text_size }}px;
      color: {{ review_text_color }};
    }

    .hero-heading-{{ section.id }} {
      margin-top: {{ heading_mt }}px;
      text-align: {{ heading_align }};
    }

    .hero-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
      color: {{ heading_color }};
    }

    .hero-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
    }

    .hero-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
      color: {{ text_color }};
    }

    .hero-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      max-width: {{ button_width }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .hero-stars-desktop-{{ section.id }} {
      display: flex !important;
    }

    .hero-stars-mobile-{{ section.id }} {
      display: none !important;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if review_text_custom %}
  <style>
    .hero-review-text-{{ section.id }} {
      font-family: {{ review_text_font.family }}, {{ review_text_font.fallback_families }};
      font-weight: {{ review_text_font.weight }};
      font-style: {{ review_text_font.style }};
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .hero-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .hero-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .hero-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if hero_height == "adapt_first" %}
  <style>
    @media(min-width: 1024px) {
      .hero-{{ section.id }} {
        height: 100vh;
      } 
    }
  </style>
{% elsif hero_height == "auto_height" %}
  <style>
    @media(min-width: 1024px) {

     .hero-{{ section.id }} {
        height: auto;
      }

    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {

      .hero-{{ section.id }} {
        height: {{ hero_fixed_height }}px;
      }
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .hero-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .hero-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .hero-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .hero-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .hero-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .hero-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} hero-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="hero-desktop-image-{{ section.id }}">
        {% if image != blank %}
          <img src="{{ image | image_url }}" alt="{{ image.alt }}">
        {% else %}
          {{ 'image' | placeholder_svg_tag }}
        {% endif %}
      </div>
      <div class="hero-body-{{ section.id }}">
        <div class="hero-content-{{ section.id }}">
          <div class="hero-top-{{ section.id }}">
            {% if stars_count != 0 %}
              <div class="hero-stars-{{ section.id }} hero-stars-mobile-{{ section.id }}">
                {% if stars_icon != blank %}
                  {% for item in (1..stars_count) %}
                    <img src="{{ stars_icon | image_url }}" alt="{{ stars_icon.alt }}">
                  {% endfor %}
                {% else %}
                  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                    {% if stars_count > 0 and stars_count < 1  %}
                      <defs>
                        <linearGradient id="grad{{ section.id }}1">
                          <stop offset="0%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_bg_color_mobile }}" />
                          <stop offset="100%" stop-color="{{ stars_bg_color_mobile }}" />
                        </linearGradient>
                      </defs>
                    {% endif %}
                    <path {% if stars_count > 0 and stars_count < 1 %}stroke="{{ stars_color_mobile }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if stars_count > 0 and stars_count < 1 %}url(#grad{{ section.id }}1){% elsif stars_count > 0.5 %}{{ stars_color_mobile }}{% endif %}"></path>
                  </svg>
                  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                    {% if stars_count > 1 and stars_count < 2  %}
                      <defs>
                        <linearGradient id="grad{{ section.id }}2">
                          <stop offset="0%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_bg_color_mobile }}" />
                          <stop offset="100%" stop-color="{{ stars_bg_color_mobile }}" />
                        </linearGradient>
                      </defs>
                    {% endif %}
                    <path {% if stars_count > 1 and stars_count < 2 %}stroke="{{ stars_color_mobile }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if stars_count > 1 and stars_count < 2 %}url(#grad{{ section.id }}2){% elsif stars_count > 1.5 %}{{ stars_color_mobile }}{% endif %}"></path>
                  </svg>
                  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                    {% if stars_count > 2 and stars_count < 3  %}
                      <defs>
                        <linearGradient id="grad{{ section.id }}3">
                          <stop offset="0%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_bg_color_mobile }}" />
                          <stop offset="100%" stop-color="{{ stars_bg_color_mobile }}" />
                        </linearGradient>
                      </defs>
                    {% endif %}
                    <path {% if stars_count > 2 and stars_count < 3 %}stroke="{{ stars_color }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if stars_count > 2 and stars_count < 3 %}url(#grad{{ section.id }}3){% elsif stars_count > 2.5 %}{{ stars_color_mobile }}{% endif %}"></path>
                  </svg>
                  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                    {% if stars_count > 3 and stars_count < 4  %}
                      <defs>
                        <linearGradient id="grad{{ section.id }}4">
                          <stop offset="0%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_bg_color_mobile }}" />
                          <stop offset="100%" stop-color="{{ stars_bg_color_mobile }}" />
                        </linearGradient>
                      </defs>
                    {% endif %}
                    <path {% if stars_count > 3 and stars_count < 4  %}stroke="{{ stars_color_mobile }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if stars_count > 3 and stars_count < 4  %}url(#grad{{ section.id }}4){% elsif stars_count > 3.5 %}{{ stars_color_mobile }}{% endif %}"></path>
                  </svg>
                  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                    {% if stars_count > 4 and stars_count < 5  %}
                      <defs>
                        <linearGradient id="grad{{ section.id }}5">
                          <stop offset="0%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_color_mobile }}" />
                          <stop offset="50%" stop-color="{{ stars_bg_color_mobile }}" />
                          <stop offset="100%" stop-color="{{ stars_bg_color_mobile }}" />
                        </linearGradient>
                      </defs>
                    {% endif %}
                    <path {% if stars_count > 4 and stars_count < 5  %}stroke="{{ stars_color_mobile }}"{% endif %} d="M10 0L12.9389 5.95492L19.5106 6.90983L14.7553 11.5451L15.8779 18.0902L10 15L4.12215 18.0902L5.24472 11.5451L0.489435 6.90983L7.06107 5.95492L10 0Z" fill="{% if stars_count > 4 and stars_count < 5  %}url(#grad{{ section.id }}5){% elsif stars_count > 4.5 %}{{ stars_color_mobile }}{% endif %}"></path>
                  </svg>
                {% endif %}
              </div>
            {% endif %}
            {% if stars_count != 0 %}
              <div class="hero-stars-{{ section.id }} hero-stars-desktop-{{ section.id }}">
                {% if stars_icon != blank %}
                  {% for item in (1..stars_count) %}
                    <img src="{{ stars_icon | image_url }}" alt="{{ stars_icon.alt }}">
                  {% endfor %}
                {% else %}
                  <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                    {% if stars_count > 0 and stars_count < 1  %}
                      <defs>
                        <linearGradient id="grad{{ section.id }}6">
                          <stop offset="0%" stop-color="{{ stars_color }}" />
                          <stop offset="50%" stop-color="{{ stars_color }}" />
                          <stop offset="50%" stop-color="transparent" />
                          <stop offset="100%" stop-color="transparent" />
                        </linearGradient>
