{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign logo = section.settings.logo
  assign logo_width = section.settings.logo_width
  assign logo_width_mobile = section.settings.logo_width_mobile

  assign heading = section.settings.heading
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_height = section.settings.heading_height
  assign heading_color = section.settings.heading_color
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile

  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile

  assign number_custom = section.settings.number_custom
  assign number_font = section.settings.number_font
  assign number_size = section.settings.number_size
  assign number_size_mobile = section.settings.number_size_mobile
  assign number_height = section.settings.number_height
  assign number_color = section.settings.number_color

  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_height = section.settings.title_height
  assign title_color = section.settings.title_color
  assign title_mt = section.settings.title_mt
  assign title_mt_mobile = section.settings.title_mt_mobile

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile

  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_height = section.settings.button_height
  assign button_color = section.settings.button_color
-%}

{%- style -%}
  {{ heading_font | font_face: font_display: 'swap' }}
  {{ number_font | font_face: font_display: 'swap' }}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}
  {{ button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
  }

  .hero-block-body-{{ section.id }} {
    position: relative;
  }

  .hero-block-{{ section.id }} {
    position: relative;
    height: 100%;
  }

  .hero-heading-{{ section.id }} {
    position: absolute;
    top: calc(100% / {{ section.blocks.size }} / 2);
    left: 50%;
    transform: translate(-50%, -50%);  
    z-index: 2;
    transition: top 0.05s ease; 
    pointer-events: none;
    width: 100%;
    text-align: {{ heading_align_mobile }};
  }

  .hero-heading-{{ section.id }} * {
    margin: 0;
    font-weight: 700;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    text-decoration: none;
  }  

  .hero-heading-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-image-{{ section.id }} {    
    position: relative;
    width: 100%;
    overflow: hidden;
  }

  .hero-image-{{ section.id }} img,
  .hero-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .hero-image-desktop-{{ section.id }} {
    display: none !important;
  }

  .hero-content-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 0;
    transform: translateY(-50%);
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    opacity: 0;
    transition: all 0.25s ease 0s;
  }  

  .hero-number-{{ section.id }} {
    margin: 0;
    font-size: {{ number_size_mobile }}px;
    color: {{ number_color }};
    line-height: {{ number_height }}%;
    text-transform: unset;
    word-wrap: break-word;
  }

  .hero-title-{{ section.id }} {
    margin: 0;
    margin-top: {{ title_mt_mobile }}px;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    word-wrap: break-word;
  }

  .hero-text-{{ section.id }} {
    margin-top: {{ text_mt_mobile }}px;
  }

  .hero-text-{{ section.id }} * {
    margin: 0;    
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    word-wrap: break-word;
  }

  .hero-button-{{ section.id }} {
    position: absolute;
    bottom: 30px;
    right: 30px;
    z-index: 2;
    margin: 0;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    cursor: pointer;
    text-transform: unset;
    text-decoration: none;
    word-wrap: break-word;
    transition: all 0.25s ease 0s;
  }

  .hero-button-{{ section.id }}:hover {
    opacity: 0.5;
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hero-block-{{ section.id }} {
      height: 100%;
    }

    .hero-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .hero-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .hero-content-{{ section.id }} {
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
      max-width: 70%;
    }  

    .hero-number-{{ section.id }} {
      font-size: {{ number_size }}px;
    }

    .hero-title-{{ section.id }} {
      margin-top: {{ title_mt }}px;
      font-size: {{ title_size }}px;
    }

    .hero-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
    }

    .hero-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .hero-button-{{ section.id }} {
      font-size: {{ button_size }}px;
    }

    .hero-image-desktop-{{ section.id }} {
      display: block !important;
    }

    .hero-image-mobile-{{ section.id }} {
      display: none !important;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .hero-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if number_custom %}
  <style>
    .hero-number-{{ section.id }} {
      font-family: {{ number_font.family }}, {{ number_font.fallback_families }};
      font-weight: {{ number_font.weight }};
      font-style: {{ number_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .hero-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .hero-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .hero-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% for block in section.blocks %} 
  <style>
      .hero-image-{{ block.id }}:after {
        position: absolute;
        content: "";
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-color: {{ block.settings.image_overlay_color | hex_to_rgba: block.settings.image_overlay }};
        transition: all 0.25s ease 0s;
        opacity: 0;
      }
  </style>

  {% if block.settings.block_height == "adapt_first" %}
    <style>
      @media(min-width: 1024px) {      
        .section-{{ section.id }}-settings {
          height: 100%;
        }

        .hero-image-{{ block.id }} img,
        .hero-image-{{ block.id }} svg {
          max-height: calc(95vh - {{ padding_top }}px - {{ padding_bottom }}px);
        }
      }
    </style>
  {% elsif block.settings.block_height == 'fixed_height' %}  
    <style>     
      .hero-image-{{ block.id }} {
        height: {{ block.settings.block_fixed_height_mobile }}px;
      }

      @media(min-width: 1024px) {

        .hero-image-{{ block.id }} {
          height: {{ block.settings.block_fixed_height }}px;
        }
      }
    </style>
  {% endif %}

  {% if block.settings.title != blank and block.settings.text != blank %}
    <style>
      .hero-block-{{ block.id }}.active .hero-content-{{ block.id }} {
        opacity: 1;
        z-index: 2;
        transition: all 0.25s ease 0s;
      }

      .hero-block-{{ block.id }}.active .hero-image-{{ block.id }}:after {
        z-index: 2;
        opacity: 1;
        transition: all 0.25s ease 0s;
      } 

      @media(min-width: 1024px) {
        .hero-block-{{ block.id }}.active .hero-content-{{ block.id }},
        .hero-block-{{ block.id }}.active .hero-image-{{ block.id }}:after {
           opacity: 0;
          z-index: 1;
        }

        .hero-block-{{ block.id }}:hover .hero-content-{{ block.id }} {
          opacity: 1;
          transition: all 0.25s ease 0s;
        }

        .hero-block-{{ block.id }}:hover .hero-image-{{ block.id }}:after {
          opacity: 1;
          transition: all 0.25s ease 0s;
        } 
      }
    </style>
  {% endif %}
{% endfor %}

<div class="section-{{ section.id }} hero-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if heading != blank or logo != blank %}
        <div class="hero-heading-{{ section.id }}">
          {% if logo != blank %}
            <img src="{{ logo | image_url }}" alt="{{ logo.alt }}">
          {% else %}
            {{ heading }}
          {% endif %}
        </div> 
      {% endif %} 
      <div class="hero-block-body-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="hero-block-{{ section.id }} hero-block-{{ block.id }}">
            <div class="hero-image-{{ section.id }} hero-image-{{ block.id }}">
              {% if block.settings.image != blank %}
                <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %} {% if block.settings.image_mobile != blank %}class="hero-image-desktop-{{ section.id }}"{% endif %}>
              {% else %}
                {{ 'image' | placeholder_svg_tag }}
              {% endif %}

              {% if block.settings.image_mobile != blank %}
                <img src="{{ block.settings.image_mobile | image_url }}" alt="{{ block.settings.image_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} class="hero-image-mobile-{{ section.id }}">
              {% endif %}
            </div>
            <div class="hero-content-{{ section.id }} hero-content-{{ block.id }}">
              {% if block.settings.number != blank %}
                <p class="hero-number-{{ section.id }}">{{ block.settings.number }}</p>
              {% endif %}
              {% if block.settings.title != blank %}
                <h2 class="hero-title-{{ section.id }}">{{ block.settings.title }}</h2>
              {% endif %}
              {% if block.settings.text != blank %}
                <div class="hero-text-{{ section.id }}">{{ block.settings.text }}</div>
              {% endif %}
            </div>
            <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}p{% endif %} class="hero-button-{{ section.id }}">
              {{ block.settings.button }}
            </{% if block.settings.url != blank %}a{% else %}p{% endif %}>
          </div>
        {% endfor %}
      </div>      
    </div>
</div>

<script>
  function initHero17() {  
      const heading = document.querySelector('.hero-heading-{{ section.id }}');  
      const section = document.querySelector('.section-{{ section.id }}');  
      const blocks = document.querySelectorAll('.hero-block-{{ section.id }}');  

      if (!heading || !section) return;  

      const minTop = 100 / {{ section.blocks.size }} / 2;  
      const maxTop = 100 - 2;  
      const headingOffset = 20;  
      const viewportOffset = 50;  

      // Store last known position and add throttling
      let lastScrollTime = 0;
      const throttleDelay = 10; // ms between updates

      const calculateInitialPosition = () => {  
          heading.style.top = `${minTop}%`;  
          heading.style.transform = `translate(-50%, -${minTop}%)`;  
          heading.classList.add('is-ready');  
      };  

      requestAnimationFrame(() => {  
          calculateInitialPosition();  
      });  

      const handleScroll = () => {  
          const now = Date.now();

          // Throttle scroll updates
          if (now - lastScrollTime < throttleDelay) {
              return;
          }
          lastScrollTime = now;

          const sectionRect = section.getBoundingClientRect();  
          const headingRect = heading.getBoundingClientRect();  
          const viewportHeight = window.innerHeight;  

          if (sectionRect.top <= viewportHeight && sectionRect.bottom >= 0) {  
              if (sectionRect.top < viewportOffset) {  
                  const scrollProgress = (viewportOffset - sectionRect.top + headingOffset) / (sectionRect.height - headingRect.height);  
                  const newTop = Math.min(Math.max(scrollProgress * 100, minTop), maxTop);  

                  heading.style.top = `${newTop}%`;  
                  heading.style.transform = `translate(-50%, -${newTop}%)`;
              } else {  
                  heading.style.top = `${minTop}%`;  
                  heading.style.transform = `translate(-50%, -${minTop}%)`;
              }  
          }  
      };  

      // Add passive scroll listener for better performance
      window.addEventListener('scroll', handleScroll, { passive: true });  

      if (blocks) {  
          blocks.forEach(el => {  
              el.addEventListener('click', () => {  
                  el.classList.toggle('active');  
              });  
          });  
      }  

      return () => {  
          window.removeEventListener('scroll', handleScroll);  
      };  
  } 

  document.addEventListener('DOMContentLoaded', initHero17);

  if (Shopify.designMode) {
      document.addEventListener('shopify:section:unload', function() {
          const cleanup = initHero17();
          if (typeof cleanup === 'function') cleanup();
      });
      document.addEventListener('shopify:section:load', initHero17);
  }
</script>
