{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign section_radius = section.settings.section_radius

  assign hero_bg_type = section.settings.hero_bg_type
  assign bg_image = section.settings.bg_image
  assign bg_video = section.settings.bg_video
  assign bg_video_url = section.settings.bg_video_url
  assign background_overlay = section.settings.background_overlay
  assign background_overlay_color = section.settings.background_overlay_color

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile

  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile
  assign reverse_direction = section.settings.reverse_direction
  assign pause_hover = section.settings.pause_hover

  assign cards_mt = section.settings.cards_mt
  assign cards_mt_mobile = section.settings.cards_mt_mobile
  assign cards_gap = section.settings.cards_gap
  assign cards_gap_mobile = section.settings.cards_gap_mobile

  assign card_padding_horizontal = section.settings.card_padding_horizontal
  assign card_padding_horizontal_mobile = section.settings.card_padding_horizontal_mobile
  assign card_padding_vertical = section.settings.card_padding_vertical
  assign card_padding_vertical_mobile = section.settings.card_padding_vertical_mobile
  assign card_radius = section.settings.card_radius
  assign card_border_thickness = section.settings.card_border_thickness
  assign card_border_color = section.settings.card_border_color
  assign card_border_hover_color = section.settings.card_border_hover_color
  assign card_bg_hover_color = section.settings.card_bg_hover_color
  assign card_width = section.settings.card_width
  assign card_width_mobile = section.settings.card_width_mobile
  assign card_bg_color = section.settings.card_bg_color
  assign card_bg_opacity = section.settings.card_bg_opacity

  assign card_text_size = section.settings.card_text_size
  assign card_text_size_mobile = section.settings.card_text_size_mobile
  assign card_text_custom = section.settings.card_text_custom
  assign card_text_font = section.settings.card_text_font
  assign card_text_height = section.settings.card_text_height
  assign card_text_color = section.settings.card_text_color
  assign card_text_hover_color = section.settings.card_text_hover_color

  assign icon_size = section.settings.icon_size
  assign icon_size_mobile = section.settings.icon_size_mobile
  assign icon_color = section.settings.icon_color
  assign icon_hover_color = section.settings.icon_hover_color

  assign first_row_count = 0
  for block in section.blocks 
    if block.settings.row == 'first_row' and block.type == 'card'
      assign first_row_count = first_row_count | plus: 1
    endif
  endfor

  assign second_row_count = 0
  for block in section.blocks  
    if block.settings.row == 'second_row' and block.type == 'card'
      assign second_row_count = second_row_count | plus: 1
    endif
  endfor

  assign third_row_count = 0
  for block in section.blocks   
    if block.settings.row == 'third_row' and block.type == 'card'
      assign third_row_count = third_row_count | plus: 1
    endif  
  endfor
-%}

{%- style -%}
  {{  card_text_font | font_face: font_display: 'swap' }}  

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
    position: relative;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    overflow: hidden;
  }

  .hero-bg-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
  }

  .hero-bg-{{ section.id }}::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    background-color: {{ background_overlay_color | hex_to_rgba: background_overlay }}
  }

  .hero-bg-{{ section.id }} img,
  .hero-bg-{{ section.id }} svg,
  .hero-bg-{{ section.id }} video,
  .hero-bg-{{ section.id }} iframe {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-bg-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .hero-body-{{ section.id }} {
    position: relative;
    z-index: 2;
  }

  .hero-scrolling-body-{{ section.id }} {
    display: flex;
    flex-direction: column;
    margin-top: {{ cards_mt_mobile }}px;
    gap: {{ cards_gap_mobile }}px;
  }

  .hero-scrolling-wrap-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    background-attachment: scroll !important;
    transform-origin: center;
    width: 200vw;
    position: relative;
    z-index: 0;
    left: -100vw;
  }

  .hero-scrolling-list-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    align-items: center;
    white-space: nowrap;
    background-attachment: scroll !important;
    flex-shrink: 0;
  }

  .hero-scrolling-wrap-{{ section.id }}:nth-child(1) .hero-scrolling-list-{{ section.id }} {   
    -webkit-animation: tickerFirst{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: tickerFirst{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
  }

  .hero-scrolling-wrap-{{ section.id }}:nth-child(2) .hero-scrolling-list-{{ section.id }} {   
    -webkit-animation: tickerSecond{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: tickerSecond{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
  }

  .hero-scrolling-wrap-{{ section.id }}:nth-child(3) .hero-scrolling-list-{{ section.id }} {   
    -webkit-animation: tickerFirst{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: tickerFirst{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
  }

  .hero-scrolling-card-{{ section.id }} {
    background-attachment: scroll !important;
    flex-shrink: 0;
    text-decoration: none;
    margin-right: {{ cards_gap_mobile }}px;
    width: {{ card_width_mobile }}px;
    background-color: {{ card_bg_color | hex_to_rgba: card_bg_opacity }};
    height: 100%;
    border: {{ card_border_thickness }}px solid {{ card_border_color }};
    padding: {{ card_padding_vertical_mobile }}px {{ card_padding_horizontal_mobile }}px;
    border-radius: {{ card_radius }}px;
    transition: transform 0.25s ease 0s;
    text-decoration: none;
    overflow: hidden;
    filter: none;
  }

  .hero-scrolling-text-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    margin: 0px;
    font-size: {{ card_text_size_mobile }}px;
    line-height: {{ card_text_height }}%;
    color: {{ card_text_color }};
    text-decoration: none;
    text-transform: unset;
    word-break: break-word;
  }

  .hero-scrolling-text-{{ section.id }} svg {
    width: {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
  }

  .hero-scrolling-text-{{ section.id }} svg path {
    fill: {{ icon_color }};
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hero-scrolling-body-{{ section.id }} {
      margin-top: {{ cards_mt }}px;
      gap: {{ cards_gap }}px;
    }

    .hero-scrolling-wrap-{{ section.id }}:nth-child(1) .hero-scrolling-list-{{ section.id }} {   
      -webkit-animation: tickerFirst{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
      animation: tickerFirst{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
    }

    .hero-scrolling-wrap-{{ section.id }}:nth-child(2) .hero-scrolling-list-{{ section.id }} {   
      -webkit-animation: tickerSecond{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
      animation: tickerSecond{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
    }

    .hero-scrolling-wrap-{{ section.id }}:nth-child(3) .hero-scrolling-list-{{ section.id }} {   
      -webkit-animation: tickerFirst{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
      animation: tickerFirst{{ section.id | replace: '-', '' }} {{ scrolling_velocity }}s infinite linear;
    }   

    .hero-scrolling-card-{{ section.id }} {
      margin-right: {{ cards_gap }}px;
      width: {{ card_width }}px;
      padding: {{ card_padding_vertical }}px {{ card_padding_horizontal }}px;
    }  

    .hero-scrolling-card-{{ section.id }}:hover {
      background-color: {{ card_bg_hover_color }};
      border: {{ card_border_thickness }}px solid {{ card_border_hover_color }};
    } 

    .hero-scrolling-text-{{ section.id }} {
      font-size: {{ card_text_size }}px;
    }

    .hero-scrolling-text-{{ section.id }} svg {
      width: {{ icon_size }}px;
      height: {{ icon_size }}px;
    }

    .hero-scrolling-card-{{ section.id }}:hover .hero-scrolling-text-{{ section.id }} svg path {
      fill: {{ icon_hover_color }};
    }

    .hero-scrolling-card-{{ section.id }}:hover .hero-scrolling-text-{{ section.id }} {
      color: {{ card_text_hover_color }};
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if card_text_custom %}
  <style>
    .hero-scrolling-text-{{ section.id }} {
      font-family: {{ card_text_font.family }}, {{ card_text_font.fallback_families }};
      font-weight: {{ card_text_font.weight }};
      font-style: {{ card_text_font.style }};
    }
  </style>
{% endif %}

{% if reverse_direction %}
	<style>
    @keyframes tickerFirst{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-50%);
      }
      100% {
        transform: translateX(0%);
      }
    }

    @keyframes tickerSecond{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-50%);
      }
    }
	</style>
{% else %}
	<style>
    @keyframes tickerFirst{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-50%);
      }
    }

    @keyframes tickerSecond{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-50%);
      }
      100% {
        transform: translateX(0%);
      }
    }
	</style>
{% endif %}

{% if pause_hover %}
	<style>
    .hero-scrolling-wrap-{{ section.id }}:hover .hero-scrolling-list-{{ section.id }} {
      animation-play-state: paused
    }
	</style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}      
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .hero-heading-{{ block.id }} {
          text-align: {{ content_align_mobile }};
        }

        .hero-heading-{{ block.id }} * {
          margin: 0px;
          font-size: {{ block.settings.heading_size_mobile }}px;
          line-height: {{ block.settings.heading_height }}%;
          color: {{ block.settings.heading_color }};
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        @media(min-width: 1024px) {
          .hero-heading-{{ block.id }} {
            text-align: {{ content_align }};
          }

          .hero-heading-{{ block.id }} * {   
            font-size: {{ block.settings.heading_size }}px;
          }
        }
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .hero-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}  
      <style>      
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .hero-text-{{ block.id }} {
          text-align: {{ content_align_mobile }};
          max-width: {{ block.settings.text_max_width_mobile }}%;
          margin-top: {{ block.settings.text_mt_mobile }}px;
        }

        .hero-text-{{ block.id }} * {
          margin: 0px;
          font-size: {{ block.settings.text_size_mobile }}px;
          line-height: {{ block.settings.text_height }}%;
          color: {{ block.settings.text_color }};
          text-transform: unset;
          word-break: break-word;
          font-weight: 400;
        }

        @media(min-width: 1024px) {                
          .hero-text-{{ block.id }} {
            text-align: {{ content_align }};
            max-width: {{ block.settings.text_max_width }}%;
            margin-top: {{ block.settings.text_mt }}px;
          }

          .hero-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .hero-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>
      {% endif %}

      {% if content_align_mobile == 'right' %}
        <style>
          .hero-text-{{ block.id }} {
            margin-left: auto;         
          }
        </style>
      {% elsif content_align_mobile == 'center' %}
        <style>
          .hero-text-{{ block.id }} {
            margin-left: auto;
            margin-right: auto;           
          }
        </style>  
      {% elsif content_align_mobile == 'left' %}
        <style>
          .hero-text-{{ block.id }} {
            margin-right: auto;          
          }
        </style>  
      {% endif %}

      {% if content_align == 'right' %}
        <style>
          @media(min-width: 1024px) {
            .hero-text-{{ block.id }} {
              margin-left: auto;
              margin-right: 0;
            }
          }   
        </style>
      {% elsif content_align == 'center' %}
        <style>
          @media(min-width: 1024px) {
            .hero-text-{{ block.id }} {
              margin-left: auto;
              margin-right: auto;
            }
          } 
        </style>  
      {% elsif content_align == 'left' %}
        <style>
          @media(min-width: 1024px) {
            .hero-text-{{ block.id }} {
              margin-right: 0;
              margin-left: 0;
            }
          } 
        </style>  
      {% endif %}

    {% when 'button' %}          
      {%- liquid
        assign button_horizontal_align = ""
      if block.settings.button_align == "center"
        assign button_horizontal_align = "center"
      elsif block.settings.button_align == "right"
        assign button_horizontal_align = "end"
      else
        assign button_horizontal_align = "start"
      endif

      assign button_horizontal_align_mobile = ""
      if block.settings.button_align_mobile == "center"
        assign button_horizontal_align_mobile = "center"
      elsif block.settings.button_align_mobile == "right"
        assign button_horizontal_align_mobile = "end"
      else
        assign button_horizontal_align_mobile = "start"
      endif -%}
      <style>    
        {{  block.settings.button_font | font_face: font_display: 'swap' }}


        .hero-button-wrap-{{ section.id }} {
          display: flex;
          align-items: center;
          width: 100%;
          justify-content: {{ button_horizontal_align_mobile }};
        }  

        .hero-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.25s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
        }

        .hero-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
        }

        .hero-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .hero-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }

        .hero-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .hero-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        @media(min-width: 1024px) {          
          .hero-button-wrap-{{ section.id }} {
            justify-content: {{ button_horizontal_align }};
          }

          .hero-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>

      {% if block.settings.button_custom %}
        <style>
          .hero-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .hero-button-{{ block.id }} {
            background-color: {{ block.settings.button_bg_color }};
          }

          .hero-button-{{ block.id }}:hover {
            background-color: {{ block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>
          .hero-button-{{ block.id }} {
            background-color: {{ block.settings.button_bg_color }};
            border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
          }

          .hero-button-{{ block.id }}:hover {
            background-color: {{ block.settings.button_bg_hover_color }};
            border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .hero-button-{{ block.id }} {
            padding: 0px !important;
          }
        </style>
      {% endif %}

  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} hero-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      {% if hero_bg_type != 'color' %}
        <div class="hero-bg-{{ section.id }}">
          {% if hero_bg_type == 'video' %}      
            {% assign video_id = bg_video.id | default: bg_video_url.id %}
            {% assign loop = '&loop=1&playlist=' | append: video_id %}
            {%- if bg_video == null and bg_video_url != null -%}
              {%- if bg_video_url.type == 'youtube' -%}
                <iframe
                  src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"
                  data-src1="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"
                  data-src2="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=0&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"
                  data-src3="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=0&autoplay=0&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"
                  allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                  allowfullscreen
                ></iframe>
                {%- else -%}
                <iframe
                  src="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"
                  data-src1="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=0&autoplay=1&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"
                  data-src2="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&autoplay=0&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"
                  data-src3="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=0&autoplay=0&controls=0&modestbranding=1&showinfo=0&rel=0{{ loop }}"
                  allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                  allowfullscreen
                ></iframe>
