{% if lazy %}
  <script src="https://cdn.jsdelivr.net/npm/vanilla-lazyload@19.1.3/dist/lazyload.min.js"></script>
{% endif %}

  <script>
    function initHero42() {      
      {% if lazy %}
        let lazyLoadInstance = new LazyLoad();

        window.addEventListener(
          "LazyLoad::Initialized",
          function (event) {
            window.lazyLoadInstance = event.detail.instance;
          },
          false
        );
      {% endif %}

      const heroSection = document.querySelector('.hero-{{ section.id }}');
      const sticker = heroSection.querySelector('.hero-sticker-{{ section.id }}');
      
      if (!sticker) return;

      const offsetX = 15;
      const offsetY = 15;
      
      sticker.style.pointerEvents = 'none';
      sticker.style.opacity = '0';
      sticker.style.transition = 'transform 0.15s ease-out, opacity 0.3s ease-in-out';

      function handleMouseMove(e) {
        const rect = heroSection.getBoundingClientRect();
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;

        const translateX = mouseX + offsetX;
        const translateY = mouseY + offsetY;

        sticker.style.opacity = '1';
        sticker.style.transform = `translate3d(${translateX}px, ${translateY}px, 0)`;
      }
      
      function handleMouseEnter() {
        sticker.style.opacity = '1';
      }

      function handleMouseLeave() {
        sticker.style.opacity = '0';
      }

      heroSection.addEventListener('mousemove', handleMouseMove);
      heroSection.addEventListener('mouseleave', handleMouseLeave);
      heroSection.addEventListener('mouseenter', handleMouseEnter);
    }

    document.addEventListener('DOMContentLoaded', initHero42);

    if (Shopify.designMode) {
      document.addEventListener('shopify:section:unload', initHero42);
      document.addEventListener('shopify:section:load', initHero42);
    }
  </script>
