{%- liquid
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign layout_height = section.settings.layout_height
  assign layout_fixed_height = section.settings.layout_fixed_height
  assign layout_fixed_height_mobile = section.settings.layout_fixed_height_mobile
  assign layout_bg_type = section.settings.layout_bg_type  
  
  assign bg_image = section.settings.bg_image
  assign bg_image_mobile = section.settings.bg_image_mobile
  assign bg_video = section.settings.bg_video
  assign bg_video_url = section.settings.bg_video_url
  assign background_url = section.settings.background_url

  assign open_in_new_tab = section.settings.open_in_new_tab

  assign bg_overlay_color = section.settings.bg_overlay_color
  assign bg_overlay = section.settings.bg_overlay

  assign content_maxwidth = section.settings.content_maxwidth
  
  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign feature_custom = section.settings.feature_custom
  assign feature_font = section.settings.feature_font
  assign feature_size = section.settings.feature_size
  assign feature_size_mobile = section.settings.feature_size_mobile
  assign feature_color = section.settings.feature_color
  assign feature_height = section.settings.feature_height
  assign feature_background = section.settings.feature_background
  assign feature_radius = section.settings.feature_radius
  assign feature_padding_horizontal = section.settings.feature_padding_horizontal
  assign feature_padding_horizontal_mobile = section.settings.feature_padding_horizontal_mobile
  assign feature_padding_vertical = section.settings.feature_padding_vertical
  assign feature_padding_vertical_mobile = section.settings.feature_padding_vertical_mobile
  assign feature_border_thickness = section.settings.feature_border_thickness
  assign feature_border_color = section.settings.feature_border_color
  assign feature_bg_color = section.settings.feature_bg_color
  
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile
  assign reverse_direction = section.settings.reverse_direction  
  assign cards_gap_mobile = section.settings.cards_gap_mobile

  assign loading_attribute = 'eager'
  if lazy 
    assign loading_attribute = 'lazy' 
  endif 
-%}

{%- style -%}
  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden; 
    position: relative;
  }

  .hero-{{ section.id }} {
    display: flex;
    flex-direction: column;
  }

  .hero-body-{{ section.id }} {
    text-decoration: none;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    flex: 1 1 0;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;  
    height: 100%;
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: end;
    overflow: hidden;
  }

  .hero-bg-{{ section.id }} .hero-bg-image-desktop {
    display: none;
  }

  .hero-bg-{{ section.id }} .hero-bg-image-mobile {
    display: block;
  }

  .hero-bg-{{ section.id }} {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 100%;
    transform: translate(-50%, -50%);   
    overflow: hidden; 
  }

  .hero-bg-{{ section.id }} img,
  .hero-bg-{{ section.id }} svg,
  .hero-bg-{{ section.id }} video,
  .hero-bg-{{ section.id }} iframe {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border: 0px;
  }  

  .hero-bg-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .hero-body-{{ section.id }} {    
    display: flex;
    justify-content: space-between;
    height: 100%;    
    justify-content: center;
    gap: 20px;
    width: 100%;
    z-index: 2;
  }

  .hero-content-{{ section.id }} {
    width: 80%;
    transform: translateY(-6%);
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
    justify-content: center;
  }

  .hero-buttons-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 16px;
    justify-content: {{ content_horizontal_align_mobile }};
    flex-wrap: wrap;
  }

  .hero-motion-wrapper-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    pointer-events: none;
    z-index: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: visible;
  }

  .hero-motion-svg-{{ section.id }} {
    position: absolute;
    top: 44%;
    left: 50%;
    transform: translate(-50%, -50%);
    height: 80%; 
    width: auto;
    max-width: 90%;
    overflow: visible;
  }

  .hero-feature-tag-{{ section.id }} {
    {% if feature_background %}
    padding: {{ feature_padding_vertical_mobile }}px {{ feature_padding_horizontal_mobile }}px;
    border: {{ feature_border_thickness }}px solid {{ feature_border_color }};
    border-radius: {{ feature_radius }}px;
    background-color: {{ feature_bg_color }};
    backdrop-filter: blur(4px);
    {% endif %}
    font-size: {{ feature_size_mobile }}px;
    color: {{ feature_color }};
    line-height: {{ feature_height }}%;
    text-transform: unset;
  }

  .hero-motion-wrapper-{{ section.id }} .hero-feature-tag-{{ section.id }} {
    margin: 0;
    position: absolute;
    top: 0; 
    left: 0;
    offset-path: url(#hero-motion-path-{{ section.id }});    
    offset-anchor: center;
    transform-box: border-box;
    opacity: 0;
  }

  .hero-feature-tag-desktop-{{ section.id }} {
    display: none;
  }

  .hero-scrolling-wrap-{{ section.id }} {
    position: absolute;
    bottom: 5%;
    left: 0;
    z-index: 1;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    scrollbar-width: none;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    gap: {{ cards_gap_mobile }}px;
    background-attachment: scroll !important;
  }

  .hero-scrolling-list-{{ section.id }} {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    white-space: nowrap;
    height: 100%;
    gap: {{ cards_gap_mobile }}px;
    background-attachment: scroll !important;
    -webkit-animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: ticker{{ section.id | replace: '-', '' }} {{ scrolling_velocity_mobile }}s infinite linear;
    flex-shrink: 0;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
      padding-bottom: 0px;
    }

    .hero-content-{{ section.id }} {
      max-width: {{ content_maxwidth }}%;
      transform: translateY(0%);
      align-items: {{ content_horizontal_align }};
      justify-content: center;
    }

    .hero-body-{{ section.id }} {
      padding-top: {{ padding_top }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
      padding-bottom: {{ padding_bottom }}px;
      align-items: center;
      justify-content: center;
    }  
    .hero-bg-{{ section.id }} {
      height: 100% !important;
    }
    
    .hero-bg-{{ section.id }} .hero-bg-image-desktop {
      display: block;
    }

    .hero-bg-{{ section.id }} .hero-bg-image-mobile {
      display: none;
    }
    
    .hero-buttons-{{ section.id }} {
      justify-content: {{ content_horizontal_align }};
    }

    .hero-motion-svg-{{ section.id }} {
      top: 50%;
      transform: translate(-50%, -50%) rotate(90deg);
      height: auto;
      width: 40%;
    }

    .hero-feature-tag-{{ section.id }} {
      {% if feature_background %}
      padding: {{ feature_padding_vertical }}px {{ feature_padding_horizontal }}px;
      {% endif %}
      font-size: {{ feature_size }}px;
    }

    .hero-feature-tag-desktop-{{ section.id }} {
      display: block;
    }

    .hero-scrolling-wrap-{{ section.id }} {
      display: none;
    }
  }
  
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }

    .hero-bg-{{ section.id }} {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: clamp(100px, var(--page-content-width, var(--page-width)), 100%);
    }

    .hero-bg-{{ section.id }} {
      max-width: clamp(100px, var(--page-content-width, var(--page-width)), 100%);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if layout_height == "full_screen" %}
  <style>
    .section-{{ section.id }}-settings {
      flex: 1 1 0;
    }

    .hero-{{ section.id }} {
      min-height: 95vh;
    }

    .hero-bg-{{ section.id }} img,
    .hero-bg-{{ section.id }} svg,
    .hero-bg-{{ section.id }} video,
    .hero-bg-{{ section.id }} iframe {
      min-height: 95vh;
    }
    
    @media(min-width: 1024px) {     
      .hero-{{ section.id }} {
        height: 95vh;
      }
    }
  </style>
{% elsif layout_height == 'fixed_height' %}  
  <style>   
    .section-{{ section.id }}-settings {
      flex: 1 1 0;
    }
      
    .hero-{{ section.id }} {
      min-height: {{ layout_fixed_height_mobile }}px;
    }

    .hero-bg-{{ section.id }} img,
    .hero-bg-{{ section.id }} svg,
    .hero-bg-{{ section.id }} video,
    .hero-bg-{{ section.id }} iframe {
      min-height: {{ layout_fixed_height_mobile }}px;
    }

    @media(min-width: 1024px) {
      .hero-{{ section.id }} {
        height: {{ layout_fixed_height }}px;
      }

      .hero-bg-{{ section.id }} img,
      .hero-bg-{{ section.id }} svg,
      .hero-bg-{{ section.id }} video,
      .hero-bg-{{ section.id }} iframe {
        height: {{ layout_fixed_height }}px;
      }
    }
  </style>
{% else %}
  <style>
    .hero-bg-{{ section.id }} {
      position: absolute;
      width: 100%;
      height: 100%;
    }

    .hero-body-{{ section.id }} {
      position: relative;
    }
  </style>
{% endif %}


{% if reverse_direction %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(-100%);
      }
      100% {
        transform: translateX(0%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes ticker{{ section.id | replace: '-', '' }} {
      0% {
        transform: translateX(0%);
      }
      100% {
        transform: translateX(-100%);
      }
    }
  </style>
{% endif %}

{% if layout_bg_type == 'video' or layout_bg_type == 'image' %}
  <style>
    .hero-bg-{{ section.id }}::after {
      content: '';
      position: absolute;
      width: 100%;
      height: 100%;
      left: 0;
      top: 0;
      z-index: 1;
      background-color: {{ bg_overlay_color | hex_to_rgba: bg_overlay }};
      transition: all 0.25s ease 0s;
    }
  </style>
{% endif %}

{% if feature_custom %}
  <style>
    {{ feature_font | font_face: font_display: 'swap' }}

    .hero-feature-tag-{{ section.id }} {
      font-family: {{ feature_font.family }}, {{ feature_font.fallback_families }};
      font-weight: {{ feature_font.weight }};
      font-style: {{ feature_font.style }};
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'dot' %}
       {%- assign initial_offset = block.settings.dot_position | plus: 20 | modulo: 100 -%}
      <style>
        .hero-dot-{{ block.id }} {
          offset-path: url(#hero-motion-path-{{ section.id }});
          offset-anchor: center; 
          transform-box: border-box;
          transition: offset-distance 1.5s ease-out;
          offset-distance: {{ initial_offset }}%;
        }
        
        @media(min-width: 1024px) {
          .section-{{ section.id }}.animate-dots-{{ section.id }} .hero-dot-{{ block.id }} {
            offset-distance: {{ block.settings.dot_position }}%;
          }

          .hero-feature-tag-{{ block.id }} {
            opacity: 0;
            transition: opacity 1s ease-in-out 1.5s;
          }

          .section-{{ section.id }}.animate-dots-{{ section.id }} .hero-feature-tag-{{ block.id }} {
            opacity: 1;
          }
        }

        @keyframes dotMove{{ section.id | replace: '-', '' }}_{{ block.id | replace: '-', '' }} {
          from {
            offset-distance: 0%;
          }
          to {
            offset-distance: 100%;
          }
        }

        @media(max-width: 1023px) {
          .hero-dot-{{ block.id }} {
            animation: dotMove{{ section.id | replace: '-', '' }}_{{ block.id | replace: '-', '' }} 
                       {{ section.settings.scrolling_velocity_mobile }}s 
                       linear infinite;
            animation-delay: calc({{ section.settings.scrolling_velocity_mobile }}s * {{ block.settings.dot_position }} / -100);
          }

          .hero-feature-tag-{{ block.id }} {
            opacity: 1;
          }
        }
      </style>
    {% when 'heading' %}  
      <style>       
        .hero-heading-{{ block.id }} {
          text-align: {{ content_align_mobile }};
          margin-top: {{ block.settings.heading_mt_mobile }}px;
        }
      
        .hero-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        @media(min-width: 1024px) {
          
          .hero-heading-{{ block.id }} {
            text-align: {{ content_align }};
            margin-top: {{ block.settings.heading_mt }}px;
          }
        
          .hero-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }         
      </style>
      
      {% if block.settings.heading_custom %}
        <style>
          {{  block.settings.heading_font | font_face: font_display: 'swap' }}

          .hero-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}    
      <style>        
        
        .hero-text-{{ block.id }} {
          text-align: {{ content_align_mobile }};
          margin-top: {{ block.settings.text_mt_mobile }}px;
        }
      
        .hero-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {
          
          .hero-text-{{ block.id }} {
            text-align: {{ content_align }};
            margin-top: {{ block.settings.text_mt }}px;
          }
        
          .hero-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }
          
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          {{  block.settings.text_font | font_face: font_display: 'swap' }}

          .hero-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>       
      {% endif %}  

    {% when 'button' %}
      <style>          
           .hero-button-{{ block.id }} {
             text-decoration: none;
           }
        
          .hero-button-inner-{{ block.id }} {
           display: flex;
           align-items: center;
           justify-content: center;
           max-width: fit-content;
           gap: 10px;
           width: 100%;
           margin: 0;
           margin-top: {{ block.settings.button_mt_mobile }}px !important;
           font-size: {{ block.settings.button_size_mobile }}px;
           color: {{ block.settings.button_color }};
           line-height: {{ block.settings.button_height }}%;
           text-align: center;
           text-transform: unset;
           text-decoration: none;
           padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
           border-radius: {{ block.settings.button_radius }}px;
           transition: all 0.25s ease 0s;
           background-color: transparent;
           border: 0px;
           cursor: pointer;
         }

         .hero-button-inner-{{ section.id }} {
           margin: 0px;
           display: flex;
           align-items: center;
           justify-content: center;
           gap: 10px;
         }

         .hero-button-inner-{{ block.id }}:hover {
           color: {{ block.settings.button_hover_color }};
           transition: all 0.25s ease 0s;
         }

         .hero-button-inner-{{ block.id }} svg {
           width: 14px !important;
           height: 14px !important;
           background: transparent;
           min-height: auto !important;
         }

         .hero-button-inner-{{ block.id }} svg path {
           fill: {{ block.settings.button_color }};
           transition: all 0.25s ease 0s;
         }

         .hero-button-inner-{{ block.id }}:hover svg path {
           fill: {{ block.settings.button_hover_color }};
           transition: all 0.25s ease 0s;
         }

         @media(min-width: 1024px) {       
           .hero-button-inner-{{ block.id }} {
             display: flex !important;
             margin-top: {{ block.settings.button_mt }}px !important;
             padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
             font-size: {{ block.settings.button_size }}px;
           }
         }
      </style>
        {% if block.settings.button_custom %}
         <style>
          {{  block.settings.button_font | font_face: font_display: 'swap' }}

           .hero-button-inner-{{ block.id }} {
             font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
             font-weight: {{ block.settings.button_font.weight }};
             font-style: {{ block.settings.button_font.style }};
           }
         </style>
       {% endif %} 
  
       {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
         <style>
           .hero-button-inner-{{ block.id }} {
             background-color: {{ block.settings.button_bg_color }};
           }
  
           .hero-button-inner-{{ block.id }}:hover {
             background-color: {{ block.settings.button_bg_hover_color }};
           }
         </style>
       {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
         <style>
           .hero-button-inner-{{ block.id }} {
             background-color: {{ block.settings.button_bg_color }};
             border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
           }
  
           .hero-button-inner-{{ block.id }}:hover {
             background-color: {{ block.settings.button_bg_hover_color }};
             border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
           }
         </style>
       {% elsif block.settings.button_style == "link" %}
         <style>
           .hero-button-inner-{{ block.id }} {
             padding: 0px !important;
           }
         </style>
       {% endif %}    
  {% endcase %}
{% endfor %}

{% capture list %}
  <div class="hero-scrolling-list-{{ section.id }}">
    {% for i in (1..3) %}
      {% for block in section.blocks %}
        {% case block.type %}
          {% when 'dot' %}
            {% if block.settings.dot_text != blank %}
              <span class="hero-feature-tag-{{ section.id }} hero-feature-tag-{{ block.id }}" data-id="{{ block.id }}">
                {{ block.settings.dot_text }}
              </span>
            {% endif %}
        {% endcase %}
      {% endfor %}
    {% endfor %}
  </div>
{% endcapture %}


<div class="section-{{ section.id }} hero-{{ section.id }}">
    <div class="section-{{ section.id }}-settings" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% else %} style="background-image: {{ background_gradient }};"{% endif %}>  

      <div class="hero-motion-wrapper-{{ section.id }}">
        
        <svg class="hero-motion-svg-{{ section.id }}" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="-5 -5 255 382">
          
          <path 
            id="hero-motion-path-{{ section.id }}" 
            d="M 122.5 0.669
                L 122.5 0.669
                C 190.747 0.669
                244.331 54.253
                244.331 122.5
                L 244.331 249.331
                C 244.331 317.578
                190.747 371.162
                122.5 371.162
                C 54.253 371.162
                0.669 317.578
                0.669 249.331
                L 0.669 122.5
                C 0.669 54.253
                54.253 0.669
                122.5 0.669" 
