<script>
  function positionHeroTags{{ section.id | replace: '-', '' }}() {
    const tags = document.querySelectorAll(`.hero-motion-wrapper-{{ section.id }} .hero-feature-tag-{{ section.id }}`);
    const parentWrapper = document.querySelector(`.hero-motion-wrapper-{{ section.id }}`);
    const svg = document.querySelector(`.hero-motion-svg-{{ section.id }}`);

    if (!parentWrapper || !svg || !tags.length) return; 

    if (window.innerWidth < 1024) return;

    const parentRect = parentWrapper.getBoundingClientRect();
    const svgRect = svg.getBoundingClientRect();
    const svgCenterX = svgRect.left + svgRect.width / 2;
    const svgCenterY = svgRect.top + svgRect.height / 2;
    const margin = 10;

    tags.forEach(tag => {
      const circleId = tag.dataset.id;
      if (!circleId) return;

      const circle = document.getElementById(circleId);
      if (!circle) return; 

      const circleRect = circle.getBoundingClientRect();
      const circleCenterX = circleRect.left + circleRect.width / 2;
      const circleCenterY = circleRect.top + circleRect.height / 2;

      const deltaX = circleCenterX - svgCenterX;
      const deltaY = circleCenterY - svgCenterY;

      let top, left;

      if (Math.abs(deltaX) > Math.abs(deltaY)) {
        top = (circleRect.top + circleRect.height / 2) - parentRect.top - (tag.offsetHeight / 2);

        if (deltaX < 0) {
          left = circleRect.left - parentRect.left - tag.offsetWidth - margin;
        } else {
          left = circleRect.right - parentRect.left + margin;
        }
      } else {
        left = (circleRect.left + circleRect.width / 2) - parentRect.left - (tag.offsetWidth / 2);

        if (deltaY < 0) {
          top = circleRect.top - parentRect.top - tag.offsetHeight - margin;
        } else {
          top = circleRect.bottom - parentRect.top + margin;
        }
      }

      tag.style.top = `${top}px`;
      tag.style.left = `${left}px`;
      
      tag.style.offsetPath = 'none'; 
    });
  }

  function initHero43() {
    {% if lazy %}
    let lazyLoadInstance = new LazyLoad();

    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
      window.lazyLoadInstance = event.detail.instance;
      },
    false
      );
    {% endif %}

    const section = document.querySelector('.section-{{ section.id }}');
    if (section) {
        const observer = new IntersectionObserver((entries, observer) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('animate-dots-{{ section.id }}');
                    
                    if (window.innerWidth >= 1024) {
                      setTimeout(positionHeroTags{{ section.id | replace: '-', '' }}, 1500); 
                    }

                    observer.unobserve(entry.target);
                }
            });
        }, {
            rootMargin: '0px 0px -10% 0px'
        });

        observer.observe(section);
    }
    
    if (section && section.classList.contains('animate-dots-{{ section.id }}')) {
        if (window.innerWidth >= 1024) {
          setTimeout(positionHeroTags{{ section.id | replace: '-', '' }}, 100);
        }
    }
    
    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {
        if (window.innerWidth >= 1024) {
          positionHeroTags{{ section.id | replace: '-', '' }}();
        }
      }, 250);
    }); 
  }

  document.addEventListener('DOMContentLoaded', initHero43);

  if (Shopify.designMode) {
      document.addEventListener('shopify:section:unload', initHero43);
      document.addEventListener('shopify:section:load', initHero43);
  }
</script>
