  <script>
    
  function updateResponsive() {
    const path = document.getElementById('curve2');
    if (path) {
      if (window.matchMedia('(max-width: 480px)').matches) {
        path.setAttribute('stroke-width', '75');
      } else {
        path.setAttribute('stroke-width', '30');
      }
    }

    const anim1 = document.getElementById('marquee1-anim');
    const anim2 = document.getElementById('marquee2-anim');
    if (anim1 && anim2) {
      if (window.matchMedia('(max-width: 480px)').matches) {
        anim1.setAttribute('dur', '15s');
        anim2.setAttribute('dur', '20s');
      } else {
        anim1.setAttribute('dur', '35s');
        anim2.setAttribute('dur', '50s');
      }
    }
  }

  window.addEventListener('load', updateResponsive);
  window.addEventListener('resize', updateResponsive);
  
    function initHero44() {      
      {% if lazy %}
        let lazyLoadInstance = new LazyLoad();

        window.addEventListener(
          "LazyLoad::Initialized",
          function (event) {
            window.lazyLoadInstance = event.detail.instance;
          },
          false
        );
      {% endif %}
    }

    document.addEventListener('DOMContentLoaded', initHero44);

    if (Shopify.designMode) {
      document.addEventListener('shopify:section:unload', initHero44);
      document.addEventListener('shopify:section:load', initHero44);
    }
  </script>
