{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_gap = section.settings.layout_gap
  assign layout_gap_mobile = section.settings.layout_gap_mobile

  assign image = section.settings.image
  assign image_width = section.settings.image_width
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_ratio = section.settings.image_ratio

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign buttons_mt = section.settings.buttons_mt
  assign buttons_mt_mobile = section.settings.buttons_mt_mobile
  assign buttons_gap = section.settings.buttons_gap
  assign buttons_gap_mobile = section.settings.buttons_gap_mobile

  assign modal_vertical_position = section.settings.modal_vertical_position
  assign modal_horizontal_position_mobile = section.settings.modal_horizontal_position_mobile
  assign modal_horizontal_position = section.settings.modal_horizontal_position
  assign modal_radius = section.settings.modal_radius
  assign modal_border_thickness = section.settings.modal_border_thickness
  assign modal_width = section.settings.modal_width
  assign modal_width_mobile = section.settings.modal_width_mobile
  assign modal_overlay = section.settings.modal_overlay
  assign modal_border_color = section.settings.modal_border_color
  assign modal_bg_color = section.settings.modal_bg_color
  assign modal_close_color = section.settings.modal_close_color

  assign video = section.settings.video
  assign video_url = section.settings.video_url
  assign source = section.settings.source
  assign video_autoplay = section.settings.video_autoplay
  assign video_loop = section.settings.video_loop
  assign use_controls = section.settings.use_controls
  assign video_ratio = section.settings.video_ratio
  assign video_ratio_mobile = section.settings.video_ratio_mobile

  assign play_icon_size = section.settings.play_icon_size
  assign play_icon_size_mobile = section.settings.play_icon_size_mobile
  assign play_border_thickness = section.settings.play_border_thickness
  assign play_border_color = section.settings.play_border_color
  assign play_border_hover_color = section.settings.play_border_hover_color
  assign play_padding = section.settings.play_padding
  assign play_padding_mobile = section.settings.play_padding_mobile
  assign play_radius =section.settings.play_radius
  assign play_bg_color = section.settings.play_bg_color
  assign play_color = section.settings.play_color
  assign play_bg_hover_color = section.settings.play_bg_hover_color
  assign play_hover_color = section.settings.play_hover_color
  
  assign testimonial_mt = section.settings.testimonial_mt
  assign testimonial_mt_mobile = section.settings.testimonial_mt_mobile
  assign testimonial_width = section.settings.testimonial_width
  assign testimonial_height = section.settings.testimonial_height
  assign testimonial_border_color = section.settings.testimonial_border_color
  assign testimonial_border_thickness = section.settings.testimonial_border_thickness
  assign testimonial_radius = section.settings.testimonial_radius
  assign testimonial_padding = section.settings.testimonial_padding
  assign testimonial_padding_mobile = section.settings.testimonial_padding_mobile
  assign testimonial_gap = section.settings.testimonial_gap
  assign testimonial_gap_mobile = section.settings.testimonial_gap_mobile
  assign testimonial_bg_color = section.settings.testimonial_bg_color

  assign testimanial_avatar_size = section.settings.testimanial_avatar_size
  assign testimanial_avatar_size_mobile = section.settings.testimanial_avatar_size_mobile
  assign testimanial_avatar_border_thickness = section.settings.testimanial_avatar_border_thickness
  assign testimanial_avatar_radius = section.settings.testimanial_avatar_radius
  assign testimonial_avatar_border_color = section.settings.testimonial_avatar_border_color

  assign testimonial_text_custom = section.settings.testimonial_text_custom
  assign testimonial_text_font = section.settings.testimonial_text_font
  assign testimonial_text_size = section.settings.testimonial_text_size
  assign testimonial_text_size_mobile = section.settings.testimonial_text_size_mobile
  assign testimonial_text_height = section.settings.testimonial_text_height
  assign testimonial_text_color = section.settings.testimonial_text_color

  assign testimonial_author_custom = section.settings.testimonial_author_custom
  assign testimonial_author_font = section.settings.testimonial_author_font
  assign testimonial_author_size = section.settings.testimonial_author_size
  assign testimonial_author_size_mobile = section.settings.testimonial_author_size_mobile
  assign testimonial_author_advanced = section.settings.testimonial_author_advanced
  assign testimonial_author_height = section.settings.testimonial_author_height
  assign testimonial_author_mt = section.settings.testimonial_author_mt
  assign testimonial_author_mt_mobile = section.settings.testimonial_author_mt_mobile
  assign testimonial_author_color = section.settings.testimonial_author_color
-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .hero-body-{{ section.id }} {
    position: relative;
    display: grid;
    align-items: center;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
    height: 100%;
  }

  .hero-content-{{ section.id }} {
    grid-area: content;
    display: flex;
    flex-direction: column;
    position: relative;
    justify-content: {{ content_vertical_align }};
    align-items: {{ content_horizontal_align_mobile }};   
    overflow: hidden;
    height: 100%;
  }

  .hero-image-{{ section.id }} {
    grid-area: image;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .hero-image-{{ section.id }} img,
  .hero-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit:cover;
  }

  .hero-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .hero-btns-{{ section.id }} {
    display: flex;
    flex-wrap: wrap;
    justify-content: {{ content_align_mobile }};
    margin-top: {{buttons_mt_mobile }}px;
    gap: {{ buttons_gap_mobile }}px;
  }

  .hero-stars-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 1px;
    max-width: fit-content;
  }

  .hero-stars-wrapper-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: {{ content_align_mobile }};
    gap: 10px;
  }

  .hero-modal-popup-{{ section.id }} {
    width: 100vw;
    height: 100%;
    top: 0;
    left: 0;
    position: fixed;
    display: flex;
    justify-content: {{ modal_horizontal_position_mobile }};
    opacity: 0;
    overflow-y: scroll;
    pointer-events: none;
    background: rgba(0, 0, 0, {{ modal_overlay | times: 0.01 }});
    padding: 10px;
  }

  .hero-modal-popup-{{ section.id }}.active {
    opacity: 1;
    z-index: 100;
    pointer-events: all;
    transition: all 250ms ease-in 0s;
  }

  .hero-modal-popup-inner-{{ section.id }} {
    position: relative;
    width: 100%;
    max-width: {{ modal_width_mobile }}%;
    border: {{ modal_border_thickness }}px solid {{ modal_border_color }};
    border-radius: {{ modal_radius }}px;
    display: flex;
    flex-direction: column;
    height: fit-content;
    overflow: hidden;
    margin: auto 0;
    background-color: {{ modal_bg_color }};
  }

  .hero-modal-popup-close-{{ section.id }} {
    position: absolute;;
    top: 10px;
    right: 8px;
    box-shadow: none;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
  }

  .hero-modal-popup-close-{{ section.id }} svg {
    display: block;
    width: 24px;
    height: 24px;
  }

  .hero-modal-popup-close-{{ section.id }} svg path {
    fill: {{ modal_close_color }};
  }
  
  .hero-video-{{ section.id }} {
    position: relative;
    overflow: hidden;
  }

  .hero-video-{{ section.id }} video,
  .hero-video-{{ section.id }} iframe {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border: 0px;
  }

  .hero-buttons-{{ section.id }} {
    display: flex;
    align-items: center;
    margin-top: {{ buttons_mt_mobile }}px;
    gap: {{ buttons_gap_mobile }}px;
    flex-wrap: wrap;
    justify-content: {{ content_horizontal_align_mobile }};
  }
  
  .hero-video-controls-{{ section.id }} {
    display: flex;
    gap: 8px;
    position: absolute;
    top: 16px;
    left: 16px;
  }
  
  .hero-video-play-{{ section.id }},
  .hero-video-sound-{{ section.id }} {
    margin: 0;
    cursor: pointer;
    z-index: 2;
    padding: {{ play_padding_mobile }}px;
    border: {{ play_border_thickness }}px solid {{ play_border_color }};
    border-radius: {{ play_radius }}px;
    background-color: {{ play_bg_color }};
    transition: all 0.25s ease 0s;
  }

  .hero-video-play-{{ section.id }} svg:first-child path,
  .hero-video-sound-{{ section.id }} svg:first-child path {
    fill: {{ play_color }};
  }

  .hero-video-play-{{ section.id }} svg:last-child path,
  .hero-video-sound-{{ section.id }} svg:last-child path {
    fill: {{ play_color }};
  }
  
  .hero-video-play-{{ section.id }} svg,
  .hero-video-sound-{{ section.id }} svg {
    display: block;
    width: {{ play_icon_size_mobile }}px;
    height: {{ play_icon_size_mobile }}px;
    object-fit: cover;
  }

  .hero-video-play-{{ section.id }} svg,
  .hero-video-sound-{{ section.id }} svg {
    background-color: transparent !important;
  }
  
  .hero-video-play-{{ section.id }} svg:nth-child(1),
  .hero-video-sound-{{ section.id }} svg:nth-child(1){
    display: none;
  }

  .hero-video-play-{{ section.id }}.active svg:nth-child(1),
  .hero-video-sound-{{ section.id }}.active svg:nth-child(1) {
    display: block;
  }

  .hero-video-play-{{ section.id }}.active svg:nth-child(2),
  .hero-video-sound-{{ section.id }}.active svg:nth-child(2) {
    display: none;
  }

  .hero-testimanial-slider-{{ section.id }} {
    position: relative;
    overflow: hidden;
    width: 100%;
    margin-top: {{testimonial_mt_mobile }}px;
    max-width: {{ testimonial_width }}px;
    min-height: {{ testimonial_height }}px;
    background: {{ testimonial_bg_color }};
    border: {{ testimonial_border_thickness }}px solid {{ testimonial_border_color }};
    border-radius: {{ testimonial_radius }}px;
  }

  .hero-testimanial-card-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
    transition: opacity 1.5s ease;
    display: flex;
    align-items: center;
    gap: {{ testimonial_gap_mobile }}px;
  }

  .hero-testimanial-card-{{ section.id }}.active {
    opacity: 1;
    z-index: 2;
  }

  .hero-testimanial-card-avatar-{{ section.id }} {
    flex: none;
    overflow: hidden;
    width: {{ testimanial_avatar_size_mobile }}px;
    height: {{ testimanial_avatar_size_mobile }}px;
    border: {{ testimanial_avatar_border_thickness }}px solid {{ testimonial_avatar_border_color }};
    border-radius: {{ testimanial_avatar_radius }}px;
  }

  .hero-testimanial-card-avatar-{{ section.id }} img,
  .hero-testimanial-card-avatar-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit:cover;
  }

  .hero-testimanial-card-avatar-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .hero-testimanial-card-quote-{{ section.id }} * {
    margin: 0;
    font-size: {{ testimonial_text_size_mobile }}px;
    color: {{ testimonial_text_color }};
    line-height: {{ testimonial_text_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  .hero-testimanial-card-author-{{ section.id }} {
    margin-top: {{ testimonial_author_mt_mobile }}px;
  }

  .hero-testimanial-card-author-{{ section.id }} * {
    margin: 0;
    font-size: {{ testimonial_author_size_mobile }}px;
    color: {{ testimonial_author_color }};
    line-height: {{ testimonial_author_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
    font-weight: 700;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hero-body-{{ section.id }} {
      gap: {{ layout_gap }}px;
    }

    .hero-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
    }

    .hero-btns-{{ section.id }} {
      justify-content: {{ content_align }};
      margin-top: {{buttons_mt }}px;
      gap: {{ buttons_gap }}px;
    }

    .hero-stars-wrapper-{{ section.id }} {
      justify-content: {{ content_align }};
    }

    .hero-modal-popup-{{ section.id }} {
      align-items: {{ modal_vertical_position }};
      justify-content: {{ modal_horizontal_position }};
    }

    .hero-modal-popup-inner-{{ section.id }} {
      display: grid;
      align-items: stretch;
      max-width: {{ modal_width }}%;
    }

    .hero-testimanial-slider-{{ section.id }} {
      margin-top: {{testimonial_mt }}px;
    }

    .hero-testimanial-card-{{ section.id }} {
      padding: {{ testimonial_padding }}px;
      gap: {{ testimonial_gap }}px;
    }

    .hero-testimanial-card-avatar-{{ section.id }} {
      width: {{ testimanial_avatar_size }}px;
      height: {{ testimanial_avatar_size }}px;
    }

    .hero-testimanial-card-quote-{{ section.id }} * {
      font-size: {{ testimonial_text_size }}px;
    }

    .hero-testimanial-card-author-{{ section.id }} {
      margin-top: {{ testimonial_author_mt }}px;
    }

    .hero-testimanial-card-author-{{ section.id }} * {
      font-size: {{ testimonial_author_size }}px;
    }
  }
  
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: clamp(100px, var(--page-content-width, var(--page-width)), 100%);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}
 
{% if testimonial_text_custom %}
  <style>
    {{ testimonial_text_font | font_face: font_display: 'swap' }}

    .hero-testimanial-card-quote-{{ section.id }} * {
      font-family: {{ testimonial_text_font.family }}, {{ testimonial_text_font.fallback_families }};
      font-weight: {{ testimonial_text_font.weight }};
      font-style: {{ testimonial_text_font.style }};
    }
  </style>
{% endif %}

{% if testimonial_author_custom %}
  <style>
    {{ testimonial_author_font | font_face: font_display: 'swap' }}

    .hero-testimanial-card-author-{{ section.id }} * {
      font-family: {{ testimonial_author_font.family }}, {{ testimonial_author_font.fallback_families }};
      font-weight: {{ testimonial_author_font.weight }};
      font-style: {{ testimonial_author_font.style }};
    }
  </style>
{% endif %}



{% if layout_mobile == 'content_image'%}
  <style>
    .hero-body-{{ section.id }} {
      grid-template-areas: 
        "content"
        "image";     
    }
  </style>
{% elsif layout_mobile == 'image_content' %}
  <style>
    .hero-body-{{ section.id }} {
      grid-template-areas: 
        "image"
        "content";     
    }
  </style>
{% endif %}

{% if layout == 'content_image' %}
  <style>
    @media(min-width: 1024px) {
      .hero-body-{{ section.id }} {
        grid-template-areas: "content image"; 
        grid-template-columns: auto {{ image_width }}%;
      }
    }
  </style>
{% elsif layout == 'image_content' %}
  <style>
    @media(min-width: 1024px) {
      .hero-body-{{ section.id }} {
        grid-template-areas: "image content";  
        grid-template-columns: {{ image_width }}% auto;
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .hero-image-{{ section.id }} {
      aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .hero-image-{{ section.id }}{
      aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .hero-image-{{ section.id }} {
      aspect-ratio: 12/12; 
    }
  </style>
{% else %}
  <style>
    .hero-image-{{ section.id }} {
      aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .hero-image-{{ section.id }} {
        aspect-ratio: 9.6/12; 
      }
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
   .hero-image-{{ section.id }}{
      aspect-ratio: 12/9; 
    }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .hero-image-{{ section.id }} {
        aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .hero-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if video_ratio_mobile == "portrait" %}
  <style>
    .hero-video-{{ section.id }} {
      aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif video_ratio_mobile == "landscape" %}
  <style>
    .hero-video-{{ section.id }} {
      aspect-ratio: 12/8;
    }
  </style>
{% elsif video_ratio_mobile == "square" %}
  <style>
    .hero-video-{{ section.id }} {
      aspect-ratio: 12/12; 
    }
  </style>
{% else %}
  {% if source == "uploaded" %}
    <style>
      .hero-video-{{ section.id }} {
        aspect-ratio: auto; 
      }
    </style>
  {% else %}
    <style>
      .hero-video-{{ section.id }} {
        aspect-ratio: 16/9; 
      }
    </style>
  {% endif %}
{% endif %}

{% if video_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .hero-video-{{ section.id }} {
        aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif video_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .hero-video-{{ section.id }}{
        aspect-ratio: 12/8; 
      }
    }
  </style>
{% elsif video_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .hero-video-{{ section.id }} {
        aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  {% if source == "uploaded" %}
    <style>
      @media(min-width: 1024px) {
        .hero-video-{{ section.id }} {
          aspect-ratio: auto; 
        }
      }
    </style>
  {% else %}
    <style>
      @media(min-width: 1024px) {
        .hero-video-{{ section.id }} {
          aspect-ratio: 16/9;
        }
      }
    </style>
  {% endif %}
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}  
      <style>

        .hero-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_horizontal_align_mobile }};
        }
      
        .hero-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }

        .hero-heading-{{ block.id }} * strong {
