      {% assign loop_url = '&loop=1&playlist=' | append: video_url_id %}
      {%- if source != 'uploaded' -%}
        {%  if video_url != blank %}
          {% if video_url.type == 'youtube' -%}
            <iframe
              {% if lazy %}data-{% endif %}src="https://www.youtube.com/embed/{{ video_url_id }}?enablejsapi=1&mute=1&autoplay={% if video_autoplay %}1{% else %}0{% endif %}&controls=1&loop={% if video_loop %}1{% else %}0{% endif %}&modestbranding=1&showinfo=0&rel=0{{ loop_url }}"                     
              data-src1="https://www.youtube.com/embed/{{ video_url_id }}?enablejsapi=1&mute=0&autoplay=0&controls=0&loop={% if video_loop %}1{% else %}0{% endif %}&modestbranding=1&showinfo=0{{ loop_url }}"   
              data-src2="https://www.youtube.com/embed/{{ video_url_id }}?enablejsapi=1&mute={% if video_autoplay %}1{% else %}0{% endif %}&autoplay=1&controls=0&loop={% if video_loop %}1{% else %}0{% endif %}&modestbranding=1&showinfo=0{{ loop_url }}"   
              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
              allowfullscreen
              class="lazy"
            ></iframe>
          {% else %}
            <iframe
              {% if lazy %}data-{% endif %}src="https://player.vimeo.com/video/{{ video_url_id }}?enablejsapi=1&mute=1&muted=1&autoplay={% if video_autoplay %}1{% else %}0{% endif %}&controls=1&loop={% if video_loop %}1{% else %}0{% endif %}&modestbranding=1&showinfo=0&rel=0{{ loop_url }}"
              data-src1="https://player.vimeo.com/video/{{ video_url_id }}?enablejsapi=1&mute=1&muted=1&autoplay=0&controls=0&loop={% if video_loop %}1{% else %}0{% endif %}&modestbranding=1&showinfo=0{{ loop_url }}"
              data-src2="https://player.vimeo.com/video/{{ video_url_id }}?enablejsapi=1&mute={% if video_autoplay %}1{% else %}0{% endif %}&muted={% if video_autoplay %}1{% else %}0{% endif %}&autoplay=1&controls=0&loop={% if video_loop %}1{% else %}0{% endif %}&modestbranding=1&showinfo=0{{ loop_url }}"
              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
              allowfullscreen
              class="lazy"
            ></iframe>              
          {% endif %}
        {% else %}
          {% assign placeholder_video = 'https://cdn.shopify.com/videos/c/o/v/fbb140fd46e3429eba459e1549dbbe38.mp4' %}
          <video {% if lazy %}data-{% endif %}src="{{ placeholder_video }}" muted {% if video_autoplay %}autoplay{% endif %} playsinline preload="none" {% if video_loop %}loop{% endif %} class="lazy"></video>
        {%  endif %}
      {% else %}
        {% assign placeholder_video = 'https://cdn.shopify.com/videos/c/o/v/fbb140fd46e3429eba459e1549dbbe38.mp4' %}
        <video {% if lazy %}data-{% endif %}src="{% if video != blank %}{{ video.sources[1].url }}{% else %}{{ placeholder_video }}{% endif %}" {% if video_autoplay %}muted{% endif %} {% if video_autoplay %}autoplay{% endif %} playsinline preload="none" {% if video_loop %}loop{% endif %} class="lazy"></video>
        {% if use_controls %}
          <div class="hero-video-controls-{{ section.id }}">
            <div class="hero-video-play-{{ section.id }} {% if video_autoplay %}active{% endif %}">
              <svg aria-hidden="true" class="pause-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                <path d="M6 4h4v16H6zm8 0h4v16h-4z"></path>
              </svg>    
              <svg aria-hidden="true" class="play-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
                <path d="M8 5v14l11-7z"></path>
              </svg>
            </div>
            <div class="hero-video-sound-{{ section.id }} {% unless video_autoplay %}active{% endunless %}"">
              <svg class="hero-unmute-{{ section.id }}" enable-background="new 0 0 512 512" height="512" viewBox="0 0 512 512" width="512" xmlns="http://www.w3.org/2000/svg">
                <g>
                  <path d="m334.2 184.7-7.3-14.2-28.4 14.5 7.3 14.2c9.6 18.8 14.2 37.3 14.2 56.7 0 18.8-4.8 37.9-14.3 56.8l-7.2 14.3 28.6 14.4 7.2-14.3c11.7-23.3 17.7-47.2 17.7-71.1 0-24.6-5.8-47.9-17.8-71.3z"></path>
                  <path d="m381.8 135.9-8.1-13.8-27.6 16.1 8.1 13.8c18.9 32.4 29.8 59.8 29.8 104 0 43.5-11 71.1-29.9 104l-8 13.9 27.7 15.9 8-13.9c21.1-36.7 34.1-69.5 34.1-120 .1-51.1-13-83.8-34.1-120z"></path>
                  <path d="m429.5 87.4-8.6-13.5-27 17.2 8.6 13.5c27.3 42.8 45.5 84.9 45.5 151.4 0 67.8-18.2 109.5-45.4 151.3l-8.7 13.4 26.8 17.5 8.7-13.4c35.9-55.1 50.6-104 50.6-168.7 0-74.4-20.2-121.2-50.5-168.7z"></path>
                  <path d="m32 176.1v159.8h93.7l130.3 104.1v-368l-130.3 104.1z"></path>
                </g>
              </svg>
              <svg class="hero-mute-{{ section.id }}" enable-background="new 0 0 512 512" height="512" viewBox="0 0 512 512" width="512" xmlns="http://www.w3.org/2000/svg">
                <g>
                  <path d="m224-8.9h32v529.8h-32z" transform="matrix(.707 -.707 .707 .707 -110.725 244.687)"></path>
                  <path d="m320 256c0 3.9-.2 7.8-.6 11.7l27.3 27.3c3.5-12.7 5.3-25.9 5.3-39.1 0-24.6-5.8-47.9-17.8-71.3l-7.3-14.3-28.4 14.7 7.3 14.3c9.5 18.8 14.2 37.3 14.2 56.7z"></path>
                  <path d="m384 256c0 25.9-3.9 46.2-11 65.3l24.5 24.5c11.7-26.2 18.5-53.4 18.5-89.8 0-51.2-13.1-83.9-34.2-120.1l-8.1-13.8-27.6 16.1 8.1 13.8c18.9 32.4 29.8 59.8 29.8 104z"></path>
                  <path d="m429.5 87.4-8.6-13.5-27 17.2 8.6 13.5c27.3 42.8 45.5 84.9 45.5 151.4 0 48.8-9.4 84-24.8 115.5l23.7 23.7c23.3-43.8 33.1-86.2 33.1-139.2 0-74.3-20.2-121.1-50.5-168.6z"></path>
                  <path d="m256 72-73.6 58.8 73.6 73.6z"></path>
                  <path d="m32 176.1v159.8h93.7l130.3 104.1v-100.4l-163.5-163.5z"></path>
                </g>
              </svg>
            </div>
          </div>
        {% endif %}
      {% endif %} 
      <button class="hero-modal-popup-close-{{ section.id }}">
        <svg width="24" height="24" viewBox="0 0 24 24" fill="none"><path fill-rule="evenodd" clip-rule="evenodd" d="M12.0002 10.586L4.70718 3.29297L3.29297 4.70718L10.586 12.0002L3.29297 19.2933L4.70718 20.7075L12.0002 13.4145L19.2933 20.7075L20.7075 19.2933L13.4145 12.0002L20.7075 4.70723L19.2933 3.29302L12.0002 10.586Z" fill="white"></path></svg>
      </button>
    </div>
  </div>
</div>

<script src="https://cdn.jsdelivr.net/npm/vanilla-lazyload@19.1.3/dist/lazyload.min.js"></script>

<script>
  {% if lazy %}
    let lazyLoadInstance = new LazyLoad();
    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
        window.lazyLoadInstance = event.detail.instance;
      },
      false
    );
  {% endif %}

  function initHero() {
    const toggle = document.querySelector('.hero-modal-button-{{ section.id }}');
    const modal = document.querySelector('.hero-modal-popup-{{ section.id }}');
    const close = document.querySelector('.hero-modal-popup-close-{{ section.id }}');

    if(toggle) {
      toggle.addEventListener('click', () => {
        if(modal) {
          modal.classList.add('active');
          document.body.style.overflow = 'hidden';
          document.body.style.transform = 'none';
        }
      })
    }

    if(close) {
       close.addEventListener('click', () => {
        document.body.style.overflow = '';
        document.body.style.transform = '';
        modal.classList.remove('active');
      });
    }

    document.addEventListener('click', (e) => {
        if (e.target == modal) {
          document.body.style.overflow = '';
          document.body.style.transform = '';
          modal.classList.remove('active');
        }
    });
  }

  function initVideo() {
    {% if use_controls %}
      const videoSlide = document.querySelectorAll('.hero-video-{{ section.id }}');
  
      videoSlide.forEach(item => {
        const video = item.querySelector('video');
        const iframe = item.querySelector('iframe');
        const videoPlay = item.querySelector('.hero-video-play-{{ section.id }}');
        const videoSound = item.querySelector('.hero-video-sound-{{ section.id }}')
  
        if(video || iframe) {    
          if(videoPlay) {
            videoPlay.addEventListener('click', function() {
              if (videoPlay.classList.contains('active')) {
                if (video) {
                  video.pause();
                }
                if (iframe) {
                  iframe.src = iframe.getAttribute('data-src1'); 
                } 
              } else {
                if (video) {      
                  video.play();
                }
                if (iframe) {
                  iframe.src = iframe.getAttribute('data-src2'); 
                }           
              }
              videoPlay.classList.toggle('active'); 
            })  
          }   
        }

        if(video) {
          if(videoSound) {
            videoSound.addEventListener('click', () => {
              videoSound.classList.toggle('active');
              if(videoSound.classList.contains('active')) {
                if(video) {
                  video.muted = false;
                  video.setAttribute("mute", "false"); 
                }
              } else {
                if(video) {
                  video.muted = true;
                  video.setAttribute("mute", "true"); 
                }
              }
            }) 
          }
        }    
        
        video.addEventListener('ended', function() {
          videoPlay.classList.remove('active');
        });
      })
    {% endif %}
  }

  function initSlider() {
    const cards = document.querySelectorAll('.hero-testimanial-card-{{ section.id }}');
    let index = 0;

    function showNextCard() {
      cards[index].classList.remove('active');
      index = (index + 1) % cards.length;
      cards[index].classList.add('active');
    }

    setInterval(showNextCard, 5000);
  }

  document.addEventListener('DOMContentLoaded', initHero);
  document.addEventListener('DOMContentLoaded', initVideo);
  document.addEventListener('DOMContentLoaded', initSlider);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initHero);
    document.addEventListener('shopify:section:load', initHero);
    document.addEventListener('shopify:section:unload', initVideo);
    document.addEventListener('shopify:section:load', initVideo);
    document.addEventListener('shopify:section:unload', initSlider);
    document.addEventListener('shopify:section:load', initSlider);
  }
</script>
