{%- liquid 
  assign initial_position = section.settings.initial_position
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius 
  assign background_style = section.settings.background_style

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_gap = section.settings.layout_gap

  assign before_image = section.settings.before_image
  assign before_image_mobile = section.settings.before_image_mobile
  assign after_image = section.settings.after_image
  assign after_image_mobile = section.settings.after_image_mobile

  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_ratio = section.settings.image_ratio
  assign image_border_color = section.settings.image_border_color
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_radius = section.settings.image_radius

  assign btn_size_mobile = section.settings.btn_size_mobile
  assign btn_size = section.settings.btn_size
  assign btn_border_thickness = section.settings.btn_border_thickness
  assign btn_border_color = section.settings.btn_border_color
  assign btn_arrow_color = section.settings.btn_arrow_color
  assign line_color = section.settings.line_color
  assign btn_arrow_size_mobile = section.settings.btn_arrow_size_mobile
  assign btn_arrow_size = section.settings.btn_arrow_size

  assign image_text_before = section.settings.image_text_before
  assign image_text_after = section.settings.image_text_after
  assign image_text_custom = section.settings.image_text_custom
  assign image_text_font = section.settings.image_text_font
  assign image_text_size = section.settings.image_text_size
  assign image_text_size_mobile = section.settings.image_text_size_mobile
  assign image_text_height = section.settings.image_text_height
  assign image_text_color = section.settings.image_text_color  

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign features_mt = section.settings.features_mt
  assign features_mt_mobile = section.settings.features_mt_mobile

  assign feature_gap = section.settings.feature_gap
  assign feature_gap_mobile = section.settings.feature_gap_mobile
  assign feature_mt = section.settings.feature_mt
  assign feature_mt_mobile = section.settings.feature_mt_mobile

  assign feature_icon_size = section.settings.feature_icon_size
  assign feature_icon_color = section.settings.feature_icon_color
  assign feature_icon_size_mobile = section.settings.feature_icon_size_mobile

  assign feature_text_custom = section.settings.feature_text_custom
  assign feature_text_font = section.settings.feature_text_font
  assign feature_text_size = section.settings.feature_text_size
  assign feature_text_size_mobile = section.settings.feature_text_size_mobile
  assign feature_text_color = section.settings.feature_text_color
  assign feature_text_height = section.settings.feature_text_height

  assign loading_attribute = 'lazy'
  unless lazy
    assign loading_attribute = 'eager'
  endunless
-%}

{%- style -%}
  {{ image_text_font | font_face: font_display: 'swap' }}
  {{ feature_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  } 

  .before-after-body-{{ section.id }} {
    display: flex;     
    flex-direction: column;
    align-items: center;
    gap: {{ layout_gap_mobile }}px;
  }   

  .before-after-image-wrapper-{{ section.id }} {
    flex: 1 1 0;
    display: flex;
    align-items: center;
    gap: 20px;
  }
  
  .before-after-button-{{ section.id }} {
    width: {{ btn_size_mobile }}px;
    height: {{ btn_size_mobile }}px;
    border-radius: 50%;
    border: {{ btn_border_thickness }}px solid {{ btn_border_color }};
    backdrop-filter: blur(7px);
    -webkit-backdrop-filter: blur(7px);
    box-shadow: rgba(0, 0, 0, 0.35) 0px 0px 7px;
    display: flex !important;
    align-items: center;
    justify-content: center;
    z-index: 3;
    position: relative;
  }

  .before-after-image-wrapper-{{ section.id }} {
    width: 100%;
  }

  .before-after-button-{{ section.id }}::after {
    content: '';
    padding: 3px;
    display: inline-block;
    border: solid {{ btn_arrow_color }};
    border-width: 0 2px 2px 0;
    transform: rotate(-45deg) scale({{ btn_arrow_size_mobile }}%) translateY(-50%);
  }

  .before-after-button-{{ section.id }}::before {
    content: '';
    padding: 3px;
    display: inline-block;
    border: solid {{ btn_arrow_color }};
    border-width: 0 2px 2px 0;
    transform: rotate(135deg) scale({{ btn_arrow_size_mobile }}%) translateX(-50%);
  }

  .before-after-button-{{ section.id }}::before {
    content: '';
    height: calc(50% - {{ btn_size_mobile | times: 0.50 | round: 0 }}px);
    top: 50%;
    left: 15%;
    width: 3px;
    color: {{ line_color }};
    z-index: -1;
    position: absolute;
  }
  
  .before-after-button-{{ section.id }}::after {
    content: '';
    height: calc(50% - {{ btn_size_mobile | times: 0.50 | round: 0 }}px);
    top: 50%;
    right: 15%;
    width: 3px;
    color: {{ line_color }};
    z-index: -1;
    position: absolute;
  }

  .before-after-image-relative-{{ section.id }} {
    position: relative;
    height: 100%;
  }

  .before-after-image-relative-{{ section.id }} img,
  .before-after-image-relative-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .before-after-image-relative-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .before-after-image-inner-{{ section.id }} {    
    position: relative;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};  
    border-radius: {{ image_radius }}px;
    user-select: none;
    overflow: hidden;
    flex: 1 1 0;
  }

  .before-after-image-cursor-{{ section.id }} {
    height: 100%;
    width: max-content;
    touch-action: none;
    --transform-logical-flip: 1;
    transform: translate(calc(var(--transform-logical-flip) * -50% + var(--clip-path-offset, 0px)),-50%);
    cursor: grab;
    will-change: transform;
    place-items: center;
    display: grid;
    position: relative;
    top: 50%;
    left: var(--before-after-initial-drag-position, 0px);
  }
  
  .before-after-image-cursor-{{ section.id }}::before {
    content: '';
    height: calc(50% - {{ btn_size_mobile | times: 0.5 | round: 0 }}px);
    top: 0;
    width: {{ btn_border_thickness }}px;
    background: {{ line_color }};
    z-index: -1;
    position: absolute;
  }
  
  .before-after-image-cursor-{{ section.id }}::after {
    content: '';
    height: calc(50% - {{ btn_size_mobile | times: 0.5 | round: 0 }}px);
    bottom: 0;
    width: {{ btn_border_thickness }}px;
    background: {{ line_color }};
    z-index: -1;
    position: absolute;
  }

  .before-after-before-text-{{ section.id }},
  .before-after-after-text-{{ section.id }} {
    position: absolute;
    bottom: 10px;
    margin: 0;    
    text-align: center;
    text-transform: unset;
    word-wrap: break-word;
    font-size: {{ image_text_size_mobile }}px;
    line-height: {{ image_text_height }}%;
    color: {{ image_text_color }};  
  }

  .before-after-before-text-{{ section.id }} {
    left: 10px;
  }

  .before-after-after-text-{{ section.id }} {
    right: 10px;
  }

  .before-after-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    position: relative;
    align-items: {{ content_horizontal_align_mobile }};
    width: 40%;
  }

  .before-after-features-{{ section.id }} {
    margin-top: {{ features_mt_mobile }}px;
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
  }
  
   .before-after-feature-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: {{ feature_gap_mobile }}px;
    text-decoration: none;
    position: relative;
    z-index: 1;
  }

  .before-after-feature-{{ section.id }}:not(:first-child) {
    margin-top: {{ feature_mt_mobile }}px;
  }

  .before-after-feature-text-{{ section.id }} {
    margin: 0;
    font-size: {{ feature_text_size_mobile }}px;
    color: {{ feature_text_color }};
    line-height: {{ feature_text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }
  
  .before-after-feature-icon-{{ section.id }} {
    flex-shrink: 0;
    height: {{ feature_icon_size_mobile }}px;
    width: {{ feature_icon_size_mobile }}px;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .before-after-feature-icon-{{ section.id }} img,
  .before-after-feature-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .before-after-feature-icon-{{ section.id }} svg path {
    fill: {{ feature_icon_color }};
  }

  .before-after-feature-icon-{{ section.id }} svg circle {
    fill: {{ feature_icon_color }};
  }
  
  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .before-after-content-{{ section.id }} {    
      align-items: {{ content_horizontal_align }};   
    }

    .before-after-body-{{ section.id }} {
      flex-direction: row;
      gap: {{ layout_gap }}px;
    } 

    .before-after-button-{{ section.id }} {
      width: {{ btn_size }}px;
      height: {{ btn_size }}px;
    }

    .before-after-image-relative-{{ section.id }} .before-after-image-first {
      display: block;
    }
    
    .before-after-image-relative-{{ section.id }} .before-after-image-mobile {
      display: none;
    }

    .before-after-image-relative-{{ section.id }} .before-after-image-second {
      display: block;
    }

    .before-after-image-relative-{{ section.id }} .before-after-image-mobile-second {
      display: none;
    }

    .before-after-button-{{ section.id }}::after {
      transform: rotate(-45deg) scale({{ btn_arrow_size }}%) translateY(-50%);
      height: calc(50% - {{ btn_size | times: 0.50 | round: 0 }}px);
    }

    .before-after-button-{{ section.id }}::before {
      transform: rotate(135deg) scale({{ btn_arrow_size }}%) translateX(-50%);
      height: calc(50% - {{ btn_size | times: 0.50 | round: 0 }}px);
    }

    .before-after-image-cursor-{{ section.id }}::before {
      height: calc(50% - {{ btn_size | times: 0.5 | round: 0 }}px);
    }

    .before-after-image-cursor-{{ section.id }}::after {
      height: calc(50% - {{ btn_size | times: 0.5 | round: 0 }}px);
    }

    .before-after-before-text-{{ section.id }},
    .before-after-after-text-{{ section.id }} {
      font-size: {{ image_text_size }}px; 
    }
    
    .before-after-features-{{ section.id }} {
      margin-top: {{ features_mt }}px;
      align-items: {{ content_horizontal_align }};
    }

     .before-after-feature-{{ section.id }}:not(:first-child) {
      margin-top: {{ feature_mt }}px;
     }

    .before-after-feature-{{ section.id }} {
      gap: {{ feature_gap }}px;     
    }
    
    .before-after-feature-text-{{ section.id }} {
      font-size: {{ feature_text_size }}px;
    }
    
    .before-after-feature-icon-{{ section.id }} {
      height: {{ feature_icon_size }}px;
      width: {{ feature_icon_size }}px;
    }
  }
  
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: clamp(100px, var(--page-content-width, var(--page-width)), 100%);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}


{% if feature_text_custom %}
  <style>
    .before-after-feature-text-{{ section.id }} {
      font-family: {{ feature_text_font.family }}, {{ feature_text_font.fallback_families }};
      font-weight: {{ feature_text_font.weight }};
      font-style: {{ feature_text_font.style }};
    }
  </style>
{% endif %}

{% if image_text_custom %}
  <style>
    .before-after-before-text-{{ section.id }},
    .before-after-after-text-{{ section.id }} {
      font-family: {{ image_text_font.family }}, {{ image_text_font.fallback_families }};
      font-weight: {{ image_text_font.weight }};
      font-style: {{ image_text_font.style }};
    }
  </style>
{% endif %}


{% if layout_mobile == "images_text" %}
  <style>    
    .before-after-content-{{ section.id }} {
      order: 2;
    }
    
    .before-after-image-wrapper-{{ section.id }} {
      order: 1;
    }
  </style>
{% else %}
  <style>
    .before-after-content-{{ section.id }} {
      order: 1;
    }
    
    .before-after-image-wrapper-{{ section.id }} {
      order: 2;
    }
  </style>
{% endif %}

{% if layout == "images_text" %}
  <style>
    @media(min-width: 1024px) {
      .before-after-content-{{ section.id }} {
        order: 2;
      }
      
      .before-after-image-wrapper-{{ section.id }} {
        order: 1;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .before-after-content-{{ section.id }} {
        order: 1;
      }
      
      .before-after-image-wrapper-{{ section.id }} {
        order: 2;
      }
    }
  </style>
{% endif %}


{% if image_ratio_mobile == "portrait" %}
  <style>
    .before-after-image-relative-{{ section.id }} {
     aspect-ratio: 9.6/13; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .before-after-image-relative-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .before-after-image-relative-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else%}
  <style>
   .before-after-image-relative-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .before-after-image-relative-{{ section.id }} {
       aspect-ratio: 9.6/13; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .before-after-image-relative-{{ section.id }} {
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .before-after-image-relative-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .before-after-image-relative-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'feature' %}    
      {% if content_align_mobile == 'right' %}
        <style>
          .before-after-feature-{{ section.id }} {
            flex-direction: row-reverse;
          }
        </style>
      {% else %}
         <style>
          .before-after-feature-{{ section.id }} {
            flex-direction: row;
          }
        </style>
      {% endif %}
  
      {% if content_align == 'right' %}
        <style>
          @media(min-width: 1024px) {
            .before-after-feature-{{ section.id }} {
              flex-direction: row-reverse;
            }
          }       
        </style>
      {% else %}
         <style>
           @media(min-width: 1024px) {
            .before-after-feature-{{ section.id }} {
              flex-direction: row;
            }
           }     
        </style>
      {% endif %}

  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} before-after-{{ section.id }}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% elsif background_style == 'gradient' %} style="background-image: {{ background_gradient }};"{% endif %}>
    <div class="section-{{ section.id }}-settings">    
      <div class="before-after-body-{{ section.id }}">
        <div class="before-after-image-wrapper-{{ section.id }}">
          <div class="before-after-image-inner-{{ section.id }}" style="--before-after-initial-drag-position: {{ initial_position }}%; --clip-path-offset: 0px;">
            <div class="before-after-image-relative-{{ section.id }}" style=" position: relative;">
              {% if before_image_mobile != blank %}
                 {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                  {{ before_image_mobile | image_url: width: 1500 | image_tag:
                    loading: loading_attribute,
                    width: before_image_mobile.width,
                    height: before_image_mobile.height,
                    sizes: sizes,
                    widths: '750, 1070, 1500, 1780, 2000',
                    class: 'before-after-image-mobile'
                  }}                    
              {% endif %}
              {% if before_image != blank %}
                {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                {% assign before_desktop_class = '' %}
                {% if before_image_mobile != blank %}
                  {% assign before_desktop_class = 'before-after-image-first' %}
                {% endif %}
                {{ before_image | image_url: width: 1500 | image_tag:
                    loading: loading_attribute,
                    width: before_image.width,
                    height: before_image.height,
                    sizes: sizes,
                    widths: '750, 1070, 1500, 1780, 2000',
                    class: before_desktop_class
                  }}  
              {% else %}
                {{  'image' | placeholder_svg_tag }}
              {% endif %}
              {% if image_text_before != blank %}
                <p class="before-after-before-text-{{ section.id }}">{{ image_text_before }}</p>
              {% endif %}
             
            </div>
            <div class="before-after-image-relative-{{ section.id }}" style="clip-path: inset(0 0 0 calc(var(--before-after-initial-drag-position, 50%) + var(--clip-path-offset, 0px))); position: absolute; top: 0; bottom: 0; left: 0; right: 0;">
              {% if after_image_mobile != blank %}
                {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                {{ after_image_mobile | image_url: width: 1500 | image_tag:
                    loading: loading_attribute,
                    width: after_image_mobile.width,
                    height: after_image_mobile.height,
                    sizes: sizes,
                    widths: '750, 1070, 1500, 1780, 2000',
                    class: 'before-after-image-mobile-second'
                  }}  
              {% endif %}
              {% if after_image != blank %}
                {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                {% assign desktop_class = '' %}
                {% if after_image_mobile != blank %}
                  {% assign desktop_class = 'before-after-image-second' %}
                {% endif %}
                {{ after_image | image_url: width: 1500 | image_tag:
                    loading: loading_attribute,
                    width: after_image.width,
                    height: after_image.height,
                    sizes: sizes,
                    widths: '750, 1070, 1500, 1780, 2000',
                    class: desktop_class
                  }}                 
              {% else %}
                {{ 'image' | placeholder_svg_tag }}
              {% endif %}
              {% if image_text_after != blank %}
                <p class="before-after-after-text-{{ section.id }}">{{ image_text_after }}</p>
              {% endif %}           
            </div>
            {% comment %} Cursor: {% endcomment %}
            <div style="position: absolute; top: 0; bottom: 0; left: 0; right: 0;">
              <split-cursor class="before-after-image-cursor-{{ section.id }}">
                <div class="before-after-button-{{ section.id }}"></div>
              </split-cursor>
            </div>         
          </div>
          <div class="before-after-image-inner-{{ section.id }}" style="--before-after-initial-drag-position: {{ initial_position }}%; --clip-path-offset: 0px;">
            <div class="before-after-image-relative-{{ section.id }}" style=" position: relative;">
              {% if second_before_image_mobile != blank %}
