-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  wave_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    overflow-x: clip
  }

  .section-{{ section.id }}-settings {
    height: 100%;
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .hero-{{ section.id }} {
    border: {{ hero_border_thickness }}px solid {{ hero_border_color }};
    border-radius: {{ hero_radius }}px;
  }

  .hero-media-desktop-{{ section.id }} {
    display: none !important;
  }

  .hero-body-{{ section.id }} {
    border-radius: {{ hero_radius }}px;
  }

  .hero-body-{{ section.id }},
  .hero-content-wrapper-{{ section.id }} {
    position: relative;
    display: flex;
    align-items: {{ content_vertical_position_mobile }};
    justify-content: {{ content_horizontal_position_mobile }};
  }

  .hero-bg-{{ section.id }} {
    position: absolute;
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
    border-radius: {{ hero_radius }}px;
    overflow: hidden;
  }

  .hero-bg-{{ section.id }} > div {
    width: 100%;
    height: 100%;
  }

  .hero-bg-{{ section.id }} img,
  .hero-bg-{{ section.id }} svg,
  .hero-bg-{{ section.id }} video,
  .hero-bg-{{ section.id }} iframe,
  .hero-media-{{ section.id }} img,
  .hero-media-{{ section.id }} svg,
  .hero-media-{{ section.id }} video,
  .hero-media-{{ section.id }} iframe{
    display: block;
    object-fit: cover;
    width: 100%;
    height: 100%;
    border: 0px;
    box-shadow: none;
  }

  .hero-bg-{{ section.id }}:after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-color: {{ overlay_color | hex_to_rgba: overlay }};
    z-index: 1;
  }

  .hero-content-{{ section.id }} {
    width: 100%;
    max-width: {{ content_maxwidth_mobile }}%;
    position: relative;
    z-index: 2;
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    border-radius: {{ content_radius }}px;
    background-color: {{ content_bg | hex_to_rgba: content_bg_opacity }};
    border: {{ content_border_thickness }}px solid {{ content_border_color }};
  }

  .hero-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .hero-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .hero-button-{{ section.id }} {
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
    position: relative;
    z-index: 2;
  }

  .hero-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .hero-media-{{ section.id }} {
    border-radius: {{ media_radius }}px;
    border: {{ media_border_thickness }}px solid {{ media_border_color }};
    overflow: hidden;
  }

  .hero-wave-{{ section.id }} {
    width: 200%;
    position: absolute;
    bottom: {{ wave_position_mobile }}%;
    left: 50%;
    transform: translateX(-50%);
    z-index: 1;
  }

  #hero-wave-text-{{ section.id }} {
    font-size: 20px;
    font-weight: 700;
    fill: {{ wave_text_color }};
  }

  @media(min-width: 768px) {
    .hero-wave-{{ section.id }} {
      bottom: {{ wave_position_tablet }}%;
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hero-media-desktop-{{ section.id }} {
      display: block !important;
    }

    .hero-media-mobile-{{ section.id }} {
      display: none !important;
    }

    .hero-body-{{ section.id }},
    .hero-content-wrapper-{{ section.id }} {
      align-items: {{ content_vertical_position }};
      justify-content: {{ content_horizontal_position }};
    }

    .hero-content-{{ section.id }} {
      max-width: {{ content_maxwidth }}%;
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .hero-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .hero-heading-{{ section.id }} * {
      font-size: {{ heading_size | times: 0.75 }}px;
    }

    .hero-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .hero-wave-{{ section.id }} {
      width: 100%;
      bottom: {{ wave_position_desktop }}%;
    }
  }

  @media(min-width: 1600px) {
    .hero-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .hero-wave-{{ section.id }} {
      bottom: {{ wave_position }}%;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .hero-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .hero-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if hero_height == "adapt_first" %}
  <style>
    .hero-{{ section.id }} {
      height: 100vh;
    }
  </style>
{% elsif hero_height == "auto_height" %}
  <style>
    /* .hero-{{ section.id }} {
      height: {{ hero_fixed_height_mobile }}px;
    } */

     .hero-{{ section.id }} {
        height: auto;
      }

    .hero-content-wrapper-{{ section.id }} {
      height: 100%;
    }

    /* @media(min-width: 1024px ) {
      .hero-{{ section.id }} {
        height: auto;
      }
    } */
  </style>
{% else %}
  <style>
    .hero-{{ section.id }} {
      height: {{ hero_fixed_height_mobile }}px;
    }

    @media(min-width: 768px) {
      .hero-{{ section.id }} {
        height: {{ hero_fixed_height }}px;
      }
    }
  </style>
{% endif %}

{% if heading_shadow %}
  <style>
    .hero-heading-{{ section.id }} {
      text-shadow: 0px 2px 2px rgba(0, 0, 0, 0.4);
    }
  </style>
{% endif %}

{% if wave_custom %}
  <style>
    #hero-wave-text-{{ section.id }} {
      font-family: {{ wave_font.family }}, {{ wave_font.fallback_families }};
      font-weight: {{ wave_font.weight }};
      font-style: {{ wave_font.style }};
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .hero-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .hero-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if wave_show_mobile %}
  <style>
    .hero-wave-{{ section.id }} {
      display: block;
    }
  </style>
{% else %}
  <style>
    .hero-wave-{{ section.id }} {
      display: none;
    }
  </style>
{% endif %}

{% if wave_show_desktop %}
  <style>
    @media(min-width: 1024px) {
     .hero-wave-{{ section.id }} {
        display: block;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .hero-wave-{{ section.id }} {
        display: none;
      } 
    }
  </style>
{% endif %}

{% if media_row %}

  <style>
    .hero-body-{{ section.id }} {
      display: grid;
      align-items: center;
      gap: {{ media_margin_mobile }}px;
      grid-template-columns: repeat(1, 1fr);
    }

    .hero-media-{{ section.id }} {
      order: {{ media_position_mobile }};
    }

    .hero-content-wrapper-{{ section.id }} {
      order: {{ content_position_mobile }};
    }

    @media(min-width: 1024px) {
      .hero-body-{{ section.id }} {
        align-items: center;
        grid-template-columns: repeat(2, 1fr);
        gap: {{ media_margin }}px;
      }

      .hero-media-{{ section.id }} {
        order: {{ media_position }};
      }

      .hero-content-wrapper-{{ section.id }} {
        order: {{ content_position }};
      }
    }
  </style>

{% endif %}

{% if media_ratio_mobile == "portrait" %}
  <style>
    .hero-media-{{ section.id }} {
     aspect-ratio: 9.6/14; 
    }
  </style>
{% elsif media_ratio_mobile == "landscape" %}
  <style>
    .hero-media-{{ section.id }}{
     aspect-ratio: 12/8; 
    }
  </style>
{% elsif media_ratio_mobile == "square" %}
  <style>
    .hero-media-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else%}
  <style>
    .hero-media-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if media_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .hero-media-{{ section.id }} {
       aspect-ratio: 9.6/14; 
      } 
    }
  </style>
{% elsif media_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .hero-media-{{ section.id }}{
       aspect-ratio: 12/8; 
      }
    }
  </style>
{% elsif media_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .hero-media-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else%}
  <style>
    @media(min-width: 1024px) {
      .hero-media-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

{% if use_content_height_mobile %}
  <style>
    .hero-content-wrapper-{{ section.id }} {
      min-height: {{ content_height_mobile }}px;
    }
    @media(min-width: 1024px) {
      .hero-content-wrapper-{{ section.id }} {
        min-height: auto;
      }
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} hero-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings hero-body-{{ section.id }}">
      {% if media_row == false and media_type == "image" or media_type == "video" %}
        <div class="hero-bg-{{ section.id }}">
          {% if media_type == "image" %}

              {% if image == blank and image_mobile == blank %}
                {{ 'lifestyle-2' | placeholder_svg_tag }}
              {% else %}

                {% if image_mobile != blank %}
                  <img class="hero-media-mobile-{{ section.id }}" src="{{ image_mobile | image_url }}" alt="{{ image_mobile.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                {% endif %}
                {% if image != blank %}
                  <img {% if image_mobile != blank %}class="hero-media-desktop-{{ section.id }}"{% endif %} src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                {% endif %}

              {% endif %}
          {% elsif media_type == "video" %}

            {% assign autoplay = '&mute=1&autoplay=1' %}

            {% if video_mobile != blank or video_url_mobile != blank %}

              {% assign video_id_mobile =  video_mobile.id | default: video_url_mobile.id %}
              {% assign loop = '&loop=1&playlist=' | append: video_id_mobile %}

              {% if video_mobile == null and video_url_mobile != null %}
                {%- if video_url_mobile.type == 'youtube' -%}
                  <iframe
                    data-src="https://www.youtube.com/embed/{{ video_id_mobile }}?enablejsapi=1{{ autoplay }}{{ loop }}"
                    class="lazy hero-media-mobile-{{ section.id }}"
                    allow="autoplay; encrypted-media"
                    allowfullscreen
                    title="Fixed Video"
                  ></iframe>
                {%- else -%}
                  <iframe
                    data-src="https://player.vimeo.com/video/{{ video_id_mobile }}{{ autoplay }}{{ loop }}"
                    class="lazy hero-media-mobile-{{ section.id }}"
                    allow="autoplay; encrypted-media"
                    allowfullscreen
                    title="Fixed Video"
                  ></iframe>
                {% endif %}
              {% else %}
                <div class="hero-media-mobile-{{ section.id }}">
                  <video data-src="{{ video_mobile.sources[1].url }}" loop autoplay muted playsinline preload="none" class="lazy"></video>
                </div>

              {% endif %}

            {% endif %}

            {% if video != blank or video_url != blank %}

              {% assign video_id =  video.id | default: video_url.id %}
              {% assign loop = '&loop=1&playlist=' | append: video_id %}

              {% if video == null and video_url != null %}
                {%- if video_url.type == 'youtube' -%}
                  <iframe
                    data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1{{ autoplay }}{{ loop }}"
                   class="lazy {% if video_mobile != blank or video_url_mobile != blank %}hero-media-desktop-{{ section.id }}{% endif %}"
                    allow="autoplay; encrypted-media"
                    allowfullscreen
                    title="Fixed Video"
                  ></iframe>
                {%- else -%}
                  <iframe
                    data-src="https://player.vimeo.com/video/{{ video_id }}{{ autoplay }}{{ loop }}"
                    class="lazy {% if video_mobile != blank or video_url_mobile != blank %}hero-media-desktop-{{ section.id }}{% endif %}"
                    allow="autoplay; encrypted-media"
                    allowfullscreen
                    title="Fixed Video"
                  ></iframe>
                {% endif %}
              {% else %}

              <div {% if video_mobile != blank or video_url_mobile != blank %}class="hero-media-desktop-{{ section.id }}"{% endif %}>

                <video data-src="{{ video.sources[1].url }}" loop autoplay muted playsinline preload="none" class="lazy"></video>
              </div>

              {% endif %}

            {% endif %}

          {% endif %}
        </div>
      {% endif %}
      {% if media_row %} <div class="hero-content-wrapper-{{ section.id }}">{% endif %}
        <div class="hero-content-{{ section.id }}">
          {% if heading != blank %}
            <div class="hero-heading-{{ section.id }}">{{ heading }}</div>
          {% endif %}
          {% if button != blank %}
            <a href="{{ button_url }}" class="hero-button-{{ section.id }}">{{ button }}</a>
          {% endif %}
        </div>
      {% if media_row %}</div>{% endif %}
      {% if media_row %}
        <div class="hero-media-{{ section.id }}">
          {% if media_type == "image" %}

              {% if image == blank and image_mobile == blank %}
                {{ 'lifestyle-2' | placeholder_svg_tag }}
              {% else %}

                {% if image_mobile != blank %}
                  <img class="hero-media-mobile-{{ section.id }}" src="{{ image_mobile | image_url }}" alt="{{ image_mobile.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                {% endif %}
                {% if image != blank %}
                  <img {% if image_mobile != blank %}class="hero-media-desktop-{{ section.id }}"{% endif %} src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}lazy="loading"{% endif %}>
                {% endif %}

              {% endif %}
          {% elsif media_type == "video" %}

            {% assign autoplay = '&mute=1&autoplay=1' %}

            {% if video_mobile != blank or video_url_mobile != blank %}

              {% assign video_id_mobile =  video_mobile.id | default: video_url_mobile.id %}
              {% assign loop = '&loop=1&playlist=' | append: video_id_mobile %}

              {% if video_mobile == null and video_url_mobile != null %}
                {%- if video_url_mobile.type == 'youtube' -%}
                  <iframe
                    data-src="https://www.youtube.com/embed/{{ video_id_mobile }}?enablejsapi=1{{ autoplay }}{{ loop }}"
                    class="lazy hero-media-mobile-{{ section.id }}"
                    allow="autoplay; encrypted-media"
                    allowfullscreen
                    title="Fixed Video"
                  ></iframe>
                {%- else -%}
                  <iframe
                    data-src="https://player.vimeo.com/video/{{ video_id_mobile }}{{ autoplay }}{{ loop }}"
                    class="lazy hero-media-mobile-{{ section.id }}"
                    allow="autoplay; encrypted-media"
                    allowfullscreen
                    title="Fixed Video"
