  assign rate_text = section.settings.rate_text
  assign rate_text_size = section.settings.rate_text_size
  assign rate_text_size_mobile = section.settings.rate_text_size_mobile
  assign rate_text_color = section.settings.rate_text_color
  assign rate_text_custom = section.settings.rate_text_custom
  assign rate_text_font = section.settings.rate_text_font
  assign rate_text_height = section.settings.rate_text_height
  assign rate_text_align = section.settings.rate_text_align
  assign rate_text_align_mobile = section.settings.rate_text_align_mobile

  assign badge_size = section.settings.badge_size
  assign badge_size_mobile = section.settings.badge_size_mobile
  assign badge_custom = section.settings.badge_custom
  assign badge_font = section.settings.badge_font
  assign badge_height = section.settings.badge_height
  assign badge_padding_vertical = section.settings.badge_padding_vertical
  assign badge_padding_horizontal = section.settings.badge_padding_horizontal
  assign badge_radius = section.settings.badge_radius
  assign badge_border_thickness = section.settings.badge_border_thickness
  assign badge_align = section.settings.badge_align
  assign badge_align_mobile = section.settings.badge_align_mobile
  assign badge_mt_mobile = section.settings.badge_mt_mobile

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  feature_text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  rate_text_font | font_face: font_display: 'swap' }}
  {{  badge_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    height: 100%;
    margin: 0 auto;
    padding-top: {{ padding_top_mobile }}px;
    padding-bottom: {{ padding_bottom_mobile }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .hero-{{ section.id }} {
    position: relative;
  }

  .hero-body-{{ section.id }} {
    display: flex;
    flex-direction: column;
    height: 100%;
  }

  .hero-image-{{ section.id }} {
    width: 100%;
    height: 100%;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
    position: relative;
  }

  .hero-image-inner-{{ section.id }} {
    height: 100%;
  }

  .hero-image-{{ section.id }} img,
  .hero-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-image-{{ section.id }} svg {
    background-color: #AEAEAE;
  }

  .hero-image-desktop-{{ section.id }} {
    display: none;
  }

  .hero-absolute-image-{{ section.id }} {
    display: none;
    width: 100%;
    max-width: {{ absolute_image_width }}px;
    border-radius: {{ absolute_image_radius }}px;
    border: {{ absolute_image_border_thickness }}px solid {{ absolute_image_border_color }};
    position: absolute;
    bottom: 0%;
    left: 50%;
    transform: translateX(-50%) translateY(35%);
    z-index: 2;
    overflow: hidden;
  }

  .hero-absolute-image-{{ section.id }} img {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-content-{{ section.id }} {
    position: relative;
    z-index: 1;
  }

  .hero-heading-{{ section.id }} {
    margin-top: {{ heading_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ heading_align_mobile }};
  }

  .hero-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .hero-text-{{ section.id }} {
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ text_align_mobile }};
  }

  .hero-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .hero-feature-text-{{ section.id }} {
    margin: 0;
    margin-top: {{ feature_text_space | times: 0.75 | round: 0 }}px;
    display: block;
    width: 100%;
    text-align: {{ feature_text_align_mobile }};
    font-size: {{ feature_text_size_mobile }}px;
    color: {{ feature_text_color }};
    line-height: {{ feature_text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .hero-feature-text-{{ section.id }}:first-child {
    margin-top: {{ feature_text_mt | times: 0.75 | round: 0 }}px;
  }

  .hero-button-{{ section.id }} {
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    gap: {{ button_gap | times: 0.75 | round: 0 }}px;
    transition: all 0.25s ease 0s;
    position: relative;
    z-index: 2;
  }

  .hero-button-{{ section.id }} * {
    margin: 0px;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-transform: unset;
    text-decoration: none;
    transition: all 0.25s ease 0s;
  }

  .hero-button-{{ section.id }} strong {
    color: {{ button_other_color }};
  }

  .hero-button-{{ section.id }}:hover {
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .hero-button-{{ section.id }}:hover * {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .hero-button-{{ section.id }}:hover strong {
    color: {{ button_hover_other_color }};
  }

  .hero-button-icon-{{ section.id }} {
    display: block;
    width: 20px;
    height: 20px;
  }

  .hero-button-icon-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-button-{{ section.id }} svg path {
    stroke: {{ button_color }};
  }

  .hero-button-{{ section.id }}:hover svg path {
    stroke: {{ button_hover_color }};
  }

  .hero-rating-{{ section.id }} {
    margin-top: {{ stars_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    gap: 8px;
    justify-content: {{ rate_text_align_mobile }};
  }

  .hero-stars-{{ section.id }} {
    display: flex;
    align-items: end;
    gap: 3px;
  }

  .hero-stars-{{ section.id }} svg,
  .hero-stars-{{ section.id }} img {
    display: block;
    width: {{ stars_size_mobile }}px;
    height: {{ stars_size_mobile }}px;
    object-fit: cover;
  }

  .hero-rate-{{ section.id }} * {
    margin: 0px;
    font-size: {{ rate_text_size_mobile }}px;
    color: {{ rate_text_color }};
    line-height: {{ rate_text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .hero-badges-{{ section.id }} {
    margin-top: {{ badge_mt_mobile }}px;
    display: flex;
    align-items: center;
    gap: 10px;
    flex-wrap: wrap;
    justify-content: {{ badge_align_mobile }};
  }

  .hero-badge-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    text-align: center;
    text-transform: uppercase;
    text-decoration: none;
    padding: {{ badge_padding_vertical | times: 0.75 | round: 0 }}px {{ badge_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ badge_radius }}px; 
    z-index: 2;
    font-weight: 700;
    font-size: {{ badge_size_mobile }}px;
    line-height: {{ badge_height }}%;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hero-body-{{ section.id }} {
      flex-direction: row;
      align-items: center;
    }

    .hero-image-{{ section.id }} {
      margin: 0px;
      max-width: 50%;
      position: absolute;
      top: 0;
      right: 0;
    }

    .hero-image-desktop-{{ section.id }} {
      display: block;
    }

    .hero-image-mobile-{{ section.id }} {
      display: none;
    }

    .hero-absolute-image-{{ section.id }} {
      display: block;
    }

    .hero-content-{{ section.id }} {
      width: 100%;
      flex: 0 0 48%;
      height: 100%;
      display: flex;
      align-items: center;
      justify-content: center;
    }

    .hero-heading-{{ section.id }} {
      margin-top: {{ heading_mt }}px;
      text-align: {{ heading_align }};
    }

    .hero-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .hero-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
    }

    .hero-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .hero-feature-text-{{ section.id }} {
      margin: 0;
      margin-top: {{ feature_text_space }}px;
      text-align: {{ feature_text_align }};
      font-size: {{ feature_text_size }}px;
    }

    .hero-feature-text-{{ section.id }}:first-child {
      margin-top: {{ feature_text_mt }}px;
    }

    .hero-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      gap: {{ button_gap }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
    }

    .hero-button-{{ section.id }} * {
      font-size: {{ button_size }}px;
    }

    .hero-rating-{{ section.id }} {
      margin-top: {{ stars_mt }}px;
      justify-content: {{ rate_text_align }};
    }

    .hero-stars-{{ section.id }} svg,
    .hero-stars-{{ section.id }} img {
      width: {{ stars_size }}px;
      height: {{ stars_size }}px;
    }

    .hero-rate-{{ section.id }} * {
      font-size: {{ rate_text_size }}px;
    }

    .hero-badges-{{ section.id }} {
      margin-top: 0px;
      justify-content: {{ badge_align }};
    }

    .hero-badge-{{ section.id }} {
      padding: {{ badge_padding_vertical }}px {{ badge_padding_horizontal }}px;
      font-size: {{ badge_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .hero-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .hero-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if feature_text_custom %}
  <style>
    .hero-feature-text-{{ section.id }} {
      font-family: {{ feature_text_font.family }}, {{ feature_text_font.fallback_families }};
      font-weight: {{ feature_text_font.weight }};
      font-style: {{ feature_text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .hero-button-{{ section.id }} * {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if badge_custom %}
  <style>
    .hero-badge-{{ section.id }} {
      font-family: {{ badge_font.family }}, {{ badge_font.fallback_families }};
      font-weight: {{ badge_font.weight }};
      font-style: {{ badge_font.style }};
    }
  </style>
{% endif %}

{% if rate_text_custom %}
  <style>
    .hero-rate-{{ section.id }} * {
      font-family: {{ rate_text_font.family }}, {{ rate_text_font.fallback_families }};
      font-weight: {{ rate_text_font.weight }};
      font-style: {{ rate_text_font.style }};
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .hero-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .hero-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if button_shadow %}
  <style>
    .hero-button-{{ section.id }} {
      box-shadow: 0 8px 16px 2px {{ button_shadow_color | hex_to_rgba: 0.33 }};
    }
  </style>
{% endif %}

{% if image_blur_desktop %}
  <style>
    @media(min-width: 1024px) {
      .hero-image-{{ section.id }}:before {
        content: '';
        width: 100%;
        height: 100%;
        position: absolute;
        left: 0;
        top: 0;
        background: linear-gradient(90deg, {{ image_blur_color | hex_to_rgba: 1 }} 0%, rgba(238, 130, 238, 0) 25%);
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% if block.type == "badge" %}
    <style>
      .hero-badge-{{ block.id }} {
        color: {{ block.settings.text_color }};
        background-color: {{ block.settings.bg_color }};
        border: {{ badge_border_thickness }}px solid {{ block.settings.border_color }};
      }
    </style>
  {% endif %}
{% endfor %}

<div class="section-{{ section.id }} hero-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
  <div class="section-{{ section.id }}-settings">
    <div class="hero-body-{{ section.id }}">
      <div class="hero-image-{{ section.id }}">
        <div class="hero-image-inner-{{ section.id }} {% if image_mobile != blank %} hero-image-desktop-{{ section.id }}{% endif %}">
          {% if image != blank %}
            <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
          {% else %}
            {{ 'lifestyle-2' | placeholder_svg_tag  }}
          {% endif %}
        </div>
        {% if image_mobile != blank %}
          <img src="{{ image_mobile | image_url }}" alt="{{ image_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} class="hero-image-mobile-{{ section.id }}">
        {% endif %}
      </div>
      <div class="hero-content-{{ section.id }}">
        <div class="hero-content-inner-{{ section.id }}">
          <div class="hero-badges-{{ section.id }}">
            {% for block in section.blocks %}
              {% if block.type == "badge" %}
                <div class="hero-badge-{{ section.id }} hero-badge-{{ block.id }}">
                  {{ block.settings.text }}
                </div>
              {% endif %}
            {% endfor %}
          </div>
          {% if heading != blank %}
            <div class="hero-heading-{{ section.id }}">
              {{ heading }}
            </div>
          {% endif %}

          {% if text != blank %}
            <div class="hero-text-{{ section.id }}">
              {{ text }}
            </div>
          {% endif %}

          <div class="hero-features-{{ section.id }}">
            {% for block in section.blocks %}
              {% if block.type == "feature" %}
                <p class="hero-feature-text-{{ section.id }}">{{ block.settings.text }}</p>
              {% endif %}
            {% endfor %}
          </div>
          {% if button != blank %}
            <a href="{{ button_url }}" class="hero-button-{{ section.id }}">
              {{ button }}
              {% if button_icon %}
                <span class="hero-button-icon-{{ section.id }}">
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M3.75 12H20.25" stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                    <path d="M13.5 5.25L20.25 12L13.5 18.75" stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                  </svg>
                </span>
              {% endif %}
            </a>
          {% endif %}
          {% if stars_count > 0 or rate_text != blank %}
            <div class="hero-rating-{{ section.id }}">
              {% if stars_count != 0 %}
                <div class="hero-stars-{{ section.id }}">
                  {% if stars_icon != blank %}
                    {% for item in (1..stars_count) %}
                      <img src="{{ stars_icon | image_url }}" alt="{{ stars_icon.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                    {% endfor %}
                  {% else %}
                    <svg xmlns="http://www.w3.org/2000/svg" width="20" height="19" viewBox="0 0 20 19" fill="none">
                      {% if stars_count > 0 and stars_count < 1  %}
                        <defs>
                          <linearGradient id="grad{{ section.id }}1">
                            <stop offset="0%" stop-color="{{ stars_color }}" />
                            <stop offset="50%" stop-color="{{ stars_color }}" />
                            <stop offset="50%" stop-color="{{ background_color }}" />
                            <stop offset="100%" stop-color="{{ background_color }}" />
                          </linearGradient>
