{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  sub_heading_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  product_button_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .hero-heading-{{ section.id }} {
    text-align: {{ heading_align_mobile }};
  }

  .hero-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .hero-subheading-{{ section.id }} {
    margin-top: {{ sub_heading_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ sub_heading_align_mobile }};
  }

  .hero-subheading-{{ section.id }} * {
    margin: 0;
    font-size: {{ sub_heading_size_mobile }}px;
    color: {{ sub_heading_color }};
    line-height: {{ sub_heading_height }}%;
    text-transform: unset;
  }

  .hero-button-{{ section.id }} {
    margin-top: {{ button_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    gap: 5px;
    font-size: {{ button_size_mobile }}px;
    font-weight: 700;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical | times: 0.75 | round: 0 }}px {{ button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ button_radius }}px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
    position: relative;
    z-index: 2;
  }

  .hero-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .hero-button-{{ section.id }} svg path {
    fill: {{ button_color }};
  }

  .hero-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
  }

  .hero-scrolling-{{ section.id }} {
    display: grid;
    margin: 0;
    margin-top: {{ scrolling_mt | times: 0.75 | round: 0 }}px;
    /* max-width: 100vw; */
    {% if full_width %}
    max-width: 100%;
    {% else %}
    max-width: {{ content_width }}rem;
    {% endif %}
    margin-inline: auto;
    overflow: hidden;
    width: 100vw;
    -ms-overflow-style: none;
    scrollbar-width: none;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
  }

  .hero-scrolling-inner-{{ section.id }} {
    max-width: unset;
    overflow: auto !important;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    gap: {{ slide_gap | times: 0.75 | round: 0 }}px;
    --card-play-duration: {{ scrolling_velocity }}s;
    -ms-overflow-style: none;
    scrollbar-width: none;
    padding: 1px 0px;
  }

  .hero-scrolling-{{ section.id }}:not(.paused) .hero-scrolling-inner-{{ section.id }}{
    --card-play-state: running;
  }

  .hero-scrolling-{{ section.id }}.paused .hero-scrolling-inner-{{ section.id }}{
    --card-play-state: paused;
  }

  .hero-scrolling-item-{{ section.id }} {
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    animation-direction: normal;
    animation-duration: var(--card-play-duration);
    animation-fill-mode: forwards;
    animation-iteration-count: infinite;
    animation-name: hero-slider-card-marquee;
    animation-play-state: var(--card-play-state);
    animation-timing-function: linear;
    gap: {{ slide_gap | times: 0.75 | round: 0 }}px;
  }

  .hero-scrolling-slide-{{ section.id }} {
    width: {{ slide_width_mobile }}px;
  }

  .hero-scrolling-card-{{ section.id }} {
    display: flex;
    flex-direction: column;
    box-sizing: border-box;
    text-decoration: none;
    position: relative;
    border-radius: {{ slide_radius }}px;
    overflow: hidden;
    cursor: pointer;
    padding: {{ slide_padding_vertical | times: 0.75 | round: 0 }}px {{ slide_padding_horizontal | times: 0.75 | round: 0 }}px;
  }

  .hero-scrolling-card-{{ section.id }}:not(.hero-scrolling-card-product-{{ section.id }}):after {
    position: absolute;
    content: '';
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0,0,0, {{ slide_overlay }});
    z-index: 2;
    opacity: 0;
    transition: all 0.25s ease 0s;
  }

  .hero-scrolling-card-{{ section.id }}:not(:first-child) {
    margin-top: {{ slide_gap | times: 0.75 | round: 0 }}px;
  }

  .hero-scrolling-card-image-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    transition: all 0.25s ease 0s;
  }

  .hero-scrolling-card-image-{{ section.id }} img,
  .hero-scrolling-card-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-scrolling-card-image-{{ section.id }} svg {
    background-color: #DADADA;
  }

  .hero-product-title-{{ section.id }} {
    display: block;
    width: 100%;
    text-align: {{ title_align_mobile }};
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    line-height: {{ title_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .hero-product-button-{{ section.id }} {
    margin-top: {{ product_button_mt | times: 0.75 | round: 0 }}px;
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    gap: 5px;
    font-size: {{ product_button_size_mobile }}px;
    font-weight: 700;
    line-height: {{ product_button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ product_button_padding_vertical | times: 0.75 | round: 0 }}px {{ product_button_padding_horizontal | times: 0.75 | round: 0 }}px;
    border-radius: {{ product_button_radius }}px;
    transition: all 0.25s ease 0s;
  }

  .hero-card-text-{{ section.id }} {
    margin: 0;
    line-height: {{ text_height }}%;
    text-transform: unset;
    display: block;
    width: 100%;
    text-align: {{ text_align_mobile }};
    font-weight: 700;
  }

  @keyframes hero-slider-card-marquee {
    0% {
        transform: translate(0);
    }
    100% {
        transform: translate(-100%);
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hero-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .hero-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .hero-subheading-{{ section.id }} {
      margin-top: {{ sub_heading_mt }}px;
      text-align: {{ sub_heading_align }};
    }

    .hero-subheading-{{ section.id }} * {
      font-size: {{ sub_heading_size }}px;
    }

    .hero-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .hero-scrolling-{{ section.id }} {
      margin-top: {{ scrolling_mt }}px;
      padding-bottom: {{ padding_bottom }}px;
    }

    .hero-scrolling-inner-{{ section.id }} {
       gap: {{ slide_gap }}px;
    }

    .hero-scrolling-item-{{ section.id }} {
      gap: {{ slide_gap }}px;
    }

    .hero-scrolling-slide-{{ section.id }} {
      width: {{ slide_width }}px;
    }

    .hero-scrolling-card-{{ section.id }} {
      padding: {{ slide_padding_vertical }}px {{ slide_padding_horizontal }}px;
    }

    .hero-scrolling-card-{{ section.id }}:not(:first-child) {
      margin-top: {{ slide_gap | times: 0.75 | round: 0 }}px;
    }

    .hero-product-title-{{ section.id }} {
      text-align: {{ title_align }};
      font-size: {{ title_size }}px;
    }

    .hero-product-button-{{ section.id }} {
      margin-top: {{ product_button_mt }}px;
      padding: {{ product_button_padding_vertical }}px {{ product_button_padding_horizontal }}px;
      font-size: {{ product_button_size }}px;
    }

    .hero-card-text-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .hero-scrolling-card-{{ section.id }}:hover:after {
      opacity: 1;
      transition: all 0.25s ease 0s;
    } 

    .hero-scrolling-card-product-{{ section.id }}:hover .hero-scrolling-card-image-{{ section.id }} {
      opacity: 0;
      pointer-events: none;
      transition: all 0.25s ease 0s;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .hero-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_custom %}
  <style>
    .hero-subheading-{{ section.id }} * {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .hero-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .hero-product-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if product_button_custom %}
  <style>
    .hero-product-button-{{ section.id }} {
      font-family: {{ product_button_font.family }}, {{ product_button_font.fallback_families }};
      font-weight: {{ product_button_font.weight }};
      font-style: {{ product_button_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .hero-card-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .hero-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .hero-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .hero-button-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if product_button_align_mobile == "center" %}
  <style>
   .hero-product-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif product_button_align_mobile == "right" %}
  <style>
   .hero-product-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if product_button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .hero-product-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif product_button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .hero-product-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .hero-product-button-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if pause_hover %}
  <style>
    .hero-scrolling-{{ section.id }}:hover .hero-scrolling-inner-{{ section.id }}{
      --card-play-state: paused;
    }
  </style>
{% endif %}

{% for block in section.blocks %}

  <style>
    .hero-scrolling-card-{{ block.id }} {
      background-color: {{ block.settings.slide_bg_color }};
      border: {{ slide_border_thickness }}px solid {{ block.settings.slide_border_color }};
      justify-content: {{ block.settings.content_vertical_align_mobile }};
    }

    .hero-scrolling-card-{{ block.id }} .hero-product-title-{{ section.id }} {
      color: {{ block.settings.title_color }};
    }

    .hero-scrolling-card-{{ block.id }} .hero-product-button-{{ section.id }} {
      color: {{ block.settings.button_color }};
      background-color: {{ block.settings.button_bg_color }};
      border: {{ product_button_border_thickness }}px solid {{ block.settings.button_border_color }};
    }

    .hero-scrolling-card-{{ block.id }} .hero-product-button-{{ section.id }} svg path {
      fill: {{ block.settings.button_color }};
    }

    .hero-scrolling-card-{{ block.id }} .hero-product-button-{{ section.id }}:hover {
      transition: all 0.25s ease 0s;
      color: {{ block.settings.button_hover_color }};
      background-color: {{ block.settings.button_bg_hover_color }};
      border: {{ product_button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
    }

    .hero-scrolling-card-{{ block.id }} .hero-product-button-{{ section.id }}:hover svg path {
      fill: {{ block.settings.button_hover_color }};
    }

    .hero-scrolling-card-{{ block.id }} .hero-card-text-{{ section.id }} {
      color: {{ block.settings.text_color }};
      font-size: {{ block.settings.text_size_mobile }}px;
    }

    @media(min-width: 1024px) {
      .hero-scrolling-card-{{ block.id }} {
        justify-content: {{ block.settings.content_vertical_align }};
      }

      .hero-scrolling-card-{{ block.id }} .hero-card-text-{{ section.id }} {
        font-size: {{ block.settings.text_size }}px;
      }
    }
  </style>

  {% if block.settings.card_size == "small" %}
    <style>
      .hero-scrolling-card-{{ block.id }} {
        aspect-ratio: 1/0.43;
      }
    </style>
  {% elsif block.settings.card_size == "medium" %}
    <style>
      .hero-scrolling-card-{{ block.id }} {
        aspect-ratio: 1/0.67;
      }
    </style>
  {% elsif block.settings.card_size == "big" %}
    <style>
      .hero-scrolling-card-{{ block.id }} {
        aspect-ratio: 1/1;
      }

    </style>
  {% elsif block.settings.card_size == "large" %}
    <style>
      .hero-scrolling-card-{{ block.id }} {
        aspect-ratio: 1/1.24;
      }
    </style>
