{% endfor %}

<div class="section-{{ section.id }} hero-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
  <div class="section-{{ section.id }}-settings">
    {% if heading != blank %}
      <div class="hero-heading-{{ section.id }}">{{ heading }}</div>
    {% endif %}

    {% if sub_heading != blank %}
      <div class="hero-subheading-{{ section.id }}">{{ sub_heading }}</div>
    {% endif %}

    {% if button != blank %}
      <a href="{{ button_url }}" class="hero-button-{{ section.id }}">
        {{ button }}
        {% if button_arrow %}
          <svg width="16" height="13" viewBox="0 0 16 13" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M0 6.625H15M15 6.625L9.33962 1M15 6.625L9.33962 12.25" stroke="currentColor" stroke-width="1.25"></path>
          </svg>
        {% endif %}
      </a>
    {% endif %}
  </div>

  <div class="hero-scrolling-{{ section.id }}">
    <div class="hero-scrolling-inner-{{ section.id }}">
      {% for i in (1..4) %}
        <div class="hero-scrolling-item-{{ section.id }}">
          {% for block in section.blocks %}
            {% assign index = forloop.index0 | modulo: 2 %}
            {% if index == 0 %}
              <div class="hero-scrolling-slide-{{ section.id }}">
            {% endif %}

              <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% elsif block.settings.product != blank %}a href="{{ block.settings.product.url }}"{% else %}div{% endif %} class="hero-scrolling-card-{{ section.id }} hero-scrolling-card-{{ block.id }} {% if block.settings.product != blank or block.settings.text != blank and block.settings.image != blank %}hero-scrolling-card-product-{{ section.id }}{% endif %}">
                <div class="hero-scrolling-card-image-{{ section.id }}">
                  {% if block.settings.image != blank %}
                    <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% elsif block.settings.product.featured_image != blank %}
                    <img src="{{ block.settings.product.featured_image | image_url }}" alt="{{ block.settings.product.featured_image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% endif %}
                </div>
                {% if block.settings.product != blank %}
                  <h2 class="hero-product-title-{{ section.id }}">{{ block.settings.product.title }}</h2>

                  {% if product_button != blank %}
                    <div class="hero-product-button-{{ section.id }}">
                      {{ product_button }}

                      {% if product_button_arrow %}
                        <svg width="16" height="13" viewBox="0 0 16 13" fill="none" xmlns="http://www.w3.org/2000/svg">
                          <path d="M0 6.625H15M15 6.625L9.33962 1M15 6.625L9.33962 12.25" stroke="currentColor" stroke-width="1.25"></path>
                        </svg>
                      {% endif %}
                    </div>
                  {% endif %}
                {% endif %}

              {% if block.settings.text != blank %}
                <p class="hero-card-text-{{ section.id }}">{{ block.settings.text }}</p>
              {% endif %}
              </{% if block.settings.url != blank %}a{% elsif block.settings.product != blank %}a{% else %}div{% endif %}>

            {% if index == 1 or forloop.last %}
              </div>
            {% endif %}
          {% endfor %}
        </div>
      {% endfor %}
    </div>
  </div>

</div>
