{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign hero_height = section.settings.hero_height
  assign hero_fixed_height = section.settings.hero_fixed_height
  assign hero_fixed_height_mobile = section.settings.hero_fixed_height_mobile

  assign image = section.settings.image
  assign image_mobile = section.settings.image_mobile

  assign overlay = section.settings.overlay
  assign overlay_color = section.settings.overlay_color

  assign content_maxwidth_mobile = section.settings.content_maxwidth_mobile
  assign content_maxwidth = section.settings.content_maxwidth
  assign content_vertical_position_mobile = section.settings.content_vertical_position_mobile
  assign content_vertical_position = section.settings.content_vertical_position
  assign content_horizontal_position_mobile = section.settings.content_horizontal_position_mobile
  assign content_horizontal_position = section.settings.content_horizontal_position
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_horizontal = section.settings.content_padding_horizontal

  assign text_align_mobile = ""
  if content_horizontal_position_mobile == "start"
    assign text_align_mobile = "left"
  elsif content_horizontal_position_mobile == "center"
    assign text_align_mobile = "center"
  else
  assign text_align_mobile = "right"
  endif

  assign text_align = ""
  if content_horizontal_position == "start"
    assign text_align = "left"
  elsif content_horizontal_position == "center"
    assign text_align = "center"
  else
  assign text_align = "right"
  endif

-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    height: 100%;
    position: relative;
  }

  .hero-image-{{ section.id }} {
    width: 100%;
    height: 100%;
  }

  .hero-image-inner-{{ section.id }} {
    height: 100%;
  }

  .hero-image-{{ section.id }} img,
  .hero-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hero-image-{{ section.id }} svg {
    background-color: #e8e8e8;
  }

  .hero-image-desktop-{{ section.id }} {
    display: none !important;
  }

  .hero-image-{{ section.id }}:after {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    background-color: {{ overlay_color | hex_to_rgba: overlay }};
    z-index: 1;
  }

  .hero-body-{{ section.id }} {
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: {{ content_vertical_position_mobile }};
    align-items: {{ content_horizontal_position_mobile }};
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    z-index: 2;
  }

  .hero-content-{{ section.id }} {
    width: 100%;
    max-width: {{ content_maxwidth_mobile }}%;
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    text-align: {{ text_align_mobile }};
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hero-image-desktop-{{ section.id }} {
      display: block !important;
    }

    .hero-image-mobile-{{ section.id }} {
      display: none !important;
    }

    .hero-body-{{ section.id }} {
      justify-content: {{ content_vertical_position }};
      align-items: {{ content_horizontal_position }};
    }

    .hero-content-{{ section.id }} {
      max-width: {{ content_maxwidth }}%;
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
      text-align: {{ text_align }};
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if hero_height == "adapt_first" %}
  <style>
    .hero-{{ section.id }} {
      height: 100vh;
    }
  </style>
{% elsif hero_height == "auto_height" %}
  <style>
    /* .hero-{{ section.id }} {
      height: {{ hero_fixed_height_mobile }}px;
    } */

     .hero-{{ section.id }} {
        height: auto;
      }

    .hero-content-wrapper-{{ section.id }} {
      height: 100%;
    }

    /* @media(min-width: 1024px ) {
      .hero-{{ section.id }} {
        height: auto;
      }
    } */
  </style>
{% else %}
  <style>
    .hero-{{ section.id }} {
      height: {{ hero_fixed_height_mobile }}px;
    }

    @media(min-width: 768px) {
      .hero-{{ section.id }} {
        height: {{ hero_fixed_height }}px;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}

  {% if block.type == "scrolling" %}

    <style>
      .hero-scrolling-{{ block.id }} {
        position: relative;
        z-index: 2;
        display: -webkit-box;
        display: -webkit-flex;
        display: -ms-flexbox;
        display: flex;
        overflow: hidden;
        width: 300vw;
        height: 100px;
        transform: translateX(-50%);
        left: -50%;
        -webkit-box-pack: start;
        -webkit-justify-content: flex-start;
        -ms-flex-pack: start;
        justify-content: flex-start;
        -webkit-box-align: center;
        -webkit-align-items: center;
        -ms-flex-align: center;
        align-items: center;
        gap: {{ block.settings.scrolling_text_space | times: 0.75 | round: 0 }}px;
        margin: 0px -{{ content_padding_horizontal_mobile }}px;
        margin-top: {{ block.settings.scrolling_text_mt | times: 0.75 | round: 0 }}px;
      }

      .hero-scrolling-inner-{{ block.id }} {
        position: absolute;
        white-space: nowrap;
        top: 0;
        bottom: 0;
        will-change: transform;
        display: -webkit-box;
        display: -webkit-flex;
        display: -ms-flexbox;
        display: flex;
        -webkit-box-pack: start;
        -webkit-justify-content: flex-start;
        -ms-flex-pack: start;
        justify-content: flex-start;
        -webkit-box-align: center;
        -webkit-align-items: center;
        -ms-flex-align: center;
        align-items: center;
        animation: marquee-horizontal-left {{ block.settings.scrolling_velocity_mobile }}s linear infinite;
        gap: {{ block.settings.scrolling_text_space | times: 0.75 | round: 0 }}px;
      }

      .hero-scrolling-item-{{ block.id }} {
        margin-right: 1vw;
        -webkit-box-flex: 0;
        -webkit-flex: 0 0 auto;
        -ms-flex: 0 0 auto;
        flex: 0 0 auto;
        color: #fff;
        text-transform: uppercase;
        text-align: center;
      }

      @keyframes marquee-horizontal-left {
        from { transform: translateX(0); }
        to { transform: translateX(-50%); }
      }

      .hero-scrolling-text-{{ block.id }} {
        margin: 0;
        font-size: {{ block.settings.scrolling_text_size_mobile }}px;
        color: {{ block.settings.scrolling_text_color }};
        line-height: {{ block.settings.scrolling_text_height }}%;
        text-transform: unset;
        font-weight: 700;
        transition: all 0.25s ease 0s;
      }

      .hero-scrolling-{{ block.id }}:hover {
        color: {{ block.settings.scrolling_text_hover_color }};
        transition: all 0.25s ease 0s;
      }

      @media(min-width: 1024px) {
        .hero-scrolling-text-{{ block.id }} {
          font-size: {{ block.settings.scrolling_text_size }}px;
        }

        .hero-scrolling-{{ block.id }} {
          gap: {{ block.settings.scrolling_text_space }}px;
          margin: 0px -{{ content_padding_horizontal }}px;
          margin-top: {{ block.settings.scrolling_text_mt }}px;
        }

        .hero-scrolling-inner-{{ block.id }} {
          gap: {{ block.settings.scrolling_text_space }}px;
          animation: marquee-horizontal-left {{ block.settings.scrolling_velocity }}s linear infinite;
        }
      }
    </style>

    {% if block.settings.scrolling_text_custom %}
      <style>
        .hero-scrolling-text-{{ block.id }} {
          font-family: {{ block.settings.scrolling_text_font.family }}, {{ block.settings.scrolling_text_font.fallback_families }};
          font-weight: {{ block.settings.scrolling_text_font.weight }};
          font-style: {{ block.settings.scrolling_text_font.style }};
        }
      </style>
    {% endif %}

    {% if block.settings.scrolling_text_gradient_color != blank %}
      <style>
        .hero-scrolling-text-{{ block.id }} {
           background: {{ block.settings.scrolling_text_gradient_color }};
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent; 
        }
      </style>
    {% endif %}

  {% elsif block.type == "text" %}
    <style>

      {{ block.settings.text_font | font_face: font_display: 'swap' }}

      .hero-text-{{ block.id }} {
        margin-top: {{ block.settings.text_mt | times: 0.75 | round: 0 }}px;
        text-align: {{ text_align_mobile }};
      }

      .hero-text-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.text_size_mobile }}px;
        color: {{ block.settings.text_color }};
        line-height: {{ block.settings.text_height }}%;
        text-transform: unset;
      }

      @media(min-width: 1024px) {
        .hero-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt }}px;
          text-align: {{ text_align }};
        }

        .hero-text-{{ block.id }} * {
          font-size: {{ block.settings.text_size }}px;
        }
      }

    </style>

    {% if block.settings.text_custom %}
      <style>
        .hero-text-{{ block.id }} * {
          font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
          font-weight: {{ block.settings.text_font.weight }};
          font-style: {{ block.settings.text_font.style }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "button" %}

    <style>

      {{  block.settings.button_font | font_face: font_display: 'swap' }}

      .hero-button-{{ block.id }} {
        margin-top: {{ block.settings.button_mt | times: 0.75 | round: 0 }}px !important;
        font-size: {{ block.settings.button_size_mobile }}px;
        font-weight: 700;
        color: {{ block.settings.button_color }};
        line-height: {{ block.settings.button_height }}%;
        text-transform: unset;
        text-decoration: none;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        max-width: fit-content;
        text-align: center;
        text-transform: unset;
        text-decoration: none;
        padding: {{ block.settings.button_padding_vertical | times: 0.75 | round: 0 }}px {{ block.settings.button_padding_horizontal | times: 0.75 | round: 0 }}px;
        border-radius: {{ block.settings.button_radius }}px;
        border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_color }};
        background-color: {{ block.settings.button_bg_color }};
        gap: {{ block.settings.button_gap | times: 0.75 | round: 0 }}px;
        transition: all 0.25s ease 0s;
        position: relative;
        z-index: 2;
      }

      .hero-button-{{ block.id }}:hover {
        color: {{ block.settings.button_hover_color }};
        border: {{ block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
        background-color: {{ block.settings.button_bg_hover_color }};
        transition: all 0.25s ease 0s;
      }

      .hero-button-icon-{{ block.id }} {
        display: block;
        width: 20px;
        height: 20px;
      }

      .hero-button-icon-{{ block.id }} svg {
        display: block;
        width: 100%;
        height: 100%;
        object-fit: cover;
      }

      .hero-button-{{ block.id }} svg path {
        stroke: {{ block.settings.button_color }};
        transition: all 0.25s ease 0s;
      }

      .hero-button-{{ block.id }}:hover svg path {
        stroke: {{ block.settings.button_hover_color }};
        transition: all 0.25s ease 0s;
      }

      @media(min-width: 1024px) {

        .hero-button-{{ block.id }} {
          margin-top: {{ block.settings.button_mt }}px !important;
          gap: {{ block.settings.button_gap }}px;
          padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
          font-size: {{ block.settings.button_size }}px;
        }
      }
    </style>

    {% if block.settings.button_custom %}
      <style>
        .hero-button-{{ block.id }} {
          font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
          font-weight: {{ block.settings.button_font.weight }};
          font-style: {{ block.settings.button_font.style }};
        }
      </style>
    {% endif %}

    {% if block.settings.button_ellipse %}
      <style>
        .hero-button-{{ block.id }} {
          border-radius: 100% / 100%;
        }
      </style>
    {% endif %}

    {% if block.settings.button_shadow %}
      <style>
        .hero-button-{{ block.id }} {
          box-shadow: 0 8px 16px 2px {{ block.settings.button_shadow_color | hex_to_rgba: 0.33 }};
        }
      </style>
    {% endif %}

  {% elsif block.type == "heading" %}

    <style>

      {{  block.settings.heading_font | font_face: font_display: 'swap' }}

      .hero-heading-{{ block.id }} {
        margin-top: {{ block.settings.heading_mt | times: 0.75 | round: 0 }}px;
        text-align: {{ text_align_mobile }};
      }

      .hero-heading-{{ block.id }} * {
        margin: 0;
        font-size: {{ block.settings.heading_size_mobile }}px;
        color: {{ block.settings.heading_color }};
        line-height: {{ block.settings.heading_height }}%;
        text-transform: unset;
      }

      @media(min-width: 1024px) {

        .hero-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt }}px;
          text-align: {{ text_align }};
        }

        .hero-heading-{{ block.id }} * {
          font-size: {{ block.settings.heading_size }}px;
        }
      }
    </style>

    {% if block.settings.heading_custom %}
      <style>
        .hero-heading-{{ block.id }} * {
          font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
          font-weight: {{ block.settings.heading_font.weight }};
          font-style: {{ block.settings.heading_font.style }};
        }
      </style>
    {% endif %}

  {% endif %}

{% endfor %}

<div class="section-{{ section.id }} hero-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="hero-image-{{ section.id }}">
        <div class="hero-image-inner-{{ section.id }} {% if image_mobile != blank %} hero-image-desktop-{{ section.id }}{% endif %}">
          {% if image != blank %}
            <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
          {% else %}
            {{ 'lifestyle-2' | placeholder_svg_tag  }}
          {% endif %}
        </div>
        {% if image_mobile != blank %}
          <img src="{{ image_mobile | image_url }}" alt="{{ image_mobile.alt }}" {% if lazy %}loading="lazy"{% endif %} class="hero-image-mobile-{{ section.id }}">
        {% endif %}
      </div>

      <div class="hero-body-{{ section.id }}">

        <div class="hero-content-{{ section.id }}">

          {% for block in section.blocks %}

            {% if block.type == "scrolling" %}

            {% if block.settings.scrolling_text != blank %}
              <div class="hero-scrolling-{{ block.id }}">
                <div class="hero-scrolling-inner-{{ block.id }}">
                  {% for item in (1..10) %}
                    <div class="hero-scrolling-item-{{ block.id }}">
                      <p class="hero-scrolling-text-{{ block.id }}">{{ block.settings.scrolling_text }}</p>
                    </div>
                  {% endfor %}

                </div>
                <div class="hero-scrolling-inner-{{ block.id }}">
                  {% for item in (1..10) %}
                    <div class="hero-scrolling-item-{{ block.id }}">
                      <p class="hero-scrolling-text-{{ block.id }}">{{ block.settings.scrolling_text }}</p>
                    </div>
                  {% endfor %}

                </div>
              </div>
            {% endif %}

            {% elsif block.type == "heading" %}

              {% if block.settings.heading != blank %}
                <div class="hero-heading-{{ block.id }}">{{ block.settings.heading }}</div>
              {% endif %}

            {% elsif block.type == "text" %}

              {% if block.settings.text != blank %}
                <div class="hero-text-{{ block.id }}">{{ block.settings.text }}</div>
              {% endif %}

            {% elsif block.type == "button" %}

              {% if block.settings.button != blank %}
                <a href="{{ block.settings.button_url }}" class="hero-button-{{ block.id }}">
                  {{ block.settings.button }}
                  {% if block.settings.button_icon %}
                    <span class="hero-button-icon-{{ block.id }}">
                      <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M3.75 12H20.25" stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                        <path d="M13.5 5.25L20.25 12L13.5 18.75" stroke="white" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                      </svg>
                    </span>
                  {% endif %}
                </a>
              {% endif %}

            {% endif %}

          {% endfor %}

        </div>

      </div>

    </div>
</div>
