{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_gap = section.settings.layout_gap

  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign image = section.settings.image
  assign image_width = section.settings.image_width
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile

  assign hotspot_bg_color = section.settings.hotspot_bg_color
  assign hotspot_bg_active_color = section.settings.hotspot_bg_active_color
  assign hotspot_icon_color = section.settings.hotspot_icon_color
  assign hotspot_icon_active_color = section.settings.hotspot_icon_active_color
  assign hotspot_size = section.settings.hotspot_size
  assign hotspot_size_mobile = section.settings.hotspot_size_mobile
  assign hotspot_animate = section.settings.hotspot_animate

  assign hotspot_content_padding_horizontal = section.settings.hotspot_content_padding_horizontal
  assign hotspot_content_padding_horizontal_mobile = section.settings.hotspot_content_padding_horizontal_mobile
  assign hotspot_content_padding_vertical = section.settings.hotspot_content_padding_vertical
  assign hotspot_content_padding_vertical_mobile = section.settings.hotspot_content_padding_vertical_mobile
  assign hotspot_content_border_thickness = section.settings.hotspot_content_border_thickness
  assign hotspot_content_border_color = section.settings.hotspot_content_border_color
  assign hotspot_content_radius = section.settings.hotspot_content_radius
  assign hotspot_content_bg_color = section.settings.hotspot_content_bg_color

  assign product_image_ratio = section.settings.product_image_ratio
  assign product_image_ratio_mobile = section.settings.product_image_ratio_mobile
  assign product_image_border_thickness = section.settings.product_image_border_thickness
  assign product_image_border_color = section.settings.product_image_border_color
  assign product_image_radius = section.settings.product_image_radius

  assign product_title_size = section.settings.product_title_size
  assign product_title_size_mobile = section.settings.product_title_size_mobile
  assign product_title_color = section.settings.product_title_color
  assign product_title_custom = section.settings.product_title_custom
  assign product_title_font = section.settings.product_title_font
  assign product_title_height = section.settings.product_title_height

  assign product_desc_size = section.settings.product_desc_size
  assign product_desc_size_mobile = section.settings.product_desc_size_mobile
  assign product_desc_color = section.settings.product_desc_color
  assign product_desc_custom = section.settings.product_desc_custom
  assign product_desc_font = section.settings.product_desc_font
  assign product_desc_height = section.settings.product_desc_height
  assign product_desc_mt = section.settings.product_desc_mt
  assign product_desc_mt_mobile = section.settings.product_desc_mt_mobile

  assign price_size_mobile = section.settings.price_size_mobile
  assign price_size = section.settings.price_size
  assign price_color = section.settings.price_color
  assign price_original_color = section.settings.price_original_color
  assign price_custom = section.settings.price_custom
  assign price_font = section.settings.price_font
  assign price_height = section.settings.price_height
  assign price_compare = section.settings.price_compare
  assign price_mt = section.settings.price_mt
  assign price_mt_mobile = section.settings.price_mt_mobile

  assign loading_attribute = 'eager'
  if lazy
    assign loading_attribute = 'lazy'
  endif
-%}

{%- style -%}  
  {{  product_title_font | font_face: font_display: 'swap' }}
  {{  product_desc_font | font_face: font_display: 'swap' }}
  {{  price_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .hotspot-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
  }

  .hotspot-content-{{ section.id }} {
    grid-area: content;
    display: flex;
    flex-direction: column;
    justify-content: {{ content_vertical_align }};
    align-items: {{ content_horizontal_align_mobile }};
  }

  .hotspot-image-{{ section.id }} {
    grid-area: image;
    position: relative;    
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .hotspot-hotspot-{{ section.id }} {
    position: absolute;
    z-index: 1;
    padding: {{ hotspot_size_mobile |  divided_by: 2 }}px;
    cursor: pointer;
  }

  .hotspot-hotspot-{{ section.id }}.active {
    z-index: 2;
  }

  .hotspot-hotspot-icon-{{ section.id }} {
    padding: {{ hotspot_size_mobile | times: 0.25 }}px;
    border-radius: 50%; 
    position: relative;
    z-index: 2;
    background-color: {{ hotspot_bg_color }} !important;
    width: {{ hotspot_size_mobile }}px !important;
    height: {{ hotspot_size_mobile }}px !important;
    background: transparent;
  }

  .hotspot-image-{{ section.id }} img,
  .hotspot-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .hotspot-image-{{ section.id }} svg {
    background: #AFAFAF;
  }

  .hotspot-hotspot-icon-{{ section.id }} path {
    fill: {{ hotspot_icon_color }};
    transition: all 0.25s ease 0s;
  }

  .hotspot-hotspot-{{ section.id }}.active .hotspot-hotspot-icon-{{ section.id }} path {
    fill: {{ hotspot_icon_active_color }};
    transition: all 0.25s ease 0s;
  }

  .hotspot-hotspot-{{ section.id }}.active .hotspot-hotspot-icon-{{ section.id }} {
    background-color: {{ hotspot_bg_active_color }} !important;
  }

  .hotspot-hotspot-content-{{ section.id }} {
    display: none;
    position: absolute;
    align-items: center;
    gap: 10px;
    left: 50%;
    min-width: 260px;
    transform-origin: left;
    transform: translateX(-50%);
    padding: {{ hotspot_content_padding_vertical_mobile }}px {{ hotspot_content_padding_horizontal_mobile }}px;
    border-radius: {{ hotspot_content_radius }}px;
    border: {{ hotspot_content_border_thickness }}px solid {{ hotspot_content_border_color }};
    background-color: {{ hotspot_content_bg_color }};
    text-decoration: none;
  }

  .hotspot-product-image-{{ section.id }} {
    width: 25%;
    flex-shrink: 0;
    border: {{ product_image_border_thickness }}px solid {{ product_image_border_color }};
    border-radius: {{ product_image_radius }}px;
    overflow: hidden;
  }

  .hotspot-product-image-{{ section.id }} img,
  .hotspot-product-image-{{ section.id }} svg {
    display: block;
    width: 100% !important;
    height: 100% !important;
    object-fit: cover;
  }

  .hotspot-product-image-{{ section.id }} svg path {
    fill: #000 !important;
  }

  .hotspot-product-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .hotspot-product-title-{{ section.id }} {
    margin: 0;    
    display: block;
    text-transform: unset;
    font-size: {{ product_title_size_mobile }}px;
    line-height: {{ product_title_height }}%;
    color: {{ product_title_color }};  
    font-weight: 700;
    text-decoration: none;
  }

  .hotspot-product-description-{{ section.id }} {
    margin-top: {{ product_desc_mt_mobile }}px;
  }

  .hotspot-product-description-{{ section.id }} * {
    margin: 0;    
    display: block;
    text-transform: unset;
    font-size: {{ product_desc_size_mobile }}px;
    line-height: {{ product_desc_height }}%;
    color: {{ product_desc_color }};  
    text-decoration: none;
  }

  .hotspot-product-price-{{ section.id }} {
    margin-top: {{ price_mt_mobile }}px;
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    gap: 5px;
  }

  .hotspot-product-price-{{ section.id }} * {
    margin: 0;
    font-size: {{ price_size_mobile }}px;
    color: {{ price_color }};
    line-height: {{ price_height }}%;
    text-transform: unset;
    font-style: normal;
  }

  .hotspot-compare-price-{{ section.id }} {
    opacity: 0.8;
    color: {{ price_original_color }};
    text-decoration: line-through !important;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .hotspot-body-{{ section.id }} {
      gap: {{ layout_gap }}px;
    }

      .hotspot-hotspot-icon-{{ section.id }} {
      padding: {{ hotspot_size | times: 0.25 }}px;
    }

    .hotspot-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
    }

    .hotspot-hotspot-{{ section.id }} {
      padding: {{ hotspot_size |  divided_by: 2 }}px;
    }

    .hotspot-hotspot-{{ section.id }} .hotspot-hotspot-icon-{{ section.id }} {
      width: {{ hotspot_size }}px !important;
      height: {{ hotspot_size }}px !important;
    }

    .hotspot-hotspot-content-{{ section.id }} {
      padding: {{ hotspot_content_padding_vertical }}px {{ hotspot_content_padding_horizontal }}px;
    }

    .hotspot-product-title-{{ section.id }} {
      font-size: {{ product_title_size }}px;
    }

    .hotspot-product-description-{{ section.id }} {
      margin-top: {{ product_desc_mt }}px;
    }

    .hotspot-product-description-{{ section.id }} * {
      font-size: {{ product_desc_size }}px;
    }

    .hotspot-product-price-{{ section.id }} {
      margin-top: {{ price_mt }}px;
    }

    .hotspot-product-price-{{ section.id }} * {
      font-size: {{ price_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if product_title_custom %}
  <style>
    .hotspot-product-title-{{ section.id }} {
      font-family: {{ product_title_font.family }}, {{ product_title_font.fallback_families }};
      font-weight: {{ product_title_font.weight }};
      font-style: {{ product_title_font.style }};
    }
  </style>
{% endif %}

{% if product_desc_custom %}
  <style>
    .hotspot-product-description-{{ section.id }} * {
      font-family: {{ product_desc_font.family }}, {{ product_desc_font.fallback_families }};
      font-weight: {{ product_desc_font.weight }};
      font-style: {{ product_desc_font.style }};
    }
  </style>
{% endif %}



{% if price_custom %}
  <style>
    .hotspot-product-price-{{ section.id }} {
      font-family: {{ price_font.family }}, {{ price_font.fallback_families }};
      font-weight: {{ price_font.weight }};
      font-style: {{ price_font.style }};
    }
  </style>
{% endif %}


{% if product_image_ratio_mobile == "portrait" %}
  <style>
    .hotspot-product-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif product_image_ratio_mobile == "landscape" %}
  <style>
  .hotspot-product-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif product_image_ratio_mobile == "square" %}
  <style>
    .hotspot-product-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .hotspot-product-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if product_image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .hotspot-product-image-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }

  </style>
{% elsif product_image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .hotspot-product-image-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif product_image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .hotspot-product-image-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .hotspot-product-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .hotspot-image-{{ section.id }} {
     aspect-ratio: 9.6/13; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .hotspot-image-{{ section.id }}{
     aspect-ratio: 12/9; 
    }

  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .hotspot-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }    
  </style>
{% else %}
  <style>
    .hotspot-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .hotspot-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .hotspot-image-{{ section.id }}{
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .hotspot-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    .hotspot-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}



{% if layout_mobile == 'image_content'%}
  <style>
    .hotspot-body-{{ section.id }} {
      grid-template-areas: 
        "image"
        "content";     
    }
  </style>
{% elsif layout_mobile == 'content_image' %}
  <style>
    .hotspot-body-{{ section.id }} {
      grid-template-areas: 
        "content"
        "image";     
    }
  </style>
{% endif %}

{% if layout == 'image_content'%}
  <style>
    @media(min-width: 1024px) {
      .hotspot-body-{{ section.id }} {
        grid-template-areas: "image content";    
        grid-template-columns: {{ image_width }}% auto;
      }
    }    
  </style>
{% elsif layout == 'content_image' %}
  <style>
    @media(min-width: 1024px) {
      .hotspot-body-{{ section.id }} {
        grid-template-areas: "content image";    
        grid-template-columns: auto {{ image_width }}%;   
      }
    }   
  </style>
{% endif %}


{% if hotspot_animate %}
  <style>
    .hotspot-hotspot-{{ section.id }}.active:after {
      display: none;
    }

    .hotspot-hotspot-{{ section.id }}::after {
      content: "";
      display: block;
      position: absolute;
      left: 50%; 
      top: 50%;   
      -webkit-animation: pulseMobile{{ section.id | replace: '-', '' }} 1.25s ease-in-out infinite;
      animation: pulseMobile{{ section.id | replace: '-', '' }} 1.25s ease-in-out infinite;
      border: 1px solid {{ hotspot_bg_color }};
      border-radius: 50%;
      box-sizing: border-box;
      transform: translate3d(-50%, -50%, 0);
      transition: opacity 0.25s linear;
      height: {{ hotspot_size_mobile }}px;
      width: {{ hotspot_size_mobile }}px;
      z-index: -1;
    }

    @keyframes pulseMobile{{ section.id | replace: '-', '' }} {
      0% {
        transform: translate3d(-50%, -50%, 0) scale(1);
        opacity: 1;
      }
      100% {
        transform: translate3d(-50%, -50%, 0) scale(2.5);
        opacity: 0;
      }
    }

    @media(min-width: 1024px) {
      .hotspot-hotspot-{{ section.id }}::after {
        -webkit-animation: pulseDesktop{{ section.id | replace: '-', '' }} 1.25s ease-in-out infinite;
        animation: pulseDesktop{{ section.id | replace: '-', '' }} 1.25s ease-in-out infinite;
        height: {{ hotspot_size }}px;
        width: {{ hotspot_size }}px;
      }

      @keyframes pulseDesktop{{ section.id | replace: '-', '' }} {
        0% {
          transform: translate3d(-50%, -50%, 0) scale(1);
          opacity: 1;
        }
        100% {
          transform: translate3d(-50%, -50%, 0) scale(1.8);
          opacity: 0;
        }
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %}  
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .hero-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_horizontal_align_mobile }};
        }

        .hero-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        .hero-heading-{{ block.id }} * strong {
          text-decoration: underline {{ block.settings.heading_border_color }};
        }

        @media(min-width: 1024px) {

          .hero-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_horizontal_align }};
          }

          .hero-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>

      {% if block.settings.heading_custom %}
        <style>
          .hero-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}

    {% when 'text' %}   
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}

        .hero-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_horizontal_align_mobile }};
          position: relative;
          z-index: 2;
        }

        .hero-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {

          .hero-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{  content_horizontal_align }};
          }

          .hero-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }

        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .hero-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %}  

    {% when 'button' %}  
      <style>
        {{  block.settings.button_font | font_face: font_display: 'swap' }}

        .hero-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;         
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          cursor: pointer;
        }

        .hero-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .hero-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
