<script>
  function initHotspots8() {
    const hotspots = document.querySelectorAll('.hotspot-hotspot-{{ section.id }}');

    if (hotspots) {
      hotspots.forEach(hotspot => {
        hotspot.addEventListener('click', (event) => {
          event.stopPropagation();

          const content = hotspot.querySelector('.hotspot-hotspot-content-{{ section.id }}');

          if (hotspot.classList.contains('active')) {
            if (content) {
              content.style.transition = 'opacity 0.3s ease, scale 0.3s ease';
              content.style.opacity = '0';
              content.style.scale = '0.5'

              setTimeout(() => {
                hotspot.classList.remove('active');
              }, 300);
            } else {
              hotspot.classList.remove('active');
            }
          } else {
            hotspots.forEach(otherHotspot => {
              otherHotspot.classList.remove('active');
              const otherContent = otherHotspot.querySelector('.hotspot-hotspot-content-{{ section.id }}');
              if (otherContent) {
                otherContent.style.opacity = '0';
                content.style.scale = '0.5'
              }
            });

            hotspot.classList.add('active');
            if (content) {
              content.style.transition = 'opacity 0.3s ease,  scale 0.3s ease';
              content.style.opacity = '1';
              content.style.scale = '1'
            }
          }
        });
      });

      document.addEventListener('click', (event) => {
        const clickedInsideHotspot = Array.from(hotspots).some(hotspot => hotspot.contains(event.target));

        if (!clickedInsideHotspot) {
          hotspots.forEach(hotspot => {
            const content = hotspot.querySelector('.hotspot-hotspot-content-{{ section.id }}');
            if (content) {
              content.style.transition = 'opacity 0.25s ease, scale 0.3s ease';
              content.style.opacity = '0';

              setTimeout(() => {
                hotspot.classList.remove('active');
              }, 300);
            } else {
              hotspot.classList.remove('active');
            }
          });
        }
      });
    }
  }

  document.addEventListener('DOMContentLoaded', initHotspots8);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initHotspots8);
    document.addEventListener('shopify:section:load', initHotspots8);
  }
</script>
