{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign content_max_width = section.settings.content_max_width
  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign layout_gap_mobile = section.settings.layout_gap_mobile
  assign layout_gap = section.settings.layout_gap
  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_mt = section.settings.layout_mt
  assign layout_mt_mobile = section.settings.layout_mt_mobile

  assign item_padding_vertical_mobile = section.settings.item_padding_vertical_mobile
  assign item_padding_vertical = section.settings.item_padding_vertical
  assign item_padding_horizontal_mobile = section.settings.item_padding_horizontal_mobile
  assign item_padding_horizontal = section.settings.item_padding_horizontal
  assign item_border_thickness = section.settings.item_border_thickness
  assign item_border_color = section.settings.item_border_color
  assign item_mt = section.settings.item_mt
  assign item_mt_mobile = section.settings.item_mt_mobile
  assign item_bg_color = section.settings.item_bg_color
  assign item_radius = section.settings.item_radius

  assign open_first  = section.settings.open_first

  assign number_size = section.settings.number_size
  assign number_size_mobile = section.settings.number_size_mobile
  assign number_radius = section.settings.number_radius
  assign number_color = section.settings.number_color
  assign number_active_color = section.settings.number_active_color
  assign number_custom = section.settings.number_custom
  assign number_font = section.settings.number_font
  assign number_height = section.settings.number_height
  assign number_bg_color = section.settings.number_bg_color
  assign number_active_bg_color = section.settings.number_active_bg_color

  assign question_size = section.settings.question_size
  assign question_size_mobile = section.settings.question_size_mobile
  assign question_color = section.settings.question_color
  assign question_active_color = section.settings.question_active_color
  assign question_custom = section.settings.question_custom
  assign question_font = section.settings.question_font
  assign question_height = section.settings.question_height
  
  assign answer_size = section.settings.answer_size
  assign answer_size_mobile = section.settings.answer_size_mobile
  assign answer_color = section.settings.answer_color
  assign answer_custom = section.settings.answer_custom
  assign answer_font = section.settings.answer_font
  assign answer_height = section.settings.answer_height
  assign answer_padding_top = section.settings.answer_padding_top

  assign icon_color = section.settings.icon_color
  assign icon_size = section.settings.icon_size
  assign icon_size_mobile = section.settings.icon_size_mobile

  assign media_type = section.settings.media_type
  assign image = section.settings.image
  assign video = section.settings.video
  assign video_url = section.settings.video_url
  assign source = section.settings.source
  assign loop = section.settings.loop
  assign autoplay = section.settings.autoplay
  assign controls = section.settings.controls
  assign media_border_thickness = section.settings.media_border_thickness
  assign media_border_color = section.settings.media_border_color
  assign media_radius = section.settings.media_radius
  assign media_ratio = section.settings.media_ratio
  assign media_ratio_mobile = section.settings.media_ratio_mobile
-%}

{%- style -%}
  {{  question_font | font_face: font_display: 'swap' }}
  {{  number_font | font_face: font_display: 'swap' }}
  {{  answer_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .works-body-{{ section.id }} {
    display: grid;
    grid-template-columns: 1fr;
    gap: {{ layout_gap_mobile }}px;
    margin-top: {{ layout_mt_mobile }}px;
  }

  .works-items-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: 0;
    grid-area: faq;
  }
  
  .works-item-{{ section.id }} {
    padding: {{ item_padding_vertical_mobile }}px {{ item_padding_horizontal_mobile }}px;
    width: 100%;
    position: relative;
    background-color: {{ item_bg_color }};
    border-radius: {{ item_radius }}px;    
    border: {{ item_border_thickness }}px solid {{ item_border_color }};
    transition: all 0.25s ease 0s;
  }

  .works-item-{{ section.id }}:not(:first-child) {
    margin-top: {{ item_mt_mobile }}px;
  }

  .works-toggle-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: space-between;
    cursor: pointer;
    gap: 12px;
  }

  .works-number-{{ section.id }} {
    margin: 0px;
    font-size: {{ number_size_mobile }}px;
    color: {{ number_color }};
    line-height: {{ number_height }}%;
    padding: 6px;
    border-radius: {{ number_radius }}px;
    background-color: {{ number_bg_color }};
    text-transform: unset;
    transition: all 0.25s ease 0s;
  }

  .works-question-wrap-{{ section.id }} {
    display: flex;
    align-items: center;
    gap: 10px;
  }

  .works-toggle-{{ section.id }}.active .works-number-{{ section.id }} {
    color: {{ number_active_color }};
    background-color: {{ number_active_bg_color }};
  }

  .works-question-{{ section.id }} {
    margin: 0px;
    font-size: {{ question_size_mobile }}px;
    color: {{ question_color }};
    line-height: {{ question_height }}%;
    text-transform: unset;
    transition: all 0.25s ease 0s;
    font-weight: 700;
  }

  .works-toggle-{{ section.id }}.active .works-question-{{ section.id }} {
    color: {{ question_active_color }};
    transition: all 0.25s ease 0s;
  }

  .works-icon-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    flex: 0 0 {{ icon_size_mobile }}px;
    height: {{ icon_size_mobile }}px;
    transition: all 0.25s ease 0s;
  }

  .works-icon-{{ section.id }} svg {
    width: 100%;
    height: 100%;
    transition: all 0.25s ease 0s;
  }

  .works-icon-{{ section.id }} svg path {
    stroke: {{ icon_color }};
  }

  .works-icon-{{ section.id }}.active svg {
    transform: rotate(180deg);
    transition: all 0.25s ease 0s;
  }

  .works-answer-{{ section.id }} {
    transition: all 0.2s ease-in-out;
    overflow: hidden;
  }

  .works-answer-{{ section.id }} * {
    margin: 0px;
    font-size: {{ answer_size_mobile }}px;
    color: {{ answer_color }};
    line-height: {{ answer_height }}%;
    text-transform: unset;
  }

  .works-height-cal-{{ section.id }} {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
    position: absolute;
    top: 0;
    left: 0;
    width: calc(100% - {{ item_padding_horizontal_mobile | times: 2 }}px);
    padding-top: {{ answer_padding_top }}px;
  }

  .works-height-cal-{{ section.id }} * {
    margin: 0px;
    font-size: {{ answer_size_mobile }}px;
    color: {{ answer_color }};
    line-height: {{ answer_height }}%;
    text-transform: unset;
  }

  .works-media-{{ section.id }} {
    grid-area: media;
    border: {{ media_border_thickness }}px solid {{ media_border_color }};
    border-radius: {{ media_radius }}px;
    overflow: hidden;
  }
  
  .works-media-{{ section.id }} img,
  .works-media-{{ section.id }} svg,
  .works-media-{{ section.id }} video,
  .works-media-{{ section.id }} iframe {    
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    border: 0;
  }

  .works-media-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .works-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    
    .works-body-{{ section.id }} {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: {{ layout_gap }}px;
      margin-top: {{ layout_mt }}px;
    }

    .works-item-{{ section.id }} {
      padding: {{ item_padding_vertical }}px {{ item_padding_horizontal }}px;
    }

    .works-item-{{ section.id }}:not(:first-child) {
      margin-top: {{ item_mt }}px;
    }
    
    .works-question-{{ section.id }} {
      font-size: {{ question_size }}px;
    }
    
    .works-icon-{{ section.id }} {
      flex: 0 0 {{ icon_size }}px;
      height: {{ icon_size }}px;
    }
    
    .works-answer-{{ section.id }} * {
      font-size: {{ answer_size }}px;
    }

    .works-height-cal-{{ section.id }} {
      width: calc(100% - {{ item_padding_horizontal | times: 2 }}px);
    }

    .works-height-cal-{{ section.id }} * {
      font-size: {{ answer_size }}px;
    }

    .works-number-{{ section.id }} {
      font-size: {{ number_size }}px;      
    }

    .works-content-{{ section.id }} {
      display: flex;
      flex-direction: column;
      align-items: {{ content_horizontal_align }};
    }
  }
  
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: var(--page-width);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if question_custom %}
  <style>
    .works-question-{{ section.id }} {
      font-family: {{ question_font.family }}, {{ question_font.fallback_families }};
      font-weight: {{ question_font.weight }};
      font-style: {{ question_font.style }};
    }
  </style>
{% endif %}

{% if number_custom %}
  <style>
    .works-number-{{ section.id }} {
      font-family: {{ number_font.family }}, {{ number_font.fallback_families }};
      font-weight: {{ number_font.weight }};
      font-style: {{ number_font.style }};
    }
  </style>
{% endif %}

{% if answer_custom %}
  <style>
    .works-answer-{{ section.id }} * {
      font-family: {{ answer_font.family }}, {{ answer_font.fallback_families }};
      font-weight: {{ answer_font.weight }};
      font-style: {{ answer_font.style }};
    }

    .works-height-cal-{{ section.id }} * {
      font-family: {{ answer_font.family }}, {{ answer_font.fallback_families }};
      font-weight: {{ answer_font.weight }};
      font-style: {{ answer_font.style }};
    }
  </style>
{% endif %}


{% if layout_mobile == 'media_faq'%}
  <style>
    .works-body-{{ section.id }} {
      grid-template-areas: 
        "media"
        "faq";     
    }
  </style>
{% elsif layout_mobile == 'faq_media' %}
  <style>
    .works-body-{{ section.id }} {
      grid-template-areas: 
        "faq"
        "media";     
    }
  </style>
{% endif %}

{% if layout == 'media_faq'%}
  <style>
    @media(min-width: 1024px) {
      .works-body-{{ section.id }} {
        grid-template-areas: "media faq";    
      }
    }    
  </style>
{% elsif layout == 'faq_media' %}
  <style>
    @media(min-width: 1024px) {
      .works-body-{{ section.id }} {
        grid-template-areas: "faq media";       
      }
    }   
  </style>
{% endif %}


{% if media_ratio_mobile == "portrait" %}
  <style>
    .works-media-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif media_ratio_mobile == "landscape" %}
  <style>
  .works-media-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif media_ratio_mobile == "square" %}
  <style>
    .works-media-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .works-media-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if media_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .works-media-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }
    
  </style>
{% elsif media_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .works-media-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif media_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .works-media-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .works-media-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
        {% when 'heading' %}
      <style>
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}

        .works-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }
      
        .works-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          word-break: break-word;
          font-weight: 700;
        }

        @media(min-width: 1024px) {
          
          .works-heading-{{ block.id }} {
            max-width: {{ content_max_width }}%;
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_align }};
          }
        
          .works-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }        
      </style>
        
      {% if block.settings.heading_custom %}
        <style>
          .works-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}
    {% when 'tag' %}
      <style>
        {{  block.settings.tag_font | font_face: font_display: 'swap' }}
        
        .works-tag-{{ block.id }} {
          margin: 0;
          margin-top: {{ block.settings.tag_mt_mobile }}px;
          text-align: center;         
          font-size: {{ block.settings.tag_size_mobile }}px;
          color: {{ block.settings.tag_color }};
          line-height: {{ block.settings.tag_height }}%;
          {% if block.settings.tag_background %}
            border: {{ block.settings.tag_border_thickness }}px solid {{ block.settings.tag_border_color }};
            border-radius: {{ block.settings.tag_radius }}px;
            background-color: {{ block.settings.tag_bg_color }};
            padding: {{ block.settings.tag_padding_vertical_mobile }}px {{ block.settings.tag_padding_horizontal_mobile }}px;
          {% endif %}
          text-transform: unset;
          position: relative;
          z-index: 2;
          font-weight: 700;
        }

        @media(min-width: 1024px) {          
          .works-tag-{{ block.id }} {            
            margin-top: {{ block.settings.tag_mt }}px;
            font-size: {{ block.settings.tag_size }}px;
            {% if block.settings.tag_background %}
              padding: {{ block.settings.tag_padding_vertical }}px {{ block.settings.tag_padding_horizontal }}px;
            {% endif %}
          }
          
        }
      </style>

      {% if block.settings.tag_custom != blank %}
        <style>
          .works-tag-{{ block.id }} {
            font-family: {{ block.settings.tag_font.family }}, {{ block.settings.tag_font.fallback_families }};
            font-weight: {{ block.settings.tag_font.weight }};
            font-style: {{ block.settings.tag_font.style }};
          }
        </style>          
      {% endif %}  

    {% when 'text' %}
      <style>
        {{  block.settings.text_font | font_face: font_display: 'swap' }}
        
        .works-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
          position: relative;
          z-index: 2;
          max-width: {{ content_max_width }}%;
        }
      
        .works-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
        }

        @media(min-width: 1024px) {
          
          .works-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{  content_align }};
          }
      
          .works-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }
          
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          .works-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %} 

    {% when 'button' %}
      <style>
        {{  block.settings.button_font | font_face: font_display: 'swap' }}

        .works-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          width: 100%;
          margin: 0;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
        }

        .works-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .works-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .works-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }

        .works-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .works-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        @media (min-width: 1024px) {
          .works-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>

      {% if block.settings.button_custom %}
        <style>
          .works-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .works-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
          }
      
          .works-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>          
          .works-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{  block.settings.button_border_color }};
          }
      
          .works-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{ block.settings.button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
