<script>
  function initFaq14() {
    let lazyLoadInstance = new LazyLoad();
    window.addEventListener(
      "LazyLoad::Initialized",
      function (event) {
        window.lazyLoadInstance = event.detail.instance;
      },
      false
    );

    const answerPaddingTop = {{ answer_padding_top }};
    const answerPaddingBottom = 0;

    const buttons = document.querySelectorAll('.works-toggle-{{ section.id }}');
    const answers = document.querySelectorAll('.works-answer-{{ section.id }}');
    const answersHeightCalElements = document.querySelectorAll('.works-height-cal-{{ section.id }}');
    const faqIcons = document.querySelectorAll('.works-icon-{{ section.id }}');

    let activeIndex = -1;

    function openAnswer(index) {
      const height = answersHeightCalElements[index].offsetHeight;

      answers[index].style.display = 'block';
      setTimeout(() => {
        answers[index].style.paddingTop = `${answerPaddingTop}px`;
        answers[index].style.paddingBottom = `${answerPaddingBottom}px`;
        answers[index].style.height = `${height}px`;
      });

      faqIcons[index].classList.add('active');
      buttons[index].classList.add('active');
    }

    function closeAnswer(index) {
      answers[index].style.paddingTop = 0;
      answers[index].style.paddingBottom = 0;
      answers[index].style.height = 0;

      setTimeout(() => {
        answers[index].style.display = 'none';
      }, 300);

      faqIcons[index].classList.remove('active');
      buttons[index].classList.remove('active');
    }

    function toggleAnswer(index) {
      if (answers[index].style.display === 'none') {
        openAnswer(index);
        activeIndex = index;
      } else {
        closeAnswer(index);
        activeIndex = -1;
      }
    }

    function closeAllAnswersBut(index) {
      answers.forEach((answer, i) => {
        if (i !== index) {
          closeAnswer(i);
        }
      });
    }

    buttons.forEach((button, index) => {
      button.addEventListener('click', () => {
        toggleAnswer(index);
        closeAllAnswersBut(index);
      });
    });

    let resizeTimer;
    window.addEventListener('resize', () => {
      clearTimeout(resizeTimer);
      resizeTimer = setTimeout(() => {
        if (activeIndex !== -1) {
          const height = answersHeightCalElements[activeIndex].offsetHeight;
          answers[activeIndex].style.height = `${height}px`;
        }
      }, 250);
    });

    {% if open_first %}
      openAnswer(0);
      activeIndex = 0;
    {% endif %}
  }

  document.addEventListener('DOMContentLoaded', initFaq14);

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initFaq14);
    document.addEventListener('shopify:section:load', initFaq14);
  }
</script>
