{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign body_gap_mobile = section.settings.body_gap_mobile
  assign body_gap = section.settings.body_gap

  assign image = section.settings.image
  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_position_mobile = section.settings.image_position_mobile
  assign image_position = section.settings.image_position

  assign content_horizontal_align_mobile = section.settings.content_horizontal_align_mobile
  assign content_horizontal_align = section.settings.content_horizontal_align
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_border_thickness = section.settings.content_border_thickness
  assign content_border_color = section.settings.content_border_color
  assign content_radius = section.settings.content_radius
  assign content_bg_color = section.settings.content_bg_color

  assign text_align = ""
  if content_horizontal_align == "start"
    assign text_align = "left"
  elsif content_horizontal_align == "center"
    assign text_align = "center"
  else
    assign text_align = "right"
  endif

  assign text_align_mobile = ""
  if content_horizontal_align_mobile == "start"
    assign text_align_mobile = "left"
  elsif content_horizontal_align_mobile == "center"
    assign text_align_mobile = "center"
  else
    assign text_align_mobile = "right"
  endif

  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height

  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_mt = section.settings.text_mt

  assign toggle_text_size = section.settings.toggle_text_size
  assign toggle_text_size_mobile = section.settings.toggle_text_size_mobile
  assign toggle_text_color = section.settings.toggle_text_color
  assign toggle_text_custom = section.settings.toggle_text_custom
  assign toggle_text_font = section.settings.toggle_text_font
  assign toggle_text_height = section.settings.toggle_text_height
  assign toggle_text_padding_vertical = section.settings.toggle_text_padding_vertical
  assign toggle_text_padding_vertical_mobile = section.settings.toggle_text_padding_vertical_mobile
  assign toggle_text_border_thickness = section.settings.toggle_text_border_thickness
  assign toggle_text_border_color = section.settings.toggle_text_border_color

-%}

{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  toggle_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .image-text-body-{{ section.id }} {
    display: grid;
    gap: {{ body_gap_mobile }}px;
  }

  .image-text-image-{{ section.id }} {
    border-radius: {{ image_radius }}px;
    outline: {{ image_border_thickness }}px solid {{ image_border_color }};
    overflow: hidden;
    position: relative;
  }

  .image-text-image-{{ section.id }} img,
  .image-text-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    box-shadow: none;
    border: 0px;
  }

  .image-text-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .image-text-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 30px;
    align-items: {{ content_horizontal_align_mobile }};
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
    border-radius: {{ content_radius }}px;
    border: {{ content_border_thickness }}px solid {{ content_border_color }};
    background-color: {{ content_bg_color }};
    overflow: hidden;
  }

  .image-text-item-{{ section.id }} {
    display: none;
  }

  .image-text-item-{{ section.id }}.active {
    display: block;
  }

  .image-text-heading-{{ section.id }} {
    width: 100%;
    text-align: {{ text_align_mobile }};
  }

  .image-text-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .image-text-text-{{ section.id }} {
    width: 100%;
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ text_align_mobile }};
  }

  .image-text-text-{{ section.id }} * {
    margin: 0;
    font-size: {{ text_size_mobile }}px;
    line-height: {{ text_height }}%;
    color: {{ text_color }};
    text-transform: unset;
    word-break: break-word;
  }

  .image-text-toggles-{{ section.id }} {
    width: 100%;
  }

  .image-text-toggle-{{ section.id }} {
    width: 100%;
    text-align: {{ text_align_mobile }};
    padding: {{ toggle_text_padding_vertical_mobile }}px 0px;
    border-top: {{ toggle_text_border_thickness }}px solid {{ toggle_text_border_color }};
  }

  .image-text-toggle-{{ section.id }} * {
    margin: 0;
    font-size: {{ toggle_text_size_mobile }}px;
    line-height: {{ toggle_text_height }}%;
    color: {{ toggle_text_color }};
    text-transform: unset;
    word-break: break-word;
    opacity: 0.75;
    transition: all 0.25s ease 0s;
    font-weight: 700;
  }

  .image-text-toggle-{{ section.id }}.active * {
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  .image-text-toggle-{{ section.id }}:hover * {
    opacity: 1;
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .image-text-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ body_gap }}px;
    }

    .image-text-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .image-text-heading-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .image-text-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .image-text-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
    }

    .image-text-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .image-text-toggle-{{ section.id }} {
      text-align: {{ text_align }};
      padding: {{ toggle_text_padding_vertical }}px 0px;
    }

    .image-text-toggle-{{ section.id }} * {
      font-size: {{ toggle_text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .image-text-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .image-text-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if toggle_text_custom %}
  <style>
    .image-text-toggle-{{ section.id }} * {
      font-family: {{ toggle_text_font.family }}, {{ toggle_text_font.fallback_families }};
      font-weight: {{ toggle_text_font.weight }};
      font-style: {{ toggle_text_font.style }};
    }
  </style>
{% endif %}

{% if image_position_mobile == "top" %}
  <style>
    .image-text-image-{{ section.id }} {
      order: 1;
    }

    .image-text-content-{{ section.id }} {
      order: 2;
    }
  </style>
{% else %}
  <style>
    .image-text-image-{{ section.id }} {
      order: 2;
    }

    .image-text-content-{{ section.id }} {
      order: 1;
    }
  </style>
{% endif %}

{% if image_position == "right" %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }} {
        order: 2;
      }

      .image-text-content-{{ section.id }} {
        order: 1;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }} {
        order: 1;
      }

      .image-text-content-{{ section.id }} {
        order: 2;
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .image-text-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .image-text-image-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .image-text-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else %}
  <style>
    .image-text-image-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .image-text-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }}{
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}


<div class="section-{{ section.id }} image-text-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="image-text-body-{{ section.id }}">
        <div class="image-text-content-{{ section.id }}">
          <div class="image-text-items-{{ section.id }}">
            {% for block in section.blocks %}
              <div class="image-text-item-{{ section.id }} {% if forloop.first %}active{% endif %}" data-id="{{ block.id }}">
                {% if block.settings.heading != blank %}
                  <div class="image-text-heading-{{ section.id }}">
                    {{ block.settings.heading }}
                  </div>
                {% endif %}

                {% if block.settings.text != blank %}
                  <div class="image-text-text-{{ section.id }}">
                    {{ block.settings.text }}
                  </div>
                {% endif %}
              </div>
            {% endfor %}
          </div>
          <div class="image-text-toggles-{{ section.id }}">
            {% for block in section.blocks %}
              <div class="image-text-toggle-{{ section.id }} {% if forloop.first %}active{% endif %}" id="{{ block.id }}">
                <p class="image-text-toggle-text-{{ section.id }}">{{ block.settings.toggle_text }}</p>
              </div>
            {% endfor %}
          </div>
        </div>
        <div class="image-text-image-{{ section.id }}">
          {% if image != blank %}
            <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
          {% else %}
            {{ 'image' | placeholder_svg_tag }}
          {% endif %}
        </div>
      </div>
    </div>
</div>

<script>
  function initImageWithText15() {

    let toggles = document.querySelectorAll('.image-text-toggle-{{ section.id }}');
    let items = document.querySelectorAll('.image-text-item-{{ section.id }}');
    if (toggles) {
      toggles.forEach((el) => {
        function activateElement() {
          toggles.forEach(element => element.classList.remove('active'));
          el.classList.add('active');

          if (items) {
            items.forEach(element => {
              element.classList.remove('active');
              let dataId = element.getAttribute('data-id');
              if (el.id == dataId) {
                element.classList.add('active');
              }
            });
          }
        }

        if (window.innerWidth < 1024) {
          el.addEventListener('click', activateElement);
        } else {
          el.addEventListener('mouseover', activateElement);
        }
      });
    }

  }

  document.addEventListener('DOMContentLoaded', initImageWithText15);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initImageWithText15);
     document.addEventListener('shopify:section:load', initImageWithText15);
  }
</script>
