{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  sub_heading_font | font_face: font_display: 'swap' }}
  {{  text_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .image-text-body-{{ section.id }} {
    display: grid;
    gap: {{ body_gap_mobile }}px;
  }

  .image-text-image-{{ section.id }} {
    border-radius: {{ image_radius }}px;
    outline: {{ image_border_thickness }}px solid {{ image_border_color }};
    overflow: hidden;
    position: relative;
  }

  .image-text-image-{{ section.id }} img,
  .image-text-image-{{ section.id }} svg,
  .image-text-image-{{ section.id }} video,
  .image-text-image-{{ section.id }} iframe {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    box-shadow: none;
    border: 0px;
  }

  .image-text-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .image-text-image-content-{{ section.id }} {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: center;
    z-index: 2;
    padding: 20px;
  }

  .image-text-heading-{{ section.id }} {
    width: 100%;
    text-align: {{ heading_align_mobile }};
  }

  .image-text-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .image-text-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: {{ content_horizontal_align_mobile }};
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
  }

  .image-text-subheading-{{ section.id }} {
    width: 100%;
    text-align: {{ text_align_mobile }};
  }

  .image-text-subheading-{{ section.id }} * {
    margin: 0;
    font-size: {{ sub_heading_size_mobile }}px;
    color: {{ sub_heading_color }};
    line-height: {{ sub_heading_height }}%;
    text-transform: unset;
    word-break: break-word;
  }

  .image-text-scrolling-wrapper-{{ section.id }} {
    height: 300px;
    max-width: fit-content;
    margin-top: {{ scrolling_mt_mobile }}px;
    overflow: hidden;
    -webkit-mask-image: linear-gradient(0deg, transparent 0, #000 20%, #000 70%, transparent);
    mask-image: linear-gradient(180deg, transparent 0, #000 20%, #000 70%, transparent);
    position: relative;
  }

  .image-text-scrolling-{{ section.id }}  {
    display: flex;
    align-items: flex-start;
    justify-content: center;
    flex-flow: column nowrap;
    flex-direction: column;
    -webkit-animation: animation{{ section.id }} {{ scrolling_velocity_mobile }}s infinite linear;
    animation: animation{{ section.id }} {{ scrolling_velocity_mobile }}s infinite linear;
  }

  .image-text-scrolling-inner-{{ section.id }} {
    display: flex;
    flex-direction: column;
    flex-flow: column nowrap;
    width: 100%;
  }

  .image-text-scrolling-text-{{ section.id }} {
    margin: 0;
    text-align: {{ text_align_mobile }};
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .image-text-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: fit-content;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
    box-sizing: border-box;
  }

  .image-text-button-{{ section.id }} svg {
    background: transparent;
    width: 14px;
    height: 14px;
  }

  .image-text-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .image-text-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .image-text-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .image-text-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;
      gap: {{ body_gap }}px;
      align-items: center;
    }

    .image-text-heading-{{ section.id }} {
      text-align: {{ heading_align }};
      max-width: {{ heading_width }}%;
    }

    .image-text-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .image-text-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .image-text-subheading-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .image-text-subheading-{{ section.id }} * {
      font-size: {{ sub_heading_size }}px;
    }

    .image-text-scrolling-wrapper-{{ section.id }} {
      height: 500px;
      margin-top: {{ scrolling_mt }}px;
    }

    .image-text-scrolling-{{ section.id }}  {
      animation-duration: {{ scrolling_velocity }}s;
    }

    .image-text-scrolling-text-{{ section.id }} {
      text-align: {{ text_align }};
      font-size: {{ text_size }}px;
    }

    .image-text-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

  }
  @media(min-width:900px){
    .image-text-scrolling-{{ section.id }}:hover {
  -webkit-animation-play-state: paused; /* Pause animation on hover */
  animation-play-state: paused; /* Pause animation on hover */
}
  }
{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .image-text-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_custom %}
  <style>
    .image-text-subheading-{{ section.id }} * {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .image-text-scrolling-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .image-text-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if image_position_mobile == "top" %}
  <style>
    .image-text-image-{{ section.id }} {
      order: 1;
    }

    .image-text-content-{{ section.id }} {
      order: 2;
    }
  </style>
{% else %}
  <style>
    .image-text-image-{{ section.id }} {
      order: 2;
    }

    .image-text-content-{{ section.id }} {
      order: 1;
    }
  </style>
{% endif %}

{% if image_position == "right" %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }} {
        order: 2;
      }

      .image-text-content-{{ section.id }} {
        order: 1;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }} {
        order: 1;
      }

      .image-text-content-{{ section.id }} {
        order: 2;
      }
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .image-text-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .image-text-image-{{ section.id }}{
     aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .image-text-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else %}
  <style>
    .image-text-image-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .image-text-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      } 
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }}{
       aspect-ratio: 12/9; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .image-text-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

{% if reverse_direction %}
  <style>
    @keyframes animation{{ section.id }} {
      0% {
        transform: translateY(-50%);
      }
      100% {
        transform: translateY(0%);
      }
    }
  </style>
{% else %}
  <style>
    @keyframes animation{{ section.id }} {
      0% {
        transform: translateY(0%);
      }
      100% {
        transform: translateY(-50%);
      }
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .image-text-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .image-text-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .image-text-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .image-text-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% elsif button_style == "link" %}
  <style>
    .image-text-button-{{ section.id }} {
      padding: 0px !important;
      max-width: fit-content !important;
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} image-text-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="image-text-body-{{ section.id }}">
        <div class="image-text-content-{{ section.id }}">
          {% if sub_heading != blank %}
            <div class="image-text-subheading-{{ section.id }}">
              {{ sub_heading }}
            </div>
          {% endif %}

          <div class="image-text-scrolling-wrapper-{{ section.id }}">
            <div class="image-text-scrolling-{{ section.id }}">
              <div class="image-text-scrolling-inner-{{ section.id }}">
                {% for i in (1..5) %}
                  {% for block in section.blocks %}
                    <p class="image-text-scrolling-text-{{ section.id }}">{{ block.settings.text }}</p>
                  {% endfor %}
                {% endfor %}
              </div>
              <div class="image-text-scrolling-inner-{{ section.id }}">
                {% for i in (1..5) %}
                  {% for block in section.blocks %}
                    <p class="image-text-scrolling-text-{{ section.id }}">{{ block.settings.text }}</p>
                  {% endfor %}
                {% endfor %}
              </div>
            </div>
          </div>
        </div>
        <div class="image-text-image-{{ section.id }}">
          {% if media_type == "image" %}
            {% if image != blank %}
              <img src="{{ image | image_url }}" alt="{{ image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          {% else %}
            {% assign video_id = video.id | default: video_url.id %}
            {% assign loop = '&loop=1&playlist=' | append: video_id %}  
            {%- if video == null and video_url != null -%}
              {%- if video_url.type == 'youtube' -%}
                <iframe
                  data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1{{ loop }}"
                  allow="autoplay; encrypted-media"
                  allowfullscreen
                  class="lazy"
                ></iframe>
              {%- else -%}
                <iframe
                  data-src="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&autoplay=1{{ loop }}"
                  allow="autoplay; encrypted-media"
                  allowfullscreen
                  class="lazy"
                ></iframe>
              {% endif %}

            {% else %}

              <video data-src="{{ video.sources[1].url }}" muted autoplay playsinline preload="none" loop class="lazy"></video>

            {% endif %}
          {% endif %}

          <div class="image-text-image-content-{{ section.id }}">
            {% if heading != blank %}
              <div class="image-text-heading-{{ section.id }}">
                {{ heading }}
              </div>
            {% endif %}

            {% if button != blank %}
              <a href="{{ button_url }}" class="image-text-button-{{ section.id }}">
                <span>{{ button }}</span>
                {% if button_style == "non_outline_arrow" or button_style == "outline_arrow" %}
