{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign layout = section.settings.layout
  assign layout_mobile = section.settings.layout_mobile
  assign layout_height = section.settings.layout_height
  assign layout_fixed_height = section.settings.layout_fixed_height

  assign image = section.settings.image
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_radius = section.settings.image_radius

  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_gap = section.settings.content_gap
  assign content_gap_mobile = section.settings.content_gap_mobile
  assign content_vertical_align = section.settings.content_vertical_align
  assign all_accordion = section.settings.all_accordion

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_height = section.settings.title_height

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_height = section.settings.text_height
  assign text_mt = section.settings.text_mt
  assign text_mt_mobile = section.settings.text_mt_mobile
  assign text_padding_top = section.settings.text_padding_top

  assign scrolling_velocity = section.settings.scrolling_velocity
  assign scrolling_velocity_mobile = section.settings.scrolling_velocity_mobile
  assign scrolling_gap = section.settings.scrolling_gap
  assign scrolling_gap_mobile = section.settings.scrolling_gap_mobile
  assign reverse_direction = section.settings.reverse_direction

  assign scrolling_text = section.settings.scrolling_text
  assign scrolling_text_custom = section.settings.scrolling_text_custom
  assign scrolling_text_font = section.settings.scrolling_text_font
  assign scrolling_text_size = section.settings.scrolling_text_size
  assign scrolling_text_size_mobile = section.settings.scrolling_text_size_mobile
  assign scrolling_text_color = section.settings.scrolling_text_color
  assign scrolling_text_height = section.settings.scrolling_text_height
  assign scrolling_bg_color = section.settings.scrolling_bg_color

  assign lazy_load_setting = 'lazy'
  unless lazy
    assign lazy_load_setting = 'eiger'
  endunless
-%}

{%- style -%}
  {{ title_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .image-with-text-body-{{ section.id }} {
    display: grid;
    position: relative;
    grid-template-columns: 1fr;    
  }

  .image-with-text-image-{{ section.id }} {
    grid-area: image;
    position: relative;
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .image-with-text-image-{{ section.id }} img,
  .image-with-text-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .image-with-text-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .image-with-text-content-{{ section.id }} {
    grid-area: content;
    display: flex;
    flex-direction: column;
    width: 100%;
    gap: {{ content_gap_mobile }}px;
    align-items: {{ content_horizontal_align_mobile }};
    justify-content: {{ content_vertical_align }};
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
  }

  .image-with-text-title-{{ section.id }} {
    margin: 0px;
    text-align: {{ content_align_mobile }};
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
    transform: skew(0deg);
    transition: all 0.25s ease 0s;
  }

  .image-with-text-text-{{ section.id }} {
    transition: all 0.2s ease-in-out;
    overflow: hidden;  
    max-height: fit-content;
    text-align: {{ content_align_mobile }};
  }

  .image-with-text-all-text-{{ section.id }} {
     margin-top: {{ text_mt_mobile }}px;
  }

  .image-with-text-text-{{ section.id }} * {
    margin: 0px;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .image-with-text-height-cal-{{ section.id }} {
    visibility: hidden;
    opacity: 0;
    pointer-events: none;
    position: absolute;
    max-height: fit-content;
    top: 0;
    left: 0;
    width: calc(50% - {{ item_padding_horizontal_mobile | times: 2 }}px);
    padding-top: {{ text_padding_top }}px;
    text-align: {{ content_align_mobile }};
  }

  .image-with-text-height-cal-{{ section.id }} * {
    margin: 0px;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .image-with-text-body-{{ section.id }} {
      grid-template-columns: 1fr 1fr;    
    }

    .image-with-text-content-{{ section.id }} {
      gap: {{ content_gap }}px;
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
      align-items: {{ content_horizontal_align }};
    }

    .image-with-text-title-{{ section.id }} {
      font-size: {{ title_size }}px;
      text-align: {{ content_align }};
    }

    .image-with-text-text-{{ section.id }} {
      text-align: {{ content_align }};
    }

     .image-with-text-all-text-{{ section.id }} {
       margin-top: {{ text_mt }}px;
    }

    .image-with-text-text-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .image-with-text-height-cal-{{ section.id }} {
      width: calc(50% - {{ item_padding_horizontal | times: 2 }}px);
    }

    .image-with-text-height-cal-{{ section.id }} * {
      font-size: {{ text_size }}px;
    }

    .image-with-text-image-{{ section.id }} {
      aspect-ratio: auto !important;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .image-with-text-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .image-with-text-text-{{ section.id }} * {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}


{% if all_accordion == false %}
  <style>
    .image-with-text-title-{{ section.id }} {
      cursor: pointer;
    }

    .image-with-text-toggle-{{ section.id }}.active .image-with-text-title-{{ section.id }} {
      transform: skew(-15deg);
      transition: all 0.25s ease 0s;
    }


    .image-with-text-title-{{ section.id }}:hover {
      transform: skew(-15deg);
      transition: all 0.25s ease 0s;
    }
  </style>
{% endif %}


{% if image_ratio_mobile == "portrait" %}
  <style>
    .image-with-text-image-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
  .image-with-text-image-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .image-with-text-image-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .image-with-text-image-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}


{% if layout_height == "full_screen" %}
  <style>

    @media(min-width: 1024px) {      
      .section-{{ section.id }}-settings {
        height: 100%;
      }

      .image-with-text-{{ section.id }} {
        height: 95vh;
      }

      .image-with-text-body-{{ section.id }} {
        height: 95vh;
      }
    }
  </style>
{% elsif layout_height == 'fixed_height' %}  
  <style>      
    @media(min-width: 1024px) {
      .section-{{ section.id }}-settings {
        height: 100%;
      }

      .image-with-text-{{ section.id }} {
        height: {{ layout_fixed_height }}px;
      }

     .image-with-text-body-{{ section.id }} {
        height: {{ layout_fixed_height }}px;
     }
    }
  </style>
{% endif %}

{% if layout_mobile == "image_content" %}
  <style>
    .image-with-text-body-{{ section.id }} {
      grid-template-columns: 1fr;
      grid-template-areas: 
        "image" 
        "content"; 
    }
  </style>
{% else %} 
  {% if layout_mobile == "content_image" %}
    <style>
      .image-with-text-body-{{ section.id }} {
        grid-template-columns: 1fr;
        grid-template-areas: 
          "content" 
          "image";    
      }
    </style>
  {% endif %}  
{% endif %}

{% if layout == "image_content" %}
  <style>
    @media (min-width: 1024px) {
      .image-with-text-body-{{ section.id }} {
        grid-template-columns: 1fr 1fr;
        grid-template-areas: "image content";
      }
    }
  </style>
{% else %}  
  {% if layout == "content_image" %}
    <style>
      @media (min-width: 1024px) {
        .image-with-text-body-{{ section.id }} {
          grid-template-columns: 1fr 1fr;
          grid-template-areas: "content image";
        }
      }
    </style>
  {% endif %}  
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'button' %}
      <style>
        {{  block.settings.button_font | font_face: font_display: 'swap' }}

        .image-with-text-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
        }

        .image-with-text-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .image-with-text-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .image-with-text-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }

        .image-with-text-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .image-with-text-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        @media (min-width: 1024px) {
          .image-with-text-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>

      {% if block.settings.button_custom %}
        <style>
          .image-with-text-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .image-with-text-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
          }

          .image-with-text-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>          
          .image-with-text-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{  block.settings.button_border_color }};
          }

          .image-with-text-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{ block.settings. button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .image-with-text-button-{{ block.id }} {
            padding: 0px !important;
            -webkit-box-shadow: none !important;
            -moz-box-shadow: none !important;
            box-shadow: none !important;
          }
        </style>
      {% endif %}  

    {% when 'scrolling_text' %}
      <style>
        {{ block.settings.scrolling_text_font | font_face: font_display: 'swap' }}

        .image-with-text-wrap-{{ block.id }} {                
          position: absolute;
          max-height: fit-content;      
          -ms-flex-wrap: nowrap;
          flex-wrap: nowrap;
          z-index: 0;        
          width: 200vw;   
          background-attachment: scroll !important;        
          padding: {{ block.settings.scrolling_padding_horizontal_mobile }}px;
          background-color: {{ block.settings.scrolling_bg_color }};
        }

        .image-with-text-wrap-mobile-{{ block.id }} {
          display: -webkit-box;
          display: -ms-flexbox;
          display: flex;
          transform-origin: left;
          left: {{ block.settings.scrolling_position_mobile }}%;
          transform: rotate(90deg) translateX(-97.5%);      
        }

        .image-with-text-wrap-desktop-{{ block.id }} {
          display: none;
          transform-origin: left;
          left: 50%;
          transform: rotate(90deg) translate(-50%, -50%);      
        }

        .image-with-text-list-{{ block.id }} {
          display: -webkit-box;
          display: -ms-flexbox;
          display: flex;
          white-space: nowrap;
          background-attachment: scroll !important;
          -webkit-animation: ticker{{ block.id | replace: '-', '' }} {{ block.settings.scrolling_velocity_mobile }}s infinite linear;
          animation: ticker{{ block.id | replace: '-', '' }} {{ block.settings.scrolling_velocity_mobile }}s infinite linear;
          flex-shrink: 0;
        }

        .image-with-text-{{ block.id }} {
          margin: 0;    
          font-weight: 700;
          font-size: {{ block.settings.scrolling_text_size_mobile }}px;
          color: {{ block.settings.scrolling_text_color }};
          line-height: {{ block.settings.scrolling_text_height }}%;
          margin-right: {{ block.settings.scrolling_gap_mobile }}px;
          text-transform: unset;
          word-wrap: break-word;
          background-attachment: scroll !important;
          position: relative;
          flex-shrink: 0;
        }  

        @media (min-width: 1024px) {        
          .image-with-text-wrap-mobile-{{ block.id }} {         
            display: none;
          }

          .image-with-text-wrap-desktop-{{ block.id }} {
            display: -webkit-box;
            display: -ms-flexbox;
            display: flex;
            padding: {{ block.settings.scrolling_padding_horizontal }}px;
          }

          .image-with-text-list-{{ block.id }} {
            -webkit-animation: ticker{{ block.id | replace: '-', '' }} {{ block.settings.scrolling_velocity }}s infinite linear;
            animation: ticker{{ block.id | replace: '-', '' }} {{ block.settings.scrolling_velocity }}s infinite linear;        
          }

          .image-with-text-{{ block.id }} {
            font-size: {{ block.settings.scrolling_text_size }}px;
            margin-right: {{ block.settings.scrolling_gap }}px;
          }  
        }
      </style>

      {% if block.settings.scrolling_text_custom %}
        <style>
          .image-with-text-{{ block.id }} {
            font-family: {{ block.settings.scrolling_text_font.family }}, {{ block.settings.scrolling_text_font.fallback_families }};
            font-weight: {{ block.settings.scrolling_text_font.weight }};
            font-style: {{ block.settings.scrolling_text_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.reverse_direction %}
        <style>          
          @keyframes ticker{{ block.id | replace: '-', '' }} {
            0% {
              transform: translateX(-100%);
            }
            100% {
              transform: translateX(0%);
            }
          }
        </style>
      {% else %}
        <style>      
          @keyframes ticker{{ block.id | replace: '-', '' }} {
            0% {
              transform: translateX(0%);
            }
            100% {
              transform: translateX(-100%);
            }
          }
        </style>
      {% endif %}

      {% if block.settings.pause_hover %}
        <style>
          .image-with-text-wrap-{{ block.id }}:hover .image-with-text-list-{{ block.id }} {
            animation-play-state: paused;
          }
        </style>
      {% endif %}
  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} image-with-text-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="image-with-text-body-{{ section.id }}">
        <div class="image-with-text-image-{{ section.id }}">
          {% if image != blank %}
            {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
              {{ image | image_url: width: 1500 | image_tag:
                loading: lazy_load_setting,
                width: image.width,
                height: image.height,
                sizes: sizes,
                widths: '165, 360, 535, 750, 1070, 1500'
              }}
          {% else %}
            {{ 'image' | placeholder_svg_tag }}
          {% endif %}   
          {% for block in section.blocks %}               
            {% if block.type == 'scrolling_text' %}             
              {% if block.settings.scrolling_text != blank %}            
                <div class="image-with-text-wrap-{{ block.id }} image-with-text-wrap-mobile-{{ block.id }}">                 
                  <div class="image-with-text-list-{{ block.id }}">
                    {% for item in (1..10) %}
                      <p class="image-with-text-{{ block.id }}">{{ block.settings.scrolling_text }}</p>
                    {% endfor %}
                  </div>
                  <div class="image-with-text-list-{{ block.id }}">
                    {% for item in (1..10) %}
                      <p class="image-with-text-{{ block.id }}">{{ block.settings.scrolling_text }}</p>
                    {% endfor %}
                  </div>         
                </div>                  
              {% endif %}            
            {% endif %}        
          {% endfor %}
        </div>
        <div class="image-with-text-content-{{ section.id }}">
          {% for block in section.blocks %}
            {% case block.type %}
              {% when 'item' %}
                {% if all_accordion %}
                  <div class="image-with-text-item-{{ section.id }}">       
                    <p class="image-with-text-title-{{ section.id }}">{{ block.settings.title }}</p>                   
                    <div class="image-with-text-text-{{ section.id }} image-with-text-all-text-{{ section.id }}">{{ block.settings.text }}</div>                   
                  </div> 
                {% else %}
                  <div class="image-with-text-item-{{ section.id }}">        
                    <div class="image-with-text-toggle-{{ section.id }}">
                      <p class="image-with-text-title-{{ section.id }}">{{ block.settings.title }}</p>                   
                    </div> 
