{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign width = section.settings.width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius
  assign background_style = section.settings.background_style

  assign layout = section.settings.layout
  assign layout_gap = section.settings.layout_gap

  assign content_max_width = section.settings.content_max_width
  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile
  assign content_vertical_align = section.settings.content_vertical_align

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign items_mt = section.settings.items_mt
  assign items_mt_mobile = section.settings.items_mt_mobile
  assign items_gap = section.settings.items_gap
  assign items_gap_mobile = section.settings.items_gap_mobile

  assign image_item_width = section.settings.image_item_width
  assign image_item_radius = section.settings.image_item_radius
  assign image_item_border_thickness = section.settings.image_item_border_thickness
  assign image_item_border_color = section.settings.image_item_border_color
  assign image_item_ratio_mobile = section.settings.image_item_ratio_mobile
  assign image_item_ratio = section.settings.image_item_ratio
  assign image_item_mt = section.settings.image_item_mt
  assign image_item_mt_mobile = section.settings.image_item_mt_mobile

  assign item_padding_horizontal = section.settings.item_padding_horizontal
  assign item_padding_horizontal_mobile = section.settings.item_padding_horizontal_mobile

  assign progress_bar_width = section.settings.progress_bar_width
  assign progress_bar_bg = section.settings.progress_bar_bg

  assign item_heading_custom = section.settings.item_heading_custom
  assign item_heading_font = section.settings.item_heading_font
  assign item_heading_size = section.settings.item_heading_size
  assign item_heading_size_mobile = section.settings.item_heading_size_mobile
  assign item_heading_height = section.settings.item_heading_height
  assign item_heading_mt = section.settings.item_heading_mt
  assign item_heading_mt_mobile = section.settings.item_heading_mt_mobile
  assign item_heading_color = section.settings.item_heading_color

  assign item_text_custom = section.settings.item_text_custom
  assign item_text_font = section.settings.item_text_font
  assign item_text_size = section.settings.item_text_size
  assign item_text_height = section.settings.item_text_height
  assign item_text_mt = section.settings.item_text_mt
  assign item_text_color = section.settings.item_text_color

-%}

{%- style -%}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }
  
  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .image-with-text-body-{{ section.id }} {
    position: relative;
  }

  .image-with-text-content-{{ section.id }} {
    grid-area: content;
    display: flex;
    flex-direction: column;
    position: relative;
    justify-content: {{ content_vertical_align }};
    align-items: {{ content_horizontal_align_mobile }};   
    height: 100%;
    overflow: hidden;
  }

  .image-with-text-image-{{ section.id }} {
    grid-area: image;
    border: {{ image_item_border_thickness }}px solid {{ image_item_border_color }};
    border-radius: {{ image_item_radius }}px;
    overflow: hidden;
    position: relative;
    margin-top: {{ image_item_mt_mobile }}px;
  }

  .image-with-text-image-{{ section.id }} img,
  .image-with-text-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit:cover;
    transition: opacity .3s ease;
  }

  .image-with-text-image-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .image-with-text-image-desktop-{{ section.id }} {
    display: none;
  }

  .image-with-text-wrap-{{ section.id }} {
    width: 100%;
    display: flex;
    flex-direction: column;
    margin-top: {{ items_mt_mobile }}px;
  }

  .image-with-text-progress-bar-{{ section.id }} {
    width: {{ progress_bar_width }}px;
    height: 100%;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
  }

  .image-with-text-progress-bar-background-{{ section.id }} {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    width: 100%;
    height: 100%;
    background: {{ progress_bar_bg }};
    opacity: .1;
  }

  .image-with-text-progress-bar-progress-{{ section.id }} {
    display: block;
    width: 100%;
    height: 100%;
    background: {{ progress_bar_bg }};
  }

  .image-with-text-items-{{ section.id }} {
    width: 100%;
    list-style-type: none;
    margin: 0;
    padding: 0;
    display: flex;
    flex-direction: column;
    gap: {{ items_gap_mobile }}px;
    text-align: {{  content_horizontal_align_mobile }};
  }

  .image-with-text-item-{{ section.id }} {
    position: relative;
    padding: 0 {{ item_padding_horizontal_mobile }}px;
  }

  .image-with-text-item-dot-overlay-{{ section.id }},
  .image-with-text-item-dot-{{ section.id }} {
    display: block;
    position: absolute;
    border-radius: 100%;
    top: {{ items_gap_mobile }}px;
    left: 50%;
    transform: translateX(-50%);
    width: 12px;
    height: 12px;
  }

  .image-with-text-item-dot-{{ section.id }} {
    z-index: 1;
    background: {{ progress_bar_bg }};
  }

  .image-with-text-item-heading-{{ section.id }} {
    padding: 8px 0;
    margin-top: {{ item_heading_mt_mobile }}px;
  }

  .image-with-text-item-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ item_heading_size_mobile }}px;
    color: {{ item_heading_color }};
    line-height: {{ item_heading_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  .image-with-text-item-text-{{ section.id }} {
    display: none;
  }

  .image-with-text-item-text-{{ section.id }} * {
    margin: 0;
    color: {{ item_text_color }};
    line-height: {{ item_text_height }}%;
    text-transform: unset;
    text-decoration: none;
    word-break: break-word;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }
    
    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .image-with-text-body-{{ section.id }} {
      display: grid;
      align-items: center;
      grid-template-columns: 1fr;
      height: 100%;
      gap: {{ layout_gap }}px;
    }

    .image-with-text-content-{{ section.id }} {
      align-items: {{ content_horizontal_align }};
      overflow: visible;
    }

    .image-with-text-wrap-{{ section.id }} {
      margin-top: {{ items_mt }}px;
      flex-direction: row;
    }

    .image-with-text-items-{{ section.id }} {
      width: 100%;
      gap: {{ items_gap }}px;
      text-align: {{  content_horizontal_align }};
    }

    .image-with-text-item-{{ section.id }} {
      padding: 0 {{ item_padding_horizontal }}px;
    }

    .image-with-text-image-{{ section.id }} {
      margin-top: {{ image_item_mt }}px;
    }

    .image-with-text-image-{{ section.id }} img,
    .image-with-text-image-{{ section.id }} svg {
      position: absolute;
    }

    .image-with-text-image-mobile-{{ section.id }} {
      display: none;
    }

    .image-with-text-image-desktop-{{ section.id }} {
      display: block;
    }

    .image-with-text-progress-bar-{{ section.id }} {
      position: relative;
      transform: translate(0);
      left: 0;
    }

    .image-with-text-progress-bar-progress-{{ section.id }} {
      height: 0;
    }

    .image-with-text-item-dot-overlay-{{ section.id }},
    .image-with-text-item-dot-{{ section.id }} {
      top: 0;
      left: 0;
      transform: translateX(calc(-50% - ({{ progress_bar_width }}px / 2)));
    }

    .image-with-text-item-dot-{{ section.id }} {
      background: {{ progress_bar_bg }};
      opacity: .5;
    }

    .image-with-text-item-heading-{{ section.id }} {
      padding: 0;
      margin-top: {{ item_heading_mt }}px;
    }

    .image-with-text-item-heading-{{ section.id }} * {
      font-size: {{ item_heading_size }}px;
    }

    .image-with-text-item-text-{{ section.id }} {
      display: block;
      margin-top: {{ item_text_mt }}px;
    }

    .image-with-text-item-text-{{ section.id }} * {
      font-size: {{ item_text_size }}px;
    }
  }
  
{%- endstyle -%}

{% if width == 'custom' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% elsif width == 'page' %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: clamp(100px, var(--page-content-width, var(--page-width)), 100%);
    }
  </style>
{% endif %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }
    
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if layout == 'content_image' %}
  <style>
    @media(min-width: 1024px) {
      .image-with-text-body-{{ section.id }} {
        grid-template-areas: "content image"; 
        grid-template-columns: auto {{ image_item_width }}%;
      }
    }
  </style>
{% elsif layout == 'image_content' %}
  <style>
    @media(min-width: 1024px) {
      .image-with-text-body-{{ section.id }} {
        grid-template-areas: "image content";  
        grid-template-columns: {{ image_item_width }}% auto;
      }
    }
  </style>
{% endif %}

{% if image_item_ratio_mobile == "portrait" %}
  <style>
    .image-with-text-image-{{ section.id }} {
      aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif image_item_ratio_mobile == "landscape" %}
  <style>
    .image-with-text-image-{{ section.id }}{
      aspect-ratio: 12/9; 
    }
  </style>
{% elsif image_item_ratio_mobile == "square" %}
  <style>
    .image-with-text-image-{{ section.id }} {
      aspect-ratio: 12/12; 
    }
  </style>
{% else %}
  <style>
    .image-with-text-image-{{ section.id }} {
      aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_item_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .image-with-text-image-{{ section.id }} {
        aspect-ratio: 9.6/12; 
      }
    }
  </style>
{% elsif image_item_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
   .image-with-text-image-{{ section.id }}{
      aspect-ratio: 12/9; 
    }
    }
  </style>
{% elsif image_item_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .image-with-text-image-{{ section.id }} {
        aspect-ratio: 12/12; 
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .image-with-text-image-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if item_heading_custom %}
  <style>
    {{ item_heading_font | font_face: font_display: 'swap' }}

    .image-with-text-item-heading-{{ section.id }} * {
      font-family: {{ item_heading_font.family }}, {{ item_heading_font.fallback_families }};
      font-weight: {{ item_heading_font.weight }};
      font-style: {{ item_heading_font.style }};
    }
  </style>
{% endif %}

{% if item_text_custom %}
  <style>
    {{ item_text_font | font_face: font_display: 'swap' }}

    .image-with-text-item-text-{{ section.id }} *  {
      font-family: {{ item_text_font.family }}, {{ item_text_font.fallback_families }};
      font-weight: {{ item_text_font.weight }};
      font-style: {{ item_text_font.style }};
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}
    {% when 'heading' %} 
      <style>
        .image-with-text-heading-{{ block.id }} {
          margin-top: {{ block.settings.heading_mt_mobile }}px;
          text-align: {{ content_horizontal_align_mobile }};
          max-width: {{ content_max_width }}%;
        }
      
        .image-with-text-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          font-weight: 700;
        }

        @media(min-width: 1024px) {
          
          .image-with-text-heading-{{ block.id }} {
            margin-top: {{ block.settings.heading_mt }}px;
            text-align: {{ content_horizontal_align }};
          }
        
          .image-with-text-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }          
      </style>
        
      {% if block.settings.heading_custom %}
        <style>
          {{ block.settings.heading_font | font_face: font_display: 'swap' }}

          .image-with-text-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}
    {% when 'text' %}   
      <style>
        
        .image-with-text-text-{{ block.id }} {
          margin-top: {{ block.settings.text_mt_mobile }}px;
          text-align: {{ content_horizontal_align_mobile }};
          max-width: {{ content_max_width }}%;
          position: relative;
          z-index: 2;
        }
      
        .image-with-text-text-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.text_size_mobile }}px;
          color: {{ block.settings.text_color }};
          line-height: {{ block.settings.text_height }}%;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }

        @media(min-width: 1024px) {
          
          .image-with-text-text-{{ block.id }} {
            margin-top: {{ block.settings.text_mt }}px;
            text-align: {{  content_horizontal_align }};
          }
      
          .image-with-text-text-{{ block.id }} * {
            font-size: {{ block.settings.text_size }}px;
          }
          
        }
      </style>

      {% if block.settings.text_custom %}
        <style>
          {{ block.settings.text_font | font_face: font_display: 'swap' }}

          .image-with-text-text-{{ block.id }} * {
            font-family: {{ block.settings.text_font.family }}, {{ block.settings.text_font.fallback_families }};
            font-weight: {{ block.settings.text_font.weight }};
            font-style: {{ block.settings.text_font.style }};
          }
        </style>          
      {% endif %}
    {% when 'button' %} 
      <style>
        .image-with-text-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: auto;     
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          cursor: pointer;
          font-weight: 700;
          position: relative;
          z-index: 1;
          max-width: {{ content_max_width }}%;
        }

        .image-with-text-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .image-with-text-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .image-with-text-button-{{ block.id }} svg {
          width: {{ block.settings.arrow_size_mobile }}px;
          height: {{ block.settings.arrow_size_mobile }}px;
        }

        .image-with-text-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .image-with-text-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        @media (min-width: 1024px) {
          .image-with-text-button-{{ block.id }} {
            display: none;
          }
        }
      </style>

      {% if block.settings.use_shadow %}
        <style>
          .image-with-text-button-{{ block.id }}:hover {
            -webkit-box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
            -moz-box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
            box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
          }
        </style>
      {% endif %}  

      {% if block.settings.button_custom %}
        <style>
          {{ block.settings.button_font | font_face: font_display: 'swap' }}

          .image-with-text-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .image-with-text-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
          }
      
          .image-with-text-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>          
          .image-with-text-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{  block.settings.button_border_color }};
          }
      
          .image-with-text-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{ block.settings. button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .image-with-text-button-{{ block.id }} {
            padding: 0px !important;
            -webkit-box-shadow: none !important;
            -moz-box-shadow: none !important;
            box-shadow: none !important;
          }
        </style>
      {% endif %}
  {% endcase %}
{% endfor %}

<div class="section-{{ section.id }} image-with-text-{{ section.id }}" {% if background_style == 'solid' %}style="background-color:{{ background_color }};"{% else %} style="background-image: {{ background_gradient }};"{% endif %}>
    <div class="section-{{ section.id }}-settings">
      <div class="image-with-text-body-{{ section.id }}">
        <div class="image-with-text-image-{{ section.id }} image-with-text-image-desktop-{{ section.id }}">
          {% for block in section.blocks %}
            {% case block.type %}
              {% when 'item' %}
              {% if block.settings.image != blank %}      
                {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
                  {{ block.settings.image | image_url: width: 1000 | image_tag:
                    loading: 'lazy',
                    sizes: sizes,
                    widths: '750, 1070, 1500'
                  }}    
              {% else %}
                {{ 'image' | placeholder_svg_tag }}
              {% endif %}
            {% endcase %}
          {% endfor %}
        </div>
        <div class="image-with-text-content-{{ section.id }}">
          {% for block in section.blocks %}
            {% case block.type %}
              {% when 'heading'%}
                {% if block.settings.heading != blank %}
                  <div class="image-with-text-heading-{{ block.id }}">
                    {{ block.settings.heading }}
                  </div>
                {% endif %}
              {% when 'text' %}
                {% if block.settings.text != blank %}
                  <div class="image-with-text-text-{{ block.id }}">
                    {{ block.settings.text }}
                  </div>
                {% endif %}
            {% endcase %}
          {% endfor %}
