<script>
  function initImageWithText24{{ section.id | replace: '-', '' }}() {
    if (window.innerWidth < 1024) {
      return;
    }

    const wrapElement = document.querySelector('.image-with-text-wrap-{{ section.id }}');

    if (!wrapElement) return;
    
    const wrapClass = Array.from(wrapElement.classList).find(cls => cls.startsWith('image-with-text-wrap-'));
    if (!wrapClass) return;
    
    const progressBar = wrapElement.querySelector('.image-with-text-progress-bar-progress-{{ section.id }}');
    const dots = wrapElement.querySelectorAll('.image-with-text-item-dot-{{ section.id }}');
    const images = document.querySelectorAll('.image-with-text-image-desktop-{{ section.id }} > *');

    if (!wrapElement || !progressBar || dots.length === 0 || images.length === 0) return;

    images.forEach((img, index) => {
      img.style.opacity = index === 0 ? '1' : '0';
    });
    
    function updateProgress() {
      const wrapRect = wrapElement.getBoundingClientRect();
      const windowHeight = window.innerHeight;
      const midScreen = windowHeight / 2;
      
      const wrapTop = wrapRect.top;
      const wrapBottom = wrapRect.bottom;
      const wrapHeight = wrapRect.height;
      
      let progressHeight;
      
      if (wrapBottom <= midScreen) {
        progressHeight = 100;
      } else if (wrapTop >= midScreen) {
        progressHeight = 0;
      } else {
        const distanceFromTopToMid = midScreen - wrapTop;
        const scrollProgress = Math.max(0, Math.min(1, distanceFromTopToMid / wrapHeight));
        progressHeight = scrollProgress * 100;
      }
      
      progressBar.style.height = `${progressHeight}%`;

      let activeIndex = 0;
      
      dots.forEach((dot, index) => {
        if (index === 0) {
          dot.style.opacity = '1';
          images[0].style.opacity = '1';
          return;
        }
        
        const dotThreshold = dots.length > 1 
          ? (index / (dots.length - 1)) * 95
          : 0;
        
        if (progressHeight >= dotThreshold) {
          dot.style.opacity = '1';
          activeIndex = index;
        } else {
          dot.style.opacity = '0.5';
        }
      });

      images.forEach((img, index) => {
        if (index === activeIndex) {
          img.style.opacity = '1';
        } else {
          img.style.opacity = '0';
        }
      });
    }
    
    if (dots[0]) {
      dots[0].style.opacity = '1';
    }
    
    window.addEventListener('scroll', updateProgress);

    updateProgress();
  }

  document.addEventListener('DOMContentLoaded', initImageWithText24{{ section.id | replace: '-', '' }});

  if (Shopify.designMode) {
    document.addEventListener('shopify:section:unload', initImageWithText24{{ section.id | replace: '-', '' }});
    document.addEventListener('shopify:section:load', initImageWithText24{{ section.id | replace: '-', '' }});
  }
</script>
