{%- style -%}

  {{  heading_font | font_face: font_display: 'swap' }}
  {{  sub_heading_font | font_face: font_display: 'swap' }}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .image-text-heading-{{ section.id }} {
    width: 100%;
    text-align: {{ heading_align_mobile }};
  }

  .image-text-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .image-text-subheading-{{ section.id }} {
    width: 100%;
    margin-top: {{ sub_heading_mt | times: 0.75 | round: 0 }}px;
    text-align: {{ sub_heading_align_mobile }};
  }

  .image-text-subheading-{{ section.id }} * {
    margin: 0;
    font-size: {{ sub_heading_size_mobile }}px;
    color: {{ sub_heading_color }};
    line-height: {{ sub_heading_height }}%;
    text-transform: unset;
  }

  .image-text-body-{{ section.id }} {
    margin-top: {{ body_mt_mobile }}px;
    display: grid;
    grid-template-columns: 1fr;
    -webkit-align-items: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    align-items: center;
    gap: 0px;
  }

  .image-text-image-{{ section.id }} {
    background-color: {{ background_color }};
    margin-bottom: 24px;
  }

  .image-text-image-wrapper-{{ section.id }} {
    max-width: {{ image_width_mobile }}%;
    width: 100%;
    border-radius: {{ image_radius }}px;
    overflow: hidden;
  }

  .image-text-image-wrapper-{{ section.id }} img,
  .image-text-image-wrapper-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    max-height: 50vh;
  }

  .image-text-image-wrapper-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .image-text-content-{{ section.id }} {
    margin-bottom: {{ body_gap_mobile }}px;
  }

  .image-text-title-{{ section.id }} {
    display: block;
    width: 100%;
    text-align: {{ title_align_mobile }};
    margin: 0;
    font-size: {{ title_size_mobile }}px;
    color: {{ title_color }};
    line-height: {{ title_height }}%;
    text-transform: unset;
  }

  .image-text-text-{{ section.id }} {
    display: block;
    width: 100%;
    text-align: {{ text_align_mobile }};
    margin: 0;
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
  }

  .image-text-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: {{ button_width_mobile }}px;
    margin: 0;
    margin-top: {{ button_mt_mobile }}px;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    position: relative;
    font-weight: 700;
    z-index: 2;
  }

  .image-text-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .image-text-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  .image-text-button-{{ section.id }} svg path {
    fill: {{ button_color }};
    transition: all 0.25s ease 0s;
  }

  .image-text-button-{{ section.id }}:hover svg path {
    fill: {{ button_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .image-text-block-{{ section.id }}.block-hidden {
    display: none !important;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .image-text-heading-{{ section.id }} {
      text-align: {{ heading_align }};
    }

    .image-text-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .image-text-subheading-{{ section.id }} {
      margin-top: {{ sub_heading_mt }}px;
      text-align: {{ sub_heading_align }};
    }

    .image-text-subheading-{{ section.id }} * {
      font-size: {{ sub_heading_size }}px;
    }

    .image-text-body-{{ section.id }} {
      margin-top: {{ body_mt }}px;
      gap: 0 {{ body_gap }}px;
    }

    .image-text-image-{{ section.id }} {
      min-height: 50vh;
      margin: 0;
      display: grid;
      place-items: center;
      grid-row: 1;
      position: -webkit-sticky;
      position: sticky;
      top: 16vh;
      z-index: 20;
      -webkit-transition: all 300ms linear;
      transition: all 300ms linear
    }

    .image-text-image-wrapper-{{ section.id }} {
      max-width: {{ image_width }}%;
    }

    .image-text-content-{{ section.id }} {
      min-height: 50vh;
      padding: 32px 0;
      margin: 0;
      display: -webkit-box;
      display: -webkit-flex;
      display: -ms-flexbox;
      display: flex;
      -webkit-flex-direction: column;
      -ms-flex-direction: column;
      flex-direction: column;
      -webkit-box-pack: center;
      -ms-flex-pack: center;
      -webkit-justify-content: center;
      justify-content: center;
      -webkit-transition: all 600ms linear;
      transition: all 600ms linear;
      box-sizing: border-box;
    }

    .image-text-title-{{ section.id }} {
      text-align: {{ title_align }};
      font-size: {{ title_size }}px;
    }

    .image-text-text-{{ section.id }} {
      margin-top: {{ text_mt }}px;
      text-align: {{ text_align }};
      font-size: {{ text_size }}px;
    }

    .image-text-block-{{ section.id }} {
      opacity: 0;
    }

    .image-text-block-{{ section.id }}.active {
      opacity: 1;
    }

    .image-text-button-{{ section.id }} {
      margin-top: {{ button_mt }}px;
      max-width: {{ button_width }}px;
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if heading_custom %}
  <style>
    .image-text-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if sub_heading_custom %}
  <style>
    .image-text-subheading-{{ section.id }} * {
      font-family: {{ sub_heading_font.family }}, {{ sub_heading_font.fallback_families }};
      font-weight: {{ sub_heading_font.weight }};
      font-style: {{ sub_heading_font.style }};
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .image-text-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .image-text-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .image-text-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if button_align_mobile == "center" %}
  <style>
   .image-text-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
   .image-text-button-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .image-text-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .image-text-button-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .image-text-button-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

{% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
  <style>
    .image-text-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
    }

    .image-text-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
    }
  </style>
{% elsif button_style == "outline" or button_style == "outline_arrow" %}
  <style>
    .image-text-button-{{ section.id }} {
      background-color: {{ button_bg_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_color }};
    }

    .image-text-button-{{ section.id }}:hover {
      background-color: {{ button_bg_hover_color }};
      border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    }
  </style>
{% endif %}

{% if heading_align_mobile == "center" %}
  <style>
    .image-text-heading-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif heading_align_mobile == "right" %}
  <style>
    .image-text-heading-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if body_layout == "image_content" %}
  <style>
    @media(min-width: 1024px) {
      .image-text-body-{{ section.id }} {
        grid-template-columns: 1.45fr 1fr;
      }

      .image-text-image-{{ section.id }} {
        grid-column: 1;
      }

      .image-text-content-{{ section.id }} {
        grid-column: 2;
      }

    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .image-text-body-{{ section.id }} {
        grid-template-columns: 1fr 1.45fr;
      }

      .image-text-image-{{ section.id }} {
        grid-column: 2;
      }

      .image-text-content-{{ section.id }} {
        grid-column: 1;
      }
    }
  </style>
{% endif %}

{% if image_align_mobile == "center" %}
  <style>
   .image-text-image-wrapper-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    } 
  </style>
{% elsif image_align_mobile == "right" %}
  <style>
   .image-text-image-wrapper-{{ section.id }} {
      margin-left: auto;
    } 
  </style>
{% endif %}

{% if image_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .image-text-image-wrapper-{{ section.id }} {
        margin-left: auto !important;
        margin-right: auto !important;
      } 
    }
  </style>
{% elsif image_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .image-text-image-wrapper-{{ section.id }} {
        margin-left: auto !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .image-text-image-wrapper-{{ section.id }} {
        margin-left: 0px !important;
        margin-right: 0px !important;
      } 
    }
  </style>
{% endif %}

<div class="section-{{ section.id }} image-text-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">

      {% if heading != blank %}
        <div class="image-text-heading-{{ section.id }}">
          {{ heading }}
        </div>
      {% endif %}

      {% if sub_heading != blank %}
        <div class="image-text-subheading-{{ section.id }}">
          {{ sub_heading }}
        </div>
      {% endif %}

      <div class="image-text-body-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="image-text-image-{{ section.id }} image-text-block-{{ section.id }} {% if forloop.first %}active{% endif %} {% if forloop.index != 1 %}block-hidden{% endif %}" data-id="{{ block.id }}">
            <div class="image-text-image-wrapper-{{ section.id }}">
              {% if block.settings.image != blank %}
                <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
              {% else %}
                  {{ 'image' | placeholder_svg_tag }}
              {% endif %}
