            {% if block.settings.title != blank %}
              <h3 class="image-text-title-{{ section.id }}">{{ block.settings.title }}</h3>
            {% endif %}

            {% if block.settings.text != blank %}
              <p class="image-text-text-{{ section.id }}">{{ block.settings.text }}</p>
            {% endif %}
          </div>
        {% endfor %}
      </div>

      {% if button != blank %}
        <a href="{{ button_url }}" class="image-text-button-{{ section.id }}">
