{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign bg_image = section.settings.bg_image

  assign header_horizontal_align_mobile = section.settings.header_horizontal_align_mobile
  assign header_horizontal_align = section.settings.header_horizontal_align
  assign header_gap = section.settings.header_gap
  assign header_gap_mobile = section.settings.header_gap_mobile

  assign text_align = ""
  if header_horizontal_align == "start"
    assign text_align = "left"
  elsif header_horizontal_align == "center"
    assign text_align = "center"
  else
    assign text_align = "right"
  endif

  assign text_align_mobile = ""
  if header_horizontal_align_mobile == "start"
    assign text_align_mobile = "left"
  elsif header_horizontal_align_mobile == "center"
    assign text_align_mobile = "center"
  else
    assign text_align_mobile = "right"
  endif

  assign left_text = section.settings.left_text
  assign left_text_url = section.settings.left_text_url
  assign left_text_size = section.settings.left_text_size
  assign left_text_size_mobile = section.settings.left_text_size_mobile
  assign left_text_color = section.settings.left_text_color
  assign left_text_custom = section.settings.left_text_custom
  assign left_text_font = section.settings.left_text_font
  assign left_text_height = section.settings.left_text_height

  assign right_text = section.settings.right_text
  assign right_text_url = section.settings.right_text_url
  assign right_text_size = section.settings.right_text_size
  assign right_text_size_mobile = section.settings.right_text_size_mobile
  assign right_text_color = section.settings.right_text_color
  assign right_text_custom = section.settings.right_text_custom
  assign right_text_font = section.settings.right_text_font
  assign right_text_height = section.settings.right_text_height

  assign heading = section.settings.heading
  assign heading_url = section.settings.heading_url
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_color = section.settings.heading_color
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_height = section.settings.heading_height

  assign items_row_mobile = section.settings.items_row_mobile
  assign items_row = section.settings.items_row
  assign items_gap_mobile = section.settings.items_gap_mobile
  assign items_gap = section.settings.items_gap
  assign items_mt = section.settings.items_mt
  assign items_mt_mobile = section.settings.items_mt_mobile

  assign image_radius = section.settings.image_radius
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color

-%}

{%- style -%}

  {{  left_text_font | font_face: font_display: 'swap' }}
  {{  heading_font | font_face: font_display: 'swap' }}
  {{  right_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .instafeed-header-{{ section.id }} {
    display: flex;
    flex-direction: column;
    gap: {{ header_gap_mobile }}px;
    align-items: {{ header_horizontal_align_mobile }};
  }

  .instafeed-mobile-text-{{ section.id }} {
    display: grid;
    max-width: fit-content;
    grid-template-columns: 1fr 1fr;
    gap: {{ header_gap_mobile }}px;
    align-items: center;
  }

  .instafeed-desktop-text-{{ section.id }} {
    display: none;
  }

  .instafeed-left-text-{{ section.id }} {
    margin: 0;
    font-size: {{ left_text_size_mobile }}px;
    color: {{ left_text_color }};
    line-height: {{ left_text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .instafeed-right-text-{{ section.id }} {
    margin: 0;
    font-size: {{ right_text_size_mobile }}px;
    color: {{ right_text_color }};
    line-height: {{ right_text_height }}%;
    text-transform: unset;
    text-decoration: none;
  }

  .instafeed-heading-{{ section.id }} {
    text-align: {{ text_align_mobile }};
  }

  .instafeed-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-transform: unset;
  }

  .instafeed-items-{{ section.id }} {
    display: grid;
    gap: {{ items_gap_mobile }}px;
    align-items: center;
    margin-top: {{ items_mt_mobile }}px;
    grid-template-columns: repeat({{ items_row_mobile}}, 1fr);
  }

  .instafeed-item-hide-mobile-{{ section.id }} {
    display: none;
  }

  .instafeed-item-{{ section.id }} {
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    overflow: hidden;
  }

  .instafeed-item-{{ section.id }} img,
  .instafeed-item-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .instafeed-item-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .instafeed-header-{{ section.id }} {
      gap: {{ header_gap }}px;
      flex-direction: row;
      align-items: center;
      justify-content: {{ header_horizontal_align }};
    }

    .instafeed-desktop-text-{{ section.id }} {
      display: block;
    }

    .instafeed-mobile-text-{{ section.id }} {
      display: none;
    }

    .instafeed-left-text-{{ section.id }} {
      font-size: {{ left_text_size }}px;
    }

    .instafeed-right-text-{{ section.id }} {
      font-size: {{ right_text_size }}px;
    }

    .instafeed-heading-{{ section.id }} {
      text-align: {{ text_align }};
    }

    .instafeed-heading-{{ section.id }} * {
      font-size: {{ heading_size }}px;
    }

    .instafeed-items-{{ section.id }} {
      gap: {{ items_gap }}px;
      margin-top: {{ items_mt }}px;
      grid-template-columns: repeat({{ items_row}}, 1fr);
    }

    .instafeed-item-hide-mobile-{{ section.id }} {
      display: block;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if bg_image != blank %}
  <style>
    .section-{{ section.id }} {
      background-image: url('{{ bg_image | image_url }}');
      background-size: cover;
      background-position: center;;
      background-repeat: no-repeat;
    }
  </style>
{% endif %}

{% if left_text_custom %}
  <style>
    .instafeed-left-text-{{ section.id }} {
      font-family: {{ left_text_font.family }}, {{ left_text_font.fallback_families }};
      font-weight: {{ left_text_font.weight }};
      font-style: {{ left_text_font.style }};
    }
  </style>
{% endif %}

{% if heading_custom %}
  <style>
    .instafeed-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if right_text_custom %}
  <style>
    .instafeed-right-text-{{ section.id }} {
      font-family: {{ right_text_font.family }}, {{ right_text_font.fallback_families }};
      font-weight: {{ right_text_font.weight }};
      font-style: {{ right_text_font.style }};
    }
  </style>
{% endif %}

{% for block in section.blocks %}

  {% if block.settings.image_ratio_mobile == "portrait" %}
    <style>
      .instafeed-item-{{ block.id }} {
       aspect-ratio: 9.6/14; 
      }
    </style>
  {% elsif block.settings.image_ratio_mobile == "landscape" %}
    <style>
      .instafeed-item-{{ block.id }}{
       aspect-ratio: 12/10; 
      }
    </style>
  {% elsif block.settings.image_ratio_mobile == "square" %}
    <style>
      .instafeed-item-{{ block.id }} {
       aspect-ratio: 12/12; 
      }
    </style>
  {% else %}
    <style>
      .instafeed-item-{{ block.id }} {
       aspect-ratio: auto; 
      }
    </style>
  {% endif %}

  {% if block.settings.image_ratio == "portrait" %}
    <style>
      @media(min-width: 1024px) {
       .instafeed-item-{{ block.id }} {
         aspect-ratio: 9.6/14; 
        } 
      }
    </style>
  {% elsif block.settings.image_ratio == "landscape" %}
    <style>
      @media(min-width: 1024px) {
        .instafeed-item-{{ block.id }}{
         aspect-ratio: 12/10; 
        }
      }
    </style>
  {% elsif block.settings.image_ratio == "square" %}
    <style>
      @media(min-width: 1024px) {
        .instafeed-item-{{ block.id }} {
         aspect-ratio: 12/12; 
        }
      }
    </style>
  {% else %}
    <style>
      @media(min-width: 1024px) {
        .instafeed-item-{{ block.id }} {
         aspect-ratio: auto; 
        }
      }
    </style>
  {% endif %}

{% endfor %}

<div class="section-{{ section.id }} instafeed-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="instafeed-header-{{ section.id }}">
        {% if left_text != blank %}
          <{% if left_text_url != blank %}a href="{{ left_text_url }}"{% else %}p{% endif %} class="instafeed-left-text-{{ section.id }} instafeed-desktop-text-{{ section.id }}">{{ left_text }}</{% if left_text_url != blank %}a{% else %}p{% endif %}>
        {% endif %}

        {% if heading != blank %}
          <div class="instafeed-heading-{{ section.id }}">{{ heading }}</div>
        {% endif %}

        {% if right_text != blank %}
          <{% if right_text_url != blank %}a href="{{ right_text_url }}"{% else %}p{% endif %} class="instafeed-right-text-{{ section.id }} instafeed-desktop-text-{{ section.id }}">{{ right_text }}</{% if right_text_url != blank %}a{% else %}p{% endif %}>
        {% endif %}

        <div class="instafeed-mobile-text-{{ section.id }}">
          {% if left_text != blank %}
            <p class="instafeed-left-text-{{ section.id }}">{{ left_text }}</p>
          {% endif %}

          {% if right_text != blank %}
            <p class="instafeed-right-text-{{ section.id }}">{{ right_text }}</p>
          {% endif %}
        </div>
      </div>

      <div class="instafeed-items-{{ section.id }}">
        {% for block in section.blocks %}
          <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div{% endif %} class="instafeed-item-{{ section.id }} instafeed-item-{{ block.id }} {% if block.settings.hide_mobile %}instafeed-item-hide-mobile-{{ section.id }}{% endif %}">
            {% if block.settings.image != blank %}
              <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </{% if block.settings.url != blank %}a{% else %}div{% endif %}>
        {% endfor %}
      </div>
    </div>
</div>
