{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign background_image_mobile = section.settings.background_image_mobile
  assign background_image = section.settings.background_image
  assign overlay = section.settings.overlay
  assign overlay_color = section.settings.overlay_color
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign section_radius = section.settings.section_radius

  assign use_heading_image = section.settings.use_heading_image  
  assign heading_image = section.settings.heading_image
  assign heading_image_size = section.settings.heading_image_size
  assign heading_image_size_mobile = section.settings.heading_image_size_mobile
  assign heading_image_radius = section.settings.heading_image_radius
  assign heading_image_align = section.settings.heading_image_align
  assign heading_image_align_mobile = section.settings.heading_image_align_mobile



  assign heading_image_horizontal_align = ""
  if heading_image_align == "center"
    assign heading_image_horizontal_align = "center"
  elsif heading_image_align == "right"
    assign heading_image_horizontal_align = "end"
  else
    assign heading_image_horizontal_align = "start"
  endif

  assign heading_image_horizontal_align_mobile = ""
  if heading_image_align_mobile == "center"
    assign heading_image_horizontal_align_mobile = "center"
  elsif heading_image_align_mobile == "right"
    assign heading_image_horizontal_align_mobile = "end"
  else
    assign heading_image_horizontal_align_mobile = "start"
  endif

  assign heading = section.settings.heading
  assign heading_custom = section.settings.heading_custom
  assign heading_font = section.settings.heading_font
  assign heading_size = section.settings.heading_size
  assign heading_size_mobile = section.settings.heading_size_mobile
  assign heading_height = section.settings.heading_height
  assign heading_color = section.settings.heading_color
  assign heading_align = section.settings.heading_align
  assign heading_align_mobile = section.settings.heading_align_mobile  
  assign heading_mt = section.settings.heading_mt
  assign heading_mt_mobile = section.settings.heading_mt_mobile

  assign list_gap = section.settings.list_gap
  assign list_gap_mobile = section.settings.list_gap_mobile
  assign list_mt = section.settings.list_mt
  assign list_mt_mobile = section.settings.list_mt_mobile

  assign use_item_image = section.settings.use_item_image
  assign item_padding_vertical = section.settings.item_padding_vertical
  assign item_padding_vertical_mobile = section.settings.item_padding_vertical_mobile
  assign item_padding_horizontal = section.settings.item_padding_horizontal
  assign item_padding_horizontal_mobile = section.settings.item_padding_horizontal_mobile
  assign item_border_thickness = section.settings.item_border_thickness
  assign item_border_color = section.settings.item_border_color
  assign item_border_hover_color = section.settings.item_border_hover_color
  assign item_radius = section.settings.item_radius
  assign item_bg = section.settings.item_bg
  assign item_bg_hover = section.settings.item_bg_hover
  assign item_image_height = section.settings.item_image_height
  assign item_image_height_mobile = section.settings.item_image_height_mobile

  assign item_image_size = section.settings.item_image_size
  assign item_image_size_mobile = section.settings.item_image_size_mobile
  assign item_image_radius = section.settings.item_image_radius

  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_height = section.settings.text_height
  assign text_color = section.settings.text_color
  assign text_color_hover = section.settings.text_color_hover
-%}

{%- style -%}
  {{ heading_font | font_face: font_display: 'swap' }}
  {{ text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
    position: relative;
    z-index: 2;
  }

  .navigation-links-{{ section.id }} {
    position: relative;
  }

  .navigation-links-{{ section.id }}:after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: {{ overlay_color | hex_to_rgba: overlay }};
    z-index: 1;
  }

  .navigation-links-image-wrap-{{ section.id }} {
    width: 100%;
    display: flex;
    justify-content: {{ heading_image_horizontal_align_mobile }};    
  }

  .navigation-links-image-{{ section.id }} {
    width: {{ heading_image_size_mobile }}px;
    border-radius: {{ heading_image_radius }}px;
    overflow: hidden;
    aspect-ratio: 1 /1;
    box-sizing: border-box;
  }

  .navigation-links-image-{{ section.id }} img,
  .navigation-links-image-{{ section.id }} svg {
      display: block;
      border-radius: {{ heading_image_radius }}px;
      width: 100%;
      height: 100%;
      object-fit: cover;
      margin: 0;      
  }

  .navigation-links-image-{{ section.id }} svg {
    background-color: #ababab;
  }


  .navigation-links-heading-{{ section.id }} * {
    margin: 0;
    font-size: {{ heading_size_mobile }}px;
    color: {{ heading_color }};
    line-height: {{ heading_height }}%;
    text-align: {{ heading_align_mobile }};
    text-transform: unset;
    text-decoration: none;
    margin-top: {{ heading_mt_mobile }}px;
  }

  .navigation-links-list-{{ section.id }} {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: {{ list_gap_mobile }}px;
    margin-top: {{ list_mt_mobile }}px;
    padding: 0 !important;
    list-style: none;
  }

  .navigation-links-item-{{ section.id }} {
    position: relative;  
    border-radius: {{ item_radius }}px;
    border: {{ item_border_thickness }}px solid {{ item_border_color }};
    padding: {{ item_padding_vertical_mobile }}px {{ item_padding_horizontal_mobile }}px;
    background-color: {{ item_bg }};
    transition: all 0.3s ease 0s;
    cursor: pointer;
    width: 100%
  }

  .navigation-links-item-{{ section.id }}:hover {
    transition: all 0.3s ease 0s;
    background-color: {{ item_bg_hover }};
    border: {{ item_border_thickness }}px solid {{ item_border_hover_color }};
  }

  .navigation-links-item-{{ section.id }} a {
    display: block;
    text-decoration: none;
  }

  .navigation-links-item-image-{{ section.id }} {
    display: block;
    width: {{ item_image_size_mobile }}px;
    object-fit: cover;
    height: {{ item_image_height_mobile }}px;       
    border-radius: {{ item_image_radius }}px;
    overflow: hidden;
    box-sizing: border-box;
    position: absolute;  
    top: 50%;
    left: {{ item_padding_horizontal_mobile }}px;
    transform: translateY(-50%);  
  }

  .navigation-links-item-image-{{ section.id }} a {
    text-decoration: none;
  }

  .navigation-links-item-image-{{ section.id }} img,
  .navigation-links-item-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
    margin: 0;      
  }

  .navigation-links-item-image-{{ section.id }} svg {
    background-color: #ababab;
  }

  .navigation-links-text-container-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: {{ item_image_height_mobile }}px;
    padding-left: {{ item_image_size_mobile | plus: 10 }}px;
    padding-right: {{ item_image_size_mobile | plus: 10 }}px;
  }

  .navigation-links-text-{{ section.id }} {
    margin: 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    text-align: center;
    transition: all 0.3s ease 0s;   
  }

  .navigation-links-item-{{ section.id }}:hover .navigation-links-text-{{ section.id }} {
    transition: all 0.3s ease 0s;
    color: {{ text_color_hover }};
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .navigation-links-image-wrap-{{ section.id }} {
      justify-content: {{ heading_image_horizontal_align }};    
    }

    .navigation-links-image-{{ section.id }} {
      width: {{ heading_image_size }}px;
      text-align: {{ heading_image_align }};
    }

    .navigation-links-heading-{{ section.id }} * {
      margin-top: {{ heading_mt }}px;
      font-size: {{ heading_size }}px;
      text-align: {{ heading_align }};
    }    

    .navigation-links-list-{{ section.id }} {
      margin-top: {{ list_mt }}px;
      gap: {{ list_gap }}px;
    }

    .navigation-links-item-{{ section.id }} {
      padding: {{ item_padding_vertical }}px {{ item_padding_horizontal }}px;
    }

    .navigation-links-item-image-{{ section.id }} {
      width: {{ item_image_size }}px;
      height: {{ item_image_height }}px;      
      left: {{ item_padding_horizontal }}px;
    }

    .navigation-links-text-container-{{ section.id }} {
      min-height: {{ item_image_height }}px;
      padding-left: {{ item_image_size | plus: 10 }}px;
      padding-right: {{ item_image_size | plus: 10 }}px;
    }

    .navigation-links-text-{{ section.id }} {
      font-size: {{ text_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if background_image_mobile != blank %}
  <style>    
    .navigation-links-{{ section.id }} {
      background-image: url('{{ background_image_mobile | image_url }}');
      background-repeat: no-repeat;
      background-size: cover;
    }  
  </style>
{% elsif background_image != blank %}
  <style>    
    .navigation-links-{{ section.id }} {
      background-image: url('{{ background_image | image_url }}');
      background-repeat: no-repeat;
      background-size: cover;
    }  
  </style>
{% endif %}

{% if background_image != blank %}
  <style>    
    @media (min-width: 1024px) {
      .navigation-links-{{ section.id }} {
        background-image: url('{{ background_image | image_url }}');
        background-repeat: no-repeat;
        background-size: cover;
      }
    }     
  </style>
{% endif %}


{% if heading_custom %}
  <style>
    .navigation-links-heading-{{ section.id }} * {
      font-family: {{ heading_font.family }}, {{ heading_font.fallback_families }};
      font-weight: {{ heading_font.weight }};
      font-style: {{ heading_font.style }};
    }
  </style>
{% endif %}

{% if text_custom %}
  <style>
    .navigation-links-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}


<div class="section-{{ section.id }} navigation-links-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="navigation-links-wrapper-{{ section.id }}">
        {% if use_heading_image %}
          <div class="navigation-links-image-wrap-{{ section.id }}">
            <div class="navigation-links-image-{{ section.id }}">
            {% if heading_image != blank %}
              <img src="{{ heading_image | image_url }}" alt="{{ heading_image.alt }}">
            {% else %}
              {{ 'image' | placeholder_svg_tag }}
            {% endif %}
          </div>
          </div>

        {% endif %}
        {%  if heading != blank %} 
          <div class="navigation-links-heading-{{ section.id }}">
            {{ heading }}
          </div>
        {% endif %}
        <ul class="navigation-links-list-{{ section.id }}">
          {% for block in section.blocks %}
            <li class="navigation-links-item-{{ section.id }}">
              {% if block.settings.url != blank %}
                <a href="{{ block.settings.url }}">
              {% endif %}
              {% if use_item_image %}
                <div class="navigation-links-item-image-{{ section.id }}">
                  {% if block.settings.image != blank %}
                    <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}">
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}
              </div>
              {% endif %}              
              {% if block.settings.text != blank %}
                <div class="navigation-links-text-container-{{ section.id }}">
                  <p class="navigation-links-text-{{ section.id }}">
                    {{ block.settings.text }}
                  </p>
                </div>                
              {% endif %}      
              {% if block.settings.url != blank %}
                </a>
              {% endif %}        
            </li>
          {% endfor %}
        </ul>
      </div>
    </div>
</div>
