{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy

  assign body_padding_vertical = section.settings.body_padding_vertical
  assign body_padding_vertical_mobile = section.settings.body_padding_vertical_mobile
  assign body_padding_horizontal = section.settings.body_padding_horizontal
  assign body_padding_horizontal_mobile = section.settings.body_padding_horizontal_mobile
  assign body_radius = section.settings.body_radius
  assign body_border_thickness = section.settings.body_border_thickness
  assign body_border_color = section.settings.body_border_color
  assign body_bg_color = section.settings.body_bg_color
  assign body_bg_image = section.settings.body_bg_image

  assign text_size = section.settings.text_size
  assign text_size_mobile = section.settings.text_size_mobile
  assign text_color = section.settings.text_color
  assign text_custom = section.settings.text_custom
  assign text_font = section.settings.text_font
  assign text_height = section.settings.text_height
  assign text_stroke = section.settings.text_stroke
  assign text_stroke_color = section.settings.text_stroke_color
  assign text_mt = section.settings.text_mt

  assign hover_text_size = section.settings.hover_text_size
  assign hover_text_size_mobile = section.settings.hover_text_size_mobile
  assign hover_text_color = section.settings.hover_text_color
  assign hover_text_custom = section.settings.hover_text_custom
  assign hover_text_font = section.settings.hover_text_font
  assign hover_text_height = section.settings.hover_text_height

  assign direction = section.settings.direction

  assign use_hover_image = section.settings.use_hover_image
  assign image_width_mobile = section.settings.image_width_mobile
  assign image_width = section.settings.image_width
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_ratio = section.settings.image_ratio
  assign image_border_thickness = section.settings.image_border_thickness
  assign image_border_color = section.settings.image_border_color
  assign image_radius = section.settings.image_radius
  assign image_rotate = section.settings.image_rotate

-%}

{%- style -%}

  {{  text_font | font_face: font_display: 'swap' }}
  {{  hover_text_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .list-body-{{ section.id }} {
    background-color: {{ body_bg_color }};
    border: {{ body_border_thickness }}px solid {{ body_border_color }};
    border-radius: {{ body_radius }}px;
    padding: {{ body_padding_vertical_mobile }}px {{ body_padding_horizontal_mobile }}px;
    overflow: hidden;
  }

  .list-item-{{ section.id }} {
    width: 100%;
    position: relative;
  }

  .list-item-{{ section.id }}:not(:first-child) {
    margin-top: {{ text_mt | times: 0.75 | round: 0 }}px;
  }

  .list-text-{{ section.id }} {
    position: relative;
    opacity: 1;
    z-index: 2;
    margin: 0px;
    font-size: {{ text_size_mobile }}px;
    color: {{ text_color }};
    line-height: {{ text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .list-hover-marquee-{{ section.id }} {
    position: absolute;
    top: 0;
    left: -100%;
    z-index: -1;
    opacity: 0;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    overflow: hidden;
    width: 300%;
    height: 100%;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    -ms-flex-pack: start;
    justify-content: flex-start;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
  }

  .list-hover-track-{{ section.id }} {
    position: absolute;
    white-space: nowrap;
    top: 0;
    bottom: 0;
    will-change: transform;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    -ms-flex-pack: start;
    justify-content: flex-start;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    gap: 5vw;
  }

  @keyframes marquee-horizontal-left {
    from { transform: translateX(0); }
    to { transform: translateX(-50%); }
  }

  @keyframes marquee-horizontal-right {
    from { transform:translateX(-50%); }
    to {transform:translateX(0%); }
  }

  .list-hover-text-{{ section.id }} {
    margin: 0px;
    font-size: {{ hover_text_size_mobile }}px;
    color: {{ hover_text_color }};
    line-height: {{ hover_text_height }}%;
    text-transform: unset;
    font-weight: 700;
  }

  .list-hover-image-{{ section.id }} {
    position: absolute;
    right: 0;
    width: {{ image_width_mobile }}px;
    border-radius: {{ image_radius }}px;
    border: {{ image_border_thickness }}px solid {{ image_border_color }};
    transform: rotate({{ image_rotate }}deg);
    aspect-ratio: 1/1;
    z-index: -1;
    opacity: 0;
  }

  .list-hover-image-{{ section.id }} img,
  .list-hover-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .list-hover-image-{{ section.id }} svg {
     background-color: #FAFAFA;
  }

  .list-item-desktop-{{ section.id }} {
    display: none;
  }

  .list-item-mobile-{{ section.id }}.active .list-text-{{ section.id }} {
    opacity: 0;
    z-index: -1;
  }

  .list-item-mobile-{{ section.id }}.active .list-hover-image-{{ section.id }} {
    opacity: 1;
    z-index: 1;
  }

  .list-item-mobile-{{ section.id }}.active .list-hover-marquee-{{ section.id }} {
    opacity: 1;
    z-index: 2;
  }

  @media(min-width: 768px) {
    .list-hover-image-{{ section.id }} {
      width: {{ image_width }}px;
    }
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

     .list-body-{{ section.id }} {
       padding: {{ body_padding_vertical }}px {{ body_padding_horizontal }}px;
     }

    .list-item-{{ section.id }}:not(:first-child) {
      margin-top: {{ text_mt }}px;
    }

    .list-text-{{ section.id }} {
      z-index: 1;
      font-size: {{ text_size }}px;
    }

    .list-hover-text-{{ section.id }} {
      font-size: {{ hover_text_size }}px;
    }

    .list-item-desktop-{{ section.id }} {
      display: block;
    }

    .list-item-mobile-{{ section.id }} {
      display: none;
    }

    .list-item-desktop-{{ section.id }}:hover .list-text-{{ section.id }} {
      opacity: 0;
      z-index: -1;
    }

    .list-item-desktop-{{ section.id }}:hover .list-hover-image-{{ section.id }} {
      opacity: 1;
      z-index: 1;
    }

    .list-item-desktop-{{ section.id }}:hover .list-hover-marquee-{{ section.id }} {
      opacity: 1;
      z-index: 2;
    }

  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if text_custom %}
  <style>
    .list-text-{{ section.id }} {
      font-family: {{ text_font.family }}, {{ text_font.fallback_families }};
      font-weight: {{ text_font.weight }};
      font-style: {{ text_font.style }};
    }
  </style>
{% endif %}

{% if hover_text_custom %}
  <style>
    .list-hover-text-{{ section.id }} {
      font-family: {{ hover_text_font.family }}, {{ hover_text_font.fallback_families }};
      font-weight: {{ hover_text_font.weight }};
      font-style: {{ hover_text_font.style }};
    }
  </style>
{% endif %}

{% if body_bg_image != blank %}
  <style>
    .list-body-{{ section.id }} {
      background-image: url('{{ body_bg_image | image_url }}');
      background-size: cover;
      background-repeat: no-repeat;
      background-position: center;
    }
  </style>
{% endif %}

{% if text_stroke != blank %}
  <style>
    .list-text-{{ section.id }} {
      color: transparent;
      -webkit-text-stroke: 1px {{ text_stroke_color }};
      text-stroke: 1px {{ text_stroke_color }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .list-hover-image-{{ section.id }} {
     aspect-ratio: 9.6/12; 
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .list-hover-image-{{ section.id }} {
     aspect-ratio: 12/8; 
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .list-hover-image-{{ section.id }} {
     aspect-ratio: 12/12; 
    }
  </style>
{% else%}
  <style>
    .list-hover-image-{{ section.id }} {
     aspect-ratio: auto; 
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px ) {
      .list-hover-image-{{ section.id }} {
       aspect-ratio: 9.6/12; 
      }
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px ) {
      .list-hover-image-{{ section.id }} {
       aspect-ratio: 12/8; 
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px ) {
      .list-hover-image-{{ section.id }} {
       aspect-ratio: 12/12; 
      }
    }
  </style>
{% else%}
  <style>
    @media(min-width: 1024px ) {
      .list-hover-image-{{ section.id }} {
       aspect-ratio: auto; 
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  <style>
    .list-hover-track-{{ block.id }} {
      animation: marquee-horizontal-{{ direction }} {{ block.settings.scrolling_speed}}s linear infinite;
    }

  </style>

  {% if block.settings.image_position == "above" %}
    <style>
      .list-hover-image-{{ block.id }} {
        bottom: 100%;
      }
    </style>
  {% elsif block.settings.image_position == "below" %}
    <style>
      .list-hover-image-{{ block.id }} {
        top: 100%;
      }
    </style>
  {% else  %}
    <style>
      .list-hover-image-{{ block.id }} {
        top: 50%;
        transform: rotate({{ image_rotate }}deg) translateY(-50%);
      }
    </style>
  {% endif %}
{% endfor %}

<div class="section-{{ section.id }} list{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="list-body-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="list-item-{{ section.id }} list-item-desktop-{{ section.id }}">
            <p class="list-text-{{ section.id }}">{{ block.settings.text }}</p>
            <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div{% endif %} class="list-hover-marquee-{{ section.id }}">
              <div class="list-hover-track-{{ section.id }} list-hover-track-{{ block.id }}">
                {% for item in (1..50) %}
                  <p class="list-hover-text-{{ section.id }}">{{ block.settings.hover_text }}</p>
                {% endfor %}
              </div>
            </{% if block.settings.url != blank %}a{% else %}div{% endif %}>
            {% if use_hover_image %}
             <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div{% endif %} class="list-hover-image-{{ section.id }} list-hover-image-{{ block.id }}">
                {% if block.settings.image != blank %}
                  <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% else %}
                  {{ 'image' | placeholder_svg_tag }}
                {% endif %}
              </{% if block.settings.url != blank %}a{% else %}div{% endif %}>
            {% endif %}
          </div>
          <div class="list-item-{{ section.id }} list-item-mobile-{{ section.id }}">
              <p class="list-text-{{ section.id }}">{{ block.settings.text }}</p>
              <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div{% endif %} class="list-hover-marquee-{{ section.id }}">
                <div class="list-hover-track-{{ section.id }} list-hover-track-{{ block.id }}">
                  {% for item in (1..50) %}
                    <p class="list-hover-text-{{ section.id }}">{{ block.settings.hover_text }}</p>
                  {% endfor %}
                </div>
              </{% if block.settings.url != blank %}a{% else %}div{% endif %}>
              {% if use_hover_image %}
                <{% if block.settings.url != blank %}a href="{{ block.settings.url }}"{% else %}div{% endif %} class="list-hover-image-{{ section.id }} list-hover-image-{{ block.id }}">
                  {% if block.settings.image != blank %}
                    <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                  {% else %}
                    {{ 'image' | placeholder_svg_tag }}
                  {% endif %}
                </{% if block.settings.url != blank %}a{% else %}div{% endif %}>
              {% endif %}
            </div>
        {% endfor %}
      </div>
    </div>
</div>

<script>
  function initMarqueeHoverList() {
    document.querySelectorAll('.list-item-mobile-{{ section.id }}').forEach(el => {
      el.querySelector('.list-text-{{ section.id }}').addEventListener('click', () => {
        document.querySelectorAll('.list-item-mobile-{{ section.id }}').forEach(element => {
          element.classList.remove('active');
        });
        el.classList.add('active');
      })
    })
  }

  document.addEventListener('DOMContentLoaded', initMarqueeHoverList);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initMarqueeHoverList);
     document.addEventListener('shopify:section:load', initMarqueeHoverList);
  }
</script>
