{%- liquid 
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign margin_horizontal = section.settings.margin_horizontal
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign collections_per_view = section.settings.collections_per_view
  assign collections_per_view_mobile = section.settings.collections_per_view_mobile
  assign collections_gap = section.settings.collections_gap
  assign collections_gap_mobile = section.settings.collections_gap_mobile

  assign collection_radius = section.settings.collection_radius
  assign collection_border_thickness = section.settings.collection_border_thickness
  assign collection_border_color = section.settings.collection_border_color
  assign collection_ratio = section.settings.collection_ratio
  assign collection_ratio_mobile = section.settings.collection_ratio_mobile

  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign title_size = section.settings.title_size
  assign title_size_mobile = section.settings.title_size_mobile
  assign title_color = section.settings.title_color
  assign title_custom = section.settings.title_custom
  assign title_font = section.settings.title_font
  assign title_height = section.settings.title_height

  assign buttons_gap = section.settings.buttons_gap
  assign buttons_gap_mobile = section.settings.buttons_gap_mobile

  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_style = section.settings.button_style
-%}

{%- style -%}
  {{  title_font | font_face: font_display: 'swap' }}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .collection-body-{{ section.id }} {
    display: grid;
    grid-template-columns: repeat({{ collections_per_view_mobile }}, 1fr);
    gap: {{ collections_gap_mobile }}px;
  }

  .collection-collection-{{ section.id }} {
    position: relative;
  }

  .collection-media-{{ section.id }} {
    height: 100%;
  }

  .collection-media-{{ section.id }} img,
  .collection-media-{{ section.id }} svg,
  .collection-media-{{ section.id }} video,
  .collection-media-{{ section.id }} iframe {
    display: block;
    border: {{ collection_border_thickness }}px solid {{ collection_border_color }};
    border-radius: {{ collection_radius }}px;
    border-radius: {{ collection_radius }}px;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .collection-media-{{ section.id }} svg {
    background-color: #AFAFAF;
  }

  .collection-content-{{ section.id }} {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px
  }

  .collection-title-{{ section.id }} {
    position: sticky;
    top: 0;
    margin: 0px;
    margin-bottom: {{ button_size_mobile | times: 1.3 }}px;
    text-align: {{ content_align_mobile }};
    font-size: {{ title_size_mobile }}px;
    line-height: {{ title_height }}%;
    color: {{ title_color }};
    text-transform: unset;
    word-break: break-word;
  }

  .collection-buttons-{{ section.id }} {
    position: sticky;
    bottom: 15px;
    margin-top: {{ title_size_mobile | times: 1.5 }}px;
    display: flex;
    align-items: center;
    gap: {{ buttons_gap_mobile }}px;
    justify-content: {{ content_horizontal_align_mobile }};
  }

  .collection-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    width: 100%;
    max-width: fit-content;
    margin: 0;
    font-size: {{ button_size_mobile }}px;
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    transition: all 0.25s ease 0s;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
  }

  .collection-button-inner-{{ section.id }} {
    margin: 0px;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
  }

  .collection-button-{{ section.id }} svg {
    width: 14px;
    height: 14px;
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .collection-body-{{ section.id }} {
      grid-template-columns: repeat({{ collections_per_view }}, 1fr);
      gap: {{ collections_gap }}px;
    }

    .collection-content-{{ section.id }} {
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px
    }

    .collection-title-{{ section.id }} {
      {% if collections_per_view == 4 %}
      margin-bottom: 50px;
      {% else %}
      margin-bottom: {{ button_size| times: 1.5 }}px;
      {% endif %}
      font-size: {{ title_size }}px;
      text-align: {{ content_align }};
    }

    .collection-buttons-{{ section.id }} {
      {% if collections_per_view == 4 %}
      margin-top: 0px;
      {% else %}
      margin-top: {{ title_size | times: 1.5 }}px;
      {% endif %}    
      justify-content: {{ content_horizontal_align }};
      gap: {{ buttons_gap }}px;
    }

    .collection-button-{{ section.id }} {     
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }
  }

{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}px;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if title_custom %}
  <style>
    .collection-title-{{ section.id }} {
      font-family: {{ title_font.family }}, {{ title_font.fallback_families }};
      font-weight: {{ title_font.weight }};
      font-style: {{ title_font.style }};
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .collection-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if collection_ratio_mobile == "portrait" %}
  <style>
    .collection-collection-{{ section.id }} {
      aspect-ratio: 9.6/13;
    }
  </style>
{% elsif collection_ratio_mobile == "landscape" %}
  <style>
  .collection-collection-{{ section.id }} {
    aspect-ratio: 12/8;
  }
  </style>
{% elsif collection_ratio_mobile == "square" %}
  <style>
    .collection-collection-{{ section.id }} {
      aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .collection-collection-{{ section.id }} {
      aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if collection_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
      .collection-collection-{{ section.id }} {
        aspect-ratio: 9.6/13;
      }
    }

  </style>
{% elsif collection_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .collection-collection-{{ section.id }} {
        aspect-ratio: 12/8;
      }
    }  
  </style>
{% elsif collection_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .collection-collection-{{ section.id }} {
        aspect-ratio: 12/12;
      }
    }    
  </style>
{% else %}
  <style>
     @media(min-width: 1024px) {
      .collection-collection-{{ section.id }} {
        aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  <style>
    .collection-first-button-{{ block.id }} {
      color: {{ block.settings.first_button_color }};
    }

    .collection-second-button-{{ block.id }} {
      color: {{ block.settings.second_button_color }};
    }


    .collection-first-button-{{ block.id }}:hover {
      color: {{ block.settings.first_button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .collection-second-button-{{ block.id }}:hover {
      color: {{block.settings. second_button_hover_color }};
      transition: all 0.25s ease 0s;
    }  

    .collection-first-button-{{ block.id }} svg path {
      fill: {{ block.settings.first_button_color }};
      transition: all 0.25s ease 0s;
    }

    .collection-second-button-{{ block.id }} svg path {
      fill: {{ block.settings.second_button_color }};
      transition: all 0.25s ease 0s;
    }

    .collection-first-button-{{ block.id }}:hover svg path {
      fill: {{ block.settings.first_button_hover_color }};
      transition: all 0.25s ease 0s;
    }

    .collection-second-button-{{ block.id }}:hover svg path {
      fill: {{ block.settings.second_button_hover_color }};
      transition: all 0.25s ease 0s;
    }
  </style>
  {% if button_style == "non_outline" or button_style == "non_outline_arrow" %}
    <style>
      .collection-first-button-{{ block.id }} {
        background-color: {{ block.settings.first_button_bg_color }};
      }

      .collection-first-button-{{ block.id }}:hover {
        background-color: {{ block.settings.first_button_bg_hover_color }};
      }

      .collection-second-button-{{ block.id }} {
        background-color: {{ block.settings.second_button_bg_color }};
      }

      .collection-second-button-{{ block.id }}:hover {
        background-color: {{ block.settings.second_button_bg_hover_color }};
      }
    </style>
  {% elsif button_style == "outline" or button_style == "outline_arrow" %}
    <style>
      .collection-first-button-{{ block.id }} {
        background-color: {{ block.settings.first_button_bg_color }};
        border: {{ button_border_thickness }}px solid {{ block.settings.first_button_border_color }};
      }

      .collection-first-button-{{ block.id }}:hover {
        background-color: {{ block.settings.first_button_bg_hover_color }};
        border: {{ button_border_thickness }}px solid {{ block.settings.first_button_border_hover_color }};
      }

      .collection-second-button-{{ block.id }} {
        background-color: {{ block.settings.second_button_bg_color }};
        border: {{ button_border_thickness }}px solid {{ block.settings.second_button_border_color }};
      }

      .collection-second-button-{{ block.id }}:hover {
        background-color: {{ block.settings.second_button_bg_hover_color }};
        border: {{ button_border_thickness }}px solid {{ block.settings.second_button_border_hover_color }};
      }
    </style>
  {% elsif button_style == "link" %}
    <style>
      .collection-button-{{ section.id }} {
        padding: 0px !important;
      }
    </style>
  {% endif %}

{% endfor %}

<div class="section-{{ section.id }} collection-{{ section.id }}" style="background-color:{{ background_color }}; background-image: {{ background_gradient }};">
    <div class="section-{{ section.id }}-settings">
      <div class="collection-body-{{ section.id }}">
        {% for block in section.blocks %}
          <div class="collection-collection-{{ section.id }}">            
            <div class="collection-media-{{ section.id }}">
              {% if block.settings.media_type == 'image' %}                   
                {% if block.settings.image != blank %}
                  <img src="{{ block.settings.image | image_url }}" alt="{{ block.settings.image.alt }}" {% if lazy %}loading="lazy"{% endif %}>
                {% else %}
                  {{ 'image' | placeholder_svg_tag }}
                {% endif %}                   
              {% elsif block.settings.media_type == 'video' %}            
                {% assign video_id = block.settings.video.id | default: block.settings.video_url.id %}
                {% assign loop = '&loop=1&playlist=' | append: video_id %}
                {%- if block.settings.video == null and block.settings.video_url != null -%}
                {%- if block.settings.video_url.type == 'youtube' -%}
                <iframe
                  data-src="https://www.youtube.com/embed/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
                  allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                  allowfullscreen
                  class="lazy"
                ></iframe>
                {%- else -%}
                <iframe
                  data-src="https://player.vimeo.com/video/{{ video_id }}?enablejsapi=1&mute=1&muted=1&autoplay=1&controls=0&showinfo=0{{ loop }}"
                  allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                  allowfullscreen
                  class="lazy"
                ></iframe>
                {% endif %}
                {% else %}
                  <video data-src="{{ block.settings.video.sources[1].url }}" muted autoplay playsinline preload="none" loop class="lazy"></video>
                {% endif %}
              {% endif %} 
