{%- liquid
  assign padding_horizontal = section.settings.padding_horizontal
  assign padding_horizontal_mobile = section.settings.padding_horizontal_mobile
  assign padding_top = section.settings.padding_top
  assign padding_bottom = section.settings.padding_bottom
  assign border_color = section.settings.border_color
  assign border_thickness = section.settings.border_thickness
  assign margin_top = section.settings.margin_top
  assign margin_bottom = section.settings.margin_bottom
  assign margin_horizontal = section.settings.margin_horizontal
  assign margin_horizontal_mobile = section.settings.margin_horizontal_mobile
  assign background_color = section.settings.background_color
  assign background_gradient = section.settings.background_gradient
  assign full_width = section.settings.full_width
  assign content_width = section.settings.content_width
  assign lazy = section.settings.lazy
  assign section_radius = section.settings.section_radius

  assign display_options = section.settings.display_options
  assign seconds = section.settings.seconds
  assign percent = section.settings.percent
  assign hide_modal_days = section.settings.hide_modal_days

  assign button = section.settings.button
  assign button_size = section.settings.button_size
  assign button_size_mobile = section.settings.button_size_mobile
  assign button_color = section.settings.button_color
  assign button_hover_color = section.settings.button_hover_color
  assign button_custom = section.settings.button_custom
  assign button_font = section.settings.button_font
  assign button_height = section.settings.button_height
  assign button_padding_vertical = section.settings.button_padding_vertical
  assign button_padding_vertical_mobile = section.settings.button_padding_vertical_mobile
  assign button_padding_horizontal = section.settings.button_padding_horizontal
  assign button_padding_horizontal_mobile = section.settings.button_padding_horizontal_mobile
  assign button_radius = section.settings.button_radius
  assign button_border_thickness = section.settings.button_border_thickness
  assign button_border_color = section.settings.button_border_color
  assign button_border_hover_color = section.settings.button_border_hover_color
  assign button_bg_color = section.settings.button_bg_color
  assign button_bg_hover_color = section.settings.button_bg_hover_color
  assign button_align_mobile = section.settings.button_align_mobile
  assign button_align = section.settings.button_align

  assign modal_vertical_position = section.settings.modal_vertical_position
  assign modal_horizontal_position_mobile = section.settings.modal_horizontal_position_mobile
  assign modal_horizontal_position = section.settings.modal_horizontal_position
  assign modal_radius = section.settings.modal_radius
  assign modal_border_thickness = section.settings.modal_border_thickness
  assign modal_width = section.settings.modal_width
  assign modal_width_mobile = section.settings.modal_width_mobile
  assign modal_overlay = section.settings.modal_overlay
  assign modal_border_color = section.settings.modal_border_color
  assign modal_bg_color = section.settings.modal_bg_color

  assign image = section.settings.image
  assign image_mobile = section.settings.image_mobile
  assign image_width_mobile = section.settings.image_width_mobile
  assign image_width = section.settings.image_width
  assign image_ratio = section.settings.image_ratio
  assign image_ratio_mobile = section.settings.image_ratio_mobile
  assign image_hide_mobile = section.settings.image_hide_mobile

  assign content_padding_vertical = section.settings.content_padding_vertical
  assign content_padding_vertical_mobile = section.settings.content_padding_vertical_mobile
  assign content_padding_horizontal = section.settings.content_padding_horizontal
  assign content_padding_horizontal_mobile = section.settings.content_padding_horizontal_mobile
  assign content_align = section.settings.content_align
  assign content_align_mobile = section.settings.content_align_mobile

  assign content_horizontal_align = ""
  if content_align == "center"
    assign content_horizontal_align = "center"
  elsif content_align == "right"
    assign content_horizontal_align = "end"
  else
    assign content_horizontal_align = "start"
  endif

  assign content_horizontal_align_mobile = ""
  if content_align_mobile == "center"
    assign content_horizontal_align_mobile = "center"
  elsif content_align_mobile == "right"
    assign content_horizontal_align_mobile = "end"
  else
    assign content_horizontal_align_mobile = "start"
  endif

  assign close_color = section.settings.close_color
-%}

{%- style -%}
  {{  button_font | font_face: font_display: 'swap' }}

  .section-{{ section.id }} {
    border-top: solid {{ border_color }} {{ border_thickness }}px;
    border-bottom: solid {{ border_color }} {{ border_thickness }}px;
    margin-top: {{ margin_top | times: 0.75 | round: 0 }}px;
    margin-bottom: {{ margin_bottom | times: 0.75 | round: 0 }}px;
    margin-left: {{ margin_horizontal_mobile }}rem;
    margin-right: {{ margin_horizontal_mobile }}rem;
    border-radius: {{ section_radius | times: 0.6 | round: 0 }}px;
    overflow: hidden;
  }

  .section-{{ section.id }}-settings {
    margin: 0 auto;
    padding-top: {{ padding_top | times: 0.75 | round: 0 }}px;
    padding-bottom: {{ padding_bottom | times: 0.75 | round: 0 }}px;
    padding-left: {{ padding_horizontal_mobile }}rem;
    padding-right: {{ padding_horizontal_mobile }}rem;
  }

  .modal-button-{{ section.id }} {
    display: flex;
    align-items: center;
    justify-content: center;
    max-width: fit-content;
    font-size: {{ button_size_mobile }}px;
    color: {{ button_color }};
    line-height: {{ button_height }}%;
    text-align: center;
    text-transform: unset;
    text-decoration: none;
    padding: {{ button_padding_vertical_mobile }}px {{ button_padding_horizontal_mobile }}px;
    border-radius: {{ button_radius }}px;
    border: 0px;
    border: {{ button_border_thickness }}px solid {{ button_border_color }};
    background-color: transparent;
    background-color: {{ button_bg_color }};
    transition: all 0.25s ease 0s;
    box-shadow: none;
    cursor: pointer;
    font-family: Montserrat, sans-serif;
  }

  .modal-button-{{ section.id }}:hover {
    color: {{ button_hover_color }};
    border: {{ button_border_thickness }}px solid {{ button_border_hover_color }};
    background-color: {{ button_bg_hover_color }};
    transition: all 0.25s ease 0s;
  }

  .modal-popup-{{ section.id }} {
    width: 100vw;
    height: 100%;
    top: 0;
    left: 0;
    position: fixed;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: {{ modal_horizontal_position_mobile }};
    opacity: 0;
    overflow-y: scroll;
    pointer-events: none;
    background: rgba(0, 0, 0, {{ modal_overlay | times: 0.01 }});
    padding: 10px;
  }

  .modal-popup-{{ section.id }}.active {
    opacity: 1;
    z-index: 100;
    pointer-events: all;
    transition: all 250ms ease-in 0s;
  }

  .modal-popup-inner-{{ section.id }} {
    position: relative;
    width: 100%;
    max-width: {{ modal_width_mobile }}%;
    border: {{ modal_border_thickness }}px solid {{ modal_border_color }};
    background-color: {{ modal_bg_color }};
    border-radius: {{ modal_radius }}px;
    display: flex;
    flex-direction: column;
    height: fit-content;
    overflow-y: scroll;
    scrollbar-width: none;
  }

  .modal-popup-left-{{ section.id }} {
   height: 100%;
    width: 100%;
  }

  .modal-popup-image-{{ section.id }} {
    margin: 0px auto;
    height: 100%;
    width: 100%;
    max-width: {{ image_width_mobile }}%;
  }

  .modal-popup-image-{{ section.id }} img,
  .modal-popup-image-{{ section.id }} svg {
    display: block;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .modal-popup-image-{{ section.id }} svg {
    background-color: #DEDEDE;
  }

  .modal-popup-image-{{ section.id }} .modal-popup-mobile-image {
    display: block;
  }

  .modal-popup-image-{{ section.id }} .modal-popup-desktop-image {
    display: none;
  }

  .modal-popup-content-{{ section.id }} {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: {{ content_padding_vertical_mobile }}px {{ content_padding_horizontal_mobile }}px;
  } 

  .modal-popup-close-{{ section.id }} {
    position: absolute;;
    top: 10px;
    right: 8px;
    box-shadow: none;
    background-color: transparent;
    border: 0px;
    cursor: pointer;
  }

  .modal-popup-close-{{ section.id }} svg {
    display: block;
    width: 24px;
    height: 24px;
  }

  .modal-popup-close-{{ section.id }} svg path {
    fill: {{ close_color }};
  }

  .modal-popup-buttons-{{ section.id }} {
    display: flex;
    align-items: center;
    flex-wrap: wrap;
    gap: 16px;
    justify-content: {{ content_horizontal_align_mobile }};
  }

  @media(min-width: 1024px) {

    .section-{{ section.id }} {
      margin-top: {{ margin_top }}px;
      margin-bottom: {{ margin_bottom }}px;
      margin-left: {{ margin_horizontal }}rem;
      margin-right: {{ margin_horizontal }}rem;
      border-radius: {{ section_radius }}px;
    }

    .section-{{ section.id }}-settings {
      padding: 0 5rem;
      padding-top: {{ padding_top }}px;
      padding-bottom: {{ padding_bottom }}px;
      padding-left: {{ padding_horizontal }}rem;
      padding-right: {{ padding_horizontal }}rem;
    }

    .modal-button-{{ section.id }} {
      padding: {{ button_padding_vertical }}px {{ button_padding_horizontal }}px;
      font-size: {{ button_size }}px;
    }

    .modal-popup-{{ section.id }} {
      justify-content: {{ modal_vertical_position }};
      align-items: {{ modal_horizontal_position }};
    }

    .modal-popup-inner-{{ section.id }} {
      display: grid;
      align-items: stretch;
      overflow: hidden;
      max-width: {{ modal_width }}%;
      grid-template-columns: {{ image_width }}% {{ 100 | minus: image_width }}%;
    }

    .modal-popup-image-{{ section.id }} {
      max-width: 100%;
    }

    .modal-popup-image-{{ section.id }} .modal-popup-mobile-image {
      display: none;
    }

     .modal-popup-image-{{ section.id }} .modal-popup-desktop-image {
      display: block;
    }

    .modal-popup-content-{{ section.id }} {
      padding: {{ content_padding_vertical }}px {{ content_padding_horizontal }}px;
    }

    .modal-popup-buttons-{{ section.id }} {
      justify-content: {{ content_horizontal_align }};
    }
  }
{%- endstyle -%}

{% unless full_width %}
  <style>
    .section-{{ section.id }}-settings {
      max-width: {{ content_width }}rem;
    }
  </style>
{% endunless %}

{% if margin_horizontal_mobile > 0 %}
  <style>
    .section-{{ section.id }} {
      border-left: solid {{ border_color }} {{ border_thickness }}px;
      border-right: solid {{ border_color }} {{ border_thickness }}px;
    }

    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: 0px;
        border-right: 0px;
      }
    }
  </style>
{% endif %}

{% if margin_horizontal > 0 %}
  <style>
    @media(min-width: 1024px) {
      .section-{{ section.id }} {
        border-left: solid {{ border_color }} {{ border_thickness }}px;
        border-right: solid {{ border_color }} {{ border_thickness }}px;
      }
    }
  </style>
{% endif %}

{% if button_custom %}
  <style>
    .modal-button-{{ section.id }} {
      font-family: {{ button_font.family }}, {{ button_font.fallback_families }};
      font-weight: {{ button_font.weight }};
      font-style: {{ button_font.style }};
    }
  </style>
{% endif %}

{% if image_ratio_mobile == "portrait" %}
  <style>
    .modal-popup-image-{{ section.id }} {
     aspect-ratio: 9.6/12;
    }
  </style>
{% elsif image_ratio_mobile == "landscape" %}
  <style>
    .modal-popup-image-{{ section.id }}{
     aspect-ratio: 12/9;
    }
  </style>
{% elsif image_ratio_mobile == "square" %}
  <style>
    .modal-popup-image-{{ section.id }} {
     aspect-ratio: 12/12;
    }
  </style>
{% else %}
  <style>
    .modal-popup-image-{{ section.id }} {
     aspect-ratio: auto;
    }
  </style>
{% endif %}

{% if image_ratio == "portrait" %}
  <style>
    @media(min-width: 1024px) {
     .modal-popup-image-{{ section.id }} {
       aspect-ratio: 9.6/12;
      }
    }
  </style>
{% elsif image_ratio == "landscape" %}
  <style>
    @media(min-width: 1024px) {
      .modal-popup-image-{{ section.id }}{
       aspect-ratio: 12/9;
      }
    }
  </style>
{% elsif image_ratio == "square" %}
  <style>
    @media(min-width: 1024px) {
      .modal-popup-image-{{ section.id }} {
       aspect-ratio: 12/12;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
      .modal-popup-image-{{ section.id }} {
       aspect-ratio: auto;
      }
    }
  </style>
{% endif %}

{% if image_hide_mobile %}
  <style>
    .modal-popup-left-{{ section.id }} {
      display: none;
    }

    @media(min-width: 1024px) {
      .modal-popup-left-{{ section.id }} {
        display: block;
      }
    }
  </style>
{% endif %} 


{% if button_align_mobile == "center" %}
  <style>
    .modal-button-{{ section.id }} {
      margin-left: auto;
      margin-right: auto;
    }
  </style>
{% elsif button_align_mobile == "right" %}
  <style>
    .modal-button-{{ section.id }} {
      margin-left: auto;
    }
  </style>
{% endif %}

{% if button_align == "center" %}
  <style>
    @media(min-width: 1024px) {
     .modal-button-{{ section.id }} {
        margin-left: auto;
        margin-right: auto;
      }
    }
  </style>
{% elsif button_align == "right" %}
  <style>
    @media(min-width: 1024px) {
     .modal-button-{{ section.id }} {
        margin-left: auto;
        margin-right: 0px;
      }
    }
  </style>
{% else %}
  <style>
    @media(min-width: 1024px) {
     .modal-button-{{ section.id }} {
        margin-left: 0px;
        margin-right: 0px;
      }
    }
  </style>
{% endif %}

{% for block in section.blocks %}
  {% case block.type %}   
    {% when 'sub_heading' %}      
      <style>
        {{  block.settings.sub_heading_font | font_face: font_display: 'swap' }}    

        .modal-popup-subheading-{{ block.id }} {
          width: 100%;
          text-align: {{ content_align_mobile }};
          margin-top: {{ block.settings.sub_heading_mt_mobile }}px;
        }

        .modal-popup-subheading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.sub_heading_size_mobile }}px;
          color: {{ block.settings.sub_heading_color }};
          line-height: {{ block.settings.sub_heading_height }}%;
          word-break: break-word;
          font-weight: 400;
          text-transform: unset;
        }

        @media(min-width: 1024px) {          
          .modal-popup-subheading-{{ block.id }} {
            text-align: {{ content_align }};
            margin-top: {{ block.settings.sub_heading_mt }}px;
          }

          .modal-popup-subheading-{{ block.id }} * {
            font-size: {{ block.settings.sub_heading_size }}px;
          }
        }
      </style>

      {% if block.settings.sub_heading_custom %}
        <style>
          .modal-popup-subheading-{{ block.id }} * {
            font-family: {{ block.settings.sub_heading_font.family }}, {{ block.settings.sub_heading_font.fallback_families }};
            font-weight: {{ block.settings.sub_heading_font.weight }};
            font-style: {{ block.settings.sub_heading_font.style }};
          }
        </style>
      {% endif %}
    {% when 'heading' %}     
      <style>       
        {{  block.settings.heading_font | font_face: font_display: 'swap' }}    
        .modal-popup-heading-{{ block.id }} {
          width: 100%;
          text-align: {{ content_align_mobile }};
          margin-top: {{ block.settings.heading_mt_mobile }}px;
        }

        .modal-popup-heading-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.heading_size_mobile }}px;
          color: {{ block.settings.heading_color }};
          line-height: {{ block.settings.heading_height }}%;
          word-break: break-word;
          font-weight: 700;
          text-transform: unset;
        }
        @media(min-width: 1024px) {

          .modal-popup-heading-{{ block.id }} {
            text-align: {{ content_align }};
            margin-top: {{ block.settings.heading_mt }}px;
          }

          .modal-popup-heading-{{ block.id }} * {
            font-size: {{ block.settings.heading_size }}px;
          }
        }
      </style> 

      {% if block.settings.heading_custom %}
        <style>
          .modal-popup-heading-{{ block.id }} * {
            font-family: {{ block.settings.heading_font.family }}, {{ block.settings.heading_font.fallback_families }};
            font-weight: {{ block.settings.heading_font.weight }};
            font-style: {{ block.settings.heading_font.style }};
          }
        </style>
      {% endif %}
    {% when 'disclaimer' %}    
      <style>     
        {{  block.settings.disclaimer_font | font_face: font_display: 'swap' }}   

        .modal-popup-disclaimer-{{ block.id }} {
          width: 100%;
          margin-top: {{ block.settings.disclaimer_mt_mobile }}px;
          text-align: {{ content_align_mobile }};
        }

        .modal-popup-disclaimer-{{ block.id }} * {
          margin: 0;
          font-size: {{ block.settings.disclaimer_size_mobile }}px;
          color: {{ block.settings.disclaimer_color }};
          line-height: {{ block.settings.disclaimer_height }}%;
          word-break: break-word;
          text-transform: unset;
        }
        @media(min-width: 1024px) {
          .modal-popup-disclaimer-{{ block.id }} {
            margin-top: {{ block.settings.disclaimer_mt }}px;
            text-align: {{ content_align }};
          }

          .modal-popup-disclaimer-{{ block.id }} * {
            font-size: {{ block.settings.disclaimer_size }}px;
          }
        }
      </style>   

      {% if block.settings.disclaimer_custom %}
        <style>
          .modal-popup-disclaimer-{{ block.id }} * {
            font-family: {{ block.settings.disclaimer_font.family }}, {{ block.settings.disclaimer_font.fallback_families }};
            font-weight: {{ block.settings.disclaimer_font.weight }};
            font-style: {{ block.settings.disclaimer_font.style }};
          }
        </style>
      {% endif %}

    {% when 'button' %}  
      <style>
        {{  block.settings.button_font | font_face: font_display: 'swap' }}

        .modal-popup-button-{{ block.id }} {
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          gap: 10px;
          width: 100%;
          margin: 0;
          margin-top: {{ block.settings.button_mt_mobile }}px;
          font-size: {{ block.settings.button_size_mobile }}px;
          color: {{ block.settings.button_color }};
          line-height: {{ block.settings.button_height }}%;
          text-align: center;
          text-transform: unset;
          text-decoration: none;
          padding: {{ block.settings.button_padding_vertical_mobile }}px {{ block.settings.button_padding_horizontal_mobile }}px;
          border-radius: {{ block.settings.button_radius }}px;
          transition: all 0.3s ease 0s;
          background-color: transparent;
          border: 0px;
          cursor: pointer;
          font-weight: 700;
        }

        .modal-popup-button-inner-{{ block.id }} {
          margin: 0px;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 10px;
          text-decoration: none;
        }

        .modal-popup-button-{{ block.id }}:hover {
          color: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        .modal-popup-button-{{ block.id }} svg {
          width: 14px;
          height: 14px;
        }

        .modal-popup-button-{{ block.id }} svg path {
          fill: {{ block.settings.button_color }};
          transition: all 0.25s ease 0s;
        }

        .modal-popup-button-{{ block.id }}:hover svg path {
          fill: {{ block.settings.button_hover_color }};
          transition: all 0.25s ease 0s;
        }

        @media (min-width: 1024px) {
          .modal-popup-button-{{ block.id }} {
            margin-top: {{ block.settings.button_mt }}px;
            padding: {{ block.settings.button_padding_vertical }}px {{ block.settings.button_padding_horizontal }}px;
            font-size: {{ block.settings.button_size }}px;
          }
        }
      </style>

      {% if block.settings.use_shadow %}
        <style>
          .modal-popup-button-{{ block.id }}:hover {
            -webkit-box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
            -moz-box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
            box-shadow: 0px 0px 8px 2px {{ block.settings.button_bg_color }};
          }
        </style>
      {% endif %}  

      {% if block.settings.button_custom %}
        <style>
          .modal-popup-button-{{ block.id }} {
            font-family: {{ block.settings.button_font.family }}, {{ block.settings.button_font.fallback_families }};
            font-weight: {{ block.settings.button_font.weight }};
            font-style: {{ block.settings.button_font.style }};
          }
        </style>
      {% endif %}

      {% if block.settings.button_style == "non_outline" or block.settings.button_style == "non_outline_arrow" %}
        <style>
          .modal-popup-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
          }

          .modal-popup-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "outline" or block.settings.button_style == "outline_arrow" %}
        <style>          
          .modal-popup-button-{{ block.id }} {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{  block.settings.button_border_color }};
          }

          .modal-popup-button-{{ block.id }}:hover {
            transition: all 0.3s ease 0s;
            background-color: {{  block.settings.button_bg_hover_color }};
            border: {{  block.settings.button_border_thickness }}px solid {{ block.settings. button_border_hover_color }};
          }
        </style>
      {% elsif block.settings.button_style == "link" %}
        <style>
          .modal-popup-button-{{ block.id }} {
            padding: 0px !important;
            -webkit-box-shadow: none !important;
            -moz-box-shadow: none !important;
            box-shadow: none !important;
          }
        </style>
      {% endif %}  
    {% when 'promocode' %}
      <style>
        {{  block.settings.promocode_font | font_face: font_display: 'swap' }}
        {{  block.settings.message_font | font_face: font_display: 'swap' }}        

        .modal-popup-promocode-{{ block.id }} {
          position: relative;
          display: flex;
          align-items: center;
          justify-content: center;
          max-width: fit-content;
          flex-wrap: wrap;
          gap: 10px;
          cursor: pointer;
          margin-top: {{ block.settings.promocode_mt_mobile }}px;
          padding: {{ block.settings.promocode_padding_vertical_mobile }}px {{ block.settings.promocode_padding_horizontal_mobile }}px;
          border: {{ block.settings.promocode_border_thickness }}px {{ block.settings.promocode_border_style }} {{ block.settings.promocode_border_color }};
          border-radius: {{ block.settings.promocode_radius }}px;
          background-color: {{ block.settings.promocode_bg_color }};
        }

        .modal-popup-promocode-text-{{ block.id }} {
          margin: 0;
          font-size: {{ block.settings.promocode_size_mobile }}px;
          color: {{ block.settings.promocode_color }};
          line-height: {{ block.settings.promocode_height }}%;
          word-break: break-word;
          text-transform: unset;
        }

        .modal-popup-promocode-icon-{{ block.id }} {
          flex-shrink: 0;
          width: {{ block.settings.promocode_icon_size_mobile }}px;
          height: {{ block.settings.promocode_icon_size_mobile }}px;
        }

        .modal-popup-promocode-icon-{{ block.id }} img,
        .modal-popup-promocode-icon-{{ block.id }} svg {
          display: block;
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .modal-popup-promocode-icon-{{ block.id }} svg path {
          fill: {{ block.settings.promocode_icon_color }};
        }

        .modal-popup-message-{{ section.id }} {
          opacity: 0;
          pointer-events: none;
          position: absolute;
          top: 0;
          left: 50%;
          max-width: fit-content;
          transform: translateY(-1rem) translateX(-50%);
          z-index: 2;
          max-width: fit-content;
          background-color: {{ block.settings.message_bg_color }};
          padding: {{ block.settings.message_padding_vertical_mobile }}px {{ block.settings.message_padding_horizontal_mobile }}px;
          border: {{ block.settings.message_border_thickness }}px solid {{ block.settings.message_border_color }};
          border-radius: {{ block.settings.message_radius }}px;
          text-align: center;
          margin-top: 20px;
          transition: all 0.25s ease 0s;
        }

        .modal-popup-message-{{ section.id }} * {
          margin: 0;    
          font-size: {{ block.settings.message_size_mobile }}px;
          color: {{ block.settings.message_color }};
          line-height: {{ block.settings.message_height }}%;   
          text-transform: unset;
          text-decoration: none;
          word-break: break-word;
        }

        .modal-popup-message-{{ section.id }}.active {
          opacity: 1;
          pointer-events: all;
          transform: translateY(-6rem) translateX(-50%);
          transition: all 0.25s ease 0s;
        }


        @media(min-width: 1024px) {
          .modal-popup-promocode-{{ block.id }} {
            margin-top: {{ block.settings.promocode_mt }}px;
            padding: {{ block.settings.promocode_padding_vertical }}px {{ block.settings.promocode_padding_horizontal }}px;
          }

          .modal-popup-promocode-text-{{ block.id }} {
            font-size: {{ block.settings.promocode_size }}px;
          }

          .modal-popup-promocode-icon-{{ block.id }} {
            width: {{ block.settings.promocode_icon_size }}px;
            height: {{ block.settings.promocode_icon_size }}px;
          }

          .modal-popup-message-{{ section.id }} {
            padding: {{ block.settings.message_padding_vertical }}px {{ block.settings.message_padding_horizontal }}px;
          }

          .modal-popup-message-{{ section.id }} * {    
            font-size: {{ block.settings.message_size }}px;
          }
        }
      </style>  

      {% if block.settings.promocode_custom %}
