<script>
  function initModalPopup2() {
    setTimeout(() => {
      const discountCodes = document.querySelectorAll(".modal-popup-promocode-text-{{ section.id }}");
      const copyIcons = document.querySelectorAll(".modal-popup-promocode-{{ section.id }}");
      const message = document.querySelector(".modal-popup-message-{{ section.id }}");

      function copyToClipboard(text) {
        // Create temporary textarea
        const tempInput = document.createElement("textarea");
        tempInput.value = text;
        document.body.appendChild(tempInput);

        // Select and copy text
        tempInput.select();
        document.execCommand("copy");

        // Remove temporary element
        document.body.removeChild(tempInput);

        // Show message
        message.classList.add('active');

        // Hide message after delay
        setTimeout(() => {
          message.classList.remove('active');
        }, 1000);
      }

      discountCodes.forEach(codeElement => {
        codeElement.addEventListener("click", () => {
          copyToClipboard(codeElement.innerText);
        });
      });

      copyIcons.forEach((copyIcon, index) => {
        copyIcon.addEventListener("click", () => {
          const codeElement = discountCodes[index];
          if (codeElement) {
            copyToClipboard(codeElement.innerText);
          }
        });
      });
    }, 400);

    const toggle = document.querySelector('.modal-button-{{ section.id }}');
    const modal = document.querySelector('.modal-popup-{{ section.id }}');
    const close = document.querySelector('.modal-popup-close-{{ section.id }}');
    const modalKey = 'modalDisplayedAt_{{ section.id }}';

    if(toggle) {
      toggle.addEventListener('click', () => {
        if(modal) {
          {% if hide_modal_days > 0 %}
            if(hasModalCookieExpired()) {
                modal.classList.add('active');
                document.body.style.overflow = 'hidden';
                document.body.style.transform = 'none';
                setModalCookie();
            }
          {% else %}
            modal.classList.add('active');
            document.body.style.overflow = 'hidden';
            document.body.style.transform = 'none';
          {% endif %}
        }
      })
    }

    if(close) {
       close.addEventListener('click', () => {
        document.body.style.overflow = '';
        document.body.style.transform = '';
        modal.classList.remove('active');
      });
    }

    document.addEventListener('click', (e) => {
        if (e.target == modal) {
          document.body.style.overflow = '';
          document.body.style.transform = '';
          modal.classList.remove('active');
        }
    });

    {% if display_options == "after_seconds" %}
      setTimeout(function() {
          if(modal) {
            {% if hide_modal_days > 0 %}
              if (hasModalCookieExpired()) {
                  modal.classList.add('active');
                  document.body.style.overflow = 'hidden';
                  document.body.style.transform = 'none';
                  setModalCookie();
              }
            {% else %}
              modal.classList.add('active');
              document.body.style.overflow = 'hidden';
              document.body.style.transform = 'none';
            {% endif %}
          }
      }, {{ seconds | times: 1000 }});
    {% endif %}

    {% if display_options == "after_scrolling" %}
      let hasShownScrollMessage = false;

      window.addEventListener("scroll", function() {
          if (!hasShownScrollMessage) {
              var scrollPosition = window.scrollY;
              var windowHeight = window.innerHeight;
              var documentHeight = document.body.clientHeight;

              var scrollPercentage = (scrollPosition + windowHeight) / documentHeight * 100;

              if (scrollPercentage >= 50) {
                {% if hide_modal_days > 0 %}
                  if (hasModalCookieExpired()) {
                      modal.classList.add("active");
                      document.body.style.overflow = 'hidden';
                      document.body.style.transform = 'none';
                      hasShownScrollMessage = true;
                      setModalCookie();
                  }
                {% else %}
                  modal.classList.add("active");
                  document.body.style.overflow = 'hidden';
                  document.body.style.transform = 'none';
                  hasShownScrollMessage = true;
                {% endif %}
              }
          }
      });
    {% endif %}

    {% if display_options == "mouse_close" %}
      let hasShownScrollMessage = false;
      document.addEventListener("mouseleave", function(event) {
          if (!hasShownScrollMessage) {
            var modal = document.querySelector(".modal-popup-{{ section.id }}");
            if (modal) {
                {% if hide_modal_days > 0 %}
                  if(hasModalCookieExpired()) {
                      modal.classList.add("active");
                      document.body.style.overflow = 'hidden';
                      document.body.style.transform = 'none';
                      hasShownScrollMessage = true;
                      setModalCookie();
                  }
                {% else %}
                  modal.classList.add("active");
                  document.body.style.overflow = 'hidden';
                  document.body.style.transform = 'none';
                  hasShownScrollMessage = true;
                {% endif %}
            }
          }
      });
    {% endif %}

    {% if hide_modal_days > 0 %}

      function hasModalCookieExpired() {
          const lastDisplayed = localStorage.getItem(modalKey);
          if (lastDisplayed) {
              const lastDisplayedDate = new Date(lastDisplayed);
              const now = new Date();
              const daysPassed = (now - lastDisplayedDate) / (1000 * 60 * 60 * 24);
              return daysPassed >= {{ hide_modal_days }};
          }
          return true;
      }

      function setModalCookie() {
        localStorage.setItem(modalKey, new Date().toISOString());
      }

    {% endif %}

    {% for block in section.blocks %}
      {% if block.type == "timer" %}  
          function startTimer() {
            const second = 1000,
              minute = second * 60,
              hour = minute * 60,
              day = hour * 24,
              wrapper = document.querySelector(
                '.countdown-timer-{{ block.id }}'
              ),
              countDown = new Date('{{  block.settings.year }}-{{  block.settings.month }}-{{  block.settings.day }}T{{  block.settings.hour }}:{{  block.settings.minute }}:00Z').getTime();

            console.log('start timer, end date:' + countDown)
            const interval = setInterval(function () {
              let now = new Date().getTime(),
                distance = countDown - now,
                days = Math.floor(distance / day),
                hours = Math.floor((distance % day) / hour),
                minutes = Math.floor((distance % hour) / minute),
                seconds = Math.floor((distance % minute) / second);

              (wrapper.querySelector('.js-timer-days').innerText = (
                days
              )),
                (wrapper.querySelector('.js-timer-hours').innerText = (
                  hours
                )),
                (wrapper.querySelector('.js-timer-minutes').innerText = (
                  minutes
                )),
                (wrapper.querySelector('.js-timer-seconds').innerText = (
                  seconds
                ));

              if (distance < 0) {
                wrapper.querySelector('.js-timer-days').innerText = '00';
                wrapper.querySelector('.js-timer-hours').innerText = '00';
                wrapper.querySelector('.js-timer-minutes').innerText = '00';
                wrapper.querySelector('.js-timer-seconds').innerText = '00';
                {% if block.settings.hide_timer_on_completed %}
                  wrapper.style.display = 'none';
                {% endif %}
                document.querySelector(
                  '.countdown-timer-completed-text-{{ block.id }}'
                ).style.display = 'block';

                clearInterval(interval);
              }

            }, second);
            document.addEventListener('shopify:section:unload', function (event) {
              if (event.detail.sectionId === '{{ section.id }}') clearInterval(interval);
            });
          }
          startTimer()
          if (window.Shopify && Shopify.designMode) {
            document.addEventListener('shopify:section:load', function (event) {
              if (event.detail.sectionId === '{{ section.id }}') {
                startTimer();
              }
            });
          }
      {% endif %}
    {% endfor %}
  }

  document.addEventListener('DOMContentLoaded', initModalPopup2);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initModalPopup2);
     document.addEventListener('shopify:section:load', initModalPopup2);
  }
</script>
