<div class="modal-popup-{{ section.id }}">
  <div class="modal-popup-inner-{{ section.id }}">
    <div class="modal-popup-left-{{ section.id }}">
      <div class="modal-popup-image-{{ section.id }}">
        {% if image != blank %}
          {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
          {% if image_mobile != blank %}          
              {{ image | image_url: width: 700 | image_tag:
                loading: lazy_load_setting,
                sizes: sizes,              
                widths: '165, 360, 535, 750, 1070, 1500',
                class: 'modal-popup-desktop-image'
              }}
          {% else %}           
              {{ image | image_url: width: 700 | image_tag:
                loading: lazy_load_setting,
                sizes: sizes,              
                widths: '165, 360, 535, 750, 1070, 1500'
              }}
          {% endif %}
        {% else %}
          {{ 'image' | placeholder_svg_tag }}
        {% endif %} 
        {% if image_mobile != blank %}
          {%- capture sizes -%}(min-width: {{ content_width }}px) {{ content_width | minus: 100 | divided_by: 2 }}px, (min-width: 750px) calc((100vw - 130px) / 2), calc((100vw - 50px) / 2){%- endcapture -%}
            {{ image_mobile | image_url: width: 700 | image_tag:
              loading: lazy_load_setting,
              sizes: sizes,              
              widths: '165, 360, 535, 750, 1070, 1500',
              class: 'modal-popup-mobile-image'
