    <div class="modal-popup-content-{{ section.id }}">

      {% for block in section.blocks %}
        {% case block.type %}
          {% when 'sub_heading' %}
            {% if block.settings.sub_heading != blank %}
              <div class="modal-popup-subheading-{{ block.id }} modal-popup-subheading-{{ section.id }}">{{ block.settings.sub_heading }}</div>
            {% endif %}
          {% when 'heading' %}
            {% if block.settings.heading != blank %}
              <div class="modal-popup-heading-{{ block.id }} modal-popup-heading-{{ section.id }}">{{ block.settings.heading }}</div>
