<script>
  function initModalPopup3() {
    setTimeout(() => {
      let thanksText = document.querySelector('.modal-popup-newsletter-thanks-text-{{ section.id }}');
      let submitButton = document.querySelector('.modal-popup-newsletter-submit-{{ section.id }}');
      let row = document.querySelector('.modal-popup-newsletter-row-{{ section.id }}');
      let image = document.querySelector('.modal-popup-left-{{ section.id }}');
      let modalInner = document.querySelector('.modal-popup-inner-{{ section.id }}');
      let heading = document.querySelector('.modal-popup-heading-{{ section.id }}');
      let subHeading = document.querySelector('.modal-popup-subheading-{{ section.id }}');
      let textBlock = document.querySelector('.modal-popup-text-{{ section.id }}');
      let disclaimer = document.querySelector('.modal-popup-disclaimer-{{ section.id }}');
      let thanksHeading = document.querySelector('.modal-popup-newsletter-heading-thanks-text-{{ section.id }}');

      const url = window.location.href;
      if (url.includes('customer_posted=true')) {
        if(thanksText) { 
          thanksText.classList.add('active');
        }

         if(thanksHeading) { 
          thanksHeading.classList.add('active');
        }
        if(heading) { 
          heading.classList.add('hide');
        }
        if(subHeading) { 
          subHeading.classList.add('hide');
        }
        if(textBlock) { 
          textBlock.classList.add('hide');
        }
        if(disclaimer) { 
          disclaimer.classList.add('hide');
        }
        if(modalInner) {
          modalInner.classList.add('newsletter-success');
        }
        if(image) {
          image.classList.add('hide');  
        }
        if(row) {
          row.classList.add('hide');
        }
        if(submitButton) {
          submitButton.setAttribute('disabled', true);
        }
      }
    }, 400);

    const toggle = document.querySelector('.modal-button-{{ section.id }}');
    const modal = document.querySelector('.modal-popup-{{ section.id }}');
    const buttonClose = document.querySelector('.modal-button-close-{{ section.id }}');
    const close = document.querySelector('.modal-popup-close-{{ section.id }}');
    const modalKey = 'modalDisplayedAt_{{ section.id }}';    

    if(toggle) {
      buttonClose.addEventListener('click', (e) => {
        e.preventDefault();
         e.stopPropagation();
        toggle.style.display = 'none';
      })

      toggle.addEventListener('click', () => {
      if(modal) {
          {% if hide_modal_days > 0 and display_options != 'click' %}
            if(hasModalCookieExpired()) {
                modal.classList.add('active');
                document.body.style.overflow = 'hidden';
                document.body.style.transform = 'none';
                setModalCookie();
            }
          {% else %}
            modal.classList.add('active');
            document.body.style.overflow = 'hidden';
            document.body.style.transform = 'none';
          {% endif %}
        }
      })
    }

    if(close) {
       close.addEventListener('click', () => {
        document.body.style.overflow = '';
        document.body.style.transform = '';
        modal.classList.remove('active');
      });
    }

    document.addEventListener('click', (e) => {
        if (e.target == modal) {
          document.body.style.overflow = '';
          document.body.style.transform = '';
          modal.classList.remove('active');
        }
    });

    {% if display_options == "after_seconds" %}
      setTimeout(function() {
          if(modal) {
            {% if hide_modal_days > 0 %}
              if (hasModalCookieExpired()) {
                  modal.classList.add('active');
                  document.body.style.overflow = 'hidden';
                  document.body.style.transform = 'none';
                  setModalCookie();
              }
            {% else %}
              modal.classList.add('active');
              document.body.style.overflow = 'hidden';
              document.body.style.transform = 'none';
            {% endif %}
          }
      }, {{ seconds | times: 1000 }});
    {% endif %}

    {% if display_options == "after_scrolling" %}
      let hasShownScrollMessage = false;

      window.addEventListener("scroll", function() {
          if (!hasShownScrollMessage) {
              var scrollPosition = window.scrollY;
              var windowHeight = window.innerHeight;
              var documentHeight = document.body.clientHeight;

              var scrollPercentage = (scrollPosition + windowHeight) / documentHeight * 100;

              if (scrollPercentage >= {{ percent }}) {
                {% if hide_modal_days > 0 %}
                  if (hasModalCookieExpired()) {
                      modal.classList.add("active");
                      document.body.style.overflow = 'hidden';
                      document.body.style.transform = 'none';
                      hasShownScrollMessage = true;
                      setModalCookie();
                  }
                {% else %}
                  modal.classList.add("active");
                  document.body.style.overflow = 'hidden';
                  document.body.style.transform = 'none';
                  hasShownScrollMessage = true;
                {% endif %}
              }
          }
      });
    {% endif %}

    {% if display_options == "mouse_close" %}
      let hasShownScrollMessage = false;
      document.addEventListener("mouseleave", function(event) {
          if (!hasShownScrollMessage) {
            var modal = document.querySelector(".modal-popup-{{ section.id }}");
            if (modal) {
                {% if hide_modal_days > 0 %}
                  if(hasModalCookieExpired()) {
                      modal.classList.add("active");
                      document.body.style.overflow = 'hidden';
                      document.body.style.transform = 'none';
                      hasShownScrollMessage = true;
                      setModalCookie();
                  }
                {% else %}
                  modal.classList.add("active");
                  document.body.style.overflow = 'hidden';
                  document.body.style.transform = 'none';
                  hasShownScrollMessage = true;
                {% endif %}
            }
          }
      });
    {% endif %}

    {% if hide_modal_days > 0 %}

      function hasModalCookieExpired() {
          const lastDisplayed = localStorage.getItem(modalKey);
          if (lastDisplayed) {
              const lastDisplayedDate = new Date(lastDisplayed);
              const now = new Date();
              const daysPassed = (now - lastDisplayedDate) / (1000 * 60 * 60 * 24);
              return daysPassed >= {{ hide_modal_days }};
          }
          return true;
      }

      function setModalCookie() {
        localStorage.setItem(modalKey, new Date().toISOString());
      }

    {% endif %}
  }

  document.addEventListener('DOMContentLoaded', initModalPopup3);

  if (Shopify.designMode) {
     document.addEventListener('shopify:section:unload', initModalPopup3);
     document.addEventListener('shopify:section:load', initModalPopup3);
  }
</script>
